INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (340, '西安城乡融合要素交易市场有限公司简介', 999999, NULL, 'xagly', '181', '2023-12-21 17:19:46', '2024-08-08 15:01:51', '2023-12-21 17:19:46', '2', 'xagly', '西安城乡融合要素交易市场', 'xagly', '800,801', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">西安城乡融合要素交易市场有限公司</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">简 &nbsp;介</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安城乡融合要素交易市场有限公司是全国首家以&ldquo;城乡融合要素交易市场&rdquo;命名的综合性城乡要素交易服务平台，于2023年11月经西安市委、市政府批准成立，是西安农业投资（集团）有限公司全资子公司。城乡融合要素交易市场在市委、市政府领导下，在西安市城乡融合要素交易市场监督管理委员会指导下，以农村集体产权制度改革为重点，以兼顾公益性和功能性为原则，以壮大村集体经济、带动农民增收致富和推动城乡融合发展为目标，推动农村要素资源由分散向集中、由小平台向大平台集聚，不断提升农村资源配置的信息化、标准化、规范化水平，促进各类要素在城乡之间平等交换、双向流动，实现我市更高水平的乡村振兴，助推都市型农业强市建设。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 公司内设综合管理部、交易部、信息部、市场拓展部4个部门，主要业务包括农村资源资产产权交易、农村产业项目招商、农产品流通和农村工程、货物及服务采购类四大板块。开发创新包括担保、融资、数字化交易、城乡融合要素综合服务等各类城乡融合要素交易衍生产品，延伸业务链条，将城乡融合要素交易平台建设为具有农村金融服务功能、乡村振兴招商引资功能、大数据信息共享功能为一体的综合性城乡融合要素交易服务平台。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 公司下设长安区、灞桥区、阎良区、高陵区、临潼区（经开区）、蓝田县、周至县、西咸新区8个子公司，高新区、国际港务区2个分公司。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>经营理念：</strong>以人为本 &nbsp;善用资源 &nbsp;创造价值 &nbsp;服务三农</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>核心价值观：</strong>公开 &nbsp;公正 &nbsp;担当 &nbsp;创新</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>服务体系：</strong>采用&ldquo;市、区（县、开发区）两级交易，市、区（县、开发区）、街道（镇）、村四级服务&rdquo;的城乡融合要素交易服务体系，市、区（县、开发区）两级为城乡融合要素交易市场，街道（镇）一级为城乡融合要素交易服务中心，村一级为城乡融合要素交易服务信息站，形成全市统一的市、区（县）、街（镇）、村四级联动的网络体系和市场体系。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>管理方式：</strong>采取&ldquo;六统一&rdquo;管理方式，即统一信息系统、统一交易规则、统一交易鉴证、统一收费标准、统一交易结算、统一交易监管。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>进度安排:</strong></span></p>\n<p><span style=\"font-size: 18pt;\">1.组建期。2023年11月底前，完成西安城乡融合要素交易市场有限公司的注册及组建工作，2023年底前市级平台正式开展业务。力争完成灞桥区、阎良区、临潼区、高陵区、长安区、鄠邑区、蓝田县、周至县、西咸新区、高新区、国际港务区分（子）公司组建工作。</span></p>\n<p><span style=\"font-size: 18pt;\">2.培育期。2024年&mdash;2026年，全面完成分子公司组建工作，侧重市场培育，完善公司各项制度，深度挖掘市场，积极宣传策划，深入基层推广各类城乡融合要素流转交易的进场工作，稳步推进交易规模扩大，力争实现自主盈利。</span></p>\n<p><span style=\"font-size: 18pt;\">3.成熟期。2026年以后，要素交易市场将打造成为城乡融合要素综合服务平台，各类交易品种齐全、市场增值服务不断优化、市场活跃，并将业务延伸至西安周边市区，辐射至整个西部地区。</span></p>', '801', 11, NULL, '西安城乡融合要素交易市场有限公司是全国首家以“城乡融合要素交易市场”命名的综合性城乡要素交易服务平台', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
