INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (346, '西安市委十四届市委全面深化改革委员会第六次会议召开', 999999, '1738827518851137537', 'xagly', '181', '2023-12-24 15:38:02', '2024-08-08 15:01:51', '2023-12-24 15:38:02', '0', 'xagly', '西安城乡融合要素交易市场', '西安管理员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 9月20日，十四届市委全面深化改革委员会召开第六次会议，传达学习二十届中央深改委第二次会议精神，研究有关改革事项。</span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 省委常委、市委书记、市委深改委主任方红卫主持并讲话。市长、市委深改委副主任叶牛平，市人大常委会主任、市委深改委副主任韩松，市政协主席、市委深改委副主任王吉德，市委副书记、市委深改委副主任李婧出席会议。</span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议研究审议了《长安先导产业创新中心有限公司组建方案》和《西安城乡融合要素交易市场组建方案》，听取了鄠邑区、浐灞生态区、市市场监管局改革工作情况汇报。</span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议指出，<strong>要深入学习贯彻习近平总书记在二十届中央深改委第二次会议上的重要讲话精神，持续深化我市开放型经济、农村、碳排放双控、高校薪酬制度等重点领域改革，确保党中央要求在西安落地落细。要坚持以开放促改革、促发展，落实好中国&mdash;中亚峰会成果，扩大与共建&ldquo;一带一路&rdquo;国家和地区的合作，打造自贸试验区等高能级开放平台，高质量建设中欧班列西安集结中心，持续推动对外贸易提质增量，构建更高水平开放型经济新体制。要加快推进现代农业产业体系、生产体系和经营体系改革突破，创新农村产权交易平台，促进城乡融合发展。要大力实施&ldquo;双碳&rdquo;西安行动，积极推进碳评价试点，探索以最少碳排放实现更高质量发展的新模式。要建立以增加知识价值为导向的收入分配制度，鼓励科研机构、高校扩大薪酬分配自主权，不断激发科研技术创新活力。</strong></span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议强调，<strong>要坚决扛起助力国家高水平科技自立自强的责任担当，抢抓&ldquo;双中心&rdquo;建设重大机遇，发挥科技、人才资源优势，深化体制机制改革，完善决策机制，加快建成投资主体多元化、管理制度现代化、运行机制市场化、用人机制灵活的新型研发机构。要着眼国家战略需求，科学谋划、勇于创新，吸引更多资源、汇聚更多力量服务基础研究，瞄准行业未来发展、产业链安全加强应用类技术攻关，抢占制高点、制胜新赛道。</strong></span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议指出，<strong>城乡融合要素交易市场要突出公益性和功能性，建立完善政策体系，推动农村要素资源由分散向集中、由小平台向大平台集聚，不断提升农村资源配置的透明度和规范化水平。要充分发挥龙头企业内引外联作用，最大限度挖掘释放农村资源价值潜力，实现村集体经济发展壮大和农民稳步增收。</strong></span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议强调，<strong>要坚持用改革的思维破解发展难题，坚持目标导向、问题导向，处理好改革、发展、稳定的关系，善于发力、巧于用力，以小切口改革推动大变化。鄠邑区要依托秦岭生态和地方民俗文化资源，持续深化农文旅融合，不断提升&ldquo;诗画鄠邑&rdquo;特色品牌。浐灞生态区要探索城市开发建设与生态保护的新路径，发挥资源优势，提高市场化水平，增强发展活力。市市场监管局要不断创新，加快提高监管服务数字化、智慧化水平，更好助力市场主体发展壮大。</strong></span></p>\n<p><br /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 市委深改委委员参加会议，市级有关部门和部分区县、开发区负责同志列席会议。</span></p>', '101', 58, NULL, '西安市委十四届市委全面深化改革委员会第六次会议召开', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
