INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (399, '《陕西省实施〈中华人民共和国农村土地承包法〉办法》自4月1日起施行', 999999, NULL, 'xagly', '181', '2024-02-02 16:42:55', '2024-08-08 15:01:51', '2024-02-02 16:42:55', '0', 'xagly', '要素交易市场', '西安管理员', '100,101', '1', 0, 0, 0, '<p>1月12日，陕西省十四届人大常委会第七次会议表决通过了《陕西省实施〈中华人民共和国农村土地承包法〉办法》，《办法》 自4月1日起施行。</p>\n<p><video src=\"http://xianshi.osswjccxt.znxdcloud.com/znxd/2024/02/02/9d57da53c16040be9d6e235a81a6ce28.mp4\" controls=\"controls\" width=\"500px\" height=\"auto\" allowfullscreen=\"true\" controlslist=\"nodownload\"></video></p>', '101', 7, NULL, '《陕西省实施〈中华人民共和国农村土地承包法〉办法》自4月1日起施行', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (400, '吸引人才投身乡村建设应该怎么做？', 999999, '1753350603004428289', 'xagly', '181', '2024-02-02 17:27:07', '2024-08-08 15:01:51', '2024-02-02 17:27:07', '2', 'xagly', '要素交易市场', '西安管理员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2021年2月，中共中央办公厅、国务院办公厅印发了《关于加快推进乡村人才振兴的意见》，明确指出坚持把乡村人力资本开发放在首位，大力培养本土人才，引导城市人才下乡，推动专业人才服务乡村，吸引各类人才在乡村振兴中建功立业。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2023年，农业农村部、国家发展改革委等九部门联合印发《&ldquo;我的家乡我建设&rdquo;活动实施方案》，引导在村农民和在外老乡共建、共治、共享美好家园，促进人才、资金、技术下乡，汇聚建设宜居宜业和美乡村力量。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 人才是推动乡村振兴的领头雁，是农业产业结构调整的基石。实施乡村振兴战略，必须加大人才引进与培育力度切实打造一支懂农业、会管理和爱农村的高素质人才队伍。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 那么吸引人才投身乡村建设，到底应该怎么做？</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>政府：</strong></span></p>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>01</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>定向培养人才</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 与高校、职业学校等教育机构合作，开展乡村人才定向培养计划，为乡村地区培养具有专业技能和知识的人才。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>02</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>提供优惠政策</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 通过提供税收减免、住房补贴、创业扶持等优惠政策，吸引更多的人才到乡村地区工作和生活。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>03</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>建立人才库</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 建立乡村人才库，将乡村地区的人才资源进行整合和优化，更好地了解乡村地区的人才需求，为人才提供更好的服务和发展机会。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>04</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>加强人才培养</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 加大对乡村地区人才的培养力度，通过开展各种培训、交流活动，提高人才的技能水平和综合素质。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>05</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>完善基础设施</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 加大对乡村地区基础设施的建设力度，改善乡村地区的交通、通讯、教育、医疗等条件，提高乡村地区的吸引力。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>06</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>引导资本投入</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 通过投资项目、建设产业园区等方式，引导更多社会资本投入乡村建设，同时，可以制定相关政策，鼓励金融机构为乡村地区的发展提供融资支持。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>企业：</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>01</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>制定人才战略</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 企业根据自身的发展需要和农村地区的实际情况，制定适合的人才战略，不断明确人才引进和培养的目标、计划和措施。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>02</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>提供发展机会</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 为人才提供良好的职业发展机会，让他们在乡村地区能拥有更多的发展空间和机会，进而激发他们的工作积极性和创造力。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>03</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>建立激励机制</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 建立有效的激励机制，通过给予优秀员工奖励、晋升等方式，激发人才工作热情和积极性，提高工作效率和质量。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>04</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>提高福利待遇</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 通过提高员工的福利待遇，如提供健康保险、住房补贴等，增强企业的吸引力和竞争力，吸引更多的人才加入企业。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>05</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>加强人才培养</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 加大对人才的培训和培养力度，通过内部培训、外部培训、实践锻炼等方式，提高人才的技能水平和综合素质，为企业的发展提供更好的人才支持。</span></p>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>06</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>参与公益活动</strong></span></p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 通过参与乡村地区的社会公益活动，如扶贫、助学、环保等，增强企业的社会责任感和形象，同时也可以吸引更多的人才加入企业。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安城乡融合要素交易市场将建立完善政策体系，集中促进人才、土地、资金、技术等要素双向流动和均衡合理配置，增强城市对农村反哺能力、带动能力，充分发挥龙头企业内引外联作用，最大限度挖掘释放农村资源价值潜力，实现村集体经济发展壮大和农民稳步增收，做到立足陕西、辐射西北、成为全国高质量城乡融合发展的典范标杆。</span></p>\n</section>', '101', 32, NULL, '人才是推动乡村振兴的领头雁，是农业产业结构调整的基石。实施乡村振兴战略，必须加大人才引进与培育力度切实打造一支懂农业、会管理和爱农村的高素质人才队伍。\n\n那么吸引人才投身乡村建设，到底应该怎么做？', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (401, '2024年中央一号文件公布', 999999, '1754057897635725313', 'xagly', '181', '2024-02-04 16:21:45', '2024-08-08 15:01:51', '2024-02-04 16:21:45', '2', 'xagly', '农业农村部公报', '西安管理员', '300,301', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">中共中央 国务院</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">关于学习运用&ldquo;千村示范、万村整治&rdquo;工程经验有力有效推进乡村全面振兴的意见</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">(2024年1月1日)</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 推进中国式现代化，必须坚持不懈夯实农业基础，推进乡村全面振兴。习近平总书记在浙江工作时亲自谋划推动&ldquo;千村示范、万村整治&rdquo;工程（以下简称&ldquo;千万工程&rdquo;），从农村环境整治入手，由点及面、迭代升级，20年持续努力造就了万千美丽乡村，造福了万千农民群众，创造了推进乡村全面振兴的成功经验和实践范例。要学习运用&ldquo;千万工程&rdquo;蕴含的发展理念、工作方法和推进机制，把推进乡村全面振兴作为新时代新征程&ldquo;三农&rdquo;工作的总抓手，坚持以人民为中心的发展思想，完整、准确、全面贯彻新发展理念，因地制宜、分类施策，循序渐进、久久为功，集中力量抓好办成一批群众可感可及的实事，不断取得实质性进展、阶段性成果。做好2024年及今后一个时期&ldquo;三农&rdquo;工作，要以习近平新时代中国特色社会主义思想为指导，全面贯彻落实党的二十大和二十届二中全会精神，深入贯彻落实习近平总书记关于&ldquo;三农&rdquo;工作的重要论述，坚持和加强党对&ldquo;三农&rdquo;工作的全面领导，锚定建设农业强国目标，以学习运用&ldquo;千万工程&rdquo;经验为引领，以确保国家粮食安全、确保不发生规模性返贫为底线，以提升乡村产业发展水平、提升乡村建设水平、提升乡村治理水平为重点&middot;，强化科技和改革双轮驱动，强化农民增收举措，打好乡村全面振兴漂亮仗，绘就宜居宜业和美乡村新画卷，以加快农业农村现代化更好推进中国式现代化建设。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 一、</strong><strong>确保国家粮食安全</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（一）抓好粮食和重要农产品生产。扎实推进新一轮千亿斤粮食产能提升行动。稳定粮食播种面积，把粮食增产的重心放到大面积提高单产上，确保粮食产量保持在1.3万亿斤以上。实施粮食单产提升工程，集成推广良田良种良机良法。巩固大豆扩种成果，支持发展高油高产品种。适当提高小麦最低收购价，合理确定稻谷最低收购价。继续实施耕地地力保护补贴和玉米大豆生产者补贴、稻谷补贴政策。完善农资保供稳价应对机制，鼓励地方探索建立与农资价格上涨幅度挂钩的动态补贴办法。扩大完全成本保险和种植收入保险政策实施范围，实现三大主粮全国覆盖、大豆有序扩面。鼓励地方发展特色农产品保险。推进农业保险精准投保理赔，做到应赔尽赔。完善巨灾保险制度。加大产粮大县支持力度。探索建立粮食产销区省际横向利益补偿机制，深化多渠道产销协作。扩大油菜面积，支持发展油茶等特色油料。加大糖料蔗种苗和机收补贴力度。加强&ldquo;菜篮子&rdquo;产品应急保供基地建设，优化生猪产能调控机制，稳定牛羊肉基础生产能力。完善液态奶标准，规范复原乳标识，促进鲜奶消费。支持深远海养殖，开发森林食品。树立大农业观、大食物观，多渠道拓展食物来源，探索构建大食物监测统计体系。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二）严格落实耕地保护制度。健全耕地数量、质量、生态&ldquo;三位一体&rdquo;保护制度体系，落实新一轮国土空间规划明确的耕地和永久基本农田保护任务。改革完善耕地占补平衡制度，坚持&ldquo;以补定占&rdquo;，将省域内稳定利用耕地净增加量作为下年度非农建设允许占用耕地规模上限。健全补充耕地质量验收制度，完善后续管护和再评价机制。加强退化耕地治理，加大黑土地保护工程推进力度，实施耕地有机质提升行动。严厉打击非法占用农用地犯罪和耕地非法取土。持续整治&ldquo;大棚房&rdquo;。分类稳妥开展违规占用耕地整改复耕，细化明确耕地&ldquo;非粮化&rdquo;整改范围，合理安排恢复时序。因地制宜推进撂荒地利用，宜粮则粮、宜经则经，对确无人耕种的支持农村集体经济组织多途径种好用好。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（三）加强农业基础设施建设。坚持质量第一，优先把东北黑土地区、平原地区、具备水利灌溉条件地区的耕地建成高标准农田，适当提高中央和省级投资补助水平，取消各地对产粮大县资金配套要求，强化高标准农田建设全过程监管，确保建一块、成一块。鼓励农村集体经济组织、新型农业经营主体、农户等直接参与高标准农田建设管护。分区分类开展盐碱耕地治理改良，&ldquo;以种适地&rdquo;同&ldquo;以地适种&rdquo;相结合，支持盐碱地综合利用试点。推进重点水源、灌区、蓄滞洪区建设和现代化改造，实施水库除险加固和中小河流治理、中小型水库建设等工程。加强小型农田水利设施建设和管护。加快推进受灾地区灾后恢复重建。加强气象灾害短期预警和中长期趋势研判，健全农业防灾减灾救灾长效机制。推进设施农业现代化提升行动。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（四）强化农业科技支撑。优化农业科技创新战略布局，支持重大创新平台建设。加快推进种业振兴行动，完善联合研发和应用协作机制，加大种源关键核心技术攻关，加快选育推广生产急需的自主优良品种。开展重大品种研发推广应用一体化试点。推动生物育种产业化扩面提速。大力实施农机装备补短板行动，完善农机购置与应用补贴政策，开辟急需适用农机鉴定&ldquo;绿色通道&rdquo;。加强基层农技推广体系条件建设，强化公益性服务功能。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（五）构建现代农业经营体系。聚焦解决&ldquo;谁来种地&rdquo;问题，以小农户为基础、新型农业经营主体为重点、社会化服务为支撑，加快打造适应现代农业发展的高素质生产经营队伍。提升家庭农场和农民合作社生产经营水平，增强服务带动小农户能力。加强农业社会化服务平台和标准体系建设，聚焦农业生产关键薄弱环节和小农户，拓展服务领域和模式。支持农村集体经济组织提供生产、劳务等居间服务。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（六）增强粮食和重要农产品调控能力。健全农产品全产业链监测预警机制，强化多品种联动调控、储备调节和应急保障。优化粮食仓储设施布局，提升储备安全水平。深化&ldquo;一带一路&rdquo;农业合作。加大农产品走私打击力度。加强粮食和重要农产品消费监测分析。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（七）持续深化食物节约各项行动。弘扬节约光荣风尚，推进全链条节粮减损，健全常态化、长效化工作机制。挖掘粮食机收减损潜力，推广散粮运输和储粮新型装具。完善粮食适度加工标准。大力提倡健康饮食，健全部门监管、行业自律、社会监督相结合的监管体系，坚决制止餐饮浪费行为。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 二、确保不发生规模性返贫</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（八）落实防止返贫监测帮扶机制。压紧压实防止返贫工作责任，持续巩固提升&ldquo;三保障&rdquo;和饮水安全保障成果。对存在因灾返贫风险的农户，符合政策规定的可先行落实帮扶措施。加强农村高额医疗费用负担患者监测预警，按规定及时落实医疗保障和救助政策。加快推动防止返贫监测与低收入人口动态监测信息平台互联互通，加强跨部门信息整合共享。研究推动防止返贫帮扶政策和农村低收入人口常态化帮扶政策衔接并轨。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（九）持续加强产业和就业帮扶。强化帮扶产业分类指导，巩固一批、升级一批、盘活一批、调整一批，推动产业提质增效、可持续发展。中央财政衔接推进乡村振兴补助资金用于产业发展的比例保持总体稳定，强化资金项目绩效管理。加强帮扶项目资产管理，符合条件的纳入农村集体资产统一管理。提升消费帮扶助农增收行动实效。推进防止返贫就业攻坚行动，落实东西部劳务协作帮扶责任，统筹用好就业帮扶车间、公益岗位等渠道，稳定脱贫劳动力就业规模。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十）加大对重点地区帮扶支持力度。将脱贫县涉农资金统筹整合试点政策优化调整至160个国家乡村振兴重点帮扶县实施，加强整合资金使用监管。国有金融机构加大对国家乡村振兴重点帮扶县金融支持力度。持续开展医疗、教育干部人才&ldquo;组团式&rdquo;帮扶和科技特派团选派。高校毕业生&ldquo;三支一扶&rdquo;计划向脱贫地区倾斜。支持易地扶贫搬迁安置区可持续发展。易地搬迁至城镇后因人口增长出现住房困难的家庭，符合条件的统筹纳入城镇住房保障范围。推动建立欠发达地区常态化帮扶机制。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 三、提升乡村产业发展水平</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十一）促进农村一二三产业融合发展。坚持产业兴农、质量兴农、绿色兴农，加快构建粮经饲统筹、农林牧渔并举、产加销贯通、农文旅融合的现代乡村产业体系，把农业建成现代化大产业。鼓励各地因地制宜大力发展特色产业，支持打造乡土特色品牌。实施乡村文旅深度融合工程，推进乡村旅游集聚区（村）建设，培育生态旅游、森林康养、休闲露营等新业态，推进乡村民宿规范发展、提升品质。优化实施农村产业融合发展项目，培育农业产业化联合体。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十二）推动农产品加工业优化升级。推进农产品生产和初加工、精深加工协同发展，促进就近就地转化增值。推进农产品加工设施改造提升，支持区域性预冷烘干、储藏保鲜、鲜切包装等初加工设施建设，发展智能化、清洁化精深加工。支持东北地区发展大豆等农产品全产业链加工，打造食品和饲料产业集群。支持粮食和重要农产品主产区建设加工产业园。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十三）推动农村流通高质量发展。深入推进县域商业体系建设，健全县乡村物流配送体系，促进农村客货邮融合发展，大力发展共同配送。推进农产品批发市场转型升级。优化农产品冷链物流体系建设，加快建设骨干冷链物流基地，布局建设县域产地公共冷链物流设施。实施农村电商高质量发展工程，推进县域电商直播基地建设，发展乡村土特产网络销售。加强农村流通领域市场监管，持续整治农村假冒伪劣产品。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十四）强化农民增收举措。实施农民增收促进行动，持续壮大乡村富民产业，支持农户发展特色种养、手工作坊、林下经济等家庭经营项目。强化产业发展联农带农，健全新型农业经营主体和涉农企业扶持政策与带动农户增收挂钩机制。促进农村劳动力多渠道就业，健全跨区域信息共享和有组织劳务输出机制，培育壮大劳务品牌。开展农民工服务保障专项行动，加强农民工就业动态监测。加强拖欠农民工工资源头预防和风险预警，完善根治欠薪长效机制。加强农民工职业技能培训，推广订单、定向、定岗培训模式。做好大龄农民工就业扶持。在重点工程项目和农业农村基础设施建设领域积极推广以工代赈，继续扩大劳务报酬规模。鼓励以出租、合作开发、入股经营等方式盘活利用农村资源资产，增加农民财产性收入。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 四、提升乡村建设水平</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十五）增强乡村规划引领效能。适应乡村人口变化趋势，优化村庄布局、产业结构、公共服务配置。强化县域国土空间规划对城镇、村庄、产业园区等空间布局的统筹。分类编制村庄规划，可单独编制，也可以乡镇或若干村庄为单元编制，不需要编制的可在县乡级国土空间规划中明确通则式管理规定。加强村庄规划编制实效性、可操作性和执行约束力，强化乡村空间设计和风貌管控。在耕地总量不减少、永久基本农田布局基本稳定的前提下，综合运用增减挂钩和占补平衡政策，稳妥有序开展以乡镇为基本单元的全域土地综合整治，整合盘活农村零散闲置土地，保障乡村基础设施和产业发展用地。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十六）深入实施农村人居环境整治提升行动。因地制宜推进生活污水垃圾治理和农村改厕，完善农民参与和长效管护机制。健全农村生活垃圾分类收运处置体系，完善农村再生资源回收利用网络。分类梯次推进生活污水治理，加强农村黑臭水体动态排查和源头治理。稳步推进中西部地区户厕改造，探索农户自愿按标准改厕、政府验收合格后补助到户的奖补模式。协同推进农村有机生活垃圾、粪污、农业生产有机废弃物资源化处理利用。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十七）推进农村基础设施补短板。从各地实际和农民需求出发，抓住普及普惠的事，干一件、成一件。完善农村供水工程体系，有条件的推进城乡供水一体化、集中供水规模化，暂不具备条件的加强小型供水工程规范化建设改造，加强专业化管护，深入实施农村供水水质提升专项行动。推进农村电网巩固提升工程。推动农村分布式新能源发展，加强重点村镇新能源汽车充换电设施规划建设。扎实推进&ldquo;四好农村路&rdquo;建设，完善交通管理和安全防护设施，加快实施农村公路桥梁安全&ldquo;消危&rdquo;行动。继续实施农村危房改造和农房抗震改造，巩固农村房屋安全隐患排查整治成果。持续实施数字乡村发展行动，发展智慧农业，缩小城乡&ldquo;数字鸿沟&rdquo;。实施智慧广电乡村工程。鼓励有条件的省份统筹建设区域性大数据平台，加强农业生产经营、农村社会管理等涉农信息协同共享。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十八）完善农村公共服务体系。优化公共教育服务供给，加强寄宿制学校建设，办好必要的乡村小规模学校。实施县域普通高中发展提升行动计划。加强乡镇卫生院和村卫生室服务能力建设，稳步提高乡村医生中具备执业（助理）医师资格的人员比例。持续提升农村传染病防控和应急处置能力。逐步提高县域内医保基金在乡村医疗卫生机构使用的比例，加快将村卫生室纳入医保定点管理。健全农村养老服务体系，因地制宜推进区域性养老服务中心建设，鼓励发展农村老年助餐和互助服务。健全城乡居民基本养老保险&ldquo;多缴多得、长缴多得&rdquo;激励机制。加强农村生育支持和婴幼儿照护服务，做好流动儿童、留守儿童、妇女、老年人、残疾人等关心关爱服务。实施产粮大县公共服务能力提升行动。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（十九）加强农村生态文明建设。持续打好农业农村污染治理攻坚战，一体化推进乡村生态保护修复。扎实推进化肥农药减量增效，推广种养循环模式。整县推进农业面源污染综合防治。加强耕地土壤重金属污染源排查整治。加强食用农产品产地质量安全控制和产品检测，提升&ldquo;从农田到餐桌&rdquo;全过程食品安全监管能力。推进兽用抗菌药使用减量化行动。强化重大动物疫病和重点人畜共患病防控。持续巩固长江十年禁渔成效。加快推进长江中上游坡耕地水土流失治理，扎实推进黄河流域深度节水控水。推进水系连通、水源涵养、水土保持，复苏河湖生态环境，强化地下水超采治理。加强荒漠化综合防治，探索&ldquo;草光互补&rdquo;模式。全力打好&ldquo;三北&rdquo;工程攻坚战，鼓励通过多种方式组织农民群众参与项目建设。优化草原生态保护补奖政策，健全对超载过牧的约束机制。加强森林草原防灭火。实施古树名木抢救保护行动。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十）促进县域城乡融合发展。统筹新型城镇化和乡村全面振兴，提升县城综合承载能力和治理能力，促进县乡村功能衔接互补、资源要素优化配置。优化县域产业结构和空间布局，构建以县城为枢纽、以小城镇为节点的县域经济体系，扩大县域就业容量。统筹县域城乡基础设施规划建设管护，推进城乡学校共同体、紧密型县域医共体建设。实施新一轮农业转移人口市民化行动，鼓励有条件的县（市、区）将城镇常住人口全部纳入住房保障政策范围。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 五、</strong><strong>提升乡村治理水平</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十一）推进抓党建促乡村振兴。坚持大抓基层鲜明导向，强化县级党委抓乡促村责任，健全县乡村三级联动争创先进、整顿后进机制。全面提升乡镇领导班子抓乡村振兴能力，开展乡镇党政正职全覆盖培训和农村党员进党校集中轮训。建好建强农村基层党组织，健全村党组织领导的村级组织体系，推行村级议事协商目录制度。加强村干部队伍建设，健全选育管用机制，实施村党组织带头人后备力量培育储备三年行动。优化驻村第一书记和工作队选派管理。进一步整合基层监督执纪力量，推动完善基层监督体系，持续深化乡村振兴领域不正之风和腐败问题专项整治。加强乡镇对县直部门派驻机构及人员的管理职责，加大编制资源向乡镇倾斜力度，县以上机关一般不得从乡镇借调工作人员，推广&ldquo;街乡吹哨、部门报到&rdquo;等做法，严格实行上级部门涉基层事务准入制度，健全基层职责清单和事务清单，推动解决&ldquo;小马拉大车&rdquo;等基层治理问题。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十二）繁荣发展乡村文化。推动农耕文明和现代文明要素有机结合，书写中华民族现代文明的乡村篇。改进创新农村精神文明建设，推动新时代文明实践向村庄、集市等末梢延伸，促进城市优质文化资源下沉，增加有效服务供给。深入开展听党话、感党恩、跟党走宣传教育活动。加强乡村优秀传统文化保护传承和创新发展。强化农业文化遗产、农村非物质文化遗产挖掘整理和保护利用，实施乡村文物保护工程。开展传统村落集中连片保护利用示范。坚持农民唱主角，促进&ldquo;村BA&rdquo;、村超、村晚等群众性文体活动健康发展。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十三）持续推进农村移风易俗。坚持疏堵结合、标本兼治，创新移风易俗抓手载体，发挥村民自治作用，强化村规民约激励约束功能，持续推进高额彩礼、大操大办、散埋乱葬等突出问题综合治理。鼓励各地利用乡村综合性服务场所，为农民婚丧嫁娶等提供普惠性社会服务，降低农村人情负担。完善婚事新办、丧事简办、孝老爱亲等约束性规范和倡导性标准。推动党员干部带头承诺践诺，发挥示范带动作用。强化正向引导激励，加强家庭家教家风建设，推广清单制、积分制等有效办法。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十四）建设平安乡村。坚持和发展新时代&ldquo;枫桥经验&rdquo;，完善矛盾纠纷源头预防、排查预警、多元化解机制。健全农村扫黑除恶常态化机制，持续防范和整治&ldquo;村霸&rdquo;，依法打击农村宗族黑恶势力及其&ldquo;保护伞&rdquo;。持续开展打击整治农村赌博违法犯罪专项行动，加强电信网络诈骗宣传防范。开展农村道路交通、燃气、消防、渔船等重点领域安全隐患治理攻坚。加强农村防灾减灾工程、应急管理信息化和公共消防设施建设，提升防灾避险和自救互救能力。加强法治乡村建设，增强农民法律意识。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 六、</strong><strong>加强党对&ldquo;三农&rdquo;工作的全面领导</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十五）健全党领导农村工作体制机制。坚持把解决好&ldquo;三农&rdquo;问题作为全党工作重中之重，坚持农业农村优先发展，改革完善&ldquo;三农&rdquo;工作体制机制，全面落实乡村振兴责任制，压实五级书记抓乡村振兴责任，明确主攻方向，扎实组织推动。加强党委农村工作体系建设，强化统筹推进乡村振兴职责。巩固拓展学习贯彻习近平新时代中国特色社会主义思想主题教育成果。各级党政领导干部要落实&ldquo;四下基层&rdquo;制度，深入调查研究，推动解决农民群众反映强烈的问题。优化各类涉农督查检查考核，突出实绩实效，能整合的整合，能简化的简化，减轻基层迎检迎考负担。按规定开展乡村振兴表彰激励。讲好新时代乡村振兴故事。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十六）强化农村改革创新。在坚守底线前提下，鼓励各地实践探索和制度创新，强化改革举措集成增效，激发乡村振兴动力活力。启动实施第二轮土地承包到期后再延长30年整省试点。健全土地流转价格形成机制，探索防止流转费用不合理上涨有效办法。稳慎推进农村宅基地制度改革。深化农村集体产权制度改革，促进新型农村集体经济健康发展，严格控制农村集体经营风险。对集体资产由村民委员会、村民小组登记到农村集体经济组织名下实行税收减免。持续深化集体林权制度改革、农业水价综合改革、农垦改革和供销合作社综合改革。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十七）完善乡村振兴多元化投入机制。坚持将农业农村作为一般公共预算优先保障领域，创新乡村振兴投融资机制，确保投入与乡村振兴目标任务相适应。落实土地出让收入支农政策。规范用好地方政府专项债券等政策工具，支持乡村振兴重大工程项目建设。强化对信贷业务以县域为主的金融机构货币政策精准支持，完善大中型银行&ldquo;三农&rdquo;金融服务专业化工作机制，强化农村中小金融机构支农支小定位。分省分类推进农村信用社改革化险。创新支持粮食安全、种业振兴等重点领域信贷服务模式。发展农村数字普惠金融，推进农村信用体系建设。发挥全国农业信贷担保体系和政府投资基金等作用。强化财政金融协同联动，在不新增地方政府隐性债务的前提下，开展高标准农田和设施农业建设等涉农领域贷款贴息奖补试点。鼓励社会资本投资农业农村，有效防范和纠正投资经营中的不当行为。加强涉农资金项目监管，严厉查处套取、骗取资金等违法违规行为。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; </strong>（二十八）壮大乡村人才队伍。实施乡村振兴人才支持计划，加大乡村本土人才培养，有序引导城市各类专业技术人才下乡服务，全面提高农民综合素质。强化农业科技人才和农村高技能人才培养使用，完善评价激励机制和保障措施。加强高等教育新农科建设，加快培养农林水利类紧缺专业人才。发挥普通高校、职业院校、农业广播电视学校等作用，提高农民教育培训实效。推广医疗卫生人员&ldquo;县管乡用、乡聘村用&rdquo;，实施教师&ldquo;县管校聘&rdquo;改革。推广科技小院模式，鼓励科研院所、高校专家服务农业农村。让我们紧密团结在以习近平同志为核心的党中央周围，坚定信心、铆足干劲、苦干实干，推进乡村全面振兴不断取得新成效，向建设农业强国目标扎实迈进。</span></p>', '301', 91, NULL, '2024年中央一号文件公布', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (403, '权威访谈｜推进乡村全面振兴不断取得新成效——中央农办负责人解读2024年中央一号文件', 999999, NULL, 'xagly', '181', '2024-02-05 15:38:11', '2024-08-08 15:01:51', '2024-02-05 15:38:11', '0', 'xagly', '新华社', '西安管理员', '300,301', '0', 0, 0, 0, '<section>\n<p><span style=\"font-size: 18pt;\">&emsp;新华社2月3日受权发布《中共中央 国务院关于学习运用&ldquo;千村示范、万村整治&rdquo;工程经验有力有效推进乡村全面振兴的意见》。这是党的十八大以来指导&ldquo;三农&rdquo;工作的第12个中央一号文件。中央农村工作领导小组办公室负责人当天就文件精神进行了全面解读。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>把学习运用&ldquo;千万工程&rdquo;经验作为重要引领</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件强调，&ldquo;要学习运用&lsquo;千万工程&rsquo;蕴含的发展理念、工作方法和推进机制&rdquo;&ldquo;把推进乡村全面振兴作为新时代新征程&lsquo;三农&rsquo;工作的总抓手&rdquo;&ldquo;集中力量抓好办成一批群众可感可及的实事&rdquo;。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;中央农办负责人表示，习近平总书记在浙江工作期间，亲自谋划推动&ldquo;千万工程&rdquo;，从整治农村环境入手，由点及面，扩容建设，经过20多年持续努力，不仅深刻改变了浙江农村的整体面貌，也为推进乡村全面振兴作出了先行探索和示范引路，各地都可学可鉴。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;必须看到，我国各地农村情况千差万别，自然条件、风土人情、发展水平、工作基础各不相同。学习运用&lsquo;千万工程&rsquo;经验，要因地制宜，不能生搬硬套、搞&lsquo;一刀切&rsquo;。&rdquo;中央农办负责人说，归根结底要让广大农民在乡村振兴中有实实在在的获得感，坚决反对搞形象工程、做表面文章。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;这位负责人表示，当前和今后一个时期，推进乡村全面振兴工作重点是确保国家粮食安全，确保不发生规模性返贫；提升乡村产业发展水平，提升乡村建设水平，提升乡村治理水平；强化科技和改革双轮驱动，强化农民增收举措。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>确保国家粮食安全、确保不发生规模性返贫</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;去年我国粮食总产量再创新高，连续9年稳定在1.3万亿斤以上。面对各种风险挑战和不确定性，如何保障粮食安全？</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件突出稳面积、增单产两手发力的导向，提出确保粮食产量保持在1.3万亿斤以上的目标。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;中央农办负责人表示，确保国家粮食安全，关键是调动农民种粮和地方抓粮积极性。要适当提高小麦最低收购价，继续实施耕地地力保护补贴、玉米大豆生产者补贴和稻谷补贴，扩大完全成本保险和种植收入保险政策实施范围，让农民种粮有钱挣。全面落实粮食安全党政同责，加大对产粮大县支持力度，探索建立粮食产销区省际横向利益补偿机制，让地方抓粮有动力。同时，把粮食增产的重心放到大面积提高单产上，实施粮食单产提升工程，集成推广良田良种良机良法，推动粮食产能迈上新台阶。以小农户为基础、新型农业经营主体为重点、社会化服务为支撑，构建现代农业经营体系，解决&ldquo;谁来种地&rdquo;问题。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;要健全耕地数量、质量、生态&lsquo;三位一体&rsquo;保护制度体系。&rdquo;这位负责人说，坚决整治乱占、破坏耕地违法行为，改革完善耕地占补平衡制度，适当提高高标准农田建设中央和省级投资补助水平，确保耕地数量有保障、质量有提升。同时，树立大农业观、大食物观，多渠道拓展食物来源，构建多元化食物供给体系。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;确保不发生规模性返贫是推进乡村全面振兴的底线要求，今年中央一号文件对此作出了部署。中央农办负责人表示，要压紧压实防止返贫工作责任，落实监测帮扶机制，加强跨部门信息整合共享，进一步提升监测及时性和帮扶精准性。加强产业和就业帮扶，强化帮扶产业分类指导，推进防止返贫就业攻坚行动。加大对重点地区帮扶支持力度，改善发展条件，增强&ldquo;造血&rdquo;能力，激发内生动力。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>提升乡村产业发展、乡村建设、乡村治理水平</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;乡村是广大农民群众的家园，只有营造安居乐业的良好环境，才能让农民有充足的获得感、幸福感、安全感。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件将&ldquo;三个提升&rdquo;作为推进乡村全面振兴的重点。中央农办负责人表示，要统筹推进乡村产业发展、乡村建设、乡村治理，建设宜居宜业和美乡村。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&mdash;&mdash;提升乡村产业发展水平。做好&ldquo;土特产&rdquo;文章，坚持产业兴农、质量兴农、绿色兴农，把农业建成现代化大产业。促进农村一二三产业融合发展，推动农产品加工业优化升级，推动农村流通高质量发展，加快构建农林牧渔并举、产加销贯通、农文旅融合的现代乡村产业体系。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&mdash;&mdash;提升乡村建设水平。加强村庄规划编制实效性、可操作性和约束力，适应乡村人口变化趋势，优化村庄布局、产业结构、公共服务配置。深入实施农村人居环境整治提升行动，完善农民参与和长效管护机制。统筹农村供水、道路、能源、信息和住房安全等建设，推进农村基础设施补短板，完善农村教育、医疗、养老等公共服务体系，及时回应群众关切。统筹新型城镇化和乡村全面振兴，促进县域城乡融合发展。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&mdash;&mdash;提升乡村治理水平，健全完善党组织领导的自治、法治、德治相结合的乡村治理体系，推进抓党建促乡村振兴，建好建强农村基层党组织。持续推进基层减负，优化各类涉农督查检查考核。加强农村精神文明建设，繁荣发展乡村文化，加强乡村优秀传统文化保护传承和创新发展，持续推进农村移风易俗，加强高额彩礼、大操大办等突出问题综合治理。坚持和发展新时代&ldquo;枫桥经验&rdquo;，建设平安乡村。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>强化科技和改革双轮驱动、强化农民增收举措</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件对强化科技和改革双轮驱动作出了系统部署。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;要协同推进科技创新和制度创新，激发农村各类要素潜能和主体活力，为推进乡村全面振兴注入强劲动能。&rdquo;中央农办负责人说，要优化农业科技创新战略布局，支持重大创新平台建设，加大核心技术攻关力度，提高农业科技创新体系整体效能。扭住种子这个要害，加快推进种业振兴行动，完善联合研发和应用协作机制。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;这位负责人表示，改革是乡村振兴的重要法宝，要启动实施第二轮土地承包到期后再延长30年整省试点，稳慎推进农村宅基地改革，深化农村集体产权制度改革。在坚守底线的前提下，鼓励各地实践探索和制度创新，强化改革举措集成增效。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;农业农村工作，说一千、道一万，增加农民收入是关键。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;党的十八大以来，农村居民收入持续较快增长，2023年农村居民人均可支配收入为21691元，城乡居民收入比进一步缩小到2.39∶1。&rdquo;中央农办负责人说，今年中央一号文件提出实施农民增收促进行动，目的就是巩固农民持续增收势头，促进共同富裕。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;这位负责人表示，要加强农民工职业技能培训，健全跨区域信息共享和有组织劳务输出机制，在重点工程项目和农业农村基础设施建设领域积极推广以工代赈。支持农户发展特色种养、手工作坊、林下经济等经营项目，完善产业联农带农机制。持续加大强农惠农富农政策力度，逐步提高农村社会保障水平，加强涉农资金项目监管，把给农民的各项补助补贴补到位。赋予农民更加充分的财产权益，鼓励以出租、合作开发、入股经营等方式盘活利用农村资源资产。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;总而言之，要紧紧围绕增加农民收入这个中心任务，广辟增收门路，让农民群众钱袋子越来越鼓、日子越过越好。&rdquo;中央农办负责人说。</span></p>\n</section>\n<p>&nbsp;</p>', '301', 13, NULL, '推进乡村全面振兴不断取得新成效——中央农办负责人解读2024年中央一号文件', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (404, '把推进乡村全面振兴作为  新时代新征程“三农”工作的总抓手', 999999, NULL, 'xagly', '181', '2024-02-06 16:42:05', '2024-08-08 15:01:51', '2024-02-06 16:42:05', '2', 'xagly', '中央农办', '西安管理员', '300,301', '0', 0, 0, 0, '<p data-mpa-powered-by=\"yiban.io\"><span style=\"font-size: 18pt;\"><strong>把推进乡村全面振兴作为</strong></span></p>\n<p data-mpa-powered-by=\"yiban.io\"><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 新时代新征程&ldquo;三农&rdquo;工作的总抓手</strong></span></p>\n<section><span style=\"font-size: 18pt;\">中央财办分管日常工作的副主任</span></section>\n<section><span style=\"font-size: 18pt;\">中央农办主任&nbsp;韩文秀</span></section>\n<section></section>\n<p><span style=\"font-size: 18pt;\">《中共中央、国务院关于学习运用&ldquo;千村示范、万村整治&rdquo;工程经验有力有效推进乡村全面振兴的意见》是党的十八大以来指导&ldquo;三农&rdquo;工作的第12个中央一号文件。文件以推进乡村全面振兴为主题，以学习运用&ldquo;千万工程&rdquo;经验为主线，对2024年及今后一个时期的&ldquo;三农&rdquo;工作作出全面部署，充分体现了以习近平同志为核心的党中央对&ldquo;三农&rdquo;工作一以贯之的高度重视。我们要认真学习贯彻，锚定建设农业强国目标，以加快农业农村现代化更好推进中国式现代化建设。</span></p>\n<p>&nbsp;</p>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 新时代新征程&ldquo;三农&rdquo;工作实现良好开局</strong></span></section>\n<section><span style=\"font-size: 18pt;\">过去一年，在以习近平同志为核心的党中央坚强领导下，各地区各部门坚决贯彻落实党中央、国务院决策部署，广大干部群众齐心协力、砥砺奋进，&ldquo;三农&rdquo;工作取得显著成效。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 粮食产量再创新高。</strong>克服较为严重的自然灾害影响，实现了农业丰产丰收。粮食总产量达到13908.2亿斤，比上年增加177.6亿斤，再创历史新高，连续9年稳定在1.3万亿斤以上。大豆面积产量双增，棉油糖稳定发展，肉蛋奶、果菜茶等菜篮子产品供应充足、价格平稳。新一轮千亿斤粮食产能提升行动启动实施，高标准农田建设任务如期完成，一批作物新品种研发取得突破，部分短板农机具实现产业化应用。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 农民收入较快增长。</strong>农村居民人均可支配收入21691元，实际增长7.6%，比城镇居民收入增速高2.8个百分点，城乡居民收入差距之比缩小到2.39∶1。脱贫攻坚成果持续巩固拓展，未发生规模性返贫现象，脱贫地区农民收入增速继续快于全国平均水平。乡村产业持续发展壮大，规模以上农产品加工企业营业收入预计达到20万亿元。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 农村社会和谐稳定。</strong>宜居宜业和美乡村建设扎实推进，全国农村卫生厕所普及率超过73%，生活垃圾收运处理的行政村比例稳定在90%以上，开展清洁行动的村庄超过95%，村容村貌明显改善。乡村治理不断加强，农村移风易俗突出问题治理扎实开展，&ldquo;村BA&rdquo;、村超、村晚等群众性文体活动蔚然成风，农村社会保持稳定安宁。</span></section>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 农稳社稷，粮安天下。</strong>面对复杂严峻形势和各种风险挑战，我国粮食连年丰收，&ldquo;三农&rdquo;形势持续向好，发挥了安民心、稳大局的&ldquo;压舱石&rdquo;作用，为稳定经济社会发展大局提供了有力支撑、赢得了战略主动。事实充分证明，党中央关于&ldquo;三农&rdquo;工作的大政方针是完全正确的，新时代新征程重农强农的弦任何时候都不能松。</span></p>\n<p>&nbsp;</p>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 集中力量打好乡村全面振兴漂亮仗</strong></span></section>\n<section><span style=\"font-size: 18pt;\">今年中央一号文件，对标实现第二个百年奋斗目标，着眼解决农民群众反映强烈的突出问题，把握目标导向和问题导向相结合、重点任务与全面部署相结合、年度任务与阶段性部署相结合，进一步明确&ldquo;三农&rdquo;工作重点抓什么、怎么抓，对打好乡村全面振兴漂亮仗作出系统安排。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 以学习运用&ldquo;千万工程&rdquo;经验为引领。</strong>&ldquo;千万工程&rdquo;是时任浙江省委书记习近平同志亲自谋划、亲自部署、亲自推动的一项重大决策，从农村环境整治入手，由点及面、迭代升级，20年持续努力造就了万千美丽乡村，造福了万千农民群众，创造了推进乡村全面振兴的成功经验和实践范例。&ldquo;千万工程&rdquo;有着一整套系统成熟、行之有效的发展理念、工作方法和推进机制。比如，从农民群众期盼中找准工作的出发点和落脚点，从实际出发想问题、作决策、办事情，以系统观念推动城乡融合发展，突出抓基层、强基础、固基本工作导向，锚定目标真抓实干，等等。这些经验不同地方都可学可鉴、长期管用。以学习运用&ldquo;千万工程&rdquo;经验为引领，具有深厚的实践支撑和深远的战略意义，有利于推动各地明确标准、聚焦任务，加大力度把推进乡村全面振兴搞上去。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 以确保国家粮食安全、确保不发生规模性返贫为底线。</strong>粮食安全是&ldquo;国之大者&rdquo;，要全面落实粮食安全党政同责，坚持稳面积、增单产两手发力，确保粮食产量保持在1.3万亿斤以上。要扎实推进新一轮千亿斤粮食产能提升行动，加强农业基础设施建设，强化农业科技支撑，构建现代农业经营体系，增强粮食和重要农产品调控能力，构建多元化食物供给体系。要健全耕地数量、质量、生态&ldquo;三位一体&rdquo;保护制度体系，坚决整治乱占、破坏耕地违法行为，提高高标准农田建设投入标准，加大管护力度，确保耕地数量有保障、质量有提升。巩固拓展脱贫攻坚成果，关键是不能松劲懈怠，不能出现规模性返贫。要压紧压实工作责任，抓好防止返贫监测和帮扶措施落实，做好帮扶产业分类指导，推进防止返贫就业攻坚行动，持续增强脱贫地区和脱贫群众内生发展动力。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 以提升乡村产业发展水平、提升乡村建设水平、提升乡村治理水平为重点。</strong>产业振兴是乡村振兴的重中之重，要坚持产业兴农、质量兴农、绿色兴农，把农业建成现代化大产业。要促进农村一二三产业融合发展，推动农产品加工业优化升级，推动农村流通高质量发展，加快构建粮经饲统筹、农林牧渔并举、产加销贯通、农文旅融合的现代乡村产业体系。乡村建设是国家现代化建设的重要内容，要适应乡村人口变化趋势，增强乡村规划引领效能，深入实施农村人居环境整治提升行动，推进农村基础设施补短板，完善农村公共服务体系，实现乡村由表及里、形神兼备的全面提升。治理有效是乡村振兴的重要保障，要健全村党组织领导的村级组织体系，健全党组织领导下自治、法治、德治相结合的乡村治理体系，推动解决&ldquo;小马拉大车&rdquo;等基层治理问题。要繁荣发展乡村文化，推动农耕文明和现代文明要素有机结合，持续推进农村移风易俗，建设平安乡村。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 以强化科技和改革双轮驱动、强化农民增收举措为支撑。</strong>乡村振兴归根结底要靠创新驱动增动力、添活力。要协同推进科技创新和制度创新，激发农村各类要素潜能和主体活力，为推进乡村全面振兴注入强劲动能。要优化农业科技创新战略布局，支持重大创新平台建设，加大核心技术攻关力度，解决各自为战、低水平重复、转化率不高等突出问题。要启动实施第二轮土地承包到期后再延长30年整省试点，稳慎推进农村宅基地制度改革，深化农村集体产权制度改革。在坚守底线前提下，鼓励各地实践探索和制度创新，强化改革举措集成增效。增加农民收入是&ldquo;三农&rdquo;工作的中心任务，要千方百计拓宽农民增收渠道，实现产业增收、就业增收、转移增收和财产增收同步发力。要实施农民增收促进行动，多措并举促进农民工稳岗就业，完善产业联农带农机制，加大惠农富农政策力度，激活农村各类资源资产要素，促进农民持续增收致富，日子越过越好。</span></section>\n<p><span style=\"font-size: 18pt;\">&ldquo;两个确保、三个提升、两个强化&rdquo;的总体框架，是党中央根据新时代新征程&ldquo;三农&rdquo;工作形势发展变化作出的重大部署，我们要以实际行动抓好落实，集中力量抓好办成一批群众可感可及的实事，推进乡村全面振兴不断取得实质性进展、阶段性成果。</span></p>\n<p>&nbsp;</p>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 加强党对&ldquo;三农&rdquo;工作的全面领导</strong></span></section>\n<section><span style=\"font-size: 18pt;\">实施乡村振兴战略是关系全面建设社会主义现代化国家的全局性、历史性任务。必须坚持把解决好&ldquo;三农&rdquo;问题作为全党工作重中之重，坚持农业农村优先发展，坚持从政治和全局高度看&ldquo;三农&rdquo;、抓&ldquo;三农&rdquo;，汇聚起全党上下、社会各方推进乡村全面振兴的强大力量。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 全面落实乡村振兴责任制。</strong>推进乡村全面振兴的战略要求和主攻方向已经明确，关键是要明确重点，压实责任，扎实组织推动。要落实五级书记抓乡村振兴责任，推动各级党政&ldquo;一把手&rdquo;把责任真正扛在肩上、抓在手上，特别是发挥县委书记&ldquo;一线总指挥&rdquo;作用。落实部门推进乡村振兴责任，加大对&ldquo;三农&rdquo;工作的支持和投入力度。发挥督查考核&ldquo;指挥棒&rdquo;作用，既要减轻基层迎检迎考负担，又要推动落实责任、发现解决问题。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 完善乡村振兴多元化投入机制。</strong>坚持将农业农村作为一般公共预算优先保障领域，创新乡村振兴投融资机制，确保投入与乡村振兴目标任务相适应。要落实土地出让收入支农政策，规范用好地方政府专项债券等政策工具，支持乡村振兴重大工程项目建设。强化财政金融协同联动，完善大中型银行&ldquo;三农&rdquo;金融服务专业化工作机制，强化农村中小金融机构支农支小定位，创新农村信贷服务模式。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 壮大乡村人才队伍。</strong>乡村振兴，关键在人。要实施乡村振兴人才支持计划，加大乡村本土人才培养，有序引导各类人才在乡村振兴中建功立业。强化农业科技人才和农村高技能人才培养使用，完善评价激励机制和保障措施。强化县乡村医疗、教育等人才一体化管理，推广医疗卫生人员&ldquo;县管乡用、乡聘村用&rdquo;，实施教师&ldquo;县管校聘&rdquo;改革，深入推进科技特派员制度，推广科技小院模式。</span></section>\n<section><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 提升工作实绩实效。</strong>要坚持从实际出发，多给基层一些自主权，鼓励基层把中央精神和地方实际结合起来创造性贯彻落实。要顺应自然规律、经济规律、社会发展规律，统筹考虑财力可持续、农民可接受，尽力而为、量力而行，不能急功近利、刮风搞运动。要尊重农民意愿、保护农民利益，不能大包大揽，更不能搞强迫命令，要让广大农民真正成为乡村振兴的参与者和受益者。</span></section>', '301', 22, NULL, '把推进乡村全面振兴作为新时代新征程“三农”工作的总抓手', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (405, '我国粮食安全有了专门保障法', 999999, NULL, 'xagly', '181', '2024-02-07 16:42:39', '2024-08-08 15:01:51', '2024-02-07 16:42:39', '2', 'xagly', '要素交易市场', '西安管理员', '300,301', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">十四届全国人大常委会第七次会议</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">12月29日表决通过</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">《中华人民共和国粮食安全保障法》</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">自2024年6月1日起施行</span></p>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 这部旨在保障粮食有效供给</span><span style=\"font-size: 18pt;\">确保国家粮食安全的法律</span><span style=\"font-size: 18pt;\">共11章74条</span><span style=\"font-size: 18pt;\">包括总则、耕地保护、粮食生产</span><span style=\"font-size: 18pt;\">粮食储备、粮食流通、粮食加工</span><span style=\"font-size: 18pt;\">粮食应急、粮食节约、监督管理、</span><span style=\"font-size: 18pt;\">法律责任、附则</span></section>\n<section></section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>中华人民共和国粮食安全保障法</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第一章　总则&emsp;</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第一条</strong>　为了保障粮食有效供给，确保国家粮食安全，提高防范和抵御粮食安全风险能力，维护经济社会稳定和国家安全，根据宪法，制定本法。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第二条</strong>　国家粮食安全工作坚持中国共产党的领导，贯彻总体国家安全观，统筹发展和安全，实施以我为主、立足国内、确保产能、适度进口、科技支撑的国家粮食安全战略，坚持藏粮于地、藏粮于技，提高粮食生产、储备、流通、加工能力，确保谷物基本自给、口粮绝对安全。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;保障国家粮食安全应当树立大食物观，构建多元化食物供给体系，全方位、多途径开发食物资源，满足人民群众对食物品种丰富多样、品质营养健康的消费需求。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;第三条</strong>　国家建立粮食安全责任制，实行粮食安全党政同责。县级以上地方人民政府应当承担保障本行政区域粮食安全的具体责任。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上人民政府发展改革、自然资源、农业农村、粮食和储备等主管部门依照本法和规定的职责，协同配合，做好粮食安全保障工作。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第四条</strong>　国家加强粮食宏观调控，优化粮食品种结构和区域布局，统筹利用国内、国际的市场和资源，构建科学合理、安全高效的粮食供给保障体系，提升粮食供给能力和质量安全。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家加强国际粮食安全合作，发挥粮食国际贸易作用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第五条</strong>　县级以上人民政府应当将粮食安全保障纳入国民经济和社会发展规划。县级以上人民政府有关部门应当根据粮食安全保障目标、任务等，编制粮食安全保障相关专项规划，按照程序批准后实施。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第六条</strong>　国家建立健全粮食安全保障投入机制，采取财政、金融等支持政策加强粮食安全保障，完善粮食生产、收购、储存、运输、加工、销售协同保障机制，建设国家粮食安全产业带，调动粮食生产者和地方人民政府保护耕地、种粮、做好粮食安全保障工作的积极性，全面推进乡村振兴，促进粮食产业高质量发展，增强国家粮食安全保障能力。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家引导社会资本投入粮食生产、储备、流通、加工等领域，并保障其合法权益。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家引导金融机构合理推出金融产品和服务，为粮食生产、储备、流通、加工等提供支持。国家完善政策性农业保险制度，鼓励开展商业性保险业务。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第七条</strong>　国家加强粮食安全科技创新能力和信息化建设，支持粮食领域基础研究、关键技术研发和标准化工作，完善科技人才培养、评价和激励等机制，促进科技创新成果转化和先进技术、设备的推广使用，提高粮食生产、储备、流通、加工的科技支撑能力和应用水平。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第八条</strong>　各级人民政府及有关部门应当采取多种形式加强粮食安全宣传教育，提升全社会粮食安全意识，引导形成爱惜粮食、节约粮食的良好风尚。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;第九条</strong>　对在国家粮食安全保障工作中做出突出贡献的单位和个人，按照国家有关规定给予表彰和奖励。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第二章　耕地保护</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第十条</strong>　国家实施国土空间规划下的国土空间用途管制，统筹布局农业、生态、城镇等功能空间，划定落实耕地和永久基本农田保护红线、生态保护红线和城镇开发边界，严格保护耕地。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国务院确定省、自治区、直辖市人民政府耕地和永久基本农田保护任务。县级以上地方人民政府应当确保本行政区域内耕地和永久基本农田总量不减少、质量有提高。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>国家建立耕地保护补偿制度，调动耕地保护责任主体保护耕地的积极性。</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;</strong><strong>第十一条　国家实行占用耕地补偿制度，严格控制各类占用耕地行为；确需占用耕地的，应当依法落实补充耕地责任，补充与所占用耕地数量相等、质量相当的耕地。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;省、自治区、直辖市人民政府应当组织本级人民政府自然资源主管部门、农业农村主管部门对补充耕地的数量进行认定、对补充耕地的质量进行验收，并加强耕地质量跟踪评价。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第十二条　国家严格控制耕地转为林地、草地、园地等其他农用地。禁止违规占用耕地绿化造林、挖湖造景等行为。禁止在国家批准的退耕还林还草计划外擅自扩大退耕范围。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;</strong><strong>&emsp;第十三条　耕地应当主要用于粮食和棉、油、糖、蔬菜等农产品及饲草饲料生产。县级以上地方人民政府应当根据粮食和重要农产品保供目标任务，加强耕地种植用途管控，落实耕地利用优先序，调整优化种植结构。具体办法由国务院农业农村主管部门制定。</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;县级以上地方人民政府农业农村主管部门应当加强耕地种植用途管控日常监督。村民委员会、农村集体经济组织发现违反耕地种植用途管控要求行为的，应当及时向乡镇人民政府或者县级人民政府农业农村主管部门报告。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第十四条</strong>　国家建立严格的耕地质量保护制度，加强高标准农田建设，按照量质并重、系统推进、永续利用的要求，坚持政府主导与社会参与、统筹规划与分步实施、用养结合与建管并重的原则，健全完善多元投入保障机制，提高建设标准和质量。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第十五条</strong>　县级以上人民政府应当建立耕地质量和种植用途监测网络，开展耕地质量调查和监测评价，采取土壤改良、地力培肥、治理修复等措施，提高中低产田产能，治理退化耕地，加强大中型灌区建设与改造，提升耕地质量。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家建立黑土地保护制度，保护黑土地的优良生产能力。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家建立健全耕地轮作休耕制度，鼓励农作物秸秆科学还田，加强农田防护林建设；支持推广绿色、高效粮食生产技术，促进生态环境改善和资源永续利用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第十六条</strong>　县级以上地方人民政府应当因地制宜、分类推进撂荒地治理，采取措施引导复耕。家庭承包的发包方可以依法通过组织代耕代种等形式将撂荒地用于农业生产。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第十七条</strong>　国家推动盐碱地综合利用，制定相关规划和支持政策，鼓励和引导社会资本投入，挖掘盐碱地开发利用潜力，分区分类开展盐碱耕地治理改良，加快选育耐盐碱特色品种，推广改良盐碱地有效做法，遏制耕地盐碱化趋势。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第三章　粮食生产</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第十八条</strong>　国家推进种业振兴，维护种业安全，推动种业高质量发展。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家加强粮食作物种质资源保护开发利用，建设国家农业种质资源库，健全国家良种繁育体系，推进粮食作物种质资源保护与管理信息化建设，提升供种保障能力。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家加强植物新品种权保护，支持育种基础性、前沿性研究和应用技术研究，鼓励粮食作物种子科技创新和产业化应用，支持开展育种联合攻关，培育具有自主知识产权的优良品种。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第十九条</strong>　省级以上人民政府应当建立种子储备制度，主要用于发生灾害时的粮食生产需要及余缺调剂。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第二十条</strong>　县级以上人民政府应当统筹做好肥料、农药、农用薄膜等农业生产资料稳定供应工作，引导粮食生产者科学施用化肥、农药，合理使用农用薄膜，增施有机肥料。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第二十一条</strong>　国家加强水资源管理和水利基础设施建设，优化水资源配置，保障粮食生产合理用水需求。各级人民政府应当组织做好农田水利建设和运行维护，保护和完善农田灌溉排水体系，因地制宜发展高效节水农业。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上人民政府应当组织开展水土流失综合治理、土壤污染防治和地下水超采治理。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第二十二条</strong>　国家推进农业机械产业发展，加强农业机械化作业基础条件建设，推广普及粮食生产机械化技术，鼓励使用绿色、智能、高效的农业机械，促进粮食生产全程机械化，提高粮食生产效率。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第二十三条</strong>　国家加强农业技术推广体系建设，支持推广应用先进适用的粮食生产技术，因地制宜推广间作套种等种植方法，鼓励创新推广方式，提高粮食生产技术推广服务水平，促进提高粮食单产。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家鼓励农业信息化建设，提高粮食生产信息化、智能化水平，推进智慧农业发展。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第二十四条</strong>　国家加强粮食生产防灾减灾救灾能力建设。县级以上人民政府应当建立健全农业自然灾害和生物灾害监测预警体系、防灾减灾救灾工作机制，加强干旱、洪涝、低温、高温、风雹、台风等灾害防御防控技术研究应用和安全生产管理，落实灾害防治属地责任，加强粮食作物病虫害防治和植物检疫工作。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家鼓励和支持开展粮食作物病虫害绿色防控和统防统治。粮食生产者应当做好粮食作物病虫害防治工作，并对各级人民政府及有关部门组织开展的病虫害防治工作予以配合。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第二十五条</strong>　国家加强粮食生产功能区和重要农产品生产保护区建设，鼓励农业生产者种植优质农作物。县级以上人民政府应当按照规定组织划定粮食生产功能区和重要农产品生产保护区并加强建设和管理，引导农业生产者种植目标作物。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第二十六条</strong>　国家采取措施稳定粮食播种面积，合理布局粮食生产，粮食主产区、主销区、产销平衡区都应当保面积、保产量。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;粮食主产区应当不断提高粮食综合生产能力，粮食主销区应当稳定和提高粮食自给率，粮食产销平衡区应当确保粮食基本自给。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家健全粮食生产者收益保障机制，以健全市场机制为目标完善农业支持保护制度和粮食价格形成机制，促进农业增效、粮食生产者增收，保护粮食生产者的种粮积极性。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;省级以上人民政府应当通过预算安排资金，支持粮食生产。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第二十七条</strong>　国家扶持和培育家庭农场、农民专业合作社等新型农业经营主体从事粮食生产，鼓励其与农户建立利益联结机制，提高粮食生产能力和现代化水平。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家支持面向粮食生产者的产前、产中、产后社会化服务，提高社会化服务水平，鼓励和引导粮食适度规模经营，支持粮食生产集约化。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第二十八条</strong>　国家健全粮食主产区利益补偿机制，完善对粮食主产区和产粮大县的财政转移支付制度，调动粮食生产积极性。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;省、自治区、直辖市人民政府可以根据本行政区域实际情况，建立健全对产粮大县的利益补偿机制，提高粮食安全保障相关指标在产粮大县经济社会发展综合考核中的比重。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第四章　粮食储备</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第二十九条</strong>　国家建立政府粮食储备体系。政府粮食储备分为中央政府储备和地方政府储备。政府粮食储备用于调节粮食供求、稳定粮食市场、应对突发事件等。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;中央政府粮食储备规模和地方政府粮食储备总量规模由国务院确定并实行动态调整。政府粮食储备的品种结构、区域布局按照国务院有关规定确定。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;政府粮食储备的收购、销售、轮换、动用等应当严格按照国家有关规定执行。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第三十条</strong>　承储政府粮食储备的企业或者其他组织应当遵守法律、法规和国家有关规定，实行储备与商业性经营业务分开，建立健全内部管理制度，落实安全生产责任和消防安全责任，对承储粮食数量、质量负责，实施粮食安全风险事项报告制度，确保政府粮食储备安全。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;承储中央政府粮食储备和省级地方政府粮食储备的企业应当剥离商业性经营业务。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;政府粮食储备的收购、销售、轮换、动用等应当进行全过程记录，实现政府粮食储备信息实时采集、处理、传输、共享，确保可查询、可追溯。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第三十一条</strong>　承储政府粮食储备的企业或者其他组织应当保证政府粮食储备账实相符、账账相符，实行专仓储存、专人保管、专账记载，不得虚报、瞒报政府粮食储备数量、质量、品种。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;承储政府粮食储备的企业或者其他组织应当执行储备粮食质量安全检验监测制度，保证政府粮食储备符合规定的质量安全标准、达到规定的质量等级。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第三十二条</strong>　县级以上地方人民政府应当根据本行政区域实际情况，指导规模以上粮食加工企业建立企业社会责任储备，鼓励家庭农场、农民专业合作社、农业产业化龙头企业自主储粮，鼓励有条件的经营主体为农户提供粮食代储服务。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第三十三条</strong>　县级以上人民政府应当加强粮食储备基础设施及质量检验能力建设，推进仓储科技创新和推广应用，加强政府粮食储备管理信息化建设。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第三十四条</strong>　县级以上人民政府应当将政府粮食储备情况列为年度国有资产报告内容，向本级人民代表大会常务委员会报告。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第五章　粮食流通</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第三十五条</strong>　国家加强对粮食市场的管理，充分发挥市场作用，健全市场规则，维护市场秩序，依法保障粮食经营者公平参与市场竞争，维护粮食经营者合法权益。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家采取多种手段加强对粮食市场的调控，保持全国粮食供求总量基本平衡和市场基本稳定。县级以上地方人民政府应当采取措施确保国家粮食宏观调控政策的贯彻执行。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第三十六条</strong>　县级以上地方人民政府应当加强对粮食仓储、物流等粮食流通基础设施的建设和保护，组织建设与本行政区域粮食收储规模和保障供应要求相匹配，布局合理、功能齐全的粮食流通基础设施，并引导社会资本投入粮食流通基础设施建设。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;任何单位和个人不得侵占、损毁、擅自拆除或者迁移政府投资建设的粮食流通基础设施，不得擅自改变政府投资建设的粮食流通基础设施的用途。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第三十七条</strong>　从事粮食收购、储存、加工、销售的经营者以及饲料、工业用粮企业，应当按照规定建立粮食经营台账，并向所在地的县级人民政府粮食和储备主管部门报送粮食购进、储存、销售等基本数据和有关情况。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第三十八条</strong>　为了保障市场供应、保护粮食生产者利益，必要时国务院可以根据粮食安全形势和财政状况，决定对重点粮食品种在粮食主产区实行政策性收储。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第三十九条</strong>　从事粮食收购、加工、销售的规模以上经营者，应当按照所在地省、自治区、直辖市人民政府的规定，执行特定情况下的粮食库存量。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第四十条</strong>　粮食供求关系和价格显著变化或者有可能显著变化时，县级以上人民政府及其有关部门可以按照权限采取下列措施调控粮食市场：</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（一）发布粮食市场信息；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（二）实行政策性粮食收储和销售；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（三）要求执行特定情况下的粮食库存量；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（四）组织投放储备粮食；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（五）引导粮食加工转化或者限制粮食深加工用粮数量；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（六）其他必要措施。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;必要时，国务院和省、自治区、直辖市人民政府可以依照《中华人民共和国价格法》的规定采取相应措施。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第四十一条</strong>　国家建立健全粮食风险基金制度。粮食风险基金主要用于支持粮食储备、稳定粮食市场等。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第六章　粮食加工</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第四十二条</strong>　国家鼓励和引导粮食加工业发展，重点支持在粮食生产功能区和重要农产品生产保护区发展粮食加工业，协调推进粮食初加工、精深加工、综合利用加工，保障粮食加工产品有效供给和质量安全。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;粮食加工经营者应当执行国家有关标准，不得掺杂使假、以次充好，对其加工的粮食质量安全负责，接受监督。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第四十三条</strong>　国家鼓励和引导粮食加工结构优化，增加优质、营养粮食加工产品供给，优先保障口粮加工，饲料用粮、工业用粮加工应当服从口粮保障。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第四十四条</strong>　县级以上地方人民政府应当根据本行政区域人口和经济社会发展水平，科学布局粮食加工业，确保本行政区域的粮食加工能力特别是应急状态下的粮食加工能力。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上地方人民政府应当在粮食生产功能区和重要农产品生产保护区科学规划布局粮食加工能力，合理安排粮食就地就近转化。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第四十五条</strong>　国家鼓励粮食主产区和主销区以多种形式建立稳定的产销关系，鼓励粮食主销区的企业在粮食主产区建立粮源基地、加工基地和仓储物流设施等，促进区域粮食供求平衡。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第四十六条</strong>　国家支持建设粮食加工原料基地、基础设施和物流体系，支持粮食加工新技术、新工艺、新设备的推广应用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第七章　粮食应急</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第四十七条</strong>　国家建立统一领导、分级负责、属地管理为主的粮食应急管理体制。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上人民政府应当加强粮食应急体系建设，健全布局合理、运转高效协调的粮食应急储存、运输、加工、供应网络，必要时建立粮食紧急疏运机制，确保具备与应急需求相适应的粮食应急能力，定期开展应急演练和培训。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第四十八条</strong>　国务院发展改革、粮食和储备主管部门会同有关部门制定全国的粮食应急预案，报请国务院批准。省、自治区、直辖市人民政府应当根据本行政区域的实际情况，制定本行政区域的粮食应急预案。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;设区的市级、县级人民政府粮食应急预案的制定，由省、自治区、直辖市人民政府决定。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第四十九条</strong>　国家建立粮食市场异常波动报告制度。发生突发事件，引起粮食市场供求关系和价格异常波动时，县级以上地方人民政府发展改革、农业农村、粮食和储备、市场监督管理等主管部门应当及时将粮食市场有关情况向本级人民政府和上一级人民政府主管部门报告。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十条</strong>　县级以上人民政府按照权限确认出现粮食应急状态的，应当及时启动应急响应，可以依法采取下列应急处置措施：</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（一）本法第四十条规定的措施；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（二）增设应急供应网点；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（三）组织进行粮食加工、运输和供应；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（四）征用粮食、仓储设施、场地、交通工具以及保障粮食供应的其他物资；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（五）其他必要措施。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;必要时，国务院可以依照《中华人民共和国价格法》的规定采取相应措施。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;出现粮食应急状态时，有关单位和个人应当服从县级以上人民政府的统一指挥和调度，配合采取应急处置措施，协助维护粮食市场秩序。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;因执行粮食应急处置措施给他人造成损失的，县级以上人民政府应当按照规定予以公平、合理补偿。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十一条　</strong>粮食应急状态消除后，县级以上人民政府应当及时终止实施应急处置措施，并恢复应对粮食应急状态的能力。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第八章　粮食节约</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十二条</strong>　国家厉行节约，反对浪费。县级以上人民政府应当建立健全引导激励与惩戒教育相结合的机制，加强对粮食节约工作的领导和监督管理，推进粮食节约工作。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上人民政府发展改革、农业农村、粮食和储备、市场监督管理、商务、工业和信息化、交通运输等有关部门，应当依照职责做好粮食生产、储备、流通、加工、消费等环节的粮食节约工作。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十三条</strong>　粮食生产者应当加强粮食作物生长期保护和生产作业管理，减少播种、田间管理、收获等环节的粮食损失和浪费。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;禁止故意毁坏在耕地上种植的粮食作物青苗。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家鼓励和支持推广适时农业机械收获和产地烘干等实用技术，引导和扶持粮食生产者科学收获、储存粮食，改善粮食收获、储存条件，保障粮食品质良好，减少产后损失。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十四条</strong>　国家鼓励粮食经营者运用先进、高效的粮食储存、运输、加工设施设备，减少粮食损失损耗。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十五条</strong>　国家推广应用粮食适度加工技术，防止过度加工，提高成品粮出品率。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;国家优化工业用粮生产结构，调控粮食不合理加工转化。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十六条</strong>　粮食食品生产经营者应当依照有关法律、法规的规定，建立健全生产、储存、运输、加工等管理制度，引导消费者合理消费，防止和减少粮食浪费。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;公民个人和家庭应当树立文明、健康、理性、绿色的消费理念，培养形成科学健康、物尽其用、杜绝浪费的良好习惯。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十七条</strong>　机关、人民团体、社会组织、学校、企业事业单位等应当加强本单位食堂的管理，定期开展节约粮食检查，纠正浪费行为。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;有关粮食食品学会、协会等应当依法制定和完善节约粮食、减少损失损耗的相关团体标准，开展节约粮食知识普及和宣传教育工作。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第九章　监督管理</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十八条</strong>　县级以上人民政府发展改革、农业农村、粮食和储备、自然资源、水行政、生态环境、市场监督管理、工业和信息化等有关部门应当依照职责对粮食生产、储备、流通、加工等实施监督检查，并建立粮食安全监管协调机制和信息共享机制，加强协作配合。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第五十九条</strong>　国务院发展改革、农业农村、粮食和储备主管部门应当会同有关部门建立粮食安全监测预警体系，加强粮食安全风险评估，健全粮食安全信息发布机制。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;任何单位和个人不得编造、散布虚假的粮食安全信息。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第六十条</strong>　国家完善粮食生产、储存、运输、加工标准体系。粮食生产经营者应当严格遵守有关法律、法规的规定，执行有关标准和技术规范，确保粮食质量安全。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上人民政府应当依法加强粮食生产、储备、流通、加工等环节的粮食质量安全监督管理工作，建立粮食质量安全追溯体系，完善粮食质量安全风险监测和检验制度。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第六十一条</strong>　县级以上人民政府有关部门依照职责开展粮食安全监督检查，可以采取下列措施：</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（一）进入粮食生产经营场所实施现场检查；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（二）向有关单位和人员调查了解相关情况；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（三）进入涉嫌违法活动的场所调查取证；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（四）查阅、复制有关文件、资料、账簿、凭证，对可能被转移、隐匿或者损毁的文件、资料、账簿、凭证、电子设备等予以封存；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（五）查封、扣押涉嫌违法活动的场所、设施或者财物；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（六）对有关单位的法定代表人、负责人或者其他工作人员进行约谈、询问。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;县级以上人民政府有关部门履行监督检查职责，发现公职人员涉嫌职务违法或者职务犯罪的问题线索，应当及时移送监察机关，监察机关应当依法受理并进行调查处置。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;第六十二条　</strong>国务院发展改革、自然资源、农业农村、粮食和储备主管部门应当会同有关部门，按照规定具体实施对省、自治区、直辖市落实耕地保护和粮食安全责任制情况的考核。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>省、自治区、直辖市对本行政区域耕地保护和粮食安全负总责，其主要负责人是本行政区域耕地保护和粮食安全的第一责任人，对本行政区域内的耕地保护和粮食安全目标负责。</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;县级以上地方人民政府应当定期对本行政区域耕地保护和粮食安全责任落实情况开展监督检查，将耕地保护和粮食安全责任落实情况纳入对本级人民政府有关部门负责人、下级人民政府及其负责人的考核评价内容。</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&emsp;&emsp;对耕地保护和粮食安全工作责任落实不力、问题突出的地方人民政府，上级人民政府可以对其主要负责人进行责任约谈。被责任约谈的地方人民政府应当立即采取措施进行整改。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第六十三条</strong>　外商投资粮食生产经营，影响或者可能影响国家安全的，应当按照国家有关规定进行外商投资安全审查。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第六十四条</strong>　县级以上人民政府发展改革、农业农村、粮食和储备等主管部门应当加强粮食安全信用体系建设，建立粮食生产经营者信用记录。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;单位、个人有权对粮食安全保障工作进行监督，对违反本法的行为向县级以上人民政府有关部门进行投诉、举报，接到投诉、举报的部门应当按照规定及时处理。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第十章　法律责任</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第六十五条</strong>　违反本法规定，地方人民政府和县级以上人民政府有关部门不履行粮食安全保障工作职责或者有其他滥用职权、玩忽职守、徇私舞弊行为的，对负有责任的领导人员和直接责任人员依法给予处分。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第六十六条　违反本法规定，种植不符合耕地种植用途管控要求作物的，由县级人民政府农业农村主管部门或者乡镇人民政府给予批评教育；经批评教育仍不改正的，可以不予发放粮食生产相关补贴；对有关农业生产经营组织，可以依法处以罚款。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第六十七条</strong>　违反本法规定，承储政府粮食储备的企业或者其他组织有下列行为之一的，依照有关行政法规的规定处罚：</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（一）拒不执行或者违反政府粮食储备的收购、销售、轮换、动用等规定；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（二）未对政府粮食储备的收购、销售、轮换、动用等进行全过程记录；</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;（三）未按照规定保障政府粮食储备数量、质量安全。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;从事粮食收购、储存、加工、销售的经营者以及饲料、工业用粮企业未按照规定建立粮食经营台账，或者报送粮食基本数据和有关情况的，依照前款规定处罚。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第六十八条</strong>　违反本法规定，侵占、损毁、擅自拆除或者迁移政府投资建设的粮食流通基础设施，或者擅自改变其用途的，由县级以上地方人民政府有关部门依照职责责令停止违法行为，限期恢复原状或者采取其他补救措施；逾期不恢复原状、不采取其他补救措施的，对单位处五万元以上五十万元以下罚款，对个人处五千元以上五万元以下罚款。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;<strong>&emsp;第六十九条</strong>　违反本法规定，粮食应急状态发生时，不服从县级以上人民政府的统一指挥和调度，或者不配合采取应急处置措施的，由县级以上人民政府有关部门依照职责责令改正，给予警告；拒不改正的，对单位处二万元以上二十万元以下罚款，对个人处二千元以上二万元以下罚款；情节严重的，对单位处二十万元以上二百万元以下罚款，对个人处二万元以上二十万元以下罚款。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第七十条</strong>　违反本法规定，故意毁坏在耕地上种植的粮食作物青苗的，由县级以上地方人民政府农业农村主管部门责令停止违法行为；情节严重的，可以处毁坏粮食作物青苗价值五倍以下罚款。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第七十一条</strong>　违反有关土地管理、耕地保护、种子、农产品质量安全、食品安全、反食品浪费、安全生产等法律、行政法规的，依照相关法律、行政法规的规定处理、处罚。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第七十二条</strong>　违反本法规定，给他人造成损失的，依法承担赔偿责任；构成违反治安管理行为的，由公安机关依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>第十一章　附则</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第七十三条</strong>　本法所称粮食，是指小麦、稻谷、玉米、大豆、杂粮及其成品粮。杂粮包括谷子、高粱、大麦、荞麦、燕麦、青稞、绿豆、马铃薯、甘薯等。</span></p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;油料、食用植物油的安全保障工作参照适用本法。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;<strong>第七十四条</strong>　本法自2024年6月1日起施行。</span></p>\n<section data-support=\"96编辑器\" data-style-id=\"51620\">\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>', '301', 15, NULL, '我国粮食安全有了专门保障法', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (406, '西安市农业类知识产权交易指导意见', 999999, '1759465915701510145', 'admin', '1', '2024-02-19 14:21:04', '2024-08-08 15:01:51', '2024-02-19 14:21:04', '0', 'admin', '要素交易市场', '新闻管理员', '300,302', '0', 0, 0, 0, '<p><img class=\"wscnph\" style=\"width: 500px;\" src=\"http://xianshi.osswjccxt.znxdcloud.com/znxd/2024/02/19/aa500e74531846f49d6f80b28bc3f0e1.png\" /></p>', '302', 21, NULL, '西安市农业类知识产权交易指导意见', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (407, '西安首家区（县、开发区）级“农交所”揭牌亮相', 999999, '1762761883465269250,1762762541975191553', 'xafby', '183', '2024-02-28 16:48:35', '2024-08-08 15:01:51', '2024-02-28 16:48:35', '2', 'xafby', ' 西安新闻网', '西安发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2月22日，城乡融合要素交易市场（西安长安）有限公司揭牌亮相，标志着西安市首家区（县、开发区）级城乡融合要素交易市场正式投入运营。西安市长安区政府副区长吴海军，西安农投集团党委委员、副总经理、西安城乡融合要素交易市场有限公司执行董事沈建锋，西安城乡融合要素交易市场有限公司相关负责人为公司揭牌。</span></p>\n<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/01c59933ae44475183abbcc337cc89b9.jpg\" width=\"1000\" /></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 城乡融合要素交易市场（西安长安）有限公司<strong>以城乡融合发展为核心，以壮大村集体经济和农民增收致富为目标，由西安城乡融合要素交易市场有限公司与西安市长安乡村振兴投资发展有限公司共同组建，是西安城乡融合要素交易市场有限公司的9个区（县）子公司之一，主要围绕农村资源资产产权交易类、农业农村项目投资招商类、农产品流通类，以及农村工程、货物及服务采购类4大类、21类资产交易进行服务。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 去年12月28日，全国首家以&ldquo;城乡融合要素&rdquo;命名的交易市场&mdash;&mdash;西安城乡融合要素交易市场正式揭牌成立。&ldquo;我们在二级交易平台上，下设有灞桥区、长安区、阎良区、高陵区、临潼区、鄠邑区、蓝田县、周至县、西咸新区9个子公司，高新区1个分公司。长安区城乡融合要素交易市场率先揭牌投运，将逐步拉开全市各区县子公司运营的帷幕。&rdquo;西安城乡融合要素交易市场有限公司相关负责人表示：<strong>&ldquo;这些子平台的运营和完善，将不断提升全市农村资源配置的信息化、标准化、规范化水平，为城乡融合发展、农业现代化、乡村振兴和农民增收注入新活力。&rdquo;</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 下一步，城乡融合要素交易市场（西安长安）有限公司将充分发挥&ldquo;政策+市场&rdquo;优势，遵循成熟一宗，推出一宗的原则，不断扩充和完善流转交易品种，积极推动引领城乡间各类要素资源高效利用，努力把城乡融合要素交易市场（西安长安）有限公司打造成实施乡村振兴战略的排头兵，助力西安农业高质量发展。</span></p>', '101', 113, NULL, '2月22日，城乡融合要素交易市场（西安长安）有限公司揭牌亮相，标志着西安市首家区（县、开发区）级城乡融合要素交易市场正式投入运营。', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (408, '权威访谈｜推进乡村全面振兴不断取得新成效——中央农办负责人解读2024年中央一号文件', 999999, NULL, 'xafby', '183', '2024-02-29 16:58:16', '2024-08-08 15:01:51', '2024-02-29 16:58:16', '2', 'xafby', '新华社', '西安发布员', '300,301', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; </span><span style=\"font-size: 18pt;\">新华社2月3日受权发布《中共中央 国务院关于学习运用&ldquo;千村示范、万村整治&rdquo;工程经验有力有效推进乡村全面振兴的意见》。这是党的十八大以来指导&ldquo;三农&rdquo;工作的第12个中央一号文件。中央农村工作领导小组办公室负责人当天就文件精神进行了全面解读。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>把学习运用&ldquo;千万工程&rdquo;经验作为重要引领</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件强调，&ldquo;要学习运用&lsquo;千万工程&rsquo;蕴含的发展理念、工作方法和推进机制&rdquo;&ldquo;把推进乡村全面振兴作为新时代新征程&lsquo;三农&rsquo;工作的总抓手&rdquo;&ldquo;集中力量抓好办成一批群众可感可及的实事&rdquo;。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;中央农办负责人表示，习近平总书记在浙江工作期间，亲自谋划推动&ldquo;千万工程&rdquo;，从整治农村环境入手，由点及面，扩容建设，经过20多年持续努力，不仅深刻改变了浙江农村的整体面貌，也为推进乡村全面振兴作出了先行探索和示范引路，各地都可学可鉴。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;必须看到，我国各地农村情况千差万别，自然条件、风土人情、发展水平、工作基础各不相同。学习运用&lsquo;千万工程&rsquo;经验，要因地制宜，不能生搬硬套、搞&lsquo;一刀切&rsquo;。&rdquo;中央农办负责人说，归根结底要让广大农民在乡村振兴中有实实在在的获得感，坚决反对搞形象工程、做表面文章。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;这位负责人表示，当前和今后一个时期，推进乡村全面振兴工作重点是确保国家粮食安全，确保不发生规模性返贫；提升乡村产业发展水平，提升乡村建设水平，提升乡村治理水平；强化科技和改革双轮驱动，强化农民增收举措。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>确保国家粮食安全、确保不发生规模性返贫</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;去年我国粮食总产量再创新高，连续9年稳定在1.3万亿斤以上。面对各种风险挑战和不确定性，如何保障粮食安全？</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件突出稳面积、增单产两手发力的导向，提出确保粮食产量保持在1.3万亿斤以上的目标。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;中央农办负责人表示，确保国家粮食安全，关键是调动农民种粮和地方抓粮积极性。要适当提高小麦最低收购价，继续实施耕地地力保护补贴、玉米大豆生产者补贴和稻谷补贴，扩大完全成本保险和种植收入保险政策实施范围，让农民种粮有钱挣。全面落实粮食安全党政同责，加大对产粮大县支持力度，探索建立粮食产销区省际横向利益补偿机制，让地方抓粮有动力。同时，把粮食增产的重心放到大面积提高单产上，实施粮食单产提升工程，集成推广良田良种良机良法，推动粮食产能迈上新台阶。以小农户为基础、新型农业经营主体为重点、社会化服务为支撑，构建现代农业经营体系，解决&ldquo;谁来种地&rdquo;问题。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;要健全耕地数量、质量、生态&lsquo;三位一体&rsquo;保护制度体系。&rdquo;这位负责人说，坚决整治乱占、破坏耕地违法行为，改革完善耕地占补平衡制度，适当提高高标准农田建设中央和省级投资补助水平，确保耕地数量有保障、质量有提升。同时，树立大农业观、大食物观，多渠道拓展食物来源，构建多元化食物供给体系。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;确保不发生规模性返贫是推进乡村全面振兴的底线要求，今年中央一号文件对此作出了部署。中央农办负责人表示，要压紧压实防止返贫工作责任，落实监测帮扶机制，加强跨部门信息整合共享，进一步提升监测及时性和帮扶精准性。加强产业和就业帮扶，强化帮扶产业分类指导，推进防止返贫就业攻坚行动。加大对重点地区帮扶支持力度，改善发展条件，增强&ldquo;造血&rdquo;能力，激发内生动力。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>提升乡村产业发展、乡村建设、乡村治理水平</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;乡村是广大农民群众的家园，只有营造安居乐业的良好环境，才能让农民有充足的获得感、幸福感、安全感。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件将&ldquo;三个提升&rdquo;作为推进乡村全面振兴的重点。中央农办负责人表示，要统筹推进乡村产业发展、乡村建设、乡村治理，建设宜居宜业和美乡村。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&mdash;&mdash;提升乡村产业发展水平。做好&ldquo;土特产&rdquo;文章，坚持产业兴农、质量兴农、绿色兴农，把农业建成现代化大产业。促进农村一二三产业融合发展，推动农产品加工业优化升级，推动农村流通高质量发展，加快构建农林牧渔并举、产加销贯通、农文旅融合的现代乡村产业体系。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&mdash;&mdash;提升乡村建设水平。加强村庄规划编制实效性、可操作性和约束力，适应乡村人口变化趋势，优化村庄布局、产业结构、公共服务配置。深入实施农村人居环境整治提升行动，完善农民参与和长效管护机制。统筹农村供水、道路、能源、信息和住房安全等建设，推进农村基础设施补短板，完善农村教育、医疗、养老等公共服务体系，及时回应群众关切。统筹新型城镇化和乡村全面振兴，促进县域城乡融合发展。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&mdash;&mdash;提升乡村治理水平，健全完善党组织领导的自治、法治、德治相结合的乡村治理体系，推进抓党建促乡村振兴，建好建强农村基层党组织。持续推进基层减负，优化各类涉农督查检查考核。加强农村精神文明建设，繁荣发展乡村文化，加强乡村优秀传统文化保护传承和创新发展，持续推进农村移风易俗，加强高额彩礼、大操大办等突出问题综合治理。坚持和发展新时代&ldquo;枫桥经验&rdquo;，建设平安乡村。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>强化科技和改革双轮驱动、强化农民增收举措</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;今年中央一号文件对强化科技和改革双轮驱动作出了系统部署。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;要协同推进科技创新和制度创新，激发农村各类要素潜能和主体活力，为推进乡村全面振兴注入强劲动能。&rdquo;中央农办负责人说，要优化农业科技创新战略布局，支持重大创新平台建设，加大核心技术攻关力度，提高农业科技创新体系整体效能。扭住种子这个要害，加快推进种业振兴行动，完善联合研发和应用协作机制。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;这位负责人表示，改革是乡村振兴的重要法宝，要启动实施第二轮土地承包到期后再延长30年整省试点，稳慎推进农村宅基地改革，深化农村集体产权制度改革。在坚守底线的前提下，鼓励各地实践探索和制度创新，强化改革举措集成增效。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;农业农村工作，说一千、道一万，增加农民收入是关键。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;党的十八大以来，农村居民收入持续较快增长，2023年农村居民人均可支配收入为21691元，城乡居民收入比进一步缩小到2.39∶1。&rdquo;中央农办负责人说，今年中央一号文件提出实施农民增收促进行动，目的就是巩固农民持续增收势头，促进共同富裕。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;这位负责人表示，要加强农民工职业技能培训，健全跨区域信息共享和有组织劳务输出机制，在重点工程项目和农业农村基础设施建设领域积极推广以工代赈。支持农户发展特色种养、手工作坊、林下经济等经营项目，完善产业联农带农机制。持续加大强农惠农富农政策力度，逐步提高农村社会保障水平，加强涉农资金项目监管，把给农民的各项补助补贴补到位。赋予农民更加充分的财产权益，鼓励以出租、合作开发、入股经营等方式盘活利用农村资源资产。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&emsp;&emsp;&ldquo;总而言之，要紧紧围绕增加农民收入这个中心任务，广辟增收门路，让农民群众钱袋子越来越鼓、日子越过越好。&rdquo;中央农办负责人说。</span></p>', '301', 26, NULL, '权威访谈｜推进乡村全面振兴不断取得新成效——中央农办负责人解读2024年中央一号文件', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (409, '陕西聚力“九个突破”推进农业农村工作', 999999, NULL, 'xafby', '183', '2024-02-29 17:05:31', '2024-08-08 15:01:51', '2024-02-29 17:05:31', '2', 'xafby', '陕西日报', '西安发布员', '300,302', '0', 0, 0, 0, '<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2023年，我省农业农村系统认真落实省委、省政府&ldquo;七个提升工程&rdquo;工作部署，守底线、促振兴、强保障，实现农业全面丰收、农民持续增收、农村和谐稳定。2024年，我省将重点围绕九项核心任务，聚力&ldquo;九个突破&rdquo;，推进农业农村高质量发展。</span></p>\n</section>\n</section>\n</section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以大面积单产提升为突破</strong>，抓好粮油生产。各地将围绕主要作物，分区域、分品种制定单产提升方案，分环节、分长势落实关键技术措施，重点在提高<a>复种指数</a>上挖潜力，同时落实好农业防灾减灾措施。</span></p>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以激发内生动力为突破</strong>，巩固拓展脱贫攻坚成果。我省将坚持监测与帮扶并重，提升监测帮扶实效；增强产业就业后劲；加强重点地区支持，将26个国家级和省级乡村振兴重点帮扶县作为重中之重，实行政策倾斜支持；凝聚社会帮扶合力，引导更多社会资本有序参与乡村振兴。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以设施农业建设为突破</strong>，稳定&ldquo;菜篮子&rdquo;供给。我省将做大畜牧业，挖掘陕北、渭北土地及饲草料的承载潜力，提升畜牧业体量及产值占比；突出设施改造做优蔬菜产业，打造10个城市近郊保供基地；用足用好部省共建机制，加快出台全省果业布局指导意见，引导产业向优势区聚集。</span></p>\n</section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以全产业链建设为突破</strong>，提升产业发展水平。我省坚持省抓千亿级、市抓百亿级、县抓十亿级农业产业链的思路不变，三级协同、梯次推进现代农业全产业链建设；进一步建强融合发展的平台载体；进一步探索联农带农的模式路径。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以高标准农田建设为突破</strong>，加强耕地保护。各地将打造高标准农田，建设好耕地；探索开展盐碱地综合利用试点，大力推广旱作节水农业，利用好耕地；治理农业面源污染，保护好耕地。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以科技赋能、改革赋能、数字赋能为突破，拉动发展引擎</strong>。我省将推动11项农业关键核心技术攻关，加快取得标志性成果；稳妥推进第二轮土地承包到期后再延长30年试点，稳慎推进农村宅基地制度改革试点，持续开展农村集体经济&ldquo;消薄培强&rdquo;行动。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>我省将以农村人居环境整治为突破，推动乡村建设</strong>。我省将以厕所改造、污水处理、垃圾治理为重点，持续深化农村人居环境整治；以科学规划为前提，不断改善和提升农村基础设施条件和公共服务水平。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 此外，我省将以坚持和发展新时代&ldquo;<a>枫桥经验</a>&rdquo;为突破，强化乡村治理；以&ldquo;千万工程&rdquo;为突破，争做乡村振兴西部示范。</span></p>\n</section>', '302', 18, NULL, '陕西聚力“九个突破”推进农业农村工作', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (410, '陕西省实施《中华人民共和国农村土地承包法》办法', 999999, NULL, 'xafby', '183', '2024-03-01 09:58:23', '2024-08-08 15:01:51', '2024-03-01 09:58:23', '2', 'xafby', '陕西日报', '西安发布员', '300,301', '0', 0, 0, 0, '<div style=\"text-align: left;\"><span style=\"font-size: 18pt;\"><strong>（2006年9月28日陕西省第十届人民代表大会常务委员会第二十七次会议通过 根据2014年11月27日陕西省第十二届人民代表大会常务委员会第十四次会议《陕西省人民代表大会常务委员会关于修改&lt;陕西省县乡两级人民代表大会代表选举实施细则&gt;等十七部地方性法规的决定》修正 2024年1月12日陕西省第十四届人民代表大会常务委员会第七次会议修订）</strong></span></div>\n<div>\n<div id=\"zoom\">\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>陕西省人民代表大会常务委员会</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>公告</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>〔十四届〕第十五号</strong></span></p>\n<p><span style=\"font-size: 18pt;\">《陕西省实施〈中华人民共和国农村土地承包法〉办法》已于2024年1月12日经陕西省第十四届人民代表大会常务委员会第七次会议修订通过，现予公布，自2024年4月1日起施行。</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">陕西省人民代表大会常务委员会&ensp;&ensp;&ensp;&ensp;&ensp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">2024年1月12日&nbsp; &nbsp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第一条</strong> &ensp; 为了实施《中华人民共和国农村土地承包法》，结合本省实际，制定本办法。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二条</strong> &ensp; 本省行政区域内的农村土地承包经营及承包经营管理，适用本办法。</span><span style=\"font-size: 18pt;\">本办法所称农村土地，是指农民集体所有和国家所有依法由农民集体使用的耕地、林地、草地以及其他依法用于农业的土地。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第三条</strong> &ensp; 县级以上人民政府农业农村、林业行政主管部门（以下统称农村土地承包主管部门）依照各自职责，负责本行政区域内农村土地承包经营及承包经营合同管理。主要职责是：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）宣传、贯彻、落实有关农村土地承包经营的法律、法规、规章和政策；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）指导农村土地承包合同和流转合同的签订；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）监督、检查农村土地承包合同和流转合同的履行；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （四）土地经营权流转市场建设和监督管理；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （五）支持有关调解组织和农村土地承包仲裁委员会依法开展工作；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （六）负责农村土地承包合同和流转合同的档案管理；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （七）做好工商企业等社会资本流转土地经营权风险防范工作；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （八）负责法律、法规、规章规定的其他工作。</span></p>\n<p><span style=\"font-size: 18pt;\">县级以上人民政府自然资源行政主管部门负责土地承包经营权登记颁证工作。</span></p>\n<p><span style=\"font-size: 18pt;\">乡（镇）人民政府、街道办事处负责本行政区域内的农村土地承包经营及承包经营合同管理。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第四条</strong> &ensp; 县级以上人民政府应当健全农村土地承包经营权信息应用平台，完善省、市、县、乡、村五级数据采集、传输、共享和隐私保护机制，提升农村土地承包经营数字化管理水平。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第五条</strong> &ensp; 县级以上人</span><span style=\"font-size: 18pt;\">民政府应当将农村土地承包经营及管理和农村土地承包经营纠纷仲裁工作所需费用，纳入本级财政预算，不得以任何名义向农民收取。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第六条</strong> &ensp; 以家庭承包方式承包农村土地的，本集体经济组织的农户是承包方。</span><span style=\"font-size: 18pt;\">农户内家庭成员依法平等享有承包土地的各项权益，承包中应当保护妇女在土地承包经营中的知情权、参与权、表达权、监督权和收益权等各项权益。</span><span style=\"font-size: 18pt;\">村民自治章程、村规民约以及村民会议或者村民代表会议的决定不得有侵犯村民土地承包经营权的内容。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第七条</strong> &ensp; 农村土地承包方案，应当依法经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并向全体村民公示，公示期不得少于十五日。对于因村民居住分散或者人户分离等原因无法集中公示的，发包方应当采取其他方式告知，保障有关人员的知情权。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第八条</strong> &ensp; 耕地的承包期为三十年，草地的承包期为三十年至五十年，林地的承包期为三十年至七十年。</span><span style=\"font-size: 18pt;\">前款规定的耕地承包期届满后再延长三十年，草地、林地承包期届满后依照前款规定相应延长。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第九条</strong> &ensp; 发包方应当依法与承包方签订书面承包合同</span><span style=\"font-size: 18pt;\">。承包合同自成立之日起生效。承包方自承包合同生效时取得土地承包经营权。</span><span style=\"font-size: 18pt;\">承包方对承包的土地享有占有、使用、收益的权利，有权自主组织生产经营和处置产品，有权依法互换、转让土地承包经营权和依法流转土地经营权。承包地被依法征收、征用、占用时，承包方有依法取得补偿的权利。</span><span style=\"font-size: 18pt;\">承包方应当依法保护和合理利用土地，不得给土地造成永久性损害，维持土地的农业用途，未经依法批准不得用于非农建设。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第十条</strong> &ensp; 实行家庭承包的，依法向登记机构申请土地承包经营权登记；以其他方式承包的，依法向登记机构申请土地经营权登记。</span><span style=\"font-size: 18pt;\">实行家庭承包的土地承包合同生效后，发包方应在三十个工作日内，将土地承包方案、承包方及承包土地的详细情况、土地承包合同等材料报乡（镇）人民政府、街道办事处初审后，报县级农村土地承包主管部门审核。经审核合格的，设区的市或者县（市、区）不动产登记机构依法向承包方颁发不动产权属证书，并登记造册，确认土地承包经营权。不动产权属证书应当列明享有土地承包经营权的全部家庭成员。</span><span style=\"font-size: 18pt;\">不动产权属证书应当发到农户，任何单位和个人不得代为保管或者扣留。</span><span style=\"font-size: 18pt;\">流转期限为五年以上的土地经营权，以及互换、转让土地承包经营权，当事人可以向登记机构申请登记；未经登记，不得对抗善意第三人。</span><span style=\"font-size: 18pt;\">不动产权属证书丢失、损毁，需要补发、换发证书的，设区的市或者县（市、区）不动产登记机构应当按规定予以办理。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第十一条</strong> &ensp; 以家庭承包方式承包土地，承包期内，发包方不得收回承包地。</span></p>\n<p><span style=\"font-size: 18pt;\">有下列情形之一的，发包方依法收回承包地：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）耕地、草地农村承包经营户内家庭成员全部死亡的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）林地农村承包经营户内家庭成员全部死亡且无继承人的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）农村承包经营户内家庭成员全部丧失中华人民共和国国籍的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （四）农村承包经营户内家庭成员全部迁入其他集体经济组织并取得承包地的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （五）农村承包经营户内家庭成员全部自愿放弃承包土地的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （六）法律、法规规定的其他情形。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第十二条</strong> &ensp; 承包期内，妇女结婚，在新居住地未取得承包地的，发包方不得收回其原承包地；妇女离婚或者丧偶，仍在原居住地生活或者不在原居住地生活但在新居住地未取得承包地的，发包方不得收回其原承包地。</span><span style=\"font-size: 18pt;\">因结婚男方到女方家落户的，适用前款规定。</span><span style=\"font-size: 18pt;\">农村集体经济组织成员结婚后在迁入地取得承包地的，迁入地的集体经济组织应当书面告知迁出地的农村集体经济组织。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第十三条</strong> &ensp; 承包期内，农村承包经营户因分户或者离婚需要对原承包地重新分配的，具有土地承包经营权的全部家庭成员应当协商达成一致意见，并形成书面分配协议，协商不成的，按照人口比例平均分配。分户后的农村承包经营户应当依据分配协议与发包方分别签订书面承包合同，并依法申请土地承包经营权转移登记。</span><span style=\"font-size: 18pt;\">承包地分割后的承包期限为家庭承包的剩余承包期限。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第十四条</strong> &ensp; 承包期内，发包方不得调整承包地。</span><span style=\"font-size: 18pt;\">承包期内，因自然灾害严重毁损承包地等特殊情形对个别农户之间承包的耕地和草地需要适当调整的，必须经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并报乡（镇）人民政府和县级农村土地承包主管部门批准。承包合同中约定不得调整的，按照其约定。</span><span style=\"font-size: 18pt;\">农村承包土地调整后，应当依法订立或者变更土地承包合同，合同终止日期应当与本集体经济组织其他同类土地承包合同终止日期一致，并依法申请土地承包经营权变更登记或者转移登记。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第十五条</strong> &ensp; 下列土地应当用于调整承包土地或者承包给新增人口：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）集体经济组织依法预留的机动地；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）集体经济组织通过依法开垦、复垦等方式增加的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）发包方依法收回的和承包方依法、自愿交回的。</span></p>\n<p><span style=\"font-size: 18pt;\">前款所列土地尚未用于调整承包土地或者发包给新增人口的，流转期不得超过五年。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第十六条</strong> &ensp; 承包方家庭成员因年龄、身体、就业等原因无法继续耕种的，发包方应当引导、支持承包方通过农业生产托管、土地经营权依法流转等方式进行耕种。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第十七条</strong> &ensp; 以家庭承包方式承包土地，承包方将承包土地弃耕抛荒连续满两年的，发包方应当督促承包方恢复耕种；经督促承包方仍未恢复耕种的，发包方可以组织临时代耕。组织代耕前，发包方应当书面告知承包方，并在本集体经济组织公告。</span><span style=\"font-size: 18pt;\">代耕期间，代耕者按照与发包方的约定享有代耕收益，同时履行承包方应当履行的法定义务。承包方要求恢复耕作的，代耕者在收获当季农作物后交回承包地，承包方应当对代耕者提高地力的投入予以合理补偿。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第十八条</strong> &ensp; 承包方在承包期限内有权依法自主决定土地经营权是否流转，以及流转对象、方式、期限等。</span><span style=\"font-size: 18pt;\">土地经营权流转收益归承包方所有，任何组织和个人不得擅自截留、扣缴。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第十九条</strong> &ensp; 承包方征得户内享有土地承包经营权的家庭成员同意后，可以采取出租（转包）、入股或者其他符合有关法律和国家政策规定的方式流转土地经营权。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十条</strong> &ensp; 土地经营权流转合同，鼓励采用农业农村部、国家林业和草原局会同国家市场监督管理总局制定的合同示范文本。乡（镇）人民政府、街道办事处应当向达成流转意向的双方提供统一文本格式的流转合同，并指导签订。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十一条</strong> &ensp; 县级以上人民政府及其有关行政主管部门应当推动建立土地经营权流转市场或者农村产权交易市场，加强业务指导，督促其建立健全运行规则，规范开展土地经营权流转政策咨询、信息发布、合同签订、交易鉴证、权益评估、融资担保、档案管理等服务。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十二条</strong> &ensp; 县级以上人民政府应当建立工商企业等社会资本通过流转取得土地经营权的分级资格审查和项目审核制度。审查审核的一般程序如下：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）受让主体与承包方就流转面积、期限、价款等进行协商并签订流转意向协议书。涉及未承包到户集体土地等集体资源的，应当按照法定程序经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并与集体经济组织签订流转意向协议书；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）受让主体按照分级审查审核规定，分别向乡（镇）人民政府或者县级以上人民政府农村土地承包主管部门提出申请，并提交流转意向协议书、农业经营能力或者资质证明、流转项目规划等相关材料；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）乡（镇）人民政府或者县级以上人民政府应当依法组织相关职能部门、农村集体经济组织代表、农民代表、专家等就土地用途、经营项目是否符合粮食生产等产业规划以及受让主体农业经营能力等进行审查审核，并于受理之日起二十个工作日内作出审查审核意见；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （四）审查审核通过的，受让主体与承包方签订土地经营权流转合同。未按规定提交审查审核申请或者审查审核未通过的，不得开展土地经营权流转活动。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十三条</strong> &ensp; 县级以上人民政府依法建立风险防范制度，对工商企业等社会资本通过流转取得土地经营权加强事中事后监管，及时查处纠正违法违规行为。</span><span style=\"font-size: 18pt;\">鼓励承包方和受让方在土地经营权流转市场或者农村产权交易市场公开交易。</span><span style=\"font-size: 18pt;\">对整村（组）土地经营权流转面积较大、涉及农户较多、经营风险较高的项目，流转双方可以协商设立风险保障金。</span><span style=\"font-size: 18pt;\">鼓励保险机构为土地经营权流转提供流转履约保证保险等多种形式保险服务。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 第二十四条</strong> &ensp; 省农村土地承包主管部门应当按照国家和本省有关规定将工商企业等社会资本在土地经营中的失信行为纳入公共信用信息共享平台，依法对严重失信主体实施惩戒措施。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十五条</strong> &ensp; 承包合同管理工作中形成的，对国家、社会和个人有保存价值的文字、图表、声像、数据等各种形式和载体的材料，应当纳入农村土地承包档案管理。</span><span style=\"font-size: 18pt;\">县级以上农村土地承包主管部门、自然资源主管部门和乡（镇）人民政府、街道办事处，应当按照各自职责建立和完善农村土地承包合同、土地承包经营权登记颁证等档案资料管理制度，确保农村土地承包档案完整与安全。</span><span style=\"font-size: 18pt;\">发包方应当将农村土地承包档案纳入村级档案管理。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十六条</strong> &ensp; 承包经营权人、利害关系人有权查阅、复制农村土地承包登记资料或者经营权证登记簿。有关部门及其工作人员应当提供便利，不得限制和阻挠，不得收取费用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十七条</strong> &ensp; 违反本办法规定的行为，《中华人民共和国农村土地承包法》和国家有关法律、法规已经作出具体处罚规定的，从其规定。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十八条</strong> &ensp; 发包方有下列行为之一的，由乡（镇）人民政府或者县级以上农业农村、林业、自然资源等行政主管部门责令限期改正；给当事人造成损失的，依法承担赔偿责任：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）擅自变更土地承包期限的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）强制代保管、扣留或者擅自更改不动产权属证书的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）其他侵害土地承包经营权的行为。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第二十九条</strong> &ensp; 承包方、土地经营权人违法将承包地用于非农建设的，由县级以上自然资源等有关行政主管部门依法予以处罚。</span><span style=\"font-size: 18pt;\">承包方给承包地造成永久性损害的，发包方有权制止，并有权要求赔偿由此造成的损失。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第三十条</strong> &ensp; 国家机关及其工作人员利用职权有下列行为之一，给承包经营当事人造成损失的，应当依法承担损害赔偿等责任；情节严重的，由上级机关或者所在单位依法给予直接责任人员处分；构成犯罪的，依法追究刑事责任：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）干涉农村土地承包经营，变更、解除承包经营合同的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）干涉承包经营当事人依法享有的生产经营自主权的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）强迫、阻碍承包经营当事人进行土地承包经营权互换、转让或者土地经营权流转等侵害土地承包经营权、土地经营权的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （四）违反规定登记、颁发、变更、注销不动产权属证书的；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （五）其他滥用职权、玩忽职守、徇私舞弊的侵害土地承包经营权的行为。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>第三十一条</strong> &ensp; 本办法自2024年4月1日起施行。</span></p>\n</div>\n</div>', '301', 52, NULL, '陕西省实施《中华人民共和国农村土地承包法》办法', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (411, '国务院批复《陕西省国土空间规划（2021-2035）》', 999999, NULL, 'xafby', '183', '2024-03-01 14:17:41', '2024-08-08 15:01:51', '2024-03-01 14:17:41', '0', 'xafby', '古都先锋公众号', '西安发布员', '300,301', '0', 0, 0, 0, '<p>&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>国务院关于</strong><strong>《陕西省国土空间规划（2021&mdash;2035年）》的批复</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">（国函〔2024〕18号）</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">全文如下：</span></p>\n<p><span style=\"font-size: 18pt;\">陕西省人民政府、自然资源部：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 你们关于报请批准《陕西省国土空间规划（2021&mdash;2035年）》的请示收悉。现批复如下：</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 一、原则同意自然资源部审查通过的《陕西省国土空间规划（2021&mdash;2035年）》（以下简称《规划》）。</strong>《规划》是陕西省空间发展的指南、可持续发展的空间蓝图，是各类开发保护建设活动的基本依据，请认真组织实施。陕西省地处我国内陆腹地，是中华民族和华夏文明重要发祥地之一，是新时代西部大开发、共建&ldquo;一带一路&rdquo;的重要节点地区。《规划》实施要坚持以习近平新时代中国特色社会主义思想为指导，全面贯彻落实党的二十大精神，完整、准确、全面贯彻新发展理念，坚持以人民为中心，统筹发展和安全，促进人与自然和谐共生，奋力谱写中国式现代化建设陕西篇章。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 二、筑牢安全发展的空间基础。</strong>到2035年，陕西省耕地保有量不低于4326.00万亩，其中永久基本农田保护面积不低于3377.60万亩；生态保护红线面积不低于4.86万平方千米；城镇开发边界扩展倍数控制在基于2020年城镇建设用地规模的1.3倍以内；单位国内生产总值建设用地使用面积下降不少于40%；用水总量不超过国家下达指标，其中2025年不超过107.0亿立方米。明确自然灾害风险重点防控区域，划定洪涝等风险控制线，落实战略性矿产资源、历史文化保护等安全保障空间，全面锚固高质量发展的空间底线。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 三、构建支撑新发展格局的国土空间体系。</strong>深入实施区域协调发展战略、区域重大战略、主体功能区战略、新型城镇化战略和乡村振兴战略，促进形成主体功能明显、优势互补、高质量发展的国土空间开发保护新格局。深度融入黄河流域生态保护和高质量发展、新时代西部大开发等国家重大战略，加强关中平原城市群与成渝地区双城经济圈以及长江中游、中原、山西中部、兰州&mdash;西宁等城市群协同发展，加快形成面向中亚南亚西亚国家的重要对外开放通道，打造内陆改革开放高地。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 四、系统优化国土空间开发保护格局。</strong>发挥区域比较优势，优化主体功能定位，细化主体功能区划分，完善差别化支持政策。以陕北长城沿线旱作区和黄土高原旱作区、关中灌区、汉中盆地为重点，拓展特色农产品生产空间，夯实粮食安全基础。健全秦岭常态化长效化保护机制，筑牢秦岭&mdash;大巴山、陕北黄土高原&mdash;长城沿线生态屏障，实施江河源头、森林、河湖、湿地系统保护修复，加强珍稀野生动植物和古树名木保护。加强黄河流域生态保护修复，推进水土流失、荒漠化综合治理，守护好黄河母亲河。严格河湖水域空间管控，实施地下水污染综合治理，强化南水北调中线水源地保护。积极培育西安都市圈，提升宝鸡、渭南、汉中、榆林等城市的综合承载能力，促进陕北、关中、陕南区域协调发展，分类优化乡村空间布局，加强传统村落和乡村特色风貌保护，提升城乡人居环境品质。发挥区位、科教和能源资源优势，统筹传统和新型基础设施空间布局，积极参与西部陆海新通道建设，打造国家重要科研和文教中心、高新技术产业和制造业基地。加强自然灾害防治，优化防灾减灾救灾设施区域布局，提高国土空间安全韧性。健全文化遗产与自然遗产空间保护机制，强化文化遗产与自然遗产整体保护和系统活化利用，加强秦岭、华山、黄帝陵、兵马俑等自然和文化遗产保护，加强延安宝塔等红色文化遗产传承保护，构建文化资源、自然资源、景观资源整体保护的空间体系。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 五、维护规划严肃性权威性。</strong>《规划》是对陕西省国土空间作出的全局安排，是省域国土空间保护、开发、利用、修复的政策和总纲，必须严格执行，任何部门和个人不得随意修改、违规变更。按照定期体检和五年一评估的要求，健全各级各类国土空间规划实施监测评估预警机制，将规划评估结果作为规划实施监督考核的重要依据。建立健全规划监督、执法、问责联动机制，实施规划全生命周期管理。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 六、做好规划实施保障。</strong>陕西省人民政府要加强组织领导，明确责任分工，健全工作机制，完善配套政策措施。做好《规划》印发和公开，强化社会监督。组织完成地方各级国土空间总体规划、详细规划、相关专项规划编制工作，加快形成统一的国土空间规划体系，统筹国土空间开发保护。强化对水利、交通、能源、农业、信息、市政等基础设施以及公共服务设施、军事设施、生态环境保护、文物保护、林业草原等专项规划的指导约束，在国土空间规划&ldquo;一张图&rdquo;上协调解决矛盾问题，合理优化空间布局。建立健全国土空间规划委员会制度，发挥对国土空间规划编制实施管理的统筹协调作用。按照&ldquo;统一底图、统一标准、统一规划、统一平台&rdquo;的要求，完善国土空间规划&ldquo;一张图&rdquo;系统和国土空间基础信息平台，建设国土空间规划实施监测网络，提高空间治理数字化水平。自然资源部要会同有关方面根据职责分工，密切协调配合，加强指导、监督和评估，确保实现《规划》确定的各项目标和任务。各有关部门要坚决贯彻党中央、国务院关于&ldquo;多规合一&rdquo;改革的决策部署，不在国土空间规划体系之外另设其他空间规划。《规划》实施中的重大事项要及时请示报告。</span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">国务院&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">2024年1月31日</span></p>', '301', 66, NULL, '国务院批复《陕西省国土空间规划（2021-2035）》', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
