INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (435, '城乡融合要素交易市场（西安灞桥）有限公司揭牌！', 999999, '1776908227400249346', 'xafby', '183', '2024-04-07 16:49:19', '2024-08-08 15:01:51', '2024-04-07 16:49:19', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月26日，城乡融合要素交易市场（西安灞桥）有限公司揭牌仪式在灞桥区京都国际举行。西安市灞桥区政府副区长刘强，西安农投集团党委副书记、副总经理李英杰，西安市灞桥区农业农村局副局长奚波，秦农银行灞桥支行行长王利安，西安城乡融合要素交易市场有限公司相关负责人为公司揭牌。</span></p>\n<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/57d59d11f016420794a5cfd8c25c3517.png\" width=\"1000\" /></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 城乡融合要素交易市场（西安灞桥）有限公司由西安城乡融合要素交易市场有限公司与西安白鹿原现代农旅投资集团有限公司共同出资组建，于2024年2月4日完成工商注册工作，交易大厅内设交易申请、交易审核、交易办理、交易鉴证、产权评估、抵押登记等6个业务窗口，与区农业农村局下属区农村产权流转交易中心合署办公。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 活动现场，工作人员对城乡融合要素交易平台的门户、管理系统进行了介绍，从发布交易项目、线上报名、缴纳保证金、交易竞价、交易成交等方面对交易流程进行详细讲解，同时介绍了手机APP客户端的使用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 城乡融合要素交易市场（西安灞桥）有限公司的成立，<strong>吹响了灞桥区城乡融合要素交易运营的号角，灞桥公司将按照区委、区政府的统一部署、统一规划，全面建立健全街道级中心站和村级信息站。按照&ldquo;应进必进、阳光交易&rdquo;的工作原则，确保各类要素在城乡之间平等交换、双向流动，推动公平交易及市场规范化建设，助力村集体经济组织和农民致富增收，加快推进灞桥区乡村振兴和城乡融合发展。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 下一步，城乡融合要素交易市场（西安灞桥）有限公司将秉承<strong>&ldquo;以人为本、善用资源、创造价值、服务三农&rdquo;的经营理念，紧抓集体经营性建设用地入市等改革机遇，充分利用集体产权制度改革的成果，以城乡融合发展为核心，以壮大村集体经济和农民增收致富为目标，发挥市场优化资源配置的作用，最大限度挖掘释放农村资源价值潜力，不断提升农村资源配置的信息化、标准化、规范化水平，助力都市型农业强市建设。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安市灞桥区农村产权流转交易服务中心主任杨向东，西安白鹿原现代农旅投资集团有限公司总经理赵楠，秦农银行乡村振兴金融部副总经理赵东，要素交易市场分、子公司负责同志，媒体代表等共计30余人参加活动。</span></p>', '101', 63, NULL, '城乡融合要素交易市场（西安灞桥）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (436, '城乡融合要素交易市场（西安鄠邑）有限公司揭牌', 999999, '1778620325671022593', 'xafby', '183', '2024-04-12 11:03:40', '2024-08-08 15:01:51', '2024-04-12 11:03:40', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月27日，城乡融合要素交易市场（西安鄠邑）有限公司揭牌仪式在西安市鄠邑区人民路秦农银行大厦一楼举行。西安市鄠邑区区委副书记任涛，西安农投集团党委委员、副总经理、西安城乡融合交易市场有限公司执行董事沈建锋为公司揭牌。</span></p>\n<section><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/535ccc1bc2024b3fad2d52f71cf03f23.png\" width=\"1000\" /></span></section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 活动现场，工作人员对城乡融合要素交易平台的门户、管理系统进行了介绍，从发布交易项目、线上报名、缴纳保证金、交易竞价、交易成交等方面对交易流程进行详细讲解，同时介绍了手机APP客户端的使用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 城乡融合要素交易市场（西安鄠邑）有限公司在区级、各街道（景区管理局）农村产权流转交易服务站的管理职责基础上，融入资产监管运营服务职责，确保城乡融合要素流转交易实现&ldquo;一站式&rdquo;服务。采取&ldquo;A+B&rdquo;模式，即&ldquo;人民路秦农银行综合交易中心+政务服务中心交易场所&rdquo;，并设&ldquo;城乡融合要素交易市场（西安鄠邑）有限公司&rdquo;统一标识。政务服务中心交易场所初步选定了1个不少于50平方米的办公场所及2个要素交易接件窗口，通过建设街道交易服务站的方式以及&ldquo;A+B的模式，鄠邑区将快速在全区形成以点盖面、点点发力的架构体系，大力推进城乡要素交易工作开展。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp;</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 据悉，鄠邑区将由<strong>区纪委监委牵头制定公职人员履行推进城乡融合要素交易市场体系建设和管理责任的执行监督机制，切实加强对农村集体&ldquo;三资&rdquo;监管，确保农村集体&ldquo;三资&rdquo;应进必进，阳光交易。同时，对在推进城乡融合要素交易市场建设和运行中工作不力、阻碍城乡融合要素&ldquo;应进必进、阳光交易&rdquo;的，区纪委监委将依法依纪严肃问责，确保城乡融合要素交易市场在阳光下运行。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 下一步，鄠邑区城乡融合交易市场将在鄠邑区委、区政府正确领导下、在市城乡融合要素公司的业务指导下以及在全区各街道办的大力支持下，积极推动公平交易及市场规范化建设，助力鄠邑区村集体经济组织和农民致富增收。</span></p>\n<p><span style=\"font-size: 18pt;\">鄠邑区发改委、区财政局、区农业农村局、区资源规划分局、区秦岭保护局（区林业局）、区水务局、区住建局、区市场监管局、鄠邑农业产业发展集团分管负责同志，各街办、景区管理局分管负责同志，秦农银行总行、鄠邑支行，要素交易市场分、子公司负责同志，媒体代表等共计40余人参加活动。</span></p>', '101', 45, NULL, '城乡融合要素交易市场（西安鄠邑）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
