INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (465, '城乡融合要素交易市场  （西安高陵）有限公司揭牌', 999999, '1795382268180115457', 'xafby', '183', '2024-05-28 16:50:40', '2024-08-08 15:01:51', '2024-05-28 16:50:40', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 4月22日，城乡融合要素交易市场（西安高陵）有限公司揭牌仪式在高陵区农经中心交易大厅举行。高陵区区委常委、副区长张跃进，西安农投集团党委委员、副总经理杨彬，西安城乡融合要素交易市场有限公司总经理贠涛、副总经理郑小龙，农业农村局党委书记、局长张军，农经中心党组书记、主任来刚，区城市建设开发有限公司董事长、总经理黄继锋，秦农银行高陵支行党委书记鹿峰为公司揭牌。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/05/28/5607cdb5fd7e40c988890535cb3577e4.jpg\" /></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 城乡融合要素交易市场（西安高陵）有限公司根据西安市委、市政府建立城乡融合要素交易市场的相关要求，在高陵区委、区政府的部署安排下，在区国资局、区农经中心、西安城乡要素交易市场有限公司、秦农银行高陵支行等部门的全力配合下，于3月28日完成工商注册，并完成办公选址、内部装修及设备配置等前期准备工作，今天正式对外投运。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 活动现场，工作人员全面介绍了城乡融合要素交易平台四大类21项交易品种，对出让申请、资料审核、信息发布、征集意向受让方、交易竞价、成交公示、合同签订、价款结算、交易鉴证等交易全流程进行了详细讲解，宣讲了农村各类资源资产进入平台交易的价值和重要意义。</span></p>\n<p>&nbsp;</p>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 未来发展中，<strong>高陵区城乡融合要素交易市场将探索创新工作模式，持续整合平台功能作用，紧抓农村宅基地制度改革试点、农村集体经营性建设用地入市试点等改革机遇，集中促进&ldquo;人、地、钱&rdquo;等各类发展要素顺畅流通，为城市和农村发展提供要素保障，盘活农村闲置资源，提高溢价率，为壮大农村集体经济、增加农民收入作出新贡献。</strong></span></p>\n<p><span style=\"font-size: 18pt;\">西安城乡融合要素交易市场有限公司、高陵区农业农村和林业局、资源规划高陵分局、区住建局、区水务局、区市场监管局分管同志及秦农银行总行相关负责人，各街道分管领导和部分村主要负责人，新闻媒体代表等共计40余人参加活动。</span></p>\n</section>\n</section>\n<section>\n<section></section>\n</section>', '101', 51, NULL, '城乡融合要素交易市场  （西安高陵）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (466, '城乡融合要素交易市场（西安周至）有限公司揭牌', 999999, '1795383899848257538', 'xafby', '183', '2024-05-28 17:16:09', '2024-08-08 15:01:51', '2024-05-28 17:16:09', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/24/310ca540338b4d20a9222737a87ac43c.jpg\" width=\"1000\" />&nbsp; &nbsp; &nbsp; 4月25日，城乡融合要素交易市场（西安周至）有限公司揭牌仪式在周至县中心东街47号举行。周至县政府副县长王刚利，西安城乡融合要素交易市场有限公司副总经理龚文军为公司揭牌。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 根据西安市委、市政府建立城乡融合要素交易市场的相关要求，城乡融合要素交易市场（西安周至）有限公司于4月8日注册成立，在完成了办公选址、内部装修及设备配置等前期准备工作后今天正式对外投运。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 未来发展中，<strong>城乡融合要素交易市场周至公司将依托周至县良好的自然环境、深厚的历史文化底蕴和丰富的农业资源，深度挖掘、整合农村资源要素，充分发挥&ldquo;统一规范、公正透明&rdquo;的阳光交易平台功能作用，把碎片化的资源进行规模化的收储、专业化的整合、市场化的运作，实现农村资产资源价值最大化，为村集体经济培育新的&ldquo;增长支点&rdquo;，为周至县乡村振村作出新贡献。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 县农业农村局、县资源规划局、县住建局、县水务局、县秦保局、西安城乡融合要素交易市场有限公司等单位分管负责同志，各镇（街）分管领导，秦农银行总行相关负责人等20余人参加活动。</span></p>\n<section data-style-id=\"23172\" data-type=\"undefined\" data-id=\"23172\" data-vip=\"1\" data-free=\"0\">\n<section>\n<section data-width=\"20%\"></section>\n</section>\n</section>', '101', 44, NULL, '城乡融合要素交易市场（西安周至）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (467, '城乡融合要素交易市场（西咸新区）有限公司揭牌', 999999, '1795384924709662721', 'xafby', '183', '2024-05-28 17:20:46', '2024-08-08 15:01:51', '2024-05-28 17:20:46', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>4月25日，城乡融合要素交易市场（西咸新区）有限公司在西咸新区沣东新城上林街道党群服务中心正式揭牌成立，西咸新区管委会副主任李志锋，西安农投集团党委委员、副总经理杨彬出席开业仪式并为公司揭牌。</p>\n<p>活动现场，相关负责人向与会者全面介绍了西咸新区城乡融合要素交易市场的组建情况、交易指南、交易品类以及交易流程等内容。</p>\n<p>开业仪式结束后，现场还开展了西咸新区城乡融合要素交易首场培训，<strong>西咸新区19个镇街分管负责同志和业务科室负责人参加培训，首次培训实现全覆盖。</strong>会上，围绕&ldquo;什么是城乡融合要素交易市场&rdquo;、&ldquo;为什么要做城乡融合要素交易市场&rdquo;以及&ldquo;城乡融合要素交易市场怎么做&rdquo;等核心问题展开深入讲解，与会人员进一步理解了农村各类资源资产进入平台交易的价值和重要意义。</p>\n<p>西咸城乡融合要素交易市场的开业标志着该交易市场正式投入运营。未来，城乡融合要素交易市场将成为连接城乡、促进资源优化配置的重要平台，为推动区域经济社会持续健康发展发挥重要作用。</p>\n<p>西咸新区基层工作部、西安城乡融合要素交易市场有限公司、西咸城乡发展集团主要负责同志和分管负责同志，西咸新区发展改革和商务局、财政金融局、基层工作部、自然资源和规划局（林业）、住房和城乡建设局（水利）、市场监督管理局分管负责同志，秦农银行总行和沣东支行相关负责人，各镇街分管负责同志和业务科室负责人，媒体代表等60余人参加活动。</p>', '101', 58, NULL, '城乡融合要素交易市场（西咸新区）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (468, '安置房租赁交易业务办理指南', 999999, NULL, 'xafby', '183', '2024-05-28 17:36:55', '2024-08-08 15:01:51', '2024-05-28 17:36:55', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '600,603', '0', 0, 0, 0, '<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 16.74%;\" width=\"16.7400%\">\n<p>交易主体</p>\n</td>\n<td style=\"width: 83.24%;\" width=\"83.2400%\">\n<p>申请资料清单</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 16.74%;\" width=\"16.7400%\">\n<p>出租人</p>\n</td>\n<td style=\"width: 83.24%;\" width=\"83.2400%\">\n<p>（一）出租申请书，包括但不限于交易标的基本情况、产权权利人签字捺印或签章证明、出租底价、出租方式、出租用途、出租期限、承租人条件等内容；</p>\n<p>（二）出租人交易承诺书；</p>\n<p>（三）安置房权属佐证材料。如拆迁安置协议、安置房收房单或其他合法有效的佐证材料；</p>\n<p>（四）出租人的身份证件或其他合法有效证件；</p>\n<p>（五）属集体所有的安置房，须提供所属集体内部决策材料；</p>\n<p>（六）出租共有产权的安置房，须提供其他共有人同意出租的证明；</p>\n<p>（七）出租委托代管的安置房，须提供委托人授权出租的证明材料；</p>\n<p>（八）转租安置房的，转租人应当提供原出租合同及出租人同意转租的书面证明；</p>\n<p>（九）应当提交的其他材料。</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 16.74%;\" width=\"18.4200%\">\n<p>意向承租人</p>\n<p>&nbsp;</p>\n</td>\n<td style=\"width: 83.24%;\" width=\"81.5600%\">\n<p>（一）意向承租申请书；</p>\n<p>（二）意向承租人交易承诺书；</p>\n<p>（三）承租人的身份证件或其他合法有效证件。如为自然人，提供个人身份证或户口本复印件；如为村集体经济组织，提供农村集体经济组织登记证复印件、法定代表人身份证复印件等；如为其他法人组织，提供工商营业执照副本复印件、法定代表人身份证复印件；如为非法人组织，提供资质证照、负责人身份证明及相关文件等。需委托他人办理交易的，提交授权委托书以及被授权人身份证明；</p>\n<p>（四）交易保证金缴费凭证；</p>\n<p>（五）符合承租资格条件的证明文件，如限制为本村集体经济组织成员或者本村村民的须提供身份证明材料；</p>\n<p>（六）应当提交的其他材料。</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>注：1.以上文件均需出租人加盖鲜章；</p>\n<p>2.出租人须对提交的所有材料（包括原件、复印件、扫描件）的真实性、合法性、有效性、完整性负责，并承担相应的法律责任。</p>', '603', 27, NULL, '安置房租赁交易业务办理指南', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (469, '村级货物和服务采购业务办理指南', 999999, NULL, 'xafby', '183', '2024-05-30 16:35:24', '2024-08-08 15:01:51', '2024-05-30 16:35:24', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '600,603', '0', 0, 0, 0, '<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 49.9631%;\" width=\"114\">\n<p>交易主体</p>\n</td>\n<td style=\"width: 49.9631%;\" width=\"489\">\n<p>申请资料清单</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 49.9631%;\" width=\"114\">\n<p>采购方</p>\n</td>\n<td style=\"width: 49.9631%;\" width=\"489\">\n<p>1.采购申请书，包括但不限于采购项目名称、项目概况、采购方式、保证金额度、采购需求，供应商应具备的资格条件、技术要求和商务要求等；</p>\n<p>2.采购方交易承诺书；</p>\n<p>3.主体资格证明材料，包括但不限于采购方资质证明，统一社会信用代码证，法定代表人身份证复印件；委托他人办理交易的，需提交授权委托书以及被授权人身份证明；</p>\n<p>4.批准采购的相关文件，包括民主决策档案材料、内部决议、有关备案文件等；</p>\n<p>5.采购预算表，包括但不限于项目预算金额、采购数量等；</p>\n<p>6.除绿色通道外的采购项目，还应提交招标文件或采购文件；</p>\n<p>7.应当提交的其他材料。</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 49.9631%;\" width=\"147\">\n<p>意向供应商</p>\n</td>\n<td style=\"width: 49.9631%;\" width=\"456\">\n<p>1.意向供应申请书；</p>\n<p>2.意向供应商交易承诺书；</p>\n<p>3.采购保证金缴纳凭证；</p>\n<p>4.主体资格证明。如为自然人，提供个人身份证或户口本复印件；如为村集体经济组织，提供农村集体经济组织登记证复印件、法定代表人身份证复印件等；如为其他法人组织，提供工商营业执照副本复印件、法定代表人身份证复印件；如为非法人组织，提供资质证照、负责人身份证明及相关文件等。需委托他人办理交易的，提交授权委托书以及被授权人身份证明；</p>\n<p>5.意向供应商资信证明、资质证明；</p>\n<p>6.除绿色通道、网络竞价外的采购项目，还应提交投标文件（响应文件）；</p>\n<p>7.应当提交的其他材料。</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>注：1. 以上文件均需采购方加盖鲜章；</p>\n<p>2.采购方须对提交的所有材料（包括原件、复印件、扫描件）的真实性、合法性、有效性、完整性负责，并承担相应的法律责任。</p>', '603', 29, NULL, '村级货物和服务采购业务办理指南', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (470, '集体林权交易业务办理指南', 999999, NULL, 'xafby', '183', '2024-05-30 17:02:12', '2024-08-08 15:01:51', '2024-05-30 17:02:12', '0', 'xafby', '西安', '西安发布员', '600,603', '0', 0, 0, 0, '<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 17.22%;\" width=\"17.2200%\">\n<p>交易主体</p>\n</td>\n<td style=\"width: 82.76%;\" width=\"82.7600%\">\n<p>申请资料清单</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 17.22%;\" width=\"17.2200%\">\n<p>出让方</p>\n</td>\n<td style=\"width: 82.76%;\" width=\"82.7600%\">\n<p>1.出让申请书。包括交易标的基本情况、产权权利人签字捺印或签章证明、受让方条件、出让底价、价款支付要求、交易保证金等内容；</p>\n<p>2.出让方交易承诺书；</p>\n<p>3.权属证明材料。集体林权证或其他合法有效的权属佐证材料；</p>\n<p>4.主体资格证明材料。包括但不限于营业执照或统一社会信用代码证，法定代表人或负责人身份证明；委托他人办理交易的，需提交授权委托书以及被授权人主体资格和身份证明；</p>\n<p>5.内部决策材料。集体统一经营管理的林地承包经营权和林木所有权流转的，流转方案应在本集体经济组织内提前公示，经本集体经济组织三分之二以上成员（成员代表）讨论通过，形成决议并报乡镇政府批准；发包方将农村林地发包给本集体经济组织以外的单位或者个人承包，应当事先经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，形成内部文件并报乡镇政府批准；</p>\n<p>6.农民个人产权须是本人自愿并征得权属共有人同意后流转交易；</p>\n<p>7.再次流转交易的出让方应当提供原发包的集体经济组织同意再次流转的证明和原流转交易的合同（或有效证明），并不得超过合同的剩余期限；</p>\n<p>8.应当提交的其他材料。</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 17.22%;\" width=\"21.1400%\">\n<p>意向受让方</p>\n</td>\n<td style=\"width: 82.76%;\" width=\"78.8400%\">\n<p>1.意向受让申请书；</p>\n<p>2.意向受让方交易承诺书；</p>\n<p>3.交易保证金缴费凭证；</p>\n<p>4.主体资格证明。如为自然人，提供个人身份证或户口本复印件；如为村集体经济组织，提供农村集体经济组织登记证复印件、法定代表人身份证复印件等；如为其他法人组织，提供工商营业执照副本复印件、法定代表人身份证复印件；如为非法人组织，提供资质证照、负责人身份证明及相关文件等。需委托他人办理交易的，提交授权委托书以及被授权人主体资格和身份证明；</p>\n<p>5.享有优先受让权的意向受让方，需提供同一集体经济组织成员的相关证明材料；</p>\n<p>6.本集体经济组织以外的单位或者个人承包的，应当提供林业经营能力或者资质的相关证明材料，包括但不限于资信情况证明材料、业绩证明材料、人员证明材料等；</p>\n<p>7.应当提交的其他材料。</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>注：1.以上文件均需意向受让方加盖鲜章；</p>\n<p>2.意向受让方须对提交的所有材料（包括原件、复印件、扫描件）的真实性、合法性、有效性、完整性负责，并承担相应的法律责任。</p>', '603', 21, NULL, '集体林权交易业务办理指南', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
