INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (516, '43轮竞价，溢价215万！西安市首宗农村集体经营性建设用地使用权交易项目入市交易成功​', 999999, '1815215447627870210,1815215879318220802', 'xafby', '183', '2024-07-22 10:40:32', '2024-08-08 15:01:51', '2024-07-22 10:40:32', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>7月19日，西安市新一轮农村集体经营性建设用地入市试点工作启动后，第一宗农村集体经营性建设用地使用权在西安城乡融合要素交易市场公开挂牌成功交易。</p>\n<p>据悉，项目宗地位于西安市灞桥区狄寨街道塘村，总面积5532.09平方米（8.298亩）。起拍价为795万元，经过43轮激烈竞价，最终成交价为1010万元，溢价215万元，溢价率27％。竞得方为陕西元宇宙实业有限公司，规划用地性质为商业服务业用地，出让年限40年。</p>\n<p>该项目的成交，标志着西安市新一轮农村集体经营性建设用地入市试点工作启动后&ldquo;集土入市&rdquo;实现从&ldquo;0&rdquo;到&ldquo;1&rdquo;的突破，是西安城乡融合要素交易市场探索农村土地市场化配置的新突破。</p>\n<p>2023年3月，灞桥区、高陵区、阎良区被列为全国农村集体经营性建设用地入市改革试点。2024年6月，市资源规划局印发《西安市农村集体经营性建设用地入市试点工作操作流程（试行)》，制定入市试点工作操作流程，明确&ldquo;农村集体经营性建设用地入市可在西安城乡融合要素交易市场、西安市公共资源交易中心实施交易&rdquo;，要求进一步盘活农村存量土地资源，充分激发农村改革发展活力，促进乡村全面振兴。</p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/07/29/abf3db57d26949028343bafc6830f966.jpg\" /></p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/07/29/71ff103cc58440568fc9c08964d44e3f.jpg\" /></p>', '101', 74, NULL, '43轮竞价，溢价215万！西安市首宗农村集体经营性建设用地使用权交易项目入市交易成功​', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', NULL);
