INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (563, '积极推进农村集体“三资”到期合同审查清理工作', 999999, '1823645083354058754', 'sjfby', '882', '2024-08-14 16:46:02', '2024-08-14 16:59:29', '2024-08-14 16:46:02', '0', 'sjfby', '西安城乡融合要素交易市场', 'xafby', '100,101', '0', 0, 1, 0, '<p>7月23日，市级公司主要领导赴西咸新区城乡融合要素交易市场开展&ldquo;三资&rdquo;合同督导落实工作，督促西咸新区公司认真做好合同梳理工作，加快推进已到期合同的梳理，确保工作落实到位。</p>\n<p>据西安城乡融合要素交易平台后台数据显示，西安市已到期合同&ldquo;三资&rdquo;8000余份，西咸新区已到期&ldquo;三资&rdquo;合同达2300余份，占比28.75%，是合同清理量最大的区县。</p>\n<p>为进一步了解街道&ldquo;三资&rdquo;合同具体情况，市级公司领导、西咸新区基层工作部农经办、西咸新区城乡融合要素交易市场负责人一同前往三桥街道办事处，查看已整改存档的合同，并就合同中涉及的资源资产现状及后续如何迅速落实任务等问题进行讨论交流。</p>\n<p>市级公司总经理贠涛强调，必须紧盯目标任务和时限要求，尽快理清工作思路、压实工作责任、做好时间规划，扎实有序加快合同梳理步伐，按照时间节点逐镇逐村细化落实，为农村集体&ldquo;三资&rdquo;入场交易奠定坚实基础。</p>', '101', 62, NULL, '积极推进农村集体“三资”到期合同审查清理工作', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
