INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (609, '西安首家区（县、开发区）级“农交所”揭牌亮相', 999999, '1762761883465269250,1762762541975191553', 'xafby', '183', '2024-02-28 16:48:35', '2024-08-28 16:57:18', '2024-02-28 16:48:35', '2', 'xafby', ' 西安新闻网', '西安发布员', '100,101', '0', 0, 1, 0, '<p>2月22日，城乡融合要素交易市场（西安长安）有限公司揭牌亮相，标志着西安市首家区（县、开发区）级城乡融合要素交易市场正式投入运营。西安市长安区政府副区长吴海军，西安农投集团党委委员、副总经理、西安城乡融合要素交易市场有限公司执行董事沈建锋，西安城乡融合要素交易市场有限公司相关负责人为公司揭牌。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/01c59933ae44475183abbcc337cc89b9.jpg\" /></p>\n<p>城乡融合要素交易市场（西安长安）有限公司以城乡融合发展为核心，以壮大村集体经济和农民增收致富为目标，由西安城乡融合要素交易市场有限公司与西安市长安乡村振兴投资发展有限公司共同组建，是西安城乡融合要素交易市场有限公司的9个区（县）子公司之一，主要围绕农村资源资产产权交易类、农业农村项目投资招商类、农产品流通类，以及农村工程、货物及服务采购类4大类、21类资产交易进行服务。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/e50c95f35bf84924ac9fc95c8c44deca.png\" /></p>\n<p>去年12月28日，全国首家以&ldquo;城乡融合要素&rdquo;命名的交易市场&mdash;&mdash;西安城乡融合要素交易市场正式揭牌成立。&ldquo;我们在二级交易平台上，下设有灞桥区、长安区、阎良区、高陵区、临潼区、鄠邑区、蓝田县、周至县、西咸新区9个子公司，高新区1个分公司。长安区城乡融合要素交易市场率先揭牌投运，将逐步拉开全市各区县子公司运营的帷幕。&rdquo;西安城乡融合要素交易市场有限公司相关负责人表示：&ldquo;这些子平台的运营和完善，将不断提升全市农村资源配置的信息化、标准化、规范化水平，为城乡融合发展、农业现代化、乡村振兴和农民增收注入新活力。&rdquo;</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/dcac5d51ad584008b60f3e72af1298cb.png\" /></p>\n<p>下一步，城乡融合要素交易市场（西安长安）有限公司将充分发挥&ldquo;政策+市场&rdquo;优势，遵循成熟一宗，推出一宗的原则，不断扩充和完善流转交易品种，积极推动引领城乡间各类要素资源高效利用，努力把城乡融合要素交易市场（西安长安）有限公司打造成实施乡村振兴战略的排头兵，助力西安农业高质量发展。</p>', '101', 100, NULL, '2月22日，城乡融合要素交易市场（西安长安）有限公司揭牌亮相，标志着西安市首家区（县、开发区）级城乡融合要素交易市场正式投入运营。', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (610, '权威访谈｜推进乡村全面振兴不断取得新成效——中央农办负责人解读2024年中央一号文件', 999999, NULL, 'xafby', '183', '2024-02-29 16:58:16', '2024-08-28 16:57:18', '2024-02-29 16:58:16', '2', 'xafby', '新华社', '西安发布员', '300,301', '0', 0, 0, 0, '<p>&emsp;新华社2月3日受权发布《中共中央 国务院关于学习运用&ldquo;千村示范、万村整治&rdquo;工程经验有力有效推进乡村全面振兴的意见》。这是党的十八大以来指导&ldquo;三农&rdquo;工作的第12个中央一号文件。中央农村工作领导小组办公室负责人当天就文件精神进行了全面解读。</p>\n<p>&nbsp;</p>\n<p><strong>把学习运用&ldquo;千万工程&rdquo;经验作为重要引领</strong></p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;今年中央一号文件强调，&ldquo;要学习运用&lsquo;千万工程&rsquo;蕴含的发展理念、工作方法和推进机制&rdquo;&ldquo;把推进乡村全面振兴作为新时代新征程&lsquo;三农&rsquo;工作的总抓手&rdquo;&ldquo;集中力量抓好办成一批群众可感可及的实事&rdquo;。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;中央农办负责人表示，习近平总书记在浙江工作期间，亲自谋划推动&ldquo;千万工程&rdquo;，从整治农村环境入手，由点及面，扩容建设，经过20多年持续努力，不仅深刻改变了浙江农村的整体面貌，也为推进乡村全面振兴作出了先行探索和示范引路，各地都可学可鉴。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&ldquo;必须看到，我国各地农村情况千差万别，自然条件、风土人情、发展水平、工作基础各不相同。学习运用&lsquo;千万工程&rsquo;经验，要因地制宜，不能生搬硬套、搞&lsquo;一刀切&rsquo;。&rdquo;中央农办负责人说，归根结底要让广大农民在乡村振兴中有实实在在的获得感，坚决反对搞形象工程、做表面文章。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;这位负责人表示，当前和今后一个时期，推进乡村全面振兴工作重点是确保国家粮食安全，确保不发生规模性返贫；提升乡村产业发展水平，提升乡村建设水平，提升乡村治理水平；强化科技和改革双轮驱动，强化农民增收举措。</p>\n<p>&nbsp;</p>\n<p><strong>确保国家粮食安全、确保不发生规模性返贫</strong></p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;去年我国粮食总产量再创新高，连续9年稳定在1.3万亿斤以上。面对各种风险挑战和不确定性，如何保障粮食安全？</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;今年中央一号文件突出稳面积、增单产两手发力的导向，提出确保粮食产量保持在1.3万亿斤以上的目标。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;中央农办负责人表示，确保国家粮食安全，关键是调动农民种粮和地方抓粮积极性。要适当提高小麦最低收购价，继续实施耕地地力保护补贴、玉米大豆生产者补贴和稻谷补贴，扩大完全成本保险和种植收入保险政策实施范围，让农民种粮有钱挣。全面落实粮食安全党政同责，加大对产粮大县支持力度，探索建立粮食产销区省际横向利益补偿机制，让地方抓粮有动力。同时，把粮食增产的重心放到大面积提高单产上，实施粮食单产提升工程，集成推广良田良种良机良法，推动粮食产能迈上新台阶。以小农户为基础、新型农业经营主体为重点、社会化服务为支撑，构建现代农业经营体系，解决&ldquo;谁来种地&rdquo;问题。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&ldquo;要健全耕地数量、质量、生态&lsquo;三位一体&rsquo;保护制度体系。&rdquo;这位负责人说，坚决整治乱占、破坏耕地违法行为，改革完善耕地占补平衡制度，适当提高高标准农田建设中央和省级投资补助水平，确保耕地数量有保障、质量有提升。同时，树立大农业观、大食物观，多渠道拓展食物来源，构建多元化食物供给体系。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;确保不发生规模性返贫是推进乡村全面振兴的底线要求，今年中央一号文件对此作出了部署。中央农办负责人表示，要压紧压实防止返贫工作责任，落实监测帮扶机制，加强跨部门信息整合共享，进一步提升监测及时性和帮扶精准性。加强产业和就业帮扶，强化帮扶产业分类指导，推进防止返贫就业攻坚行动。加大对重点地区帮扶支持力度，改善发展条件，增强&ldquo;造血&rdquo;能力，激发内生动力。</p>\n<p>&nbsp;</p>\n<p><strong>提升乡村产业发展、乡村建设、乡村治理水平</strong></p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;乡村是广大农民群众的家园，只有营造安居乐业的良好环境，才能让农民有充足的获得感、幸福感、安全感。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;今年中央一号文件将&ldquo;三个提升&rdquo;作为推进乡村全面振兴的重点。中央农办负责人表示，要统筹推进乡村产业发展、乡村建设、乡村治理，建设宜居宜业和美乡村。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&mdash;&mdash;提升乡村产业发展水平。做好&ldquo;土特产&rdquo;文章，坚持产业兴农、质量兴农、绿色兴农，把农业建成现代化大产业。促进农村一二三产业融合发展，推动农产品加工业优化升级，推动农村流通高质量发展，加快构建农林牧渔并举、产加销贯通、农文旅融合的现代乡村产业体系。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&mdash;&mdash;提升乡村建设水平。加强村庄规划编制实效性、可操作性和约束力，适应乡村人口变化趋势，优化村庄布局、产业结构、公共服务配置。深入实施农村人居环境整治提升行动，完善农民参与和长效管护机制。统筹农村供水、道路、能源、信息和住房安全等建设，推进农村基础设施补短板，完善农村教育、医疗、养老等公共服务体系，及时回应群众关切。统筹新型城镇化和乡村全面振兴，促进县域城乡融合发展。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&mdash;&mdash;提升乡村治理水平，健全完善党组织领导的自治、法治、德治相结合的乡村治理体系，推进抓党建促乡村振兴，建好建强农村基层党组织。持续推进基层减负，优化各类涉农督查检查考核。加强农村精神文明建设，繁荣发展乡村文化，加强乡村优秀传统文化保护传承和创新发展，持续推进农村移风易俗，加强高额彩礼、大操大办等突出问题综合治理。坚持和发展新时代&ldquo;枫桥经验&rdquo;，建设平安乡村。</p>\n<p>&nbsp;</p>\n<p><strong>强化科技和改革双轮驱动、强化农民增收举措</strong></p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;今年中央一号文件对强化科技和改革双轮驱动作出了系统部署。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&ldquo;要协同推进科技创新和制度创新，激发农村各类要素潜能和主体活力，为推进乡村全面振兴注入强劲动能。&rdquo;中央农办负责人说，要优化农业科技创新战略布局，支持重大创新平台建设，加大核心技术攻关力度，提高农业科技创新体系整体效能。扭住种子这个要害，加快推进种业振兴行动，完善联合研发和应用协作机制。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;这位负责人表示，改革是乡村振兴的重要法宝，要启动实施第二轮土地承包到期后再延长30年整省试点，稳慎推进农村宅基地改革，深化农村集体产权制度改革。在坚守底线的前提下，鼓励各地实践探索和制度创新，强化改革举措集成增效。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;农业农村工作，说一千、道一万，增加农民收入是关键。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&ldquo;党的十八大以来，农村居民收入持续较快增长，2023年农村居民人均可支配收入为21691元，城乡居民收入比进一步缩小到2.39∶1。&rdquo;中央农办负责人说，今年中央一号文件提出实施农民增收促进行动，目的就是巩固农民持续增收势头，促进共同富裕。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;这位负责人表示，要加强农民工职业技能培训，健全跨区域信息共享和有组织劳务输出机制，在重点工程项目和农业农村基础设施建设领域积极推广以工代赈。支持农户发展特色种养、手工作坊、林下经济等经营项目，完善产业联农带农机制。持续加大强农惠农富农政策力度，逐步提高农村社会保障水平，加强涉农资金项目监管，把给农民的各项补助补贴补到位。赋予农民更加充分的财产权益，鼓励以出租、合作开发、入股经营等方式盘活利用农村资源资产。</p>\n<p>&nbsp;</p>\n<p>&emsp;&emsp;&ldquo;总而言之，要紧紧围绕增加农民收入这个中心任务，广辟增收门路，让农民群众钱袋子越来越鼓、日子越过越好。&rdquo;中央农办负责人说。</p>', '301', 11, NULL, '权威访谈｜推进乡村全面振兴不断取得新成效——中央农办负责人解读2024年中央一号文件', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (611, '陕西聚力“九个突破”推进农业农村工作', 999999, NULL, 'xafby', '183', '2024-02-29 17:05:31', '2024-08-28 16:57:18', '2024-02-29 17:05:31', '2', 'xafby', '陕西日报', '西安发布员', '300,302', '0', 0, 0, 0, '<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">2023年，我省农业农村系统认真落实省委、省政府&ldquo;七个提升工程&rdquo;工作部署，守底线、促振兴、强保障，实现农业全面丰收、农民持续增收、农村和谐稳定。2024年，我省将重点围绕九项核心任务，聚力&ldquo;九个突破&rdquo;，推进农业农村高质量发展。</span></p>\n</section>\n</section>\n</section>\n<section>\n<p><span style=\"font-size: 18pt;\">我省将以大面积单产提升为突破，抓好粮油生产。各地将围绕主要作物，分区域、分品种制定单产提升方案，分环节、分长势落实关键技术措施，重点在提高<a>复种指数</a>上挖潜力，同时落实好农业防灾减灾措施。</span></p>\n<section>\n<p><span style=\"font-size: 18pt;\">我省将以激发内生动力为突破，巩固拓展脱贫攻坚成果。我省将坚持监测与帮扶并重，提升监测帮扶实效；增强产业就业后劲；加强重点地区支持，将26个国家级和省级乡村振兴重点帮扶县作为重中之重，实行政策倾斜支持；凝聚社会帮扶合力，引导更多社会资本有序参与乡村振兴。</span></p>\n<p><span style=\"font-size: 18pt;\">我省将以设施农业建设为突破，稳定&ldquo;菜篮子&rdquo;供给。我省将做大畜牧业，挖掘陕北、渭北土地及饲草料的承载潜力，提升畜牧业体量及产值占比；突出设施改造做优蔬菜产业，打造10个城市近郊保供基地；用足用好部省共建机制，加快出台全省果业布局指导意见，引导产业向优势区聚集。</span></p>\n</section>\n<p><span style=\"font-size: 18pt;\">我省将以全产业链建设为突破，提升产业发展水平。我省坚持省抓千亿级、市抓百亿级、县抓十亿级农业产业链的思路不变，三级协同、梯次推进现代农业全产业链建设；进一步建强融合发展的平台载体；进一步探索联农带农的模式路径。</span></p>\n<p><span style=\"font-size: 18pt;\">我省将以高标准农田建设为突破，加强耕地保护。各地将打造高标准农田，建设好耕地；探索开展盐碱地综合利用试点，大力推广旱作节水农业，利用好耕地；治理农业面源污染，保护好耕地。</span></p>\n<p><span style=\"font-size: 18pt;\">我省将以科技赋能、改革赋能、数字赋能为突破，拉动发展引擎。我省将推动11项农业关键核心技术攻关，加快取得标志性成果；稳妥推进第二轮土地承包到期后再延长30年试点，稳慎推进农村宅基地制度改革试点，持续开展农村集体经济&ldquo;消薄培强&rdquo;行动。</span></p>\n<p><span style=\"font-size: 18pt;\">我省将以农村人居环境整治为突破，推动乡村建设。我省将以厕所改造、污水处理、垃圾治理为重点，持续深化农村人居环境整治；以科学规划为前提，不断改善和提升农村基础设施条件和公共服务水平。</span></p>\n<p><span style=\"font-size: 18pt;\">此外，我省将以坚持和发展新时代&ldquo;<a>枫桥经验</a>&rdquo;为突破，强化乡村治理；以&ldquo;千万工程&rdquo;为突破，争做乡村振兴西部示范。</span></p>\n</section>', '302', 8, NULL, '陕西聚力“九个突破”推进农业农村工作', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (612, '陕西省实施《中华人民共和国农村土地承包法》办法', 999999, NULL, 'xafby', '183', '2024-03-01 09:58:23', '2024-08-28 16:57:18', '2024-03-01 09:58:23', '2', 'xafby', '陕西日报', '西安发布员', '300,301', '0', 0, 0, 0, '<div><strong><span style=\"font-size: 18pt;\">（2006年9月28日陕西省第十届人民代表大会常务委员会第二十七次会议通过 根据2014年11月27日陕西省第十二届人民代表大会常务委员会第十四次会议《陕西省人民代表大会常务委员会关于修改〈陕西省县乡两级人民代表大会代表选举实施细则〉等十七部地方性法规的决定》修正 2024年1月12日陕西省第十四届人民代表大会常务委员会第七次会议修订）</span></strong></div>\n<div>\n<div id=\"zoom\">\n<p><span style=\"font-size: 18pt;\"><strong>陕西省人民代表大会常务委员会</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>公告</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>〔十四届〕第十五号</strong></span></p>\n<p><span style=\"font-size: 18pt;\">《陕西省实施〈中华人民共和国农村土地承包法〉办法》已于2024年1月12日经陕西省第十四届人民代表大会常务委员会第七次会议修订通过，现予公布，自2024年4月1日起施行。</span></p>\n<p><span style=\"font-size: 18pt;\">陕西省人民代表大会常务委员会&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;</span></p>\n<p><span style=\"font-size: 18pt;\">2024年1月12日&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;</span></p>\n<p><span style=\"font-size: 18pt;\">第一条 &ensp; 为了实施《中华人民共和国农村土地承包法》，结合本省实际，制定本办法。</span></p>\n<p><span style=\"font-size: 18pt;\">第二条 &ensp; 本省行政区域内的农村土地承包经营及承包经营管理，适用本办法。</span></p>\n<p><span style=\"font-size: 18pt;\">本办法所称农村土地，是指农民集体所有和国家所有依法由农民集体使用的耕地、林地、草地以及其他依法用于农业的土地。</span></p>\n<p><span style=\"font-size: 18pt;\">第三条 &ensp; 县级以上人民政府农业农村、林业行政主管部门（以下统称农村土地承包主管部门）依照各自职责，负责本行政区域内农村土地承包经营及承包经营合同管理。主要职责是：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）宣传、贯彻、落实有关农村土地承包经营的法律、法规、规章和政策；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）指导农村土地承包合同和流转合同的签订；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）监督、检查农村土地承包合同和流转合同的履行；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）土地经营权流转市场建设和监督管理；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）支持有关调解组织和农村土地承包仲裁委员会依法开展工作；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）负责农村土地承包合同和流转合同的档案管理；</span></p>\n<p><span style=\"font-size: 18pt;\">（七）做好工商企业等社会资本流转土地经营权风险防范工作；</span></p>\n<p><span style=\"font-size: 18pt;\">（八）负责法律、法规、规章规定的其他工作。</span></p>\n<p><span style=\"font-size: 18pt;\">县级以上人民政府自然资源行政主管部门负责土地承包经营权登记颁证工作。</span></p>\n<p><span style=\"font-size: 18pt;\">乡（镇）人民政府、街道办事处负责本行政区域内的农村土地承包经营及承包经营合同管理。</span></p>\n<p><span style=\"font-size: 18pt;\">第四条 &ensp; 县级以上人民政府应当健全农村土地承包经营权信息应用平台，完善省、市、县、乡、村五级数据采集、传输、共享和隐私保护机制，提升农村土地承包经营数字化管理水平。</span></p>\n<p><span style=\"font-size: 18pt;\">第五条 &ensp; 县级以上人民政府应当将农村土地承包经营及管理和农村土地承包经营纠纷仲裁工作所需费用，纳入本级财政预算，不得以任何名义向农民收取。</span></p>\n<p><span style=\"font-size: 18pt;\">第六条 &ensp; 以家庭承包方式承包农村土地的，本集体经济组织的农户是承包方。</span></p>\n<p><span style=\"font-size: 18pt;\">农户内家庭成员依法平等享有承包土地的各项权益，承包中应当保护妇女在土地承包经营中的知情权、参与权、表达权、监督权和收益权等各项权益。</span></p>\n<p><span style=\"font-size: 18pt;\">村民自治章程、村规民约以及村民会议或者村民代表会议的决定不得有侵犯村民土地承包经营权的内容。</span></p>\n<p><span style=\"font-size: 18pt;\">第七条 &ensp; 农村土地承包方案，应当依法经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并向全体村民公示，公示期不得少于十五日。对于因村民居住分散或者人户分离等原因无法集中公示的，发包方应当采取其他方式告知，保障有关人员的知情权。</span></p>\n<p><span style=\"font-size: 18pt;\">第八条 &ensp; 耕地的承包期为三十年，草地的承包期为三十年至五十年，林地的承包期为三十年至七十年。</span></p>\n<p><span style=\"font-size: 18pt;\">前款规定的耕地承包期届满后再延长三十年，草地、林地承包期届满后依照前款规定相应延长。</span></p>\n<p><span style=\"font-size: 18pt;\">第九条 &ensp; 发包方应当依法与承包方签订书面承包合同。承包合同自成立之日起生效。承包方自承包合同生效时取得土地承包经营权。</span></p>\n<p><span style=\"font-size: 18pt;\">承包方对承包的土地享有占有、使用、收益的权利，有权自主组织生产经营和处置产品，有权依法互换、转让土地承包经营权和依法流转土地经营权。承包地被依法征收、征用、占用时，承包方有依法取得补偿的权利。</span></p>\n<p><span style=\"font-size: 18pt;\">承包方应当依法保护和合理利用土地，不得给土地造成永久性损害，维持土地的农业用途，未经依法批准不得用于非农建设。</span></p>\n<p><span style=\"font-size: 18pt;\">第十条 &ensp; 实行家庭承包的，依法向登记机构申请土地承包经营权登记；以其他方式承包的，依法向登记机构申请土地经营权登记。</span></p>\n<p><span style=\"font-size: 18pt;\">实行家庭承包的土地承包合同生效后，发包方应在三十个工作日内，将土地承包方案、承包方及承包土地的详细情况、土地承包合同等材料报乡（镇）人民政府、街道办事处初审后，报县级农村土地承包主管部门审核。经审核合格的，设区的市或者县（市、区）不动产登记机构依法向承包方颁发不动产权属证书，并登记造册，确认土地承包经营权。不动产权属证书应当列明享有土地承包经营权的全部家庭成员。</span></p>\n<p><span style=\"font-size: 18pt;\">不动产权属证书应当发到农户，任何单位和个人不得代为保管或者扣留。</span></p>\n<p><span style=\"font-size: 18pt;\">流转期限为五年以上的土地经营权，以及互换、转让土地承包经营权，当事人可以向登记机构申请登记；未经登记，不得对抗善意第三人。</span></p>\n<p><span style=\"font-size: 18pt;\">不动产权属证书丢失、损毁，需要补发、换发证书的，设区的市或者县（市、区）不动产登记机构应当按规定予以办理。</span></p>\n<p><span style=\"font-size: 18pt;\">第十一条 &ensp; 以家庭承包方式承包土地，承包期内，发包方不得收回承包地。</span></p>\n<p><span style=\"font-size: 18pt;\">有下列情形之一的，发包方依法收回承包地：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）耕地、草地农村承包经营户内家庭成员全部死亡的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）林地农村承包经营户内家庭成员全部死亡且无继承人的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）农村承包经营户内家庭成员全部丧失中华人民共和国国籍的；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）农村承包经营户内家庭成员全部迁入其他集体经济组织并取得承包地的；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）农村承包经营户内家庭成员全部自愿放弃承包土地的；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）法律、法规规定的其他情形。</span></p>\n<p><span style=\"font-size: 18pt;\">第十二条 &ensp; 承包期内，妇女结婚，在新居住地未取得承包地的，发包方不得收回其原承包地；妇女离婚或者丧偶，仍在原居住地生活或者不在原居住地生活但在新居住地未取得承包地的，发包方不得收回其原承包地。</span></p>\n<p><span style=\"font-size: 18pt;\">因结婚男方到女方家落户的，适用前款规定。</span></p>\n<p><span style=\"font-size: 18pt;\">农村集体经济组织成员结婚后在迁入地取得承包地的，迁入地的集体经济组织应当书面告知迁出地的农村集体经济组织。</span></p>\n<p><span style=\"font-size: 18pt;\">第十三条 &ensp; 承包期内，农村承包经营户因分户或者离婚需要对原承包地重新分配的，具有土地承包经营权的全部家庭成员应当协商达成一致意见，并形成书面分配协议，协商不成的，按照人口比例平均分配。分户后的农村承包经营户应当依据分配协议与发包方分别签订书面承包合同，并依法申请土地承包经营权转移登记。</span></p>\n<p><span style=\"font-size: 18pt;\">承包地分割后的承包期限为家庭承包的剩余承包期限。</span></p>\n<p><span style=\"font-size: 18pt;\">第十四条 &ensp; 承包期内，发包方不得调整承包地。</span></p>\n<p><span style=\"font-size: 18pt;\">承包期内，因自然灾害严重毁损承包地等特殊情形对个别农户之间承包的耕地和草地需要适当调整的，必须经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并报乡（镇）人民政府和县级农村土地承包主管部门批准。承包合同中约定不得调整的，按照其约定。</span></p>\n<p><span style=\"font-size: 18pt;\">农村承包土地调整后，应当依法订立或者变更土地承包合同，合同终止日期应当与本集体经济组织其他同类土地承包合同终止日期一致，并依法申请土地承包经营权变更登记或者转移登记。</span></p>\n<p><span style=\"font-size: 18pt;\">第十五条 &ensp; 下列土地应当用于调整承包土地或者承包给新增人口：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）集体经济组织依法预留的机动地；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）集体经济组织通过依法开垦、复垦等方式增加的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）发包方依法收回的和承包方依法、自愿交回的。</span></p>\n<p><span style=\"font-size: 18pt;\">前款所列土地尚未用于调整承包土地或者发包给新增人口的，流转期不得超过五年。</span></p>\n<p><span style=\"font-size: 18pt;\">第十六条 &ensp; 承包方家庭成员因年龄、身体、就业等原因无法继续耕种的，发包方应当引导、支持承包方通过农业生产托管、土地经营权依法流转等方式进行耕种。</span></p>\n<p><span style=\"font-size: 18pt;\">第十七条 &ensp; 以家庭承包方式承包土地，承包方将承包土地弃耕抛荒连续满两年的，发包方应当督促承包方恢复耕种；经督促承包方仍未恢复耕种的，发包方可以组织临时代耕。组织代耕前，发包方应当书面告知承包方，并在本集体经济组织公告。</span></p>\n<p><span style=\"font-size: 18pt;\">代耕期间，代耕者按照与发包方的约定享有代耕收益，同时履行承包方应当履行的法定义务。承包方要求恢复耕作的，代耕者在收获当季农作物后交回承包地，承包方应当对代耕者提高地力的投入予以合理补偿。</span></p>\n<p><span style=\"font-size: 18pt;\">第十八条 &ensp; 承包方在承包期限内有权依法自主决定土地经营权是否流转，以及流转对象、方式、期限等。</span></p>\n<p><span style=\"font-size: 18pt;\">土地经营权流转收益归承包方所有，任何组织和个人不得擅自截留、扣缴。</span></p>\n<p><span style=\"font-size: 18pt;\">第十九条 &ensp; 承包方征得户内享有土地承包经营权的家庭成员同意后，可以采取出租（转包）、入股或者其他符合有关法律和国家政策规定的方式流转土地经营权。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十条 &ensp; 土地经营权流转合同，鼓励采用农业农村部、国家林业和草原局会同国家市场监督管理总局制定的合同示范文本。乡（镇）人民政府、街道办事处应当向达成流转意向的双方提供统一文本格式的流转合同，并指导签订。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十一条 &ensp; 县级以上人民政府及其有关行政主管部门应当推动建立土地经营权流转市场或者农村产权交易市场，加强业务指导，督促其建立健全运行规则，规范开展土地经营权流转政策咨询、信息发布、合同签订、交易鉴证、权益评估、融资担保、档案管理等服务。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十二条 &ensp; 县级以上人民政府应当建立工商企业等社会资本通过流转取得土地经营权的分级资格审查和项目审核制度。审查审核的一般程序如下：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）受让主体与承包方就流转面积、期限、价款等进行协商并签订流转意向协议书。涉及未承包到户集体土地等集体资源的，应当按照法定程序经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并与集体经济组织签订流转意向协议书；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）受让主体按照分级审查审核规定，分别向乡（镇）人民政府或者县级以上人民政府农村土地承包主管部门提出申请，并提交流转意向协议书、农业经营能力或者资质证明、流转项目规划等相关材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）乡（镇）人民政府或者县级以上人民政府应当依法组织相关职能部门、农村集体经济组织代表、农民代表、专家等就土地用途、经营项目是否符合粮食生产等产业规划以及受让主体农业经营能力等进行审查审核，并于受理之日起二十个工作日内作出审查审核意见；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）审查审核通过的，受让主体与承包方签订土地经营权流转合同。未按规定提交审查审核申请或者审查审核未通过的，不得开展土地经营权流转活动。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十三条 &ensp; 县级以上人民政府依法建立风险防范制度，对工商企业等社会资本通过流转取得土地经营权加强事中事后监管，及时查处纠正违法违规行为。</span></p>\n<p><span style=\"font-size: 18pt;\">鼓励承包方和受让方在土地经营权流转市场或者农村产权交易市场公开交易。</span></p>\n<p><span style=\"font-size: 18pt;\">对整村（组）土地经营权流转面积较大、涉及农户较多、经营风险较高的项目，流转双方可以协商设立风险保障金。</span></p>\n<p><span style=\"font-size: 18pt;\">鼓励保险机构为土地经营权流转提供流转履约保证保险等多种形式保险服务。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十四条 &ensp; 省农村土地承包主管部门应当按照国家和本省有关规定将工商企业等社会资本在土地经营中的失信行为纳入公共信用信息共享平台，依法对严重失信主体实施惩戒措施。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十五条 &ensp; 承包合同管理工作中形成的，对国家、社会和个人有保存价值的文字、图表、声像、数据等各种形式和载体的材料，应当纳入农村土地承包档案管理。</span></p>\n<p><span style=\"font-size: 18pt;\">县级以上农村土地承包主管部门、自然资源主管部门和乡（镇）人民政府、街道办事处，应当按照各自职责建立和完善农村土地承包合同、土地承包经营权登记颁证等档案资料管理制度，确保农村土地承包档案完整与安全。</span></p>\n<p><span style=\"font-size: 18pt;\">发包方应当将农村土地承包档案纳入村级档案管理。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十六条 &ensp; 承包经营权人、利害关系人有权查阅、复制农村土地承包登记资料或者经营权证登记簿。有关部门及其工作人员应当提供便利，不得限制和阻挠，不得收取费用。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十七条 &ensp; 违反本办法规定的行为，《中华人民共和国农村土地承包法》和国家有关法律、法规已经作出具体处罚规定的，从其规定。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十八条 &ensp; 发包方有下列行为之一的，由乡（镇）人民政府或者县级以上农业农村、林业、自然资源等行政主管部门责令限期改正；给当事人造成损失的，依法承担赔偿责任：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）擅自变更土地承包期限的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）强制代保管、扣留或者擅自更改不动产权属证书的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）其他侵害土地承包经营权的行为。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十九条 &ensp; 承包方、土地经营权人违法将承包地用于非农建设的，由县级以上自然资源等有关行政主管部门依法予以处罚。</span></p>\n<p><span style=\"font-size: 18pt;\">承包方给承包地造成永久性损害的，发包方有权制止，并有权要求赔偿由此造成的损失。</span></p>\n<p><span style=\"font-size: 18pt;\">第三十条 &ensp; 国家机关及其工作人员利用职权有下列行为之一，给承包经营当事人造成损失的，应当依法承担损害赔偿等责任；情节严重的，由上级机关或者所在单位依法给予直接责任人员处分；构成犯罪的，依法追究刑事责任：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）干涉农村土地承包经营，变更、解除承包经营合同的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）干涉承包经营当事人依法享有的生产经营自主权的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）强迫、阻碍承包经营当事人进行土地承包经营权互换、转让或者土地经营权流转等侵害土地承包经营权、土地经营权的；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）违反规定登记、颁发、变更、注销不动产权属证书的；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）其他滥用职权、玩忽职守、徇私舞弊的侵害土地承包经营权的行为。</span></p>\n<p><span style=\"font-size: 18pt;\">第三十一条 &ensp; 本办法自2024年4月1日起施行。</span></p>\n</div>\n</div>', '301', 18, NULL, '陕西省实施《中华人民共和国农村土地承包法》办法', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (613, '国务院批复《陕西省国土空间规划（2021-2035）》', 999999, NULL, 'xafby', '183', '2024-03-01 14:17:41', '2024-08-28 16:57:18', '2024-03-01 14:17:41', '2', 'xafby', '古都先锋公众号', '西安发布员', '300,301', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">2月4日</span></p>\n<p><span style=\"font-size: 18pt;\">中国政府网发布</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>国务院关于</strong><strong>《陕西省国土空间规划（2021&mdash;2035年）》的批复</strong></span></p>\n<p><span style=\"font-size: 18pt;\">（国函〔2024〕18号）</span></p>\n<p><span style=\"font-size: 18pt;\">全文如下：</span></p>\n<p><span style=\"font-size: 18pt;\">陕西省人民政府、自然资源部：</span></p>\n<p><span style=\"font-size: 18pt;\">你们关于报请批准《陕西省国土空间规划（2021&mdash;2035年）》的请示收悉。现批复如下：</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>一、原则同意自然资源部审查通过的《陕西省国土空间规划（2021&mdash;2035年）》（以下简称《规划》）。</strong>《规划》是陕西省空间发展的指南、可持续发展的空间蓝图，是各类开发保护建设活动的基本依据，请认真组织实施。陕西省地处我国内陆腹地，是中华民族和华夏文明重要发祥地之一，是新时代西部大开发、共建&ldquo;一带一路&rdquo;的重要节点地区。《规划》实施要坚持以习近平新时代中国特色社会主义思想为指导，全面贯彻落实党的二十大精神，完整、准确、全面贯彻新发展理念，坚持以人民为中心，统筹发展和安全，促进人与自然和谐共生，奋力谱写中国式现代化建设陕西篇章。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>二、筑牢安全发展的空间基础。</strong>到2035年，陕西省耕地保有量不低于4326.00万亩，其中永久基本农田保护面积不低于3377.60万亩；生态保护红线面积不低于4.86万平方千米；城镇开发边界扩展倍数控制在基于2020年城镇建设用地规模的1.3倍以内；单位国内生产总值建设用地使用面积下降不少于40%；用水总量不超过国家下达指标，其中2025年不超过107.0亿立方米。明确自然灾害风险重点防控区域，划定洪涝等风险控制线，落实战略性矿产资源、历史文化保护等安全保障空间，全面锚固高质量发展的空间底线。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>三、构建支撑新发展格局的国土空间体系。</strong>深入实施区域协调发展战略、区域重大战略、主体功能区战略、新型城镇化战略和乡村振兴战略，促进形成主体功能明显、优势互补、高质量发展的国土空间开发保护新格局。深度融入黄河流域生态保护和高质量发展、新时代西部大开发等国家重大战略，加强关中平原城市群与成渝地区双城经济圈以及长江中游、中原、山西中部、兰州&mdash;西宁等城市群协同发展，加快形成面向中亚南亚西亚国家的重要对外开放通道，打造内陆改革开放高地。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>四、系统优化国土空间开发保护格局。</strong>发挥区域比较优势，优化主体功能定位，细化主体功能区划分，完善差别化支持政策。以陕北长城沿线旱作区和黄土高原旱作区、关中灌区、汉中盆地为重点，拓展特色农产品生产空间，夯实粮食安全基础。健全秦岭常态化长效化保护机制，筑牢秦岭&mdash;大巴山、陕北黄土高原&mdash;长城沿线生态屏障，实施江河源头、森林、河湖、湿地系统保护修复，加强珍稀野生动植物和古树名木保护。加强黄河流域生态保护修复，推进水土流失、荒漠化综合治理，守护好黄河母亲河。严格河湖水域空间管控，实施地下水污染综合治理，强化南水北调中线水源地保护。积极培育西安都市圈，提升宝鸡、渭南、汉中、榆林等城市的综合承载能力，促进陕北、关中、陕南区域协调发展，分类优化乡村空间布局，加强传统村落和乡村特色风貌保护，提升城乡人居环境品质。发挥区位、科教和能源资源优势，统筹传统和新型基础设施空间布局，积极参与西部陆海新通道建设，打造国家重要科研和文教中心、高新技术产业和制造业基地。加强自然灾害防治，优化防灾减灾救灾设施区域布局，提高国土空间安全韧性。健全文化遗产与自然遗产空间保护机制，强化文化遗产与自然遗产整体保护和系统活化利用，加强秦岭、华山、黄帝陵、兵马俑等自然和文化遗产保护，加强延安宝塔等红色文化遗产传承保护，构建文化资源、自然资源、景观资源整体保护的空间体系。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>五、维护规划严肃性权威性。</strong>《规划》是对陕西省国土空间作出的全局安排，是省域国土空间保护、开发、利用、修复的政策和总纲，必须严格执行，任何部门和个人不得随意修改、违规变更。按照定期体检和五年一评估的要求，健全各级各类国土空间规划实施监测评估预警机制，将规划评估结果作为规划实施监督考核的重要依据。建立健全规划监督、执法、问责联动机制，实施规划全生命周期管理。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>六、做好规划实施保障。</strong>陕西省人民政府要加强组织领导，明确责任分工，健全工作机制，完善配套政策措施。做好《规划》印发和公开，强化社会监督。组织完成地方各级国土空间总体规划、详细规划、相关专项规划编制工作，加快形成统一的国土空间规划体系，统筹国土空间开发保护。强化对水利、交通、能源、农业、信息、市政等基础设施以及公共服务设施、军事设施、生态环境保护、文物保护、林业草原等专项规划的指导约束，在国土空间规划&ldquo;一张图&rdquo;上协调解决矛盾问题，合理优化空间布局。建立健全国土空间规划委员会制度，发挥对国土空间规划编制实施管理的统筹协调作用。按照&ldquo;统一底图、统一标准、统一规划、统一平台&rdquo;的要求，完善国土空间规划&ldquo;一张图&rdquo;系统和国土空间基础信息平台，建设国土空间规划实施监测网络，提高空间治理数字化水平。自然资源部要会同有关方面根据职责分工，密切协调配合，加强指导、监督和评估，确保实现《规划》确定的各项目标和任务。各有关部门要坚决贯彻党中央、国务院关于&ldquo;多规合一&rdquo;改革的决策部署，不在国土空间规划体系之外另设其他空间规划。《规划》实施中的重大事项要及时请示报告。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">国务院</span></p>\n<p><span style=\"font-size: 18pt;\">2024年1月31日</span></p>', '301', 27, NULL, '国务院批复《陕西省国土空间规划（2021-2035）》', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (614, '灞桥区邵平店村“畅通城乡要素流动、促进农民集体双增收”培训宣传', 999999, '1764824049370513409,1764824871131136002', 'xafby', '183', '2024-03-05 09:22:03', '2024-08-28 16:57:18', '2024-03-05 09:22:03', '2', 'xafby', '公众号', '西安发布员', '100,101', '0', 0, 0, 0, '<p>3月2日，西安城乡融合要素交易市场有限公司工作人员赴灞桥区邵平店村开展以&ldquo;畅通城乡要素流动、促进农民集体双增收&rdquo;为主题的培训宣传活动。</p>\n<p>活动现场，工作人员向村民们介绍了西安城乡融合要素交易市场有限公司功能定位、经营理念、服务体系等基本情况，并重点围绕城乡融合要素交易的概念、在城乡融合要素交易市场进行交易的原因及优势等方面进行详细讲解，帮助村民充分认识到城乡融合要素交易市场在保障农户合法权益、促进资产资源保值增值、发展壮大村集体经济等方面起到的重要作用。</p>\n<p>下一步，西安城乡融合要素交易市场将继续对接全市区县（开发区）、街道（镇）和村组，积极开展业务培训和各类宣传宣讲活动，进一步引导更多的资源要素参与到城乡融合要素交易中来，真正使农民群众、各类新型农业经营主体成为交易市场建设的参与者和受益者。</p>\n<p>&nbsp;</p>', '101', 38, NULL, '灞桥区邵平店村“畅通城乡要素流动、促进农民集体双增收”培训宣传', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (623, '我市召开西安城乡融合要素交易市场现场观摩暨工作推进会', 999999, '1771094222422675457', 'xafby', '183', '2024-03-22 16:29:44', '2024-08-28 16:57:18', '2024-03-22 16:29:44', '2', 'xafby', '西安农投公众号', '西安发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 14pt;\">为进一步推进西安城乡融合要素交易市场组建和运营工作，3月21日上午，我市召开西安城乡融合要素交易现场观摩暨工作推进会。仵江副市长主持会议并讲话。市农业农村局（市委农办）、西安农投集团主要负责同志及市委改革办、市纪委监委负责同志出席。</span></p>\n<p><span style=\"font-size: 14pt;\"><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/03/27/72f1a3204c424d25925f264f8d059aaa.jpg\" /></span></p>\n<section><span style=\"font-size: 14pt;\">观摩现场，仵江副市长一行观看了西安城乡融合要素交易市场有限公司视频介绍，并听取了交易平台规范化建设、服务模式与农业类知识产权、农村闲置住房和宅基地使用权流转交易共13个挂牌项目基本情况的介绍，详细了解了平台账号注册、出让方提交申请、形式审查、发布信息、征求意向受让方、组织交易、成交公示、签订合同、交易鉴证等交易流程。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 14pt;\">在随后召开的工作推进会上，西安农投集团党委书记、董事长成斌汇报了西安城乡融合要素交易市场组建运行情况；市委农办主任、市农业农村局局长裴靖瑜结合当前工作进展情况，安排部署下一步工作；相关区县就城乡融合要素交易工作落实情况进行了交流和表态发言。</span></section>\n<section><span style=\"font-size: 14pt;\"><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/03/27/05f929f815484ce1a7f028f1544869c4.jpg\" /></span></section>\n<section>\n<p><span style=\"font-size: 14pt;\">仵江副市长对西安城乡融合要素交易市场目前的工作进展情况给予充分肯定，并就下一步推进西安城乡融合要素交易工作提出要求：一是要提高站位，强化认识，把思想和行动统一到市委、市政府的决策部署上，加强统筹协调、明确进度安排，确保各项工作落实、落细、落具体。二是要加快推进落实，全面做好体系建设与业务开展有效衔接。按照&ldquo;应进必进、阳光交易&rdquo;的工作原则，积极推动各项交易工作顺利进行。三是要强化责任担当，充分发挥市场优化资源配置的作用，推动公平交易及市场规范化建设，助力村集体经济组织和农民致富增收，加快推进我市城乡融合发展。</span></p>\n<p><span style=\"font-size: 14pt;\">市级相关部门、各相关区县（开发区）、西安农投集团分管负责同志及农业农村部门、相关处室，秦农银行负责同志，西安城乡融合要素交易市场全体干部职工参加会议。</span></p>\n</section>', '101', 57, NULL, '我市召开西安城乡融合要素交易市场现场观摩暨工作推进会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (624, '西安日报报道西安城乡融合要素交易市场现场观摩暨工作推进会', 999999, '1771095563199057921', 'xafby', '183', '2024-03-22 16:42:50', '2024-08-28 16:57:18', '2024-03-22 16:42:50', '2', 'xafby', '西安日报', '西安发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 12pt;\">&nbsp; &nbsp; &nbsp; 3月21日，我市召开西安城乡融合要素交易市场现场观摩暨工作推进会。这场&ldquo;重大改革事项落地督办会&rdquo;让&ldquo;城乡融合要素交易市场&rdquo;这个新生事物进入公众视野。西安城乡融合要素交易市场是怎样一个市场？交易什么？如何交易？这项重大改革事项在我市推进进展如何？让我们循着这场会议一起了解一下。</span></p>\n<p><span style=\"font-size: 12pt;\">　　2023年11月7日，市委、市政府共同印发《西安城乡融合要素交易市场组建方案》。方案要求，逐步建成全市统一的网络体系和市、区县（开发区）、街道（镇）、村四级联动的城乡融合要素交易市场。通过三至五年努力，逐步将城乡融合要素交易平台升级为具有农村产权综合服务功能、乡村振兴招商引资功能、大数据信息共享功能为一体的综合性城乡融合要素交易服务平台，走在全省乃至西部前列。</span></p>\n<p><span style=\"font-size: 12pt;\">　　2023年11月，经市委、市政府批准，由西安农业投资集团有限公司全资成立西安城乡融合要素交易市场有限公司，这也是全国首家以&ldquo;城乡融合要素交易市场&rdquo;命名的综合性城乡要素交易服务平台。</span></p>\n<p><span style=\"font-size: 12pt;\">　　3月17日，西安城乡融合要素交易市场首笔交易业务挂牌，标志着我市在激活农村闲置资源、推动城乡资源流动、优化资源配置方面迈出了重要一步。</span></p>\n<p><span style=\"font-size: 12pt;\">　　共4大类21项品种纳入交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　按照&ldquo;应进必进、阳光交易&rdquo;的工作原则，西安城乡融合要素交易市场按照统一信息系统、统一交易规则、统一交易鉴证、统一收费标准、统一交易结算、统一交易监管的&ldquo;六统一&rdquo;管理模式运行，开展城乡融合要素流转交易一站式服务。主要业务包括4大类21项交易品种。</span></p>\n<p><span style=\"font-size: 12pt;\">　　4大类包括：</span></p>\n<p><span style=\"font-size: 12pt;\">　　1.农村资源资产产权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　2.农村产业项目招商</span></p>\n<p><span style=\"font-size: 12pt;\">　　3.农产品流通和农村工程</span></p>\n<p><span style=\"font-size: 12pt;\">　　4.货物及服务采购</span></p>\n<p><span style=\"font-size: 12pt;\">　　21项交易品种涵盖：</span></p>\n<p><span style=\"font-size: 12pt;\">　　1.农村土地经营权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　2.农村&ldquo;四荒地&rdquo;使用权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　3.农村水域、滩涂养殖权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　4.农村集体资产交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　5.闲置农村住房和宅基地使用权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　6.农村集体资产股权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　7.集体所有的农业生产设施设备所有权、使用权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　8.集体林权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　9.农村集体经营性建设用地使用权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　10.城乡建设用地增减挂钩节余指标交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　11.建设占用耕地占补平衡指标交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　12.安置房租赁交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　13.小型水利设施使用权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　14.农业类知识产权交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　15.全域土地综合整治项目</span></p>\n<p><span style=\"font-size: 12pt;\">　　16.农业产业项目</span></p>\n<p><span style=\"font-size: 12pt;\">　　17.农产品加工和乡村服务业项目</span></p>\n<p><span style=\"font-size: 12pt;\">　　18.农村生物资产交易</span></p>\n<p><span style=\"font-size: 12pt;\">　　19.西安市储备粮（商品贸易粮）购销</span></p>\n<p><span style=\"font-size: 12pt;\">　　20.农村工程建设项目采购</span></p>\n<p><span style=\"font-size: 12pt;\">　　21.村级货物和服务采购</span></p>\n<p><span style=\"font-size: 12pt;\">　　会上，西安农业投资集团有限公司董事长成斌介绍，目前交易平台挂牌农业类知识产权交易、闲置农村住房和宅基地使用权交易2类交易品种共13个交易项目，挂牌项目总金额约256万元；预挂牌农村土地经营权交易项目2个，预挂牌项目总金额约128.22万元；收集储备农村土地经营权交易、农村集体经营性建设用地使用权交易、农村集体资产交易、安置房租赁交易4类交易品种共11个项目，储备项目总额约7118.01万元。</span></p>\n<p><span style=\"font-size: 12pt;\">　　成斌透露，下一步，要素交易市场在原有4大类21项交易品种的基础上，还将探索创新农业碳汇、非遗文化产品等交易品种。</span></p>\n<p><span style=\"font-size: 12pt;\">　　要素交易一般需经过8个环节</span></p>\n<p><span style=\"font-size: 12pt;\">　　在要素流转交易中，出让方是指农村集体经济组织及其成员、各类涉农经营主体等，包括依法流转城乡要素的法人、非法人组织和具有完全行为能力的自然人。受让方是指具有完全民事权利能力和民事行为能力的自然人、独立享有民事权利和承担民事义务的法人或其他组织。流转交易方式包括协议成交、竞价、拍卖、招标等方式，也可以采取法律、法规、规章规定的其他方式。</span></p>\n<p><span style=\"font-size: 12pt;\">　　要素流转交易如何进行，观摩会上，西安城乡融合要素交易市场有限公司相关负责人以闲置农村住房和宅基地交易为例做了详细介绍。&ldquo;这样一笔交易，从挂牌到最终完成，一般要经历8个环节。&rdquo;该负责人说。</span></p>\n<p><span style=\"font-size: 12pt;\">　　具体流程上，首先出让方需要登录交易平台网站或者手机端，按照个人模块或者单位用户模块进行相应注册。注册完成后，按照平台要求提交出让申请书、主体资格证明、权属证明等材料。第二环节为形式审查，平台会在3个工作日内对提交的材料进行审查，审查合格予以受理。第三环节发布公共信息，借助市级及区县级交易平台，公开发布公告信息，公告期不少于5个工作日。公告内容包括项目基本情况、流转期限、保证金金额、流转方式、交易方式等。第四环节为征集意向受让方环节，意向受让方提交材料并足额缴纳交易保证金后，完成报名。</span></p>\n<p><span style=\"font-size: 12pt;\">　　5个工作日的公告期满之后，如果产生一个及以上符合条件的意向受让方，要素交易市场会按照公告载明的方式组织交易。交易成功后，进入成交公示环节，公示期为3个工作日。成交结果公示结束且无异议后，次日起30日内组织交易双方签订交易合同并完成相应资金结算。</span></p>\n<p><span style=\"font-size: 12pt;\">　　全市首例挂牌要素计划今日开展竞价</span></p>\n<p><span style=\"font-size: 12pt;\">　　记者从会上获悉，西安城乡融合要素交易市场有限公司分别在西咸新区、灞桥区、阎良区 、临潼区 、长安区 、高陵区、鄠邑区、蓝田县、周至县下设9个子公司，高新区1个分公司。</span></p>\n<p><span style=\"font-size: 12pt;\">　　会上，通报了各子公司和分公司建设进展。目前，长安区、灞桥区、阎良区、鄠邑区、临潼区、蓝田县6个子公司和高新区分公司已完成注册，高陵区、周至县、西咸新区子公司预计4月底前全部注册完成。</span></p>\n<p><span style=\"font-size: 12pt;\">　　据悉，西安城乡融合要素交易市场长安区子公司（以下简称长安区子公司），是我市首家城乡融合要素交易区县级子公司。该公司今年1月26日完成注册，2月22日挂牌运营。目前长安区子公司已审核完成首批交易要素资料。3月15日，王曲街道江兆村、曙光村8宗闲置农村住房和宅基地使用权流转项目成功在线上平台挂牌，成为全市首例挂牌要素，计划在3月22日开展竞价。</span></p>\n<p><span style=\"font-size: 12pt;\">　　位于灞桥区半坡堡子村商圈的灞桥区城乡融合要素交易市场，计划3月26日正式挂牌开业。当前灞桥区包装了三方面项目，农村集体经营性建设用地使用权交易1宗，位于灞桥区狄寨街道塘村，宗地总面积8.298亩，评估价格95万元/亩；闲置农村住房和宅基地使用权交易项目12个，涉及面积1300平方米；农村土地经营权交易项目多个，涉及土地1000余亩。市场挂牌运行后，以上三个项目即可尽快投入交易。</span></p>\n<p><span style=\"font-size: 12pt;\">　　会议的召开，让参会的碑林区文艺路街道旅馆村金东股份经济合作社理事长韩瑞强心里有了底。他们合作社正面临村集体和合作社成员共约1.3万平方米的回迁安置商铺通过合法合规渠道挂牌出租的情形。韩瑞强说，将回迁安置商铺交由政府主导的平台挂牌交易，更能做到公平、公正、公开，更能最大限度保护全体合作社成员的集体利益，&ldquo;这也是我们大家共同商议的结果，大家都觉得政府主导的交易平台，更安全更让人放心！&rdquo;</span></p>', '101', 148, NULL, '西安日报报道西安城乡融合要素交易市场现场观摩暨工作推进会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (625, '西安市农业农村局 关于印发《西安市农村产权流转交易管理办法（试行）》的通知', 999999, NULL, 'xafby', '183', '2024-03-25 09:37:21', '2024-08-28 16:57:18', '2024-03-25 09:37:21', '2', 'xafby', '西安市农业农村局 ', '西安发布员', '300,302', '0', 1, 0, 0, '<p style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; <span style=\"font-size: 18pt;\">各涉农区县（开发区）农业农村部门，新城区、碑林区、莲湖区城棚改事务中心：</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\">为规范农村产权流转交易行为，推动城乡生产要素平等交换，保障农村集体经济组织和农民群众的合法权益，结合本市实际，市农业农村局制定了《西安市农村产权流转交易管理办法（试行）》，现印发你们，请遵照执行。</span></p>\n<p style=\"text-align: right;\">&nbsp;</p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">联系人：张伟亮 &nbsp;电话：029-86787557</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;西安市农业农村局</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2024年3月12日</span></p>\n<p style=\"padding-left: 30px; text-align: center;\"><span style=\"font-size: 18pt;\"><strong>西安市农村产权流转交易管理办法（试行）</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第一章 总 则</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第一条</strong>为规范农村产权流转交易行为，推动城乡生产要素平等交换，保障农村集体经济组织和农民群众的合法权益，根据《中华人民共和国农村土地承包法》《陕西省农村产权流转交易管理办法》《西安城乡融合要素交易市场组建方案》等法律法规和政策规定，制定本办法。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二条</strong>凡在本市从事农村产权流转交易活动的，适用本办法。法律、法规、规章另有规定的，从其规定。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三条</strong>农村产权流转交易应当遵守有关法律、法规、规章和政策规定，并遵循下列原则：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）坚持依法、自愿、有偿，公开、公平、公正原则；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）坚持城乡融合发展，集约利用农村资源原则；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）坚持保护农村集体经济组织和农民对农村产权的占有、使用、收益等合法权益原则；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）坚持不得改变土地集体所有性质，不得改变土地用途，不得突破耕地、生态保护红线，不得损害农民利益，符合国家和省、市有关法律法规及环境保护、农业产业发展规划、国土空间规划等相关规定。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第四条</strong>交易的农村产权权属应当清晰，权属关系不明确或者存在权属纠纷的不得交易。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第二章 组织机构</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第五条</strong>西安城乡融合要素交易市场监督管理委员会负责全市农村产权流转交易组织协调和指导监管工作，办公室设在中共西安市委农村工作领导小组办公室。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第六条</strong>凡在本市从事农村产权流转交易活动的机构，须在西安城乡融合要素交易市场监督管理委员会办公室登记备案，接受各级城乡融合要素交易监督管理部门指导和管理。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第七条</strong>全市城乡融合要素交易机构要按照交易规模和标的额，制定分级管理权限，实行分级管理、分级负责。市、区县（开发区）级城乡融合要素交易机构，提供农村产权流转交易政策咨询、受理交易申请、信息发布、组织交易、交易鉴证等服务。有条件的机构可逐步开展法律服务、资产评估、投融资、招标采购等配套服务。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第八条</strong>鼓励中介服务机构开展与农村产权流转交易相关的服务活动。从事农村产权流转交易相关的资产评估、产权经纪、项目推介及其他中介服务的机构应当具备下列条件：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）依法批准设立，具有良好信誉和经营业绩；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）遵守国家法律、法规、规章和政策；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）承诺遵守城乡融合要素交易机构的各项规章制度和业务规则；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）承诺为农村产权流转交易活动提供优质服务。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第三章 交易内容、方式和程序</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第九条</strong>农村产权流转交易的内容主要包括：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）农村土地经营权。家庭承包的耕地、草地等经营权权利人对其拥有的土地经营权，采取出租、入股等方式流转交易，流转期限不能超过法定期限。</span></p>\n<p><span style=\"font-size: 18pt;\">（二）农村&ldquo;四荒地&rdquo;使用权。农村&ldquo;四荒地&rdquo;（荒山、荒沟、荒丘、荒滩）权利人将其拥有的&ldquo;四荒地&rdquo;使用权，采取转让、出租、入股、抵押等方式流转交易。</span></p>\n<p><span style=\"font-size: 18pt;\">（三）农村水域、滩涂养殖权。权利人将其拥有的农村水域、滩涂养殖权，采取转包、出租、互换、转让、入股或者其他依法可以流转的形式流转交易。</span></p>\n<p><span style=\"font-size: 18pt;\">（四）农村集体资产。农村集体经营性资产所有权、使用权交易；农村集体非经营性资产使用权交易；街道（镇）持有、代管及受托管理的集体资产所有权、使用权的交易。</span></p>\n<p><span style=\"font-size: 18pt;\">（五）农村集体资产股权。农村集体经济组织所有未确权到户的土地、房屋及其他集体资产清产核资后，折股量化到集体经济组织成员，成员按份额或股份享受的农村集体资产财产权益的权利交易。</span></p>\n<p><span style=\"font-size: 18pt;\">（六）集体所有的农业生产设施设备所有权或使用权。产权权利人将其拥有的农业生产设施设备所有权、使用权，采取转让、租赁、拍卖等方式交易。</span></p>\n<p><span style=\"font-size: 18pt;\">（七）村级货物和服务采购。村（社区、集体经济组织）集体使用自有资金、财政资金（依法必招限额以下工程项目和政府采购限额以下项目）、捐赠资金或其他来源的资金采购货物和服务采购。</span></p>\n<p><span style=\"font-size: 18pt;\">（八）农村工程建设项目采购。街道（镇）、村（社区、集体经济组织）集体使用自有资金、财政资金（依法必招限额以下工程项目和政府采购限额以下项目）、捐赠资金或其他来源的资金实施的各类农村工程建设项目采购。</span></p>\n<p><span style=\"font-size: 18pt;\">（九）农业产业项目。包括蔬菜粮食等各类农产品种植类项目、中药材种植项目、畜牧养殖项目等农业产业项目的招商和转让。</span></p>\n<p><span style=\"font-size: 18pt;\">（十）农产品加工和乡村服务业项目。包括农产品加工项目、农文旅融合项目的招商和转让。</span></p>\n<p><span style=\"font-size: 18pt;\">（十一）闲置农村住房和宅基地使用权交易。权利人自愿进行的出租、入股等农村闲置住房流转交易，宅基地使用权在同一集体经济组织成员内部进行的流转交易。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十条</strong>农村产权申请流转交易时，应当提交下列材料：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）农村产权流转交易申请书（含权属共有人签字、转让标的的基本情况、出让价格、受让方条件等内容）；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）转让方的主体资格证明或者其他有效证明材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）委托办理交易手续的，需提交授权委托书及受托方主体资格证明、法定代表人或者负责人身份证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）产权权属的合法有效权属证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）农村集体产权转让的决议及批准文件；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）依法需进行评估的，应提供资产评估报告；</span></p>\n<p><span style=\"font-size: 18pt;\">（七）城乡融合要素交易机构和相关行政监督部门要求提交的其他材料。</span></p>\n<p><span style=\"font-size: 18pt;\">转让方应当对所提交资料的真实性、完整性、合法性、有效性负责，并承担相应的法律责任。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十一条</strong>城乡融合要素交易机构对申请产权项目资料进行齐全性和规范性审核，必要时可提请相关职能部门协助查档确认权属。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十二条</strong>城乡融合交易机构对交易项目信息披露应符合以下要求：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）披露信息包括但不限于以下内容：出让标的基本情况；产权出让行为的决策及批准情况；受让方资格条件（适用于对受让方有特殊要求情形）；交易条件、出让价格、交易方式；法律法规规定的其他需要披露的事项。</span></p>\n<p><span style=\"font-size: 18pt;\">（二）首次信息公告期限应当不少于5个工作日，可在城乡融合要素交易机构网站、街道（镇）公告栏、村务公开栏等相关场所和媒介同步发布信息，公告内容应保持一致，以城乡融合交易机构网站首次信息公告日为起始日。</span></p>\n<p><span style=\"font-size: 18pt;\">（三）转让方不得在信息发布期间擅自变更或取消所发布的信息。如特殊原因确需变更或取消的，转让方必须在公告时间截止前3个工作日将变更或取消内容报城乡融合要素交易机构，经审核后在原发布网站进行公告并通知已报名人，并重新计算公告期。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十三</strong><strong>条</strong>意向受让方申请受让产权，应当提交下列材料，并接受相应城乡融合要素交易机构资格审查。</span></p>\n<p><span style=\"font-size: 18pt;\">（一）农村产权意向受让申请书；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）受让方的主体资格证明材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）同意受让的内部决议或批复文件；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）受让方的资信证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）委托代理的，需提交授权委托书及受托人身份证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）联合受让的，需提交联合受让协议书、代表推举书或相关证明材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（七）需要提交的其他材料。</span></p>\n<p><span style=\"font-size: 18pt;\">意向受让方应对其提交资料的真实性、完整性、有效性负责。转让方在披露的产权流转交易信息中要求受让方缴纳保证金的，意向受让方在规定时间内交纳交易保证金（以到账时间为准）后获得受让资格；逾期未交纳交易保证金的，视为放弃受让资格。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十四条</strong>农村产权转让信息披露期满、产生符合条件的受让方的，按照披露的交易方式由城乡融合要素交易机构组织交易。交易可以采取协议、竞价、综合评审、拍卖、招标以及其他方式，且不得违反国家法律、法规的规定。</span></p>\n<p><span style=\"font-size: 18pt;\">采取拍卖的交易方式，应当按照《中华人民共和国拍卖法》及有关规定组织实施。采取招投标的交易方式，应当按照《中华人民共和国招标投标法》及有关规定组织实施。</span></p>\n<p><span style=\"font-size: 18pt;\">采取协议方式交易的，要对交易的主要事项和主要内容进行公示。公示期内，如有争议应当就争议事项进行调整或协商，在解决争议后继续履行交易程序或重新确定受让方。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十五条</strong>受让方确定后，交易机构应当将交易结果在项目信息发布的同一媒介、场所进行公告，公告内容包括标的名称、转让方、成交价格、成交时间、受让方，公告期不少于3个工作日。如无异议，城乡融合要素交易机构组织交易双方签订产权流转交易合同，转让方和受让方签字盖章，产权交易合同生效。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十六条</strong>交易合同生效，且受让方按照合同约定支付交易价款后，城乡融合要素交易机构出具交易鉴证书。交易鉴证书应当载明如下事项：项目编号、签约日期、转让方全称、受让方全称、转让方或受让方有效证件号码、中介服务机构全称、标的全称及面积、交易方式、成交价款、支付方式等内容。</span></p>\n<p><span style=\"font-size: 18pt;\">交易鉴证书使用统一格式打印，手写、涂改无效。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十七条</strong>凡在城乡融合要素交易机构进行流转交易，依法需要办理登记的，转让方、受让方应当凭城乡融合要素交易机构出具的交易鉴证书和相关材料到产权登记部门按要求办理相关权属变更手续。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第四章 交易行为规范</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十八条</strong>各级城乡融合要素交易机构完成的农村产权流转交易，需5个工作日内在同级城乡融合要素交易监督管理机构备案。西安城乡融合要素交易监督管理机构依托现有西安市农村产权交易平台建立西安市农村产权交易监管系统，做好农村产权流转交易合同系统备案、预警监测、交易双方履约监管等。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第十九条</strong>农村产权流转交易实行信息化管理，使用城乡融合要素交易机构提供的统一格式合同文本。城乡融合要素交易机构要分类别建立交易台账。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十条</strong>农村产权流转交易过程中，有下列情形之一的，经城乡融合要素交易机构确认后中止流转交易：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）农村产权流转交易行政监督管理部门或产权登记部门提出中止流转交易的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）转让方或者与产权有直接利害关系的第三方提出正当理由，并经有关行政监督管理部门批准的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）产权存在权属争议的；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）其他依法应当中止流转交易的情形。</span></p>\n<p><span style=\"font-size: 18pt;\">中止期限由城乡融合要素交易机构根据实际情况设定，一般不超过30天。城乡融合要素交易机构应当在中止期间对相关的申请事由或争议事项进行调查核实，也可转请相关部门进行调查核实，及时作出恢复或者终结流转交易的决定。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十一条</strong>在产权交易过程中，出现下列情形之一的，经城乡融合要素交易机构确认后终止交易：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）中止期限届满后，仍未能消除影响交易中止的因素导致交易无法继续进行的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）有关行政监督部门依法提出终止流转交易的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）转让方或者与产权有直接利害关系的第三方向有关行政监督部门书面提出终止交易申请，并经批准的；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）人民法院、仲裁机构依法发出终止交易书面通知的；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）其他依法应当终止交易的情形。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十二条</strong>交易中出现中止、终止、恢复情形的，应当在城乡融合要素交易机构网站上公告。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十三条</strong>在产权交易活动中，禁止下列行为：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）操纵交易市场或者扰乱交易秩序的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）有损于转让方、受让方进行公平交易的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）法律、法规、规章禁止的其他行为。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第五章 交易权益保障</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十四条</strong>农村集体产权的流转交易，须经本集体经济组织三分之二以上成员（成员代表）同意，形成决议并报街道（镇）批准。决议需有三分之二以上农村集体经济组织成员（成员代表）签名并加盖集体经济组织公章。交易价格，应以有资质的资产评估机构的评估值作为依据；对无法评估或评估成本占标的估算价比例过高的资产，可由城乡融合要素交易机构、金融机构会同农户、农民合作组织、农村集体和涉农企业等产权持有人经市场调查后，参照同期同类型资产市场价格确定标的评估价。转让方在评估价的基础上，确定流转交易标的底价。</span></p>\n<p><span style=\"font-size: 18pt;\">农民个人产权流转交易须是本人自愿并征得权属共有人同意后流转交易。交易价格可以依据有资质的资产评估机构的评估值，也可由转让方自行确定。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十五条</strong>农村集体产权的流转交易，在同等条件下，本集体经济组织成员享有优先受让权。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十六条</strong>农村集体经济组织获得的交易收益，纳入农村集体财产统一管理，按照有关规定进行使用。农户个人产权的交易收益，归个人所有。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第六章 配套服务</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十七条</strong>为鼓励农村产权流转交易，转让方为农村集体经济组织或者农民的，免收流转交易服务费用。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十八条</strong>各银行、保险机构和融资性担保公司按照相关规定制定农村承包土地经营权、住房财产权等权利抵押贷款及相关保险、担保和反担保业务的管理制度和办法，明确抵押的条件、对象和流程等事项。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二十九条</strong>农村集体所有的依法可以抵押的产权，应当有本集体经济组织三分之二以上成员（成员代表）同意抵押的书面决议和抵押人同意实现抵押权的书面承诺。以农村土地承包经营权作抵押的，抵押期限不得超过农村土地承包期内剩余期限；以通过流转方式取得农村土地经营权抵押的，须经发包方和承包方的同意，抵押期限不得超过其流转剩余期限。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三十条</strong>有条件的区县可探索设立农村产权抵押担保风险保障金，用于收购抵债资产。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第七章 交易监管和争议处理</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三十一条</strong>市、区县（开发区）城乡融合要素交易监督管理机构应当依据本办法规定，对农村产权流转交易活动进行监督和管理。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三十二条</strong>流转交易双方及中介服务机构有违法违规违约行为，给城乡融合要素交易机构及相关交易主体造成损失的，依法承担法律责任。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三十三条</strong>在城乡融合要素交易机构进行流转交易过程中，发生产权流转交易纠纷的，当事人可以向城乡融合要素交易机构或者当地城乡融合要素交易监督管理部门申请调解，也可以依据合同约定申请仲裁或者依法向人民法院提起诉讼。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三十四条</strong>除农户宅基地使用权、农民住房财产权、农户持有的集体资产股权之外，流转交易的受让方原则上没有资格限制，外资企业和境外投资者按照有关法律法规执行。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>第八章 附 则</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三十五条</strong>本办法自公布之日起30日后生效，有效期2年。</span></p>', '302', 73, NULL, '西安市农业农村局 关于印发《西安市农村产权流转交易管理办法（试行）》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (626, '西安市市场监督管理局（知识产权局） 关于印发《西安市农业类知识产权 交易指导意见》的通知', 999999, NULL, 'xafby', '183', '2024-03-25 16:28:12', '2024-08-28 16:57:18', '2024-03-25 16:28:12', '2', 'xafby', '西安市市场监督管理局（知识产权局）', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>西安市市场监督管理局（知识产权局）关于印发《西安市农业类知识产权交易指导意见》的通知</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 各区县、西咸新区市场监管局，各开发区分局，各有关单位：</span></p>\n<p><span style=\"font-size: 18pt;\">《西安市农业类知识产权交易指导意见》已经市市场监督管&nbsp;理局&nbsp;2024&nbsp;年第&nbsp;1&nbsp;次局长办公会审议通过，现印发你们，请结合工</span></p>\n<p><span style=\"font-size: 18pt;\">作实际，认真贯彻执行。</span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">西安市市场监督管理局（知识产权局）</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">2024&nbsp;年&nbsp;2&nbsp;月&nbsp;8 日</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>西安市市场监督管理局（知识产权局） </strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>西安</strong><strong>市农业类知识产权交易指导意见</strong></span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">第一章 &nbsp;&nbsp;总则</span></p>\n<p><span style=\"font-size: 18pt;\">第一条 &nbsp;为促进和规范农业类知识产权交易，保障交易双方&nbsp;当事人合法权益，维护农村产权交易市场秩序，根据《中华人民&nbsp;共和国民法典》《中华人民共和国专利法》《中华人民共和国商&nbsp;标法》《陕西省农村产权流转交易管理办法》等规定，制定本意见。</span></p>\n<p><span style=\"font-size: 18pt;\">第二条 &nbsp;在西安市辖区内进行的农业类知识产权交易活动，适用本意见。法律、法规和规章另有规定的，从其规定。</span></p>\n<p><span style=\"font-size: 18pt;\">第三条 &nbsp;本意见所称农业类知识产权交易是指涉农专利、商标、版权、新品种、新技术等农业类知识产权交易。</span></p>\n<p><span style=\"font-size: 18pt;\">第四条 &nbsp;农业类知识产权交易的出让方和受让方，可以向城&nbsp;乡融合要素交易市场申请交易，也可以以法律、法规认可的其他 方式进行交易。鼓励引导农村集体所有的农业类知识产权交易按 照《陕西省农村产权流转交易管理办法》相关规定，依托城乡融合要素交易市场进行交易。</span></p>\n<p><span style=\"font-size: 18pt;\">第五条 &nbsp;农业类知识产权交易应遵守法律、法规和政策的规&nbsp;定，遵循自愿平等、诚实信用和公开、公平、公正的原则，不得&nbsp;侵犯他人的合法权益和损害社会公共利益，依法保护知识产权交易当事人的合法权益。</span></p>\n<p><span style=\"font-size: 18pt;\">第六条 &nbsp;农业类知识产权交易可以采取下列方式进行：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）协议转让；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）拍卖；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）招标；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）许可使用；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）电子竞价；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）国家法律、法规规定的其他方式。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">第二章 &nbsp;&nbsp;交易程序</span></p>\n<p><span style=\"font-size: 18pt;\">第七条 &nbsp;交易的基本条件：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）交易双方必须是具有完全民事权利能力和民事行为能&nbsp;力的自然人、法人、农村集体经济组织或其他组织，并具有交易的真实意愿。</span></p>\n<p><span style=\"font-size: 18pt;\">（二）知识产权权属明晰，权属证明齐全有效。</span></p>\n<p><span style=\"font-size: 18pt;\">（三）交易项目符合国家的法律法规及相关政策规定。</span></p>\n<p><span style=\"font-size: 18pt;\">第八条 &nbsp;出让方可以直接向交易机构申请并提交下列材料：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）主体资格证明材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）知识产权权属证明及有效性证明材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）出让申请书；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）内部决策材料和批准出让的文件；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）委托他人办理的提交委托材料，包括授权委托书及受托人身份证；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）需要评估的，须由有资质的资产评估机构进行评估，并提供评估报告；</span></p>\n<p><span style=\"font-size: 18pt;\">（七）城乡融合要素交易机构要求提供的其他材料。</span></p>\n<p><span style=\"font-size: 18pt;\">第九条 &nbsp;交易机构依据出让方提交的材料制作挂牌信息，在&nbsp;交易机构网站、乡镇（街道）公告栏、村务公开栏等相关场所和 媒介同步发布信息，公告内容应保持一致，首次挂牌时间不得少 于&nbsp;5&nbsp;个工作日，&nbsp;以交易机构网站首次信息公告日为起始日。挂牌&nbsp;期间出让方不得擅自变更或取消所发布的信息，特殊原因确需变 更或取消的，出让方须在公告时间截止前&nbsp;3&nbsp;个工作日将变更或取&nbsp;消内容报交易机构，经审核后在原发布网站及相关场所和媒介进行公告并通知已报名人，并重新计算公告期。</span></p>\n<p><span style=\"font-size: 18pt;\">第十条 &nbsp;凡对交易机构挂牌出让项目有受让意向者，应在挂牌期间直接向交易机构递交受让申请及相关文件：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）意向受让申请书；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）主体资格证明材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）同意受让的内部决议或批复文件；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）委托代理的，需提交授权委托书及受托人身份证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）需要提交的其他材料。</span></p>\n<p><span style=\"font-size: 18pt;\">意向受让方应对其提交资料的真实性、完整性、有效性负责。</span></p>\n<p><span style=\"font-size: 18pt;\">出让方在披露的产权交易信息中要求受让方缴纳保证金的，&nbsp;意向受让方在规定时间内交纳交易保证金（以到账时间为准）后获得受让资格；逾期未交纳交易保证金的，视为放弃受让资格。</span></p>\n<p><span style=\"font-size: 18pt;\">第十一条 &nbsp;挂牌期间，交易机构接受意向受让方的咨询洽谈，&nbsp;对意向受让方的申请进行登记，根据出让方委托按出让方提交的 &nbsp;交易要求对意向受让方进行资格审查。挂牌期满，如只产生一个 &nbsp;符合条件的意向受让方，可以采取协议转让的方式；产生两个及 &nbsp;以上符合条件的意向受让方，可以采取电子竞价、综合评审、拍 &nbsp;卖、招标以及其他不违反国家法律、法规规定的竞价方式确定受让方。</span></p>\n<p><span style=\"font-size: 18pt;\">采取拍卖方式转让的，应当按照《中华人民共和国拍卖法》及有关规定组织实施。</span></p>\n<p><span style=\"font-size: 18pt;\">采取招标方式转让的，应当按照《中华人民共和国招投标法》及有关规定组织实施。</span></p>\n<p><span style=\"font-size: 18pt;\">第十二条 &nbsp;确定受让方后，交易机构按照相关规定进行不少&nbsp;于&nbsp;3&nbsp;个工作日的公告。如无异议，交易双方签订农业类知识产权交易合同。</span></p>\n<p><span style=\"font-size: 18pt;\">第十三条 &nbsp;经交易机构达成协议的受让方通过交易机构互联网平台或按照合同约定的方式进行成交价款结算。</span></p>\n<p><span style=\"font-size: 18pt;\">第十四条 &nbsp;经交易机构达成协议的出让方与受让方应支付交易服务费并按相关规定享受优惠待遇。</span></p>\n<p><span style=\"font-size: 18pt;\">第十五条 &nbsp;经交易机构达成协议的由交易机构出具《交易鉴定证书》。</span></p>\n<p><span style=\"font-size: 18pt;\">交易双方凭生效协议及相关证明材料，到原发证机构办理相关登记或变更手续。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">第三章 &nbsp;&nbsp;行为规范</span></p>\n<p><span style=\"font-size: 18pt;\">第十六条 &nbsp;中国单位或者个人向外国人、外国企业或者外国其他组织转让知识产权的，必须依照有关法律、法规要求办理。</span></p>\n<p><span style=\"font-size: 18pt;\">第十七条 &nbsp;在农业类知识产权交易过程中，出现下列情形之一的，可终止交易：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）不符合相关法律、法规要求进行交易的；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）存在权属争议的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）行政主管部门提出终止交易的；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）处于质押状态，没有解除质押的（出质人与质权人协商同意的除外）；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）出让方或与该农业类知识产权有直接利害关系的第三方提出正当理由，并经行政主管部门批准的；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）司法机关已对标的物采取查封、扣押、冻结等措施的；</span></p>\n<p><span style=\"font-size: 18pt;\">（七）其他依法应当终止交易的情形。</span></p>\n<p><span style=\"font-size: 18pt;\">第十八条 &nbsp;在交易过程中，禁止下列行为：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）操纵交易市场或者扰乱交易秩序；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）有损于交易双方进行公平交易的；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）法律、法规、规章禁止的其他行为。</span></p>\n<p><span style=\"font-size: 18pt;\">第十九条 &nbsp;农业类知识产权交易当事人与交易机构在交易服务&nbsp;时发生交易纠纷的，可以向交易机构或西安城乡融合要素交易市场监&nbsp;督管理委员会等当地农村产权流转交易监督管理部门申请调解，也可</span></p>\n<p><span style=\"font-size: 18pt;\">以依据合同约定申请仲裁或者依法向人民法院提起诉讼。</span></p>\n<p><span style=\"font-size: 18pt;\">第二十条 &nbsp;交易双方及中介服务机构有违法违规违约行为，给交易机构及相关交易主体造成损失的，依法承担法律责任。</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">第四章&nbsp;&nbsp;&nbsp;附 &nbsp;&nbsp;则</span></p>\n<p><span style=\"font-size: 18pt;\">第二十一条 &nbsp;本意见自公布之日起&nbsp;30 日后生效，有效期&nbsp;2&nbsp;年。</span></p>', '302', 14, NULL, '西安市市场监督管理局（知识产权局） 关于印发《西安市农业类知识产权 交易指导意见》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (627, '西安市自然资源和规划局关于印发《西安市全域土地综合整治项目实施管理暂行办法》的通知', 999999, NULL, 'xafby', '183', '2024-03-25 16:43:41', '2024-08-28 16:57:18', '2024-03-25 16:43:41', '2', 'xafby', '西安市自然资源和规划局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><strong><span style=\"font-size: 12pt;\">西安市自然资源和规划局关于印发《西安市全域土地综合整治项目实施管理暂行办法》的通知</span></strong></p>\n<p>市资源规划局各分局，各县资源规划局，市局机关有关处室，市国土整治和生态修复中心：</p>\n<p>&nbsp; &nbsp; &nbsp; 经市局领导研究同意，市局对《西安市全域土地综合整治项目实施管理暂行办法》(市资源发〔2023〕176号)进行了修订，增加了第二十四条全域土地综合整治项目可在西安城乡融合要素交易市场平台进行招商，省级有规定的从其规定。现将修订后的《西安市全域土地综合整治项目实施管理暂行办法》印发给你们，请遵照执行。</p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\">西安市自然资源和规划局</p>\n<p style=\"text-align: right;\">2024年3月4日</p>\n<p>附件</p>\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 12pt;\">西安市全域土地综合整治项目实施管理暂行办法</span></strong></p>\n<p style=\"text-align: center;\">第一章 总 则</p>\n<p><strong>第一条</strong> 为扎实做好我市全域土地综合整治项目工作，规范项目实施管理，根据自然资源部《关于开展全域土地综合整治试点工作的通知》（自然资发〔2019〕194 号）、自然资源部生</p>\n<p>态修复司《关于印发&lt;全域土地综合整治试点实施要点&gt;（试行）的函》（自然资生态修复函〔2020〕37 号）、陕西省自然资源厅《关于印发&lt;陕西省全域土地综合整治项目实施管理暂行办法&gt;</p>\n<p>的通知》（陕自然资修复发〔2022〕10 号）等文件要求，结合我市实际,制定本办法。</p>\n<p><strong>第二条</strong> 开展全域土地综合整治项目，要以科学规划为前提，以乡镇（街道）为基本实施单元，整体推进农用地整理、建设用地整理、乡村生态保护修复和乡村历史文化保护，优化</p>\n<p>生产、生活、生态空间格局，促进耕地保护和土地节约集约利用，改善农村人居环境，助推乡村全面振兴。</p>\n<p><strong>第三条</strong> 开展全域土地综合整治项目，应当遵循&ldquo;生态优先、保护优先，规划引领、节约集约，政府主导、多方参与，守住底线、改革创新，因地制宜、循序渐进&rdquo;的原则。</p>\n<p><strong>第四条</strong> 本办法适用于我市范围内的国家级全域土地综合整治试点项目和省级全域土地综合整治项目。</p>\n<p style=\"text-align: center;\">第二章 职责分工</p>\n<p><strong>第五条</strong> 市自然资源和规划局牵头成立全域土地综合整治项目工作领导机构，负责全市全域土地综合整治项目领导工作，办公室设在国土空间生态修复处。负责指导县（区）级自然资源部门选点、审查、转报相关资料以及实施过程中的监督检查。负责国家级项目的协助选点、审查报送、施工督导、竣工初验等工作；负责省级项目的协助选点、审查报送、施工督导、竣工验收等工作。市国土整治和生态修复中心为全域土地综合整治项目的技术服务单位，协助市自然资源和规划局做好项目的业务咨询和技术支撑工作。</p>\n<p><strong>第六条</strong> 项目所在县（区）人民政府为全域土地综合整治项目的实施主体，负责牵头组织实施，协调各类涉农部门、整合涉农资金积极参与全域土地综合整治项目工作，协调项目所在乡镇人民政府（街道办）、村组织予以全力配合。</p>\n<p><strong>第七条</strong> 项目所在的县（区）级自然资源部门需成立相应的实施机构，负责项目的选点、组织项目实施及监督检查指导项目所在乡镇开展工作。负责国家级试点项目的选点、组织实施、监督检查及指导项目所在乡镇开展工作；负责省级项目的选点、组织实施、监督检查及指导项目所在乡镇开展工作、项目初验。</p>\n<p style=\"text-align: center;\">第三章 项目申报</p>\n<p><strong>第八条</strong> 实施区域。以乡镇（街道）为基本实施单元，整治区域可以是乡镇全部或同一个镇相对集中连片的几个行政村、一条或几条相邻小流域为单元，达到集中连片的整体效应。</p>\n<p><strong>第九条</strong> 选定条件。</p>\n<p>1.所在县、镇两级人民政府高度重视，当地群众拥护支持。</p>\n<p>2.项目区域具有已经完成或者具备优先完成村庄规划编制的基础条件和技术保障。</p>\n<p>3.项目区域内土地整治潜力较大，有较多可用于城乡建设用地增减挂钩指标、耕地后备资源丰富等条件。</p>\n<p>4.项目区域具有一定的经济优势，现有产业、计划开发产业对项目区实施支持力度明显。</p>\n<p>5.涉及永久基本农田需要调整的，新增永久基本农田面积增加不少于调整面积的 5%，整治区域内新增耕地面积不少于原有耕地面积的 5%。</p>\n<p><strong>第十条</strong> 申报程序。</p>\n<p><strong>1.</strong><strong>国家级试点项目：</strong>对符合申报条件的乡镇（街道），在县（区）人民政府确定后，由县（区）级自然资源主管部门征求财政、农业、林业、水利、生态环境等部门意见牵头组织申报，经市自然资源和规划局审查后，报省自然资源厅审批、备案。</p>\n<p><strong>2.</strong><strong>省级项目：</strong>对符合申报条件的乡镇（街道），在县（区）人民政府确定后，由县（区）级自然资源主管部门征求财政、农业、林业、水利、生态环境等部门意见牵头组织申报，经市自然资源和规划局审核，报省自然资源厅审批、备案。</p>\n<p><strong>第十一条</strong> 申报资料。县（区）级自然资源主管部门委托具备相应资质的规划设计单位编制项目区域建议书。</p>\n<p><strong>主要内容包 </strong><strong>括：</strong>1.项目建设的背景；2.项目建设的范围；3.项目建设的基础条件；4.项目区域土地利用现状；5.项目区域耕地后备资源情况；6.项目主要建设内容；7.项目建设目标；8.资金估算、资金来源及使用方向。<strong>附件内容包括：</strong>1.区域县级国土空间规划图（已经发布的）；2.村庄规划图；3.项目区建设规划图和主要工程规划图；4.项目区域内土地利用现状图；5.永久基本农田调整方案（调整区域）；6.市、县两级自然资源主管部门意见。</p>\n<p><strong>第十二条 </strong>负面清单。1.违背农民意愿搞合村并居、大拆大建，群众基础差的。</p>\n<p>2.借全域土地综合整治变相占用调整永久基本农田,或者调整的永久基本农田质量和布局不符合有关要求的。</p>\n<p>3.难以完成耕地和永久基本农田面积增加 5%要求的。</p>\n<p>4.单纯追求节余建设用地和补充耕地等指标交易的。</p>\n<p>5.破坏乡村风貌和历史文化文脉,在需要重点保护的传统村落搞不符合规定的拆建活</p>\n<p>动的。</p>\n<p>6.突破生态保护红线管理要求,破坏生态环境砍树挖山填湖，占用耕地搞人造景观的。</p>\n<p>7.违法用地情况严重且未及时处理到位的。</p>\n<p>8.借全域土地综合整治变相主要搞房地产开发，或整治后建设用地增加的。</p>\n<p>9.未经科学论证和评估，不符合相关规划，擅自进行大规模未利用地开发的。</p>\n<p>10.项目资金保障措施不到位，地方筹措资金渠道不明确、难以落实的。</p>\n<p>11.经综合判断不适合开展项目的其他情形。</p>\n<p style=\"text-align: center;\">第四章 项目实施</p>\n<p><strong>第十三条</strong> 全域土地综合整治项目区域建议书通过省自然资源厅备案后，由项目所在地县（区）人民政府委托具有相应资质的技术单位编制项目实施方案。实施方案编制按照《陕西省全域土地综合整治试点实施方案编制提纲（试行）》有关要求执行。</p>\n<p><strong>第十四条</strong> 项目区未编制村庄规划的，应按照相关要求依程序进行编制。已编制村庄规划但不能满足项目建设需要的，可重新进行编制或调整。</p>\n<p><strong>第十五条</strong> 项目区涉及永久基本农田调整的，由县（区）级自然资源主管部门组织编制永久基本农田调整方案（应满足新增永久基本农田面积增加不少于调整面积的 5%要求），经市自然资源和规划局审查同意，报省自然资源厅会同省农业农村厅评审并出具审查意见，报自然资源部备案后，纳入村庄规划。</p>\n<p><strong>第十六条</strong> 根据省自然资源厅相关文件，通过全域土地综合整治项目产生的涉及城乡建设用地增减挂钩、耕地占补平衡指标，可在省域内优先流转使用，所得收益用于项目区农村人居环境整治、高标准农田建设、乡村振兴等。审批程序按照现行相关政策执行。</p>\n<p style=\"text-align: center;\">第五章 项目验收</p>\n<p><strong>第十七条</strong> 国家级试点项目建设任务完成后，由市自然资源和规划局组织竣工初验，报省自然资源厅申请竣工验收。省级项目建设任务完成后，由县（区）级自然资源主管部门组织竣工初验，市自然资源和规划局组织竣工验收。</p>\n<p><strong>第十八条</strong> 验收应当运用土地利用现状调查成果、遥感监测影像成果及有关信息化技术手段，对照乡镇国土空间规划和村庄规划，结合实地核查，对有无严格按照规划确定的整治任务实施推进整体把关，重点对各项具体整治工程的完成情况及质量，特别是耕地和永久基本农田数量和质量、建设用地规模等是否达标进行验收，确保图、表、数据库和实地一致性。</p>\n<p><strong>第十九条</strong> 市自然资源和规划局收到项目所在县（区）人民政府验收申请后，会同市农业农村、水利、林业、财政等部门及相关行业专家对工程建设情况及建设质量进行全面验收并形成市级验收意见。</p>\n<p><strong>第二十条</strong> 项目竣工验收资料。</p>\n<p><strong>1.管理资料：</strong>项目区域实施规划，单项工程设计、预算、相关图件，项目实施的管理资料、施工资料、监理资料、财务资料。</p>\n<p><strong>2.调整资料：</strong>建设用地、永久基本农田、土地权属调整的审查资料，新增耕地的相关资料，省域内指标流转情况相关资料。</p>\n<p><strong>3.其他资料：</strong>相关部门、社会资本投入退出的相关资料。</p>\n<p style=\"text-align: center;\">第六章 附 则</p>\n<p><strong>第二十一条</strong> 各级要加强对全域土地综合整治项目政策的宣传，发挥农村集体经济组织和农民主体作用，充分激发当地群众参与全域土地综合整治项目的积极性。</p>\n<p><strong>第二十二条</strong> 全域土地综合整治项目的工程建设管理、资金管理等（在自然资源部未下发新的管理规定之前）应按照现行的部、省两级印发的全域土地综合整治试点项目相关文件执行。</p>\n<p><strong>第二十三条</strong> 全域土地综合整治项目的工程内容可参照土地整理工程、乡村振兴工程等相关管理规定执行。</p>\n<p><strong>第二十四条</strong> 全域土地综合整治项目可在西安城乡融合要素交易市场平台进行招商，省级有规定的从其规定。</p>\n<p><strong>第二十五条</strong> 本办法由市自然资源和规划局负责解释，自下发之日起执行，有效期两年。</p>', '302', 15, NULL, '西安市自然资源和规划局 关于印发《西安市全域土地综合整治项目实施管理暂行办法》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (628, '西安市水务局关于印发《小型水利设施使用权交易管理办法 （试行）》的通知', 999999, NULL, 'xafby', '183', '2024-03-25 17:39:24', '2024-08-28 16:57:18', '2024-03-25 17:39:24', '2', 'xafby', '西安市水务局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><strong>西安市水务局关于印发《小型水利设施使用权交易管理办法&nbsp;</strong><strong>（</strong><strong>试行</strong><strong>）</strong><strong>》的通知</strong></p>\n<p>&nbsp;&nbsp;&nbsp;各有关区县水务（农林水）局，灞桥区秦保局，西咸新区住建局，各开发区涉水事务管理部门，各有关单位：</p>\n<p>为规范我市小型水利设施使用权交易，推动城乡融合要素平等交换，保障农民和农村集体经济组织的财产权益，根据西安市委办、政府办印发的《西安城乡&nbsp;融合要素交易市场组建方案》等相关政策文件，结合我市实际，我局制定了《西安市小型水利设施使用权交易管理办法&nbsp;（&nbsp;试行）》,&nbsp;现印发你们，请遵照执行。</p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\">西安市水务局</p>\n<p style=\"text-align: right;\">2024年3月21日</p>\n<p style=\"text-align: center;\"><strong>西安市小型水利设施使用权交易管理办法</strong><strong>（</strong><strong>试行</strong><strong>）</strong></p>\n<p style=\"text-align: center;\">第一章&nbsp;总则</p>\n<p>第一条&nbsp;为规范我市小型水利设施使用权交易，推动城乡融合要素平等交换，保障农民和农村集体经济组织的财产权益，根据国务院办公厅印发的《关于引&nbsp;导农村产权流转交易&nbsp;市场健康发展的意见》、省农业农村厅、水利厅等7部门印发的《陕西省农村产权流转交易管理办法》及西安市委办、政府办印发的《西安城乡融合要素交易市场组建方案》等相关政策文件，结合我市实际，特制定本交易管理办法。</p>\n<p>第二条&nbsp;在西安市辖区内从事小型水利设施使用权交易活动的，适用本办法。法律法规、规章另&nbsp;有规定的，从其规定。</p>\n<p>第三条&nbsp;小型水利设施使用权交易应当遵循依法、自愿&nbsp;、有偿&nbsp;、公开、公平&nbsp;、公正的原则，不得改变小型水利设施使用属性，任何组&nbsp;织和个人不得强迫或妨碍自主交易。</p>\n<p>第四条&nbsp;本办法所称小型水利设施使用权是指农户&nbsp;、农民合作组织&nbsp;、农村集体和涉农企业等拥有的小型水利设施使用权。</p>\n<p>第五条&nbsp;小型水利设施使用权交易，交易方式可以采取承包、租赁、转让&nbsp;、抵押、股份合作等方式，也可以采用公开协议、网络竞价、综合评审、拍卖、招标以及其他竞价方式，且不得违反法律法规的规定。采用招标方式进行流转交易&nbsp;的，应当按照《中</p>\n<p>&nbsp;华人民共和国招投标法》及有关规定组织实施。采取拍卖方式进&nbsp;行流转交易的，应&nbsp;当按照《中华人民共和国拍卖法》及有关规定组织实施。</p>\n<p style=\"text-align: center;\">第二章&nbsp;交易条件和程序</p>\n<p>第六条&nbsp;小型水利设施使用权交易&nbsp;，出让方可以直接向交易机构申请并提交下列材料：</p>\n<p>（&nbsp;一）&nbsp;出让方使用权流转交易申请书（含权属共有人签字、出让标的基本情况、&nbsp;出让价格、受让方条件等内容；</p>\n<p>（&nbsp;二）&nbsp;出让方主体资格证明材料；</p>\n<p>（三）出让方委托办理交易手续的，需提交授权委托书及受托方主体资格证明、法定代表人或者负责人身份证明；</p>\n<p>（四）&nbsp;使用&nbsp;权是集体的或者企业的，提供同意受让的内部&nbsp;决议或批复文件；使用权是个人的，提供含权属共有人签字的自愿流转情况说明；</p>\n<p>（五）出让方的标的物法定权属证明；</p>\n<p>（六）依法需进行评估的，应提供资产评估报告；</p>\n<p>（七）流转交易机构和行政监督管理部门要求提交的其他材料。</p>\n<p>出让方应当对所提交材料的真实性、完整性、合法性、有效性负责，并承担相应的法律责任。</p>\n<p>第七条&nbsp;交易机构对出让方提交的材料进行齐全性和规范性审核&nbsp;，必要时可提请相关职能部门协助查档确认权属。</p>\n<p>第八条&nbsp;交易机构对出让方提交的材料审核合规后制作披露信息，披露信息包括但不限于出&nbsp;让标的基本情况、使用权出让行为的决策及批准情况、受让方资格条件（适用于对受让方有特殊要求情形）、交易条件、出让价格、交易方式和法律法规规定的其他需要披露的事项。披露信息可在交易机构网站、乡镇（街道）公告栏、村务公告栏等相关场所和媒介同步发布信息，公告内容应保持一&nbsp;致。首次信息公告期限不得少于&nbsp;5个工作日，以交易机构网&nbsp;站首次信息公告日为起始日。</p>\n<p>第九条&nbsp;挂牌期间出让方不得在信息发布期间擅自变更或取消所发布的信息。如特殊原因确需变更或取消的，出让方必须在公告时间截止前&nbsp;3&nbsp;个工作日将变更或取消内容报流转交易机构，经审核后在原发布网站进行公告并通知已&nbsp;报名人，并重新计算公告期。</p>\n<p>第十条&nbsp;小型水利设施使用权交易，受让方应提交下列材料，并接受流转交易机构资格审查：</p>\n<p>（&nbsp;一）&nbsp;意向受让申请书；</p>\n<p>（二）受让主体资格证明材料；</p>\n<p>（三）&nbsp;流转后使用权是集体的或者企业的，提供同意受让的内部决议或批复文件；流转后使用权是个人的，提供自愿受让情况说明；</p>\n<p>（四）委托代理的，需提交授权委托书及受托人身份证明；</p>\n<p>（五）联合受让的，需提交联合受让协议书&nbsp;、代表推举书或相关证明材料；</p>\n<p>（六）&nbsp;其他需要提交的资料。意向&nbsp;受让方应对其提交资料的真实性、完整性、有效性负责。出让方在披露的流转交易信息中要求受让方缴纳保证金的，意向受让方在规定时间内缴纳交易保证金（以到账时间为准）后获得受让资格；逾期未交纳交易保证金的，视为放弃受让资格。</p>\n<p>第十一条&nbsp;确定受让方后，交易机构按照相关规定应当将交易结果在项目信息发布的同&nbsp;一媒介、场所进行公告；公告内容包括标的名&nbsp;称&nbsp;、出让方&nbsp;、成交价格、成交时间、受让方；公告期不少于&nbsp;3个工作日&nbsp;。</p>\n<p>第十二条&nbsp;挂牌期间，交易机构应提供发布交易信&nbsp;息&nbsp;、受理交易咨询和申请、协助小型水利设施使用&nbsp;权查询&nbsp;、组织交易&nbsp;、出具小型水利设施使用权流转交易鉴证书，协助办理小型水利设施使用权变更登记和资金结算手续等基本服务；交易机构可以根据自身条件，开展资产评估、法律服务、产权经纪、项目&nbsp;推介、抵押融资等配套服务，还可以引入财会、法律、资产评估等中介服务组织以及银行、保险等金融机构和担保公司，为小型水利设施使用权流转交易&nbsp;提供专业化服务。</p>\n<p>第十三条&nbsp;挂牌期间，交易双方如无异议，交易机构组织交易双方签订小型水利设施使用权交易合同，合同生效。</p>\n<p>第十四&nbsp;条&nbsp;交易合同&nbsp;生效，且受让方按照合同约定支付交易价款后，交易机构出具小型水利设施使用权《交易鉴证书》。《交易鉴证书》应当&nbsp;载&nbsp;明&nbsp;项目&nbsp;编号、签约日&nbsp;期、出让方全称、受让方全称、中介服务机构全称、标的全称、交易方式、成交价款、支付方式等内容&nbsp;。《交易鉴证书》使用&nbsp;统一格式打印，手写&nbsp;、涂改无效。</p>\n<p>第十五条&nbsp;交易完成后，交易机构将交易过程形成的文字、图表、声像、电子数据等原始资料，按照有关档案法规、标准进行收集、整理、&nbsp;归档，并集中统一保管备查。</p>\n<p>第十六条&nbsp;交易双方凭小型水利设施使用权《交易鉴证书&nbsp;》和相关材料到小型水利设施使用&nbsp;权证书&nbsp;原&nbsp;登记机构办理相关权属变更手续。</p>\n<p style=\"text-align: center;\">第三章&nbsp;行为规范</p>\n<p>第十七条&nbsp;小型水利设施使用权属于集体的，流转交易须经本集体组织三分之二以上成员（成员代表）讨论通过，形成决议并报镇街政府批准；小型水利设施使用权属于个人的，流转交易须是本人自愿并征得权属共有人同意。</p>\n<p>第十八&nbsp;条&nbsp;小型水利设施使用&nbsp;权属于集体的，流转交易收益纳入集体财产统一管理；小型水利设施使用权属于企业的，流转交易收益归企业所有；小型水利设施使用权属于个人的，流转交易收益归个人所有。</p>\n<p>第十九条&nbsp;在小型水利设施使用&nbsp;权流转交易过程中&nbsp;，出现下列情形之&nbsp;一&nbsp;的，经流转交易机构确认后中止交易：</p>\n<p>（一）流转交易行政监督部门或产权登记部门提出中止流转交易的；</p>\n<p>（二）出让方或者与产权有直接利害关系&nbsp;的第三方提出正当理由，并经有关行政监督部门批准的；</p>\n<p>（三）产权存在权属争议的；</p>\n<p>（四）其他依法应当中止交易&nbsp;的情形。</p>\n<p>中止期限由各级流转交易机构根据实际情况设定，一般不超过30天。各级流转交易机构应当在中止期间对相关的申请事由或争议事项进行调查核实，也可转请相关部&nbsp;门进行调查核实，及时作出恢复或者终结交易&nbsp;的决定。</p>\n<p>第二十条&nbsp;在小型水利设施使用权流转交易活动&nbsp;中&nbsp;&nbsp;,&nbsp;禁止下列行为：</p>\n<p>（一）操纵交易市场或者扰乱交易秩序的；</p>\n<p>（二）影响交易双方进行公平交易的；</p>\n<p>（三）法律、法规、规章禁止的其他行为。</p>\n<p>第二十一条交易过程中发生产权流转交易纠纷的，当事人可以&nbsp;向流转交易机构或者流转交易监督管理部门申请调解，也可以依据合同约定申请仲裁或者依法向人民法院提起诉讼。</p>\n<p>第二十二条交易双方及服务机构有违法违规违约行为，给交易机构及相关交易主体造成损失的，依法承担法律责任。</p>\n<p style=\"text-align: center;\">第四章&nbsp;附则</p>\n<p>第二十三条本办法自公布之日起30日后生效，有效期2年。</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', '302', 11, NULL, '西安市水务局关于印发《小型水利设施使用权交易管理办法 （试行）》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (629, '西安市住房和城乡建设局关于印发《西安市安置房租赁交易管理办法（试行）》的通知', 999999, NULL, 'xafby', '183', '2024-03-26 16:31:19', '2024-08-28 16:57:18', '2024-03-26 16:31:19', '2', 'xafby', '西安市住房和城乡建设局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">西安市住房和城乡建设局关于印发《西安市安置房租赁交易管理办法（试行）》的通知</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">市建发〔2024〕25号</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">各区县、开发区住建局，市住房租赁行业协会，各住房租赁企业：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 现将《西安市安置房租赁交易管理办法（试行）》印发你们，请结合实际，认真遵照执行。</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">西安市住房和城乡建设局</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">2024年3月18日</span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">西安市安置房租赁交易管理办法（试行）</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">第一条&nbsp;为规范西安市安置房租赁交易行为，加强安置房租赁交易管理，根据《中华人民共和国民法典》《西安城乡融合要素交易市场组建方案》等法律法规和政策规定，制定本办法。</span></p>\n<p><span style=\"font-size: 18pt;\">第二条&nbsp;西安市行政区域内城中村改造和城乡建设用地增减挂钩试点项目建设的集中居住区的住房、商铺、设施用房，以及因建设项目拆迁，未办理国有产权登记手续但可提供其他权属依据的集中居住区的住房、商铺、设施用房等安置房的租赁交易适用本办法。法律、法规和规章另有规定的，从其规定。</span></p>\n<p><span style=\"font-size: 18pt;\">第三条&nbsp;安置房租赁交易应当遵循依法依规、平等自愿、诚</span></p>\n<p><span style=\"font-size: 18pt;\">实守信和公平公正的原则，不得损害交易各方的合法权益。</span></p>\n<p><span style=\"font-size: 18pt;\">第四条&nbsp;安置房租赁交易应具备以下基本条件：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）有合法出租权且权属清晰无争议；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）租赁双方具有安置房租赁交易的真实意愿；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）符合房屋质量安全相关要求；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）符合国家和省、市有关法律法规及相关政策规定。</span></p>\n<p><span style=\"font-size: 18pt;\">第五条&nbsp;集体经营部分的安置房租赁交易需通过西安城乡融合要素交易平台进行交易，鼓励引导个人的安置房租赁交易通过西安城乡融合要素交易平台进行交易。城乡融合要素交易机构为安置房租赁交易提供服务。</span></p>\n<p><span style=\"font-size: 18pt;\">第六条&nbsp;安置房租赁交易按照提出申请、挂牌发布、签订合同、完成交易的程序进行。城乡融合要素交易机构应当在办公场所公示具体交易流程。</span></p>\n<p><span style=\"font-size: 18pt;\">第七条&nbsp;提出申请。安置房出租人、承租人通过西安城乡融合要素交易平台向城乡融合要素交易机构提出申请的，需提交下列资料：</span></p>\n<p><span style=\"font-size: 18pt;\">（一）安置房权属佐证材料；</span></p>\n<p><span style=\"font-size: 18pt;\">（二）出租人、承租人的身份证件或其他合法有效证件；</span></p>\n<p><span style=\"font-size: 18pt;\">（三）出租共有产权的安置房，须提供其他共有人同意出租的证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（四）出租委托代管的安置房，须提供委托人授权出租的证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（五）转租安置房的，转租人应当提供出租人同意转租的证明；</span></p>\n<p><span style=\"font-size: 18pt;\">（六）其他需要提交的资料。</span></p>\n<p><span style=\"font-size: 18pt;\">安置房出租人、承租人应对其提交资料的真实性负责。</span></p>\n<p><span style=\"font-size: 18pt;\">第八条&nbsp;挂牌发布。通过西安城乡融合要素交易平台进行安置房租赁交易的，城乡融合要素交易机构应及时发布租赁信息。</span></p>\n<p><span style=\"font-size: 18pt;\">第九条&nbsp;签订合同。确定承租人后，城乡融合要素交易机构组织安置房出租人、承租人签订租赁合同。</span></p>\n<p><span style=\"font-size: 18pt;\">第十条&nbsp;完成交易。城乡融合要素交易机构组织安置房出租人、承租人应按照合同约定的租金、押金支付方式进行结算。</span></p>\n<p><span style=\"font-size: 18pt;\">第十一条&nbsp;安置房租赁交易完成后，城乡融合要素交易机构应当将交易过程中形成的所有具有保存价值的文字、图表、声像、电子数据等资料，按照档案管理要求进行收集、整理和归档，并集中统一保管备查。</span></p>\n<p><span style=\"font-size: 18pt;\">第十二条&nbsp;在西安城乡融合要素交易平台进行安置房租赁交易过程中，发生安置房租赁交易纠纷的，当事人可以向城乡融合要素交易机构申请调解，也可以依据合同约定申请仲裁或者依法向人民法院提起诉讼。</span></p>\n<p><span style=\"font-size: 18pt;\">第十三条&nbsp;本办法自公布之日起30日后生效，有效期两年。</span></p>\n<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:14701/newsManage/newsManage/publishttp:/www.xianchengxiangietp.com.cn:8001/znxd/2024/03/26/a8f7333407114be6a2615f2a56c6b1a9.png\" /><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:14701/newsManage/newsManage/publishttp:/www.xianchengxiangietp.com.cn:8001/znxd/2024/03/26/07464d2c887d41ec8f86d87730c23a04.png\" /><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:14701/newsManage/newsManage/publishttp:/www.xianchengxiangietp.com.cn:8001/znxd/2024/03/26/5df26d8c8f064cedb23446f980356395.png\" /><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:14701/newsManage/newsManage/publishttp:/www.xianchengxiangietp.com.cn:8001/znxd/2024/03/26/a44581c5df3c4160aa7b6fe73aa9a114.png\" /></span></p>', '302', 18, NULL, '西安市住房和城乡建设局关于印发《西安市安置房租赁交易管理办法（试行）》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (630, '西安市自然资源和规划局关于印发《西安市城乡建设用地增减挂钩节余指标流转流程》的补充通知', 999999, NULL, 'xafby', '183', '2024-03-26 17:25:30', '2024-08-28 16:57:18', '2024-03-26 17:25:30', '2', 'xafby', '西安市自然资源和规划局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><strong><span style=\"font-size: 12pt;\">西安市自然资源和规划局关于印发《西安市城乡建设用地增减挂钩节余指标流转流程》的补充通知</span></strong></p>\n<p>市资源规划局各分局、各县资源规划局：</p>\n<p>&nbsp; &nbsp; &nbsp; 为切实做好西安市城乡融合要素交易市场工作，促进全市城乡建设用地增减挂钩节余指标流转工作有序进行，经研究，对原《西安市城乡建设用地增减挂钩节余指标流转流程》 进行了补充</p>\n<p>和完善，现印发你们，请遵照执行。</p>\n<p style=\"text-align: right;\">西安市 自然资源和规划局</p>\n<p style=\"text-align: right;\"><span style=\"font-size: 12pt;\">2024 年 2月 27 日</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>西安市城乡建设用地增减挂钩节余指标流转流程</strong></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p>为贯彻落实十四届市委全面深化改革委员会 第六次会议和市政府11月17日专题会议精神要求，切实做好城乡融合要素交易市场工作，促进城乡建设用地增减挂钩节余指标流转工作公开、公平、公正有序进行，特制定如下增减挂钩节余指标流转交易流程。</p>\n<p>一、流转依据</p>\n<p>《城乡建设用地增减挂钩试点管理办法》(国土资发〔 2008〕138 号)、《巩固拓展脱贫攻坚成果同乡村振兴有效衔接过渡期内城乡建设用地增减挂钩节余指标跨省域调济管理办法》 ( 自然资发〔2021〕178号)、《关于加快推进城乡建设用地增减挂钩助推脱贫攻坚有关工 作的通知》(陕国土资发〔 2018〕 116号)、《关于进一步加强城乡建设用地增减挂钩管理工作的通知》(陕自然资发〔2022〕35号)、《陕西省城乡建设用地增减挂钩节余指标省域内流转规划》(陕自然资办发〔 2022〕79 号)等。</p>\n<p>二、指标来源</p>\n<p>按照《 城乡建设用地增减挂钩项目 区申报流程》 (陕自然资用途管制发〔2022〕23号)和《关于做好城乡建设用地增减挂钩验收成果日常变更及相关工作的通知》(陕自然资用途管制发〔2023〕2号) 等相关规定，增减挂钩项目区实施方案由县级人民政府负责组织编制， 并报市级人民政府审核，经市级人民政府审核同意后报省级批准。 项目区在完成增减挂钩拆旧复垦任务后，经县、市、省级验收且验收合格，在省自然资源厅增减挂钩监管系统备案， 由省自然资源厅下达增减挂钩项目区指标核定的批复。</p>\n<p>三、流转方式</p>\n<p>全市指标产生方县级自然资源主管部门依据省级验收核定的节余指标逐级向省自然资源厅申请流转指标规模。 省自然资源厅定期在&ldquo;陕西省自然资源厅网站&rdquo;上发布增减挂钩节余指标流转信息， 指标使用方根据网上指标流转信息自主选择流转对象，并在西安城乡融合要素交易市场进行公开交易。</p>\n<p>四、流转协议</p>\n<p>协议甲方为指标产生方县级人民政府，乙方为指标使用方县级人民政府。各高新区、开发区等签订增减挂钩节余指标流转协议时履行乙方职责， 并受所在地县级人民政府委托，无书面委托书不允许签订协议。指标产生方、指标使用方县级人民政府自主协商节余指标流转事宜，并签订增减挂钩节余指标流转协议。双方协议签订后 10 个工作日 内报省厅备案，逾期不备案的，省厅不予审查指标使用方编制的增减挂钩建新区实施方案。</p>\n<p>五、流转价格</p>\n<p>增减挂钩节余指标省域内流转价格继续按照《关于加快推进城乡建设用地增减挂钩助推脱贫攻坚有关工作的通知》(陕国土资发〔2018〕116号) 执行，省域内流转基准价格为28万元/亩，市域内流转价格在基准价格基础上可下浮10%。</p>\n<p>六、 指标使用</p>\n<p>甲、乙方县人民政府签订指标流转协议后，协议双方按照约定履行相关职责。按照《城乡建设用地增减挂钩建新区实施方案申报审查流程》(陕自 然资办发〔2023〕19 号) 等相关规定，增减挂钩建新区实施方案由指标使用方县级人民政府负责编制，并报市级人民政府审核，经市级人民政府审核同意后报省级批准。</p>\n<p>七、协议履行</p>\n<p>增减挂钩节余指标流转协议签订后，指标产生方、 指标使用方县级人民政府应严格履行协议，按照协议约定时间兑付节余指标流转价款，并承担相关法律责任。协议双方不严格履行协议并造成负面影响的， 市局将对其失信行为予以公开通报。省级有规定的从其规定。</p>\n<p>附件：西安市自然资源和规划局城乡建设用地增减挂钩节余指标流转流程图</p>', '302', 23, NULL, '西安市自然资源和规划局关于印发《西安市城乡建设用地增减挂钩节余指标流转流程》的补充通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (632, '城乡融合要素交易市场（西安灞桥）有限公司揭牌！', 999999, '1776908227400249346', 'xafby', '183', '2024-04-07 16:49:19', '2024-08-28 16:57:18', '2024-04-07 16:49:19', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>3月26日，城乡融合要素交易市场（西安灞桥）有限公司揭牌仪式在灞桥区京都国际举行。西安市灞桥区政府副区长刘强，西安农投集团党委副书记、副总经理李英杰，西安市灞桥区农业农村局副局长奚波，秦农银行灞桥支行行长王利安，西安城乡融合要素交易市场有限公司相关负责人为公司揭牌。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/57d59d11f016420794a5cfd8c25c3517.png\" /></p>\n<p>城乡融合要素交易市场（西安灞桥）有限公司由西安城乡融合要素交易市场有限公司与西安白鹿原现代农旅投资集团有限公司共同出资组建，于2024年2月4日完成工商注册工作，交易大厅内设交易申请、交易审核、交易办理、交易鉴证、产权评估、抵押登记等6个业务窗口，与区农业农村局下属区农村产权流转交易中心合署办公。</p>\n<p>活动现场，工作人员对城乡融合要素交易平台的门户、管理系统进行了介绍，从发布交易项目、线上报名、缴纳保证金、交易竞价、交易成交等方面对交易流程进行详细讲解，同时介绍了手机APP客户端的使用。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/4b2e110d51c54271b9c4a455a13930b2.png\" /></p>\n<p>城乡融合要素交易市场（西安灞桥）有限公司的成立，吹响了灞桥区城乡融合要素交易运营的号角，灞桥公司将按照区委、区政府的统一部署、统一规划，全面建立健全街道级中心站和村级信息站。按照&ldquo;应进必进、阳光交易&rdquo;的工作原则，确保各类要素在城乡之间平等交换、双向流动，推动公平交易及市场规范化建设，助力村集体经济组织和农民致富增收，加快推进灞桥区乡村振兴和城乡融合发展。</p>\n<p>下一步，城乡融合要素交易市场（西安灞桥）有限公司将秉承&ldquo;以人为本、善用资源、创造价值、服务三农&rdquo;的经营理念，紧抓集体经营性建设用地入市等改革机遇，充分利用集体产权制度改革的成果，以城乡融合发展为核心，以壮大村集体经济和农民增收致富为目标，发挥市场优化资源配置的作用，最大限度挖掘释放农村资源价值潜力，不断提升农村资源配置的信息化、标准化、规范化水平，助力都市型农业强市建设。</p>\n<p>西安市灞桥区农村产权流转交易服务中心主任杨向东，西安白鹿原现代农旅投资集团有限公司总经理赵楠，秦农银行乡村振兴金融部副总经理赵东，要素交易市场分、子公司负责同志，媒体代表等共计30余人参加活动。</p>', '101', 56, NULL, '城乡融合要素交易市场（西安灞桥）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (633, '城乡融合要素交易市场（西安鄠邑）有限公司揭牌', 999999, '1778620325671022593', 'xafby', '183', '2024-04-12 11:03:40', '2024-08-28 16:57:18', '2024-04-12 11:03:40', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>3月27日，城乡融合要素交易市场（西安鄠邑）有限公司揭牌仪式在西安市鄠邑区人民路秦农银行大厦一楼举行。西安市鄠邑区区委副书记任涛，西安农投集团党委委员、副总经理、西安城乡融合交易市场有限公司执行董事沈建锋为公司揭牌。</p>\n<section><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/535ccc1bc2024b3fad2d52f71cf03f23.png\" /></section>\n<p>活动现场，工作人员对城乡融合要素交易平台的门户、管理系统进行了介绍，从发布交易项目、线上报名、缴纳保证金、交易竞价、交易成交等方面对交易流程进行详细讲解，同时介绍了手机APP客户端的使用。</p>\n<p>城乡融合要素交易市场（西安鄠邑）有限公司在区级、各街道（景区管理局）农村产权流转交易服务站的管理职责基础上，融入资产监管运营服务职责，确保城乡融合要素流转交易实现&ldquo;一站式&rdquo;服务。采取&ldquo;A+B&rdquo;模式，即&ldquo;人民路秦农银行综合交易中心+政务服务中心交易场所&rdquo;，并设&ldquo;城乡融合要素交易市场（西安鄠邑）有限公司&rdquo;统一标识。政务服务中心交易场所初步选定了1个不少于50平方米的办公场所及2个要素交易接件窗口，通过建设街道交易服务站的方式以及&ldquo;A+B的模式，鄠邑区将快速在全区形成以点盖面、点点发力的架构体系，大力推进城乡要素交易工作开展。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/26ba6038b32f452ebb1744d49e258240.png\" /></p>\n<p>据悉，鄠邑区将由区纪委监委牵头制定公职人员履行推进城乡融合要素交易市场体系建设和管理责任的执行监督机制，切实加强对农村集体&ldquo;三资&rdquo;监管，确保农村集体&ldquo;三资&rdquo;应进必进，阳光交易。同时，对在推进城乡融合要素交易市场建设和运行中工作不力、阻碍城乡融合要素&ldquo;应进必进、阳光交易&rdquo;的，区纪委监委将依法依纪严肃问责，确保城乡融合要素交易市场在阳光下运行。</p>\n<p>下一步，鄠邑区城乡融合交易市场将在鄠邑区委、区政府正确领导下、在市城乡融合要素公司的业务指导下以及在全区各街道办的大力支持下，积极推动公平交易及市场规范化建设，助力鄠邑区村集体经济组织和农民致富增收。</p>\n<p>鄠邑区发改委、区财政局、区农业农村局、区资源规划分局、区秦岭保护局（区林业局）、区水务局、区住建局、区市场监管局、鄠邑农业产业发展集团分管负责同志，各街办、景区管理局分管负责同志，秦农银行总行、鄠邑支行，要素交易市场分、子公司负责同志，媒体代表等共计40余人参加活动。</p>', '101', 43, NULL, '城乡融合要素交易市场（西安鄠邑）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (634, '城乡融合要素交易市场（西安蓝田）有限公司揭牌', 999999, '1778621311353434114', 'xafby', '183', '2024-04-12 11:07:34', '2024-08-28 16:57:18', '2024-04-12 11:07:34', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>3月29日，城乡融合要素交易市场（西安蓝田）有限公司揭牌仪式在蓝田县工业园区文姬路中国邮政三楼举行。西安市蓝田县政府副县长李永波，西安农投集团党委委员、副总经理、西安城乡融合要素交易市场有限公司执行董事沈建锋，蓝田县农业农村局党委书记、局长田相权，蓝田县农商银行执行董事苗丁，西安城乡融合要素交易市场有限公司相关负责人为公司揭牌。<ins cite=\"mailto:%E8%8B%A5%E5%BD%A4\" datetime=\"2024-03-29T11:52\"></ins></p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/e005329ae02a4b1e9017dbe0708e19ba.png\" /></p>\n<p>城乡融合要素交易市场（西安蓝田）有限公司由西安城乡融合要素交易市场有限公司与蓝田县滋水农林投资有限公司合资组建，于2024年2月19日完成工商注册，今日正式投运并将同步启动蓝田县小寨镇董岭村闲置农村住房项目挂牌流转交易。交易大厅内设交易咨询、交易申请、交易审核、交易签约、金融服务、交易签证等6个业务窗口，主要业务包括农村资源资产产权交易、农村产业项目招商、农产品流通和农村工程、货物及服务采购等4大类21项品种，采用&ldquo;六统一&rdquo;的管理方式，开展城乡融合要素流转交易一站式服务。</p>\n<section>活动现场，蓝田县农业农村局党委书记、局长田相权围绕加强城乡融合要素交易市场监管工作发表讲话。他强调，农村农业局作为城乡融合要素交易市场的主要监管部门，要从加强部门间沟通协作、提升规范经营水平和加强自身建设三个方面加强城乡融合要素交易市场监管工作，保障交易市场公平公正。</section>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/9dcb82fe6816415690b2d416923d4b30.png\" /></p>\n<section>未来发展中，蓝田县城乡融合要素交易市场将在县委、县政府的坚强领导下，在县级部门和镇街的支持配合下，在西安城乡融合要素交易市场的正确指导下，全面健全完善蓝田县镇街中心站和村级信息站，秉承&ldquo;以人为本、善用资源、创造价值、服务三农&rdquo;的经营理念，按照&ldquo;应进必进、阳光交易&rdquo;的工作原则，集中促进人才、土地、资金、技术等要素在城乡之间平等交换、双向流动，为助力蓝田乡村振兴作出应有的贡献。</section>\n<section>蓝田县农业农村局、县资源规划局、县住建局、县水务局、县秦保局、县市场监管局分管同志，蓝田县投资控股集团有限公司、秦农银行总行相关负责人，各镇街分管领导及部分村主要负责人，要素交易市场及子公司有关人员共计40余人参加活动。</section>', '101', 67, NULL, '城乡融合要素交易市场（西安蓝田）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (635, '西安城乡融合要素交易市场首批公开挂牌项目入市交易成功', 999999, NULL, 'xafby', '183', '2024-04-12 11:12:48', '2024-08-28 16:57:18', '2024-04-12 11:12:48', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<section>\n<section data-style-id=\"35191\" data-type=\"undefined\" data-id=\"35191\" data-vip=\"3\" data-free=\"0\">\n<section>\n<section>\n<section>\n<section>\n<section>\n<p>3月22日，长安区王曲街道江兆村、曙光村8宗闲置农村住房和宅基地使用权流转项目在西安城乡融合要素交易市场平台竞价成交。这是西安城乡融合要素交易市场首批成功交易项目，标志着我市在激活农村闲置资源、推动城乡资源流动、优化资源配置方面迈出了重要一步。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/0d83e1a568c74d80b579c15367d07f8b.webp\" /></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-style-id=\"35191\" data-type=\"undefined\" data-id=\"35191\" data-vip=\"3\" data-free=\"0\">\n<section>\n<section>\n<section>\n<section>\n<section>\n<p>&nbsp;此次交易涉及王曲街道江兆村及曙光村八户闲置农村住房和宅基地，房屋面积共计约1300平方米，交易总额为206万元。后续，将按照交易流程组织交易双方进行成交签约，完成交易结算后，为交易双方发放西安城乡融合要素交易鉴证书。整个交易过程公开透明、程序规范、结果可溯。</p>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-style-id=\"35191\" data-type=\"undefined\" data-id=\"35191\" data-vip=\"3\" data-free=\"0\">\n<section>\n<section>\n<section>\n<section>\n<section>\n<p>&nbsp;此次闲置农村住房的成功交易，盘活利用了农村资源，也让村民们认识到城乡融合要素交易平台实实在在为村民服务，为乡村发展带来了更多机遇。&ldquo;公开、公平、公正&rdquo;的城乡要素自由流动市场机制，让农村&ldquo;三资&rdquo;的价值由市场说了算，擦亮了农村资源资产&ldquo;阳光交易&rdquo;的名片，让群众对西安城乡融合要素交易市场有了更深刻的认识。</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/2a0bd37f12164dcb98bb8d648884591e.png\" /></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section data-style-id=\"35191\" data-type=\"undefined\" data-id=\"35191\" data-vip=\"3\" data-free=\"0\">\n<section>\n<section>\n<section>\n<section>\n<section>\n<p>&nbsp;下一步，西安城乡融合要素交易市场将进一步总结经验，不断提升农村资源配置的信息化、标准化、规范化水平，充分发挥市场优化资源配置的作用，促进各类要素在城乡之间平等交换、双向流动渠道，逐步实现壮大村集体经济和农民致富增收的目标。</p>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>', '101', 20, NULL, '西安城乡融合要素交易市场首批公开挂牌项目入市交易成功', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (636, '长安区城乡融合要素交易市场首笔挂牌成交项目签约仪式成功举办', 999999, '1778734937263386625', 'xafby', '183', '2024-04-12 18:39:10', '2024-08-28 16:57:18', '2024-04-12 18:39:10', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<section>3月28日，长安区城乡融合要素交易市场首笔挂牌成交项目签约仪式在长安区城乡融合要素交易市场成功举办。长安区副区长吴海军，市农投集团副总经理沈建锋，西安城乡融合要素交易市场有限公司总经理贠涛、副总经理龚文军，王曲街道党工委书记王朝朋，长安区城乡融合要素交易市场总经理曹宇等相关领导和负责人共同见证。</section>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/3ea649e9f92d4292ab9073bec736aa72.jpg\" /></p>\n<section>签约仪式上，国梦御秀公司和曙光村经济合作社、江兆村经济合作社分别签订了成交协议，交易涉及王曲街道江兆村及曙光村八户闲置农村住房和宅基地，房屋面积共计约1300平方米，成交总额达206万元。</section>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/12/a827da9e6864421bb7ae647ba65e9ddf.jpg\" /></p>\n<section></section>\n<p>国梦御秀公司总经理李纬卜表示,双方将在农村产权流转、土地资源整合等领域展开深度合作，共同助力长安区城乡融合要素市场的蓬勃发展。</p>\n<p>此次签约不仅是长安区城乡融合要素交易市场的重要里程碑，更是对市场运营模式和服务能力的有力验证，也为其他地区的城乡融合发展提供了宝贵经验。</p>\n<p>下一步，长安区城乡融合要素交易市场将持续优化交易流程，提升服务品质，为更多城乡融合项目提供卓越的交易平台，为推动区域经济发展和乡村振兴贡献更大力量。</p>', '101', 56, NULL, '长安区城乡融合要素交易市场首笔挂牌成交项目签约仪式成功举办', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (637, '七部门联合印发《西安城乡融合要素流转交易管理办法（试行）》', 999999, '1780157059579850754', 'xafby', '183', '2024-04-16 16:50:45', '2024-08-28 16:57:18', '2024-04-16 16:50:45', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '300,302', '0', 1, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>关于印发《西安城乡融合要素流转交易管理办法（试行）》的通知</strong></span></p>\n<p>各区县（开发区）农业农村部门、发改委、财政局、资源规划分局、住建局、水务局、市场监管局：为规范城乡融合要素流转交易行为，推动城乡融合要素平等交换，保障农村集体经济组织和农民群众的合法权益，结合本市实际，市农业农村局、市发改委、市财政局、市资源规划局、市住建局、市水务局、市市场监管局联合制定了《西安城乡融合要素流转交易管理办法（试行）》，现印发你们，请遵照执行。</p>\n<p style=\"text-align: center;\"><strong>西安城乡融合要素流转交易管理办法（试行）</strong></p>\n<p style=\"text-align: center;\">第一章 总则</p>\n<p>第一条 为规范城乡融合要素流转交易行为，推动城乡融合要素平等交换，保障农村集体经济组织和农民群众的合法权益，根据《国务院办公厅关于引导农村产权流转交易市场健康发展的意见》《陕西省构建更加完善的要素市场化配置体制机制的实施方案》《西安城乡融合要素交易市场组建方案》以及相关法律法规和政策规定，结合我市实际，特制订本办法。</p>\n<p>第二条 凡在本市从事城乡融合要素流转交易活动的，适用本办法。法律法规、规章另有规定的，从其规定。</p>\n<p>第三条 城乡融合要素流转交易应当遵循民主决策、平等自愿、诚实守信和公开、公平、公正的原则，不得侵犯国家、集体和个人的合法权益。</p>\n<p>第四条 城乡融合要素流转交易应具备以下条件：</p>\n<p>（一）权属清晰无争议；</p>\n<p>（二）参与主体具有流转交易城乡融合要素的真实意愿；</p>\n<p>（三）交易双方具有相应的民事行为能力；</p>\n<p>（四）流转交易不违反法律、行政法规的强制性规定，不违背公序良俗，符合环境保护、农业产业发展规划、国土空间规划等政策规定。</p>\n<p>第五条 涉及城乡融合要素流转交易的行政监督管理部门应当按照各自职责，鼓励引导各类城乡融合要素在城乡融合要素交易机构进行流转交易。</p>\n<p style=\"text-align: center;\">第二章 交易主体及范围</p>\n<p>第六条 凡是法律法规和政策没有限制的法人和自然人均可以进入市场参与流转交易，具体准入条件按照相关法律法规和政策执行。</p>\n<p>第七条 城乡融合要素流转交易主要包括农村资源资产产权交易类、农业农村项目投资招商类、农产品流通类和农村工程、货物及服务采购类等，凡法律法规没有限制的城乡各类资源资产均可以进入市场流转交易。</p>\n<p>第八条 农村集体资金、资产、资源（简称&ldquo;三资&rdquo;）流转应当按照&ldquo;应进必进、阳光交易&rdquo;的要求进入市场公开流转交易，杜绝私下交易，暗箱操作。</p>\n<p style=\"text-align: center;\">第三章&nbsp;交易方式和程序</p>\n<p>第九条 城乡融合要素流转交易方式包括公开协议、网络竞价、综合评审、拍卖、招标以及其他竞价方式，且不得违反法律法规的规定。采用招标方式进行流转交易的,应当按照《中华人民共和国招投标法》及有关规定组织实施。采取拍卖方式进行流转交易的，应当按照《中华人民共和国拍卖法》及有关规定组织实施。</p>\n<p>第十条 需要进行流转交易的项目，按照提出申请、审核发布、组织交易、结果确认、签订合同、交易结算、交易鉴证、归档备查的程序进行。</p>\n<p>第十一条 出让方提出申请。出让方申请流转交易时，应当提交下列材料：</p>\n<p>（一）城乡融合要素出让申请书（含权属共有人签字、出让标的基本情况、出让价格、受让方条件等内容）；</p>\n<p>（二）出让方主体资格证明材料；</p>\n<p>（三）委托办理交易手续的，需提交授权委托书及受托方主</p>\n<p>体资格证明、法定代表人或负责人身份证明；</p>\n<p>（四）出让方的决议及批准文件；</p>\n<p>（五）出让标的合法有效权属佐证资料；</p>\n<p>（六）依法需进行评估的，应提供资产评估报告；</p>\n<p>（七）城乡融合要素交易机构和行政监督管理部门要求提交的其他材料。出让方应当对所提交材料的真实性、完整性、有效性、合法性负责，并承担相应的法律责任。</p>\n<p>第十二条 受让方申请交易。意向受让方申请受让城乡融合要素的，应当提交下列材料并接受资格审查：</p>\n<p>（一）城乡融合要素意向受让申请书；</p>\n<p>（二）受让主体资格证明材料；</p>\n<p>（三）同意受让的内部决议或批复文件；</p>\n<p>（四）委托代理的，需提交授权委托书及受托人身份证明；</p>\n<p>（五）联合受让的，需提交联合受让协议书、代表推举书或</p>\n<p>相关证明材料；</p>\n<p>（六）其他需要提交的材料。</p>\n<p>意向受让方应对其提交资料的真实性、完整性、有效性负责。</p>\n<p>出让方在流转交易信息中要求受让方缴纳交易保证金的，意向受让方在规定时间内交纳交易保证金（以到账时间为准）后获得受让资格；逾期未交纳交易保证金的，视为放弃受让资格。</p>\n<p>第十三条 审核发布。城乡融合要素交易机构对申请资料进行齐全性和规范性审核，必要时可提请相关职能部门协助查档确认权属。审核通过的，受理申请并按相关规定发布交易信息公告。</p>\n<p>交易信息应包括以下主要内容：</p>\n<p>（一）信息公告包括但不限于以下内容：出让标的基本情况；产权出让行为的决策及批准情况；受让方资格条件（适用于对受让方有特殊要求情形）；交易条件、出让价格、交易方式；法律法规规定的其他需要披露的事项。</p>\n<p>（二）首次信息公告期限应当不少于 5 个工作日，可在城乡融合要素交易机构网站、街道（镇）公告栏、村务公开栏等相关场所和媒介同步发布信息，公告内容应保持一致，以城乡融合要素交易机构网站首次信息公告日为起始日。</p>\n<p>（三）出让方不得在信息发布期间擅自变更或取消所发布的信息。如特殊原因确需变更或取消的，出让方必须在公告时间截止前 3 个工作日将变更或取消内容报城乡融合要素交易机构，经审核后在原发布网站进行公告并通知已报名人，并重新计算公告期。</p>\n<p>第十四条 组织交易。城乡融合要素交易机构应按照公告载明的交易方式，组织流转交易双方进行交易。</p>\n<p>第十五条 结果确认。受让方确定后，城乡融合要素交易机构应当将交易结果在项目信息发布的同一媒介、场所进行公示，公示内容包括标的名称、出让方、成交价格、成交时间、受让方，公示期不少于 3 个工作日。</p>\n<p>第十六条 签订合同。公示结束且无异议后，城乡融合要素交易机构组织交易双方签订交易合同，交易合同生效。</p>\n<p>第十七条 交易结算。交易合同生效，受让方按照合同约定和有关交易规则进行交易结算。</p>\n<p>第十八条 交易鉴证。交易双方完成交易结算后，城乡融合要素交易机构出具《交易鉴证书》。《交易鉴证书》应当载明如下事项：项目编号、签约日期、出让方全称、受让方全称、交易机构全称、标的全称、交易方式、成交价款、支付方式等内容。《交易鉴证书》使用统一格式打印，手写、涂改无效。</p>\n<p>第十九条 归档备查。城乡融合要素交易机构应将交易过程中形成的文字、图表、声像、电子数据等原始记录，按照有关档案法规、标准进行收集、整理、归档，并集中统一保管备查。</p>\n<p>第二十条 城乡融合要素流转交易过程中，有下列情形之一的，经城乡融合要素交易机构确认后中止流转交易：</p>\n<p>（一）城乡融合要素流转交易行政监督管理部门或产权登记部门提出中止流转交易的；</p>\n<p>（二）出让方或者与产权有直接利害关系的第三方提出正当理由，并经有关行政监督管理部门批准的；</p>\n<p>（三）产权存在权属争议的；</p>\n<p>（四）其他依法应当中止流转交易的情形。中止期限由城乡融合要素交易机构根据实际情况设定，一般不超过 30 天。城乡融合要素交易机构应当在中止期间对相关的申请事由或争议事项进行调查核实，也可转请相关部门进行调查核实，及时作出恢复或者终结流转交易的决定。</p>\n<p>第二十一条 城乡融合要素流转交易过程中，有下列情形之一的，经城乡融合要素交易机构确认后终止流转交易：</p>\n<p>（一）中止期限届满后，仍未能消除影响交易中止的因素导致流转交易无法继续进行的；</p>\n<p>（二）有关行政监督部门依法提出终止流转交易的；</p>\n<p>（三）出让方或者与产权有直接利害关系的第三方向有关行政监督管理部门书面提出终止流转交易申请，并经批准的；</p>\n<p>（四）人民法院、仲裁机构依法发出终止流转交易书面通知的；</p>\n<p>（五）其他依法应当终止流转交易的情形。</p>\n<p>第二十二条 流转交易中出现中止、终止、恢复情形的，应当在城乡融合要素交易机构网站上公告。</p>\n<p style=\"text-align: center;\">第四章 争议处理</p>\n<p>第二十三条 在城乡融合要素交易机构进行流转交易过程中，发生流转交易纠纷的，当事人可以向城乡融合要素交易机构或者城乡融合要素交易监督管理部门申请调解，也可以依据合同约定申请仲裁或者依法向人民法院提起诉讼。</p>\n<p>第二十四条 流转交易双方及中介服务机构有违法违规违约行为，给城乡融合要素交易机构及相关交易主体造成损失的，依法承担法律责任。</p>\n<p style=\"text-align: center;\">第五章 附则</p>\n<p>第二十五条 本办法自公布之日起30日后生效，有效期2年。</p>\n<p>附件：西安城乡融合要素流转交易目录</p>\n<p><img class=\"wscnph\" src=\"http://www.xianchengxiangietp.com.cn:8001/znxd/2024/04/16/e61492f3cad44ac897b591dc631170b0.png\" /></p>', '302', 66, NULL, '市农业农村局、市发改委、市财政局、市资源规划局、市住建局、市水务局、市市场监管局联合制定《西安城乡融合要素流转交易管理办法（试行）》', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (638, '西安市林业局关于印发《西安市集体林权流转管理办法(试行)》的通知', 999999, NULL, 'xafby', '183', '2024-04-17 17:27:40', '2024-08-28 16:57:18', '2024-04-17 17:27:40', '2', 'xafby', '西安市林业局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>西安市林业局关于印发《西安市集体林权流转管理办法(试行)》的通知</strong></span></p>\n<p style=\"text-align: left;\">各区(县)林业主管部门:<br />按照《中共西安市委办公厅、西安市人民政府办公厅关于印发&lt;西安城乡融合要素交易市场组建方案&gt;的通知》(市办字〔2023]192 号)精神，为规范集体林权流转，我局制定了《西安市集体林权流转管理办法(试行)》，现印发给你们，请遵照执行。</p>\n<p style=\"text-align: right;\">西安市林业局</p>\n<p style=\"text-align: right;\">2024年4月I6日</p>\n<p style=\"text-align: center;\"><strong>西安市集体林权流转管理办法</strong><br /><strong>(试行)</strong></p>\n<p style=\"text-align: center;\">第一章 总则</p>\n<p style=\"text-align: left;\">第一条 为规范集体林权流转行为，健全集体林地所有权、承包权、经营权分置运行机制，保障流转当事人的合法权益，促进森林资源合理利用和林业产业高质量发展，根据《中华人民共和国民法典》《中华人民共和国森林法》《中华人民共和国农村土地承包法》《不动产登记暂行条例》《深化集体林权制度改革方案》(中办发〔2023]45号)《西安城乡融合要素交易市场组建方案》(市办字【2023]192号)等法律法规及政策，结合实际，制定本办法。</p>\n<p style=\"text-align: left;\">第二条 凡在本市从事集体林权流转交易活动的，适用本办法。</p>\n<p style=\"text-align: left;\">第三条 集体林权流转遵循下列原则:<br />(一)依法、自愿、有偿，流转的意愿、价格、期限、方式、对象等应由林权权利人依法自主决定，任何组织和个人不得强迫或者阻碍集体林权流转。</p>\n<p style=\"text-align: left;\">(二)不得改变林地用途，不得擅自改变公益林性质;流转后的林地要严格依法依规合理开发利用。</p>\n<p style=\"text-align: left;\">(三)流转期限不得超过剩余承包期限。</p>\n<p style=\"text-align: left;\">(四)不损害国家、集体、个人的合法权益和社会公共利益不破坏森林资源和生态环境。</p>\n<p style=\"text-align: left;\">(五)流入方须有林业经营能力或者资质。</p>\n<p style=\"text-align: left;\">第四条 同等条件下，本集体经济组织成员享有集体林权流转的优先受让权。鼓励农民通过依法流转组建专业合作社、合作林场、股份制林场、家庭林场等新型林业经营主体。第五条集体林权流转交易活动必须依据西安城乡融合要素流转交易管理办法有关规定实施。</p>\n<p style=\"text-align: center;\"><br />第二章 流转方式与期限</p>\n<p style=\"text-align: left;\"><br />第七条 林权流转方式包括:转让、互换、转包、出租、入股、抵押等方式。</p>\n<p style=\"text-align: left;\">第八条 依法采取转让方式流转林地承包经营权的，应当经原发包的集体经济组织同意;采取转包、出租、互换、入股、抵押或者其他方式流转的，应当报原发包的集体经济组织备案。</p>\n<p style=\"text-align: left;\">第九条 公益林在不改变性质的前提下，可以采取出租、转包、入股方式进行流转，发展林下种养业或森林旅游业。</p>\n<p style=\"text-align: left;\">第十条 集体林地经营权流转应当签订书面合同。林地经营权流转合同一般包括流转双方的权利义务、流转期限、流转价款及支付方式、流转期限届满林地上的林木和固定生产设施的处置、违约责任等内容。鼓励积极推广和引导当事人使用国家统一规范的《集体林权流转合同》(编号:GF-2020-2603)。<br />受让方违反法律规定或者合同约定造成森林、林木、林地严重毁坏的，发包方或者承包方有权收回林地经营权。</p>\n<p style=\"text-align: center;\">第三章 流转程序</p>\n<p style=\"text-align: left;\">第十一条 集体统一经营管理的林地经营权和林木所有权流转的，流转方案应在本集体经济组织内提前公示。经本集体经济组织三分之二以上成员(成员代表)讨论通过，形成决议并报乡镇政府批准;农民个人产权须是本人自愿并征得权属共有人同意后流转交易。</p>\n<p style=\"text-align: left;\">第十二条 发包方将农村土地发包给本集体经济组织以外的单位或者个人承包，应当事先经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并报乡<br />(镇)人民政府批准。由本集体经济组织以外的单位或者个人承包的，应当对承包方的资信情况和经营能力进行审查后，再签订承包合同。第十三条 流转期限为五年以上的，当事人可以向登记机构申请权属登记。林权变更、转移登记程序按照国家相关规定执行。未经登记不得对抗善意第三人。</p>\n<p style=\"text-align: center;\">第四章 林权流转服务</p>\n<p style=\"text-align: left;\"><br />第十四条 市、区(县)级不动产登记机构要按照有关要求全面履行林权类不动产登记职责。</p>\n<p style=\"text-align: left;\">第十五条 农村集体经济组织获得的交易收益，纳入农村集体财产统一管理，用于本集体经济组织成员分配和社会保障、新农村建设等公益事业。农民个人产权的交易收益，归个人所有。</p>\n<p style=\"text-align: left;\">第十六条在农村产权流转交易机构进行流转交易过程中发生产权流转交易纠纷的，当事人可以向农村产权流转交易机构或者当地农村产权流转交易监督管理部门申请调解，也可以依据合同约定申请仲裁或者依法向人民法院提起诉讼。</p>\n<p style=\"text-align: left;\">第十七条 流转交易双方及中介服务机构有违法违规违约行为，给农村产权流转交易机构及相关交易主体造成损失的，依法承担法律责任。</p>\n<p style=\"text-align: center;\">第五章附则</p>\n<p style=\"text-align: left;\">第十八条 本办法实施前已经依法发生的森林、林木、林地流转并办理了林权变更、转移登记手续的，其流转行为继续有效。第十九条本办法如与国家、省新制定的有关集体林权流转管理政策规定不相符的，应从其规定。</p>\n<p style=\"text-align: left;\">第二十条 本办法自印发之日起30日后生效。</p>', '302', 3, NULL, '西安市林业局关于印发《西安市集体林权流转管理办法(试行)》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (646, '关于印发《西安市农村集体经营性建设用地人市试点工作操作流程(试行)》的通知', 999999, NULL, 'xafby', '183', '2024-05-08 13:59:40', '2024-08-28 16:57:18', '2024-05-08 13:59:40', '2', 'xafby', '西安市自然资源和规划局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\">关于印发《西安市农村集体经营性建设用地入市试点工作操作流程(试行)》的通知</span></p>\n<p style=\"text-align: left;\"><br />灞桥区、阎良区、高陵区人民政府，市集建入市协调机制各成员单位,西安市公共资源交易中心，西安城乡融合要素交易市场有限公司:<br />现将《西安市农村集体经营性建设用地入市试点工作操作流程(试行)》予以印发，请结合工作实际，抓好贯彻落实。</p>\n<p style=\"text-align: right;\">西安市深化农村集体经营性建设用地入市试点上作协调机制办公室(西安市自然资源和规划局代章)</p>\n<p style=\"text-align: right;\">2024年3月29日</p>\n<p style=\"text-align: center;\">西安市农村集体经营性建设用地人市试点工作操作流程(试行)</p>\n<p style=\"text-align: left;\">按照中央办公厅、国务院办公厅印发《关于深化农村集体经营性建设用地入市试点工作的意见》(厅字〔2022】34号)，自然资源部《深化农村集体经营性建设用地入市试点工作方案》(自然资办函〔2023】364号)要求，为规范西安市灞桥区、高陵区、阎良区开展农村集体经营性建设用地入市工作，制定以下入市试点工作操作流程。<br />一、确定入市地块<br />(一)明确主体。农村集体经营性建设用地属于农民集体所有，土地所属的村(组)集体经济组织、镇街农村集体经济组织是农村集体经营性建设用地入市的主体。如需委托其他法人组织代理实施入市，须经本集体经济组织成员集体决策同意。在入市前，应取得农业农村部门发放的农村集体经济组织登记证书、并有统一社会信用代码。<br />(二)确定地块。入市地块应符合以下条件:1.产权明晰无纠纷，已依法办理集体土地所有权登记，集体建设用地使用权登记;2.国土空间规划确定为工业、商业等经营性用途;3.具备开发建设所需基础设施配套等条件;4.不得涉及文物保护单位、一般不可移动文物和重要考古发现。符合上述条件后，经本集体经济组织成员集体决策同意，可确定为入市地块<br />(三)勘测定界。对拟入市地块集体经济组织须委托有资质的测量机构进行勘测定界，出具地块勘测定界成果(成果表、宗地图)。<br />二、明确规划指标和相关要求<br />(四)提出规划申请。由入市主体向辖区资源规划部门提出申请，申请内容为入市地块位置、面积、用地性质、地块规划指标等事项。<br />(五)核提规划条件。辖区资源规划部门受理申请后，依据实用性村庄规划，提出拟出让的农村集体经营性建设用地的规划指标，明确土地界址、面积、用地性质和开发建设强度等，报资源规划分局局长办公会审定，规划指标须在区政府官方网站对外公示7日，无异议后出具《农村集体经营性建设用地规划条件书》，报市资源规划局村镇规划处备案<br />(六)提出准入要求。入市主体同步向辖区政府有关部门申请相关指标要求，其中区发改部门明确项目产业准入要求，区生态环境部门明确入市地块生态环境保护要求。<br />(七)开展考古勘探。入市主体在选定地块时需向区文物部门申请核查地块是否涉及级别文物保护单位或一般不可移动文物。确定入市地块后需开展考古勘探和必要的考古发掘工作，报市文物局审核并出具意见<br />三、编制出让方案<br />(八)委托地价评估。农村集体经营性建设用地使用权入市地价，由入市主体委托土地估价专业机构进行评估，土地估价报告需经&ldquo;土地估价报告备案系统&rdquo;备案并取得电子备案号。入市主体可参考评估结果确定入市地价。</p>\n<p style=\"text-align: left;\">(九)编制出让方案。农村集体经营性建设用地使用权以出让、租赁方式入市，参照同类用途国有建设用地的年限和交易程序，采取招标、拍卖、挂牌方式在统一的交易平台公开交易。租赁期限不得少于5年，最高租赁期限不得超过20年。集体经济组织要依据规划条件、产业准入和生态环境保护要求等，编制集体经营性建设用地出让等方案，应当载明出让宗地的土地界址、面积、用途、规划条件、使用期限、入市价格、交易价款支付、交地时间和开工竣工期限、产业准入和生态环境保护要求，收益调节金提取比例，集体收益分配安排等内容。<br />(十)集体决策入市事项。农村集体经营性建设用地属村(组)集体经济组织的，由集体经济组织召开成员会议或成员代表会议对入市地块出让方案进行研究，经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意后，形成《农村集体经营性建设用地入市决议》。地块所在街办应对入市主体资格、决策程序等进行核对并予以盖章确认。<br />(十一)核对出让方案。入市主体在出让前须将入市方案等有关资料报辖区资源规划部门，由辖区政府组织资源规划、发改、农业、财政、住建、生态环境、文物等部门对出让方案进行研究在收到方案后五个工作日内反馈意见。入市方案需报区人民政府核对同意后，由辖区资源规划分局将入市方案等有关资料报市资源规划局核对，经市资源规划局局长办公会研究，报市人民政府同意并反馈意见。<br />四、组织公开交易<br />(十二)交易受理。农村集体经营性建设用地入市可在西安城乡融合要素交易市场、西安市公共资源交易中心实施交易。入市前，由入市主体将出让公告、出让文件报辖区资源规划分局核对，于发布公告的前一日向交易平台递交相关材料，其中出让公告文件应当包括加盖辖区资源规划分局公章的出让公告原件的PDF 版，以及出让公告的电子文档。<br />(十三)发布公告。交易平台按照出让文件明确的时间发布土地出让公告，公告期不少于20日、挂牌期不少于10个工作日土地所在辖区资源规划分局还需在自然资源市场监测与监管系统填报入市土地出让公告相关信息，同步在中国土地市场网发布。</p>\n<p style=\"text-align: left;\">(十四)竞买申请。意向竞买人须登录交易平台网站了解系统报名、报价等有关操作流程。在公告规定期限内登陆系统报名，在保证金缴纳截止日期前，足额缴纳竞买保证金并确认，完成竞买申请。保证金缴纳时间以到达指定账户时间为准。<br />(十五)成交服务。土地竞买成交后，交易平台成交当日发布成交结果公示，公示期2日，公示结束即向竞得人出具《成交结果通知书》。入市主体依据《成交结果通知书》与竞得人签订《成交确认书》。竟得人持《成交确认书》《集体经营性建设用地使用权出让合同》及相关资料，到交易平台办理竞买保证金划转抵土地出让价款手续。交易平台应当在受理保证金划转申请后3个工作日内，将竟买保证金划转至银行监管账户。对未竞得人的竞买保证金，在交易活动结束后3个工作日内退还原账户，退还竞买保证金不计利息。<br />(十六)成交公告。土地交易活动结束后，交易平台根据《成交确认书》在网站公布土地成交结果，包括土地位置、面积、用途、土地级别、容积率、出让年限、供地方式、中标人(受让人)成交价格和成交时间等。入市主体要将交易结果当日在集体经济组织事务公示栏进行公布，接受群众监督。入市地块所在辖区资源规划分局要及时在自然资源市场监测与监管系统填报该地块成交结果。<br />五、签订出让合同<br />(十七)签订监管协议。签订土地出让合同前，须由土地出让人、受让人、土地所在区人民政府签订《集体经营性建设用地使用权出让监管协议》，监管协议模板按照自然资源部办公厅国家市场监督管理总局办公厅发布的《集体经营性建设用地使用权出让监管协议》(自然资办发〔2023]9号)执行。<br />(十八)签订出让合同。《成交确认书》签订之后10个工作日内签订《集体经营性建设用地使用权出让合同》，签订前须由土地所在资源规划分局将出让合同相关信息录入土地市场监测与监管系统，由系统统一配置编号后形成的制式合同打印一式陆份。入市主体、受让人应当按《成交确认书》约定的时间内，在辖区资源规划部门的见证下，正式签订《集体经营性建设用地使用权出让合同》并现场拍照，报辖区资源规划部门备案。<br />六、办理权属登记<br />(十九)交付土地。监管协议和出让合同签订后，出让人应当按合同约定交付土地，所交付的土地须达到合同约定的土地条件，双方于交地当日签订《交地确认书》。未按合同交付土地的:受让人有权解除合同，并可向入市主体请求违约赔偿<br />(二十)支付价款和税费。受让人签订出让合同后，应当按照合同约定支付土地价款及相关税费。土地价款须缴纳至银行监管账户，该账户须由出让人、银行、辖区资源规划部门共同签订监管协议，待调节金缴纳完成后解除监管。分期支付的须通过监管账户向出让人支付利息。<br />(二十一)缴纳土地增值收益调节金。辖区资源规划部门根据《交地确认书》向出让人开具《缴纳土地增值收益调节金通知书》，应载明成交土地地块、面积、交易方式、成交总价款、调节金金额、缴纳义务人和缴纳期限等。出让人持缴款通知书向监管银行发起缴款申请，调节金全额缴入地方国库后，向辖区资源规划部门申请出具土地增值收益调节金缴纳凭证<br />土地增值收益调节金具体征收按财政部、自然资源部印发《关于延续实施农村集体经营性建设用地土地增值收益调节金政策的通知》(财税〔2023〕52号)执行。<br />(二十二)办理不动产登记证。受让人持入市核准通知书、交地通知书、集体土地所有权证书、交易鉴证书、集体建设用地出让合同、价款支付凭证、土地增值收益调节金缴纳凭证以及相关税费缴纳凭证等材料，向土地所在的不动产登记机构申请办理不动产登记。出让土地原有集体建设用地使用权证的一并办理注销登记。未按合同约定缴清全部土地价款的，不得办理土地登记，(二十三)办理建设审批手续。依法取得的农村集体经营性建设用地入市地块，要按照合同约定开工时间之前，依据相关法律法规和规章要求，分别向市区政府有关部门办理建设用地和工程规划许可、建设工程施工许可等相关手续，项目建设完成后须向区政府有关部门申请竣工验收备案手续。</p>\n<p style=\"text-align: left;\">七、强化批后监管<br />(二十四)入市土地利用监管。土地所在区人民政府是入市地块的监管责任主体，要按照签订《集体经营性建设用地使用权出让监管协议》约定的监管内容、期限及方式等履行监管职责。辖区资源规划分局要加强对入市土地开发利用情况监管，严肃查处违反国土空间规划和用途管制等违法行为，督促使用权人依法依规使用土地，按期开发建设，发现存在闲置土地或未按规定要求使用土地的，要及时督促整改。<br />(二十五)土地二级市场交易。集体经营性建设用地的出租集体建设用地使用权的转让、互换、出资、赠与、抵押等，参照同类用途的国有建设用地执行，法律、行政法规另有规定的除外本入市试点工作操作流程仅限灞桥、高陵、阎良试点区，自印发之日起施行，有效期至2024年12月31日。</p>', '302', 33, NULL, '关于印发《西安市农村集体经营性建设用地人市试点工作操作流程(试行)》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (647, '西安市林业局关于印发《西安市集体林权流转管理办法(试行)》的通知', 999999, NULL, 'xafby', '183', '2024-05-08 14:43:36', '2024-08-28 16:57:18', '2024-05-08 14:43:36', '2', 'xafby', '西安市林业局', '西安发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\">西安市林业局关于印发《西安市集体林权流转管理办法(试行)》的通知</span></p>\n<p>各区(县)林业主管部门:<br />按照《中共西安市委办公厅、西安市人民政府办公厅关于印发&lt;西安城乡融合要素交易市场组建方案&gt;的通知》(市办字〔2023]192号)精神，为规范集体林权流转，我局制定了《西安市集体林权流转管理办法(试行)》，现印发给你们，请遵照执行。<br /><br /></p>\n<p style=\"text-align: right;\">西安市林业局</p>\n<p style=\"text-align: right;\">2024年4月16日</p>\n<p style=\"text-align: center;\">西安市集体林权流转管理办法<br />(试行)<br />第一章 总 则</p>\n<p style=\"text-align: left;\">第一条 为规范集体林权流转行为，健全集体林地所有权、承包权、经营权分置运行机制，保障流转当事人的合法权益，促进森林资源合理利用和林业产业高质量发展，根据《中华人民共和国民法典》《中华人民共和国森林法》《中华人民共和国农村土地承包法》《不动产登记暂行条例》《深化集体林权制度改革方案》(中办发〔2023]45号)《西安城乡融合要素交易市场组建方案》(市办字〔2023]192号)等法律法规及政策，结合实际，制定本办法。<br />第二条 凡在本市从事集体林权流转交易活动的，适用本办<br />第三条 集体林权流转遵循下列原则:<br />(一)依法、自愿、有偿，流转的意愿、价格、期限、方式、对象等应由林权权利人依法自主决定，任何组织和个人不得强迫或者阻碍集体林权流转。<br />(二)不得改变林地用途，不得擅自改变公益林性质;流转后的林地要严格依法依规合理开发利用。<br />(三)流转期限不得超过剩余承包期限。<br />(四)不损害国家、集体、个人的合法权益和社会公共利益:不破坏森林资源和生态环境。<br />(五)流入方须有林业经营能力或者资质。第四条 同等条件下，本集体经济组织成员享有集体林权流转的优先受让权。鼓励农民通过依法流转组建专业合作社、合作林场、股份制林场、家庭林场等新型林业经营主体。第五条集体林权流转交易活动必须依据西安城乡融合要素流转交易管理办法有关规定实施。</p>\n<p style=\"text-align: center;\">第二章 流转方式与期限</p>\n<p style=\"text-align: left;\">第七条 林权流转方式包括:转让、互换、转包、出租、入股、抵押等方式。<br />第八条 依法采取转让方式流转林地承包经营权的，应当经原发包的集体经济组织同意;采取转包、出租、互换、入股、抵押或者其他方式流转的，应当报原发包的集体经济组织备案<br />第九条 公益林在不改变性质的前提下，可以采取出租、转包、入股方式进行流转，发展林下种养业或森林旅游业。<br />第十条 集体林地经营权流转应当签订书面合同。林地经营权流转合同一般包括流转双方的权利义务、流转期限、流转价款及支付方式、流转期限届满林地上的林木和固定生产设施的处置、违约责任等内容。鼓励积极推广和引导当事人使用国家统一规范的《集体林权流转合同》(编号:GF-2020-2603)。<br />受让方违反法律规定或者合同约定造成森林、林木、林地严重毁坏的，发包方或者承包方有权收回林地经营权。</p>\n<p style=\"text-align: center;\">第三章 流转程序</p>\n<p style=\"text-align: left;\">第十一条 集体统一经营管理的林地经营权和林木所有权流转的，流转方案应在本集体经济组织内提前公示。经本集体经济组织三分之二以上成员(成员代表)讨论通过，形成决议并报乡镇政府批准;农民个人产权须是本人自愿并征得权属共有人同意后流转交易、<br />第十二条 发包方将农村土地发包给本集体经济组织以外的单位或者个人承包，应当事先经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，并报乡<br />(镇)人民政府批准。由本集体经济组织以外的单位或者个人承包的，应当对承包方的资信情况和经营能力进行审查后，再签订承包合同第十三条 流转期限为五年以上的，当事人可以向登记机构申请权属登记。林权变更、转移登记程序按照国家相关规定执行。未经登记不得对抗善意第三人。</p>\n<p style=\"text-align: center;\">第四章 林权流转服务</p>\n<p style=\"text-align: left;\">第十四条 市、区(县)级不动产登记机构要按照有关要求全面履行林权类不动产登记职责。<br />第十五条 农村集体经济组织获得的交易收益，纳入农村集体财产统一管理，用于本集体经济组织成员分配和社会保障、新农村建设等公益事业。农民个人产权的交易收益，归个人所有第十六条在农村产权流转交易机构进行流转交易过程中发生产权流转交易纠纷的，当事人可以向农村产权流转交易机构或者当地农村产权流转交易监督管理部门申请调解，也可以依据合同约定申请仲裁或者依法向人民法院提起诉讼。<br />第十七条 流转交易双方及中介服务机构有违法违规违约行为，给农村产权流转交易机构及相关交易主体造成损失的，依法承担法律责任。</p>\n<p style=\"text-align: center;\">第五章附则</p>\n<p style=\"text-align: left;\">第十八条 本办法实施前已经依法发生的森林、林木、林地流转并办理了林权变更、转移登记手续的，其流转行为继续有效。第十九条，本办法如与国家、省新制定的有关集体林权流转管理政策规定不相符的，应从其规定。<br />第二十条 本办法自印发之日起30日后生效。</p>', '302', 21, NULL, '西安市林业局关于印发《西安市集体林权流转管理办法(试行)》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (653, '城乡融合要素交易市场  （西安高陵）有限公司揭牌', 999999, '1795382268180115457', 'xafby', '183', '2024-05-28 16:50:40', '2024-08-28 16:57:18', '2024-05-28 16:50:40', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<p>4月22日，城乡融合要素交易市场（西安高陵）有限公司揭牌仪式在高陵区农经中心交易大厅举行。高陵区区委常委、副区长张跃进，西安农投集团党委委员、副总经理杨彬，西安城乡融合要素交易市场有限公司总经理贠涛、副总经理郑小龙，农业农村局党委书记、局长张军，农经中心党组书记、主任来刚，区城市建设开发有限公司董事长、总经理黄继锋，秦农银行高陵支行党委书记鹿峰为公司揭牌。</p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/05/28/5607cdb5fd7e40c988890535cb3577e4.jpg\" /></p>\n<p>城乡融合要素交易市场（西安高陵）有限公司根据西安市委、市政府建立城乡融合要素交易市场的相关要求，在高陵区委、区政府的部署安排下，在区国资局、区农经中心、西安城乡要素交易市场有限公司、秦农银行高陵支行等部门的全力配合下，于3月28日完成工商注册，并完成办公选址、内部装修及设备配置等前期准备工作，今天正式对外投运。</p>\n<p>活动现场，工作人员全面介绍了城乡融合要素交易平台四大类21项交易品种，对出让申请、资料审核、信息发布、征集意向受让方、交易竞价、成交公示、合同签订、价款结算、交易鉴证等交易全流程进行了详细讲解，宣讲了农村各类资源资产进入平台交易的价值和重要意义。</p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/05/28/8b6f45bdeec24a1c977512cd757db678.jpg\" /></p>\n<section>\n<section>\n<p>未来发展中，高陵区城乡融合要素交易市场将探索创新工作模式，持续整合平台功能作用，紧抓农村宅基地制度改革试点、农村集体经营性建设用地入市试点等改革机遇，集中促进&ldquo;人、地、钱&rdquo;等各类发展要素顺畅流通，为城市和农村发展提供要素保障，盘活农村闲置资源，提高溢价率，为壮大农村集体经济、增加农民收入作出新贡献。</p>\n<p>西安城乡融合要素交易市场有限公司、高陵区农业农村和林业局、资源规划高陵分局、区住建局、区水务局、区市场监管局分管同志及秦农银行总行相关负责人，各街道分管领导和部分村主要负责人，新闻媒体代表等共计40余人参加活动。</p>\n</section>\n</section>\n<section>\n<section></section>\n</section>', '101', 25, NULL, '城乡融合要素交易市场  （西安高陵）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (654, '城乡融合要素交易市场（西安周至）有限公司揭牌', 999999, '1795383899848257538', 'xafby', '183', '2024-05-28 17:16:09', '2024-08-28 16:57:18', '2024-05-28 17:16:09', '2', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<p>4月25日，城乡融合要素交易市场（西安周至）有限公司揭牌仪式在周至县中心东街47号举行。周至县政府副县长王刚利，西安城乡融合要素交易市场有限公司副总经理龚文军为公司揭牌。</p>\n<p>根据西安市委、市政府建立城乡融合要素交易市场的相关要求，城乡融合要素交易市场（西安周至）有限公司于4月8日注册成立，在完成了办公选址、内部装修及设备配置等前期准备工作后今天正式对外投运。</p>\n<p>未来发展中，城乡融合要素交易市场周至公司将依托周至县良好的自然环境、深厚的历史文化底蕴和丰富的农业资源，深度挖掘、整合农村资源要素，充分发挥&ldquo;统一规范、公正透明&rdquo;的阳光交易平台功能作用，把碎片化的资源进行规模化的收储、专业化的整合、市场化的运作，实现农村资产资源价值最大化，为村集体经济培育新的&ldquo;增长支点&rdquo;，为周至县乡村振村作出新贡献。</p>\n<p>县农业农村局、县资源规划局、县住建局、县水务局、县秦保局、西安城乡融合要素交易市场有限公司等单位分管负责同志，各镇（街）分管领导，秦农银行总行相关负责人等20余人参加活动。</p>\n<section data-style-id=\"23172\" data-type=\"undefined\" data-id=\"23172\" data-vip=\"1\" data-free=\"0\">\n<section>\n<section data-width=\"20%\"></section>\n</section>\n</section>', '101', 20, NULL, '城乡融合要素交易市场（西安周至）有限公司揭牌', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (655, '安置房租赁交易业务办理指南', 999999, NULL, 'xafby', '183', '2024-05-28 17:36:55', '2024-08-28 16:57:18', '2024-05-28 17:36:55', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '600,603', '0', 0, 0, 0, '<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 16.74%;\" width=\"16.7400%\">\n<p>交易主体</p>\n</td>\n<td style=\"width: 83.24%;\" width=\"83.2400%\">\n<p>申请资料清单</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 16.74%;\" width=\"16.7400%\">\n<p>出租人</p>\n</td>\n<td style=\"width: 83.24%;\" width=\"83.2400%\">\n<p>（一）出租申请书，包括但不限于交易标的基本情况、产权权利人签字捺印或签章证明、出租底价、出租方式、出租用途、出租期限、承租人条件等内容；</p>\n<p>（二）出租人交易承诺书；</p>\n<p>（三）安置房权属佐证材料。如拆迁安置协议、安置房收房单或其他合法有效的佐证材料；</p>\n<p>（四）出租人的身份证件或其他合法有效证件；</p>\n<p>（五）属集体所有的安置房，须提供所属集体内部决策材料；</p>\n<p>（六）出租共有产权的安置房，须提供其他共有人同意出租的证明；</p>\n<p>（七）出租委托代管的安置房，须提供委托人授权出租的证明材料；</p>\n<p>（八）转租安置房的，转租人应当提供原出租合同及出租人同意转租的书面证明；</p>\n<p>（九）应当提交的其他材料。</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 16.74%;\" width=\"18.4200%\">\n<p>意向承租人</p>\n<p>&nbsp;</p>\n</td>\n<td style=\"width: 83.24%;\" width=\"81.5600%\">\n<p>（一）意向承租申请书；</p>\n<p>（二）意向承租人交易承诺书；</p>\n<p>（三）承租人的身份证件或其他合法有效证件。如为自然人，提供个人身份证或户口本复印件；如为村集体经济组织，提供农村集体经济组织登记证复印件、法定代表人身份证复印件等；如为其他法人组织，提供工商营业执照副本复印件、法定代表人身份证复印件；如为非法人组织，提供资质证照、负责人身份证明及相关文件等。需委托他人办理交易的，提交授权委托书以及被授权人身份证明；</p>\n<p>（四）交易保证金缴费凭证；</p>\n<p>（五）符合承租资格条件的证明文件，如限制为本村集体经济组织成员或者本村村民的须提供身份证明材料；</p>\n<p>（六）应当提交的其他材料。</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>注：1.以上文件均需出租人加盖鲜章；</p>\n<p>2.出租人须对提交的所有材料（包括原件、复印件、扫描件）的真实性、合法性、有效性、完整性负责，并承担相应的法律责任。</p>', '603', 17, NULL, '安置房租赁交易业务办理指南', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (656, '村级货物和服务采购业务办理指南', 999999, NULL, 'xafby', '183', '2024-05-30 16:35:24', '2024-08-28 16:57:18', '2024-05-30 16:35:24', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '600,603', '0', 0, 0, 0, '<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 49.9631%;\" width=\"114\">\n<p>交易主体</p>\n</td>\n<td style=\"width: 49.9631%;\" width=\"489\">\n<p>申请资料清单</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 49.9631%;\" width=\"114\">\n<p>采购方</p>\n</td>\n<td style=\"width: 49.9631%;\" width=\"489\">\n<p>1.采购申请书，包括但不限于采购项目名称、项目概况、采购方式、保证金额度、采购需求，供应商应具备的资格条件、技术要求和商务要求等；</p>\n<p>2.采购方交易承诺书；</p>\n<p>3.主体资格证明材料，包括但不限于采购方资质证明，统一社会信用代码证，法定代表人身份证复印件；委托他人办理交易的，需提交授权委托书以及被授权人身份证明；</p>\n<p>4.批准采购的相关文件，包括民主决策档案材料、内部决议、有关备案文件等；</p>\n<p>5.采购预算表，包括但不限于项目预算金额、采购数量等；</p>\n<p>6.除绿色通道外的采购项目，还应提交招标文件或采购文件；</p>\n<p>7.应当提交的其他材料。</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 49.9631%;\" width=\"147\">\n<p>意向供应商</p>\n</td>\n<td style=\"width: 49.9631%;\" width=\"456\">\n<p>1.意向供应申请书；</p>\n<p>2.意向供应商交易承诺书；</p>\n<p>3.采购保证金缴纳凭证；</p>\n<p>4.主体资格证明。如为自然人，提供个人身份证或户口本复印件；如为村集体经济组织，提供农村集体经济组织登记证复印件、法定代表人身份证复印件等；如为其他法人组织，提供工商营业执照副本复印件、法定代表人身份证复印件；如为非法人组织，提供资质证照、负责人身份证明及相关文件等。需委托他人办理交易的，提交授权委托书以及被授权人身份证明；</p>\n<p>5.意向供应商资信证明、资质证明；</p>\n<p>6.除绿色通道、网络竞价外的采购项目，还应提交投标文件（响应文件）；</p>\n<p>7.应当提交的其他材料。</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>注：1. 以上文件均需采购方加盖鲜章；</p>\n<p>2.采购方须对提交的所有材料（包括原件、复印件、扫描件）的真实性、合法性、有效性、完整性负责，并承担相应的法律责任。</p>', '603', 17, NULL, '村级货物和服务采购业务办理指南', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (657, '集体林权交易业务办理指南', 999999, NULL, 'xafby', '183', '2024-05-30 17:02:12', '2024-08-28 16:57:18', '2024-05-30 17:02:12', '0', 'xafby', '西安', '西安发布员', '600,603', '0', 0, 0, 0, '<table style=\"border-collapse: collapse; width: 100%;\" border=\"1\">\n<tbody>\n<tr>\n<td style=\"width: 17.22%;\" width=\"17.2200%\">\n<p>交易主体</p>\n</td>\n<td style=\"width: 82.76%;\" width=\"82.7600%\">\n<p>申请资料清单</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 17.22%;\" width=\"17.2200%\">\n<p>出让方</p>\n</td>\n<td style=\"width: 82.76%;\" width=\"82.7600%\">\n<p>1.出让申请书。包括交易标的基本情况、产权权利人签字捺印或签章证明、受让方条件、出让底价、价款支付要求、交易保证金等内容；</p>\n<p>2.出让方交易承诺书；</p>\n<p>3.权属证明材料。集体林权证或其他合法有效的权属佐证材料；</p>\n<p>4.主体资格证明材料。包括但不限于营业执照或统一社会信用代码证，法定代表人或负责人身份证明；委托他人办理交易的，需提交授权委托书以及被授权人主体资格和身份证明；</p>\n<p>5.内部决策材料。集体统一经营管理的林地承包经营权和林木所有权流转的，流转方案应在本集体经济组织内提前公示，经本集体经济组织三分之二以上成员（成员代表）讨论通过，形成决议并报乡镇政府批准；发包方将农村林地发包给本集体经济组织以外的单位或者个人承包，应当事先经本集体经济组织成员的村民会议三分之二以上成员或者三分之二以上村民代表的同意，形成内部文件并报乡镇政府批准；</p>\n<p>6.农民个人产权须是本人自愿并征得权属共有人同意后流转交易；</p>\n<p>7.再次流转交易的出让方应当提供原发包的集体经济组织同意再次流转的证明和原流转交易的合同（或有效证明），并不得超过合同的剩余期限；</p>\n<p>8.应当提交的其他材料。</p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 17.22%;\" width=\"21.1400%\">\n<p>意向受让方</p>\n</td>\n<td style=\"width: 82.76%;\" width=\"78.8400%\">\n<p>1.意向受让申请书；</p>\n<p>2.意向受让方交易承诺书；</p>\n<p>3.交易保证金缴费凭证；</p>\n<p>4.主体资格证明。如为自然人，提供个人身份证或户口本复印件；如为村集体经济组织，提供农村集体经济组织登记证复印件、法定代表人身份证复印件等；如为其他法人组织，提供工商营业执照副本复印件、法定代表人身份证复印件；如为非法人组织，提供资质证照、负责人身份证明及相关文件等。需委托他人办理交易的，提交授权委托书以及被授权人主体资格和身份证明；</p>\n<p>5.享有优先受让权的意向受让方，需提供同一集体经济组织成员的相关证明材料；</p>\n<p>6.本集体经济组织以外的单位或者个人承包的，应当提供林业经营能力或者资质的相关证明材料，包括但不限于资信情况证明材料、业绩证明材料、人员证明材料等；</p>\n<p>7.应当提交的其他材料。</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>注：1.以上文件均需意向受让方加盖鲜章；</p>\n<p>2.意向受让方须对提交的所有材料（包括原件、复印件、扫描件）的真实性、合法性、有效性、完整性负责，并承担相应的法律责任。</p>', '603', 12, NULL, '集体林权交易业务办理指南', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', NULL);
