INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (843, '市纪委副书记杨帆一行莅临公司调研指导', 999999, '1836964080770142209', 'xafby', '183', '2024-09-20 11:02:09', '2024-09-20 11:04:27', '2024-09-20 11:02:09', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>8月28日，西安市纪委副书记、秘书长，市监察委员会副主任杨帆一行莅临西安城乡融合要素交易市场开展专项调研。西安农业投资（集团）有限公司党委书记、董事长成斌，市纪委监委党风政风监督室副主任刘建平等参加调研座谈。</p>\n<p>调研组参观了交易大厅，现场听取了交易工作开展情况、重点交易案例、农村集体&ldquo;三资&rdquo;合同清理工作推进情况，深入了解了项目审核、竞价、成交等交易流程。</p>\n<p>杨帆对西安城乡融合要素交易市场成立至今取得的成绩表示了充分的肯定，他指出，城乡融合要素交易平台是规范基层农村资源资产流转交易、推动党风廉政建设向基层延伸的有力抓手，在开展业务的过程中要高度重视交易的合法性、合规性和合理性。他强调，<strong>一是要主动出击，撬动农村资源资产盘活利用。</strong>农投集团要抓住机遇，通过市场化平台促进人力、土地等各类资源要素在城乡之间双向流动，推动资源变资产、资产变项目、项目变产业、产业变资金，促进城乡统筹发展；<strong>二是要推动体制机制、政策体系创新。</strong>在现有制度基础上，要不断探索和实践新的交易模式和管理机制，扩充交易品类，丰富市场内容，拓宽交易领域，不断完善顶层设计，加强制度创新，使交易有效果、有成果、有价值，平台更可信、更有影响力；<strong>三是要建立健全监督机制，确保阳光交易。</strong>公平、公正、公开交易是平台的生命线，更关乎平台的公信力，要强化责任担当，加强规范化建设，确保交易合法、合理、有效，实现阳光交易。</p>\n<p>成斌表示，城乡融合要素交易市场在破解城乡二元结构和推进城乡统筹发展等方面，发挥着不可替代的重要作用，西安农投集团将凝心聚力、开拓创新、锐意进取、奋力拼搏，将城乡融合要素交易规范化建设工作作为担当使命，扛在肩上、落到实地。</p>', '101', 2, NULL, '8月28日，西安市纪委副书记、秘书长，市监察委员会副主任杨帆一行莅临西安城乡融合要素交易市场开展专项调研。', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', '西安城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (844, '许克涛副市长一行莅临公司调研指导', 999999, '1836964840312459265', 'xafby', '183', '2024-09-20 11:04:55', '2024-09-20 11:06:45', '2024-09-20 11:04:55', '0', 'xafby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>8月29日，许克涛副市长赴西安城乡融合要素交易市场开展&ldquo;健全城乡融合要素交易体系、赋能更高水平乡村全面振兴&rdquo;调研。</p>\n<p>许克涛一行参观了交易服务大厅，观看了公司宣传片，深入了解了平台建设、重点交易案例、工作实效及推进过程中存在的问题等情况，听取了关于西安农投集团发展情况及西安城乡融合要素交易市场基本情况的汇报。</p>\n<p>许克涛对西安城乡融合要素交易市场取得的工作成绩表示了充分的肯定，他指出，<strong>在</strong><strong>思想层面，要坚持解放思想，不断摸索创新。</strong>要深入贯彻落实党的二十届三中全会精神，不断解放思想，在总结其他先行地区工作经验、成功做法的基础上，结合西安实际，探索创新交易品种、交易内容，规范、稳步推进交易工作；<strong>在组织层面，要加强统筹意识，健全体制机制。</strong>切实处理好政府与市场的关系，监督管理委员会要加强统筹协调，理顺监管机制、建立沟通机制，主体责任和监督责任要同时发力，做到政府依法行政、企业合规经营。<strong>在操作层面，要强化宣传推广，广泛引导参与。</strong>借助市农业农村局、市纪委等行政部门的力量，广泛进行宣传引导，城乡融合要素交易市场积极配合，深入街道（镇）、村进行培训指导，提高农村集体经济组织、村民进场交易的积极性和参与度，形成政府引导、企业配合、村集体和村民参与的良好局面。</p>\n<p>他强调，西安农投集团作为<strong>保障国家粮食安全和农副产品保供的担当者、农业农村现代化的引领者和农民就业增收的带动者，</strong>要紧紧围绕都市型农业强市总目标，紧盯&ldquo;服务城市、城乡融合、联农带农&rdquo;职能，贯彻落实市委、市政府赋予的主责主业，为西安都市型农业强市建设贡献力量。</p>\n<p>市政府督查专员鲁强，市农业农村局党组书记、局长裴靖瑜，西安农投集团党委书记、董事长成斌、市纪委监委、市发改委、市资源规划局、市农业农村局、市农村产权流转交易中心相关业务处室负责同志，西安城乡融合要素交易市场负责同志等参加调研活动。</p>', '101', 1, NULL, '8月29日，许克涛副市长赴西安城乡融合要素交易市场开展“健全城乡融合要素交易体系、赋能更高水平乡村全面振兴”调研。', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0101', '西安城乡融合要素交易市场');
