INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (846, '联动西北，共谱新篇——西安城乡融合要素交易市场与酒泉市农业发展集团签署合作协议', 999999, '1838515562501951489,1838515578654216194', 'sjfby', '882', '2024-09-24 17:32:55', '2024-09-24 17:52:08', '2024-09-24 17:32:55', '0', 'sjfby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 12pt;\">9月24日，西安城乡融合要素交易市场有限公司与酒泉市农业发展集团有限公司签署合作协议。酒泉市农发集团（寒旱经投集团）党委书记、董事长鲁辉，酒泉市农发集团（寒旱经投集团）党委委员、纪委书记高华，西安城乡融合要素交易市场有限公司主要领导等13人参加活动。</span></p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/09/24/ab640daef8c84a4b89ddead3fe0f8da9.jpg\" /></p>\n<p><span style=\"font-size: 12pt;\">西安城乡融合要素交易市场一行首先参观考察了中以（酒泉）绿色生态产业园。园内配备了先进的智能温室、水肥一体化系统设备，展示了现代农业的高科技应用。大家对示范园的智能化管理和高效生产模式给予了高度评价。</span></p>\n<p><span style=\"font-size: 12pt;\">参观结束后双方进行了对标学习交流合作座谈会。西安城乡融合要素交易市场总经理贠涛详细介绍了公司的组建过程、资源配置优化、交易效率提升等方面的经验和做法。酒泉市农发集团（寒旱经投集团）党委书记、董事长鲁辉表示，西安城乡融合要素交易市场在盘活农村资产和壮大集体经济方面的成功经验对于酒泉有非常强的借鉴意义。特别是在如何利用市场机制盘活农村资产、吸引社会资本下乡等方面，为他们打开了工作思路，带来了新的启示。</span></p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/09/24/0cdefb69aa20402498146d32e988c23c.jpg\" /></p>\n<p><span style=\"font-size: 12pt;\">随后，双方举行了签约仪式，双方代表签署了合作协议并就平台资源共享、招商引资、人才交流等方面初步达成合作。此次合作不仅是两家企业的合作，更是两个城市在农业和城乡融合发展道路上的一次重要跨越。双方将通过合作实现资源共享、优势互补，共同推动农业和城乡融合的高质量发展。共建西北地区农业和城乡融合的&ldquo;新高地&rdquo;。</span></p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/09/24/7ac987813b854e5f8c96ddb7f3b3d091.jpg\" /></p>\n<p><span style=\"font-size: 12pt;\">酒泉是古丝绸之路的重要节点城市，拥有丰富的自然资源和独特的地理优势，为农业发展提供了得天独厚的条件。酒泉市农业发展集团有限公司是酒泉市政府国资委直接管理的的市属国有企业，肩负着全市乡村振兴重大项目建设和推动农村现代化发展的重要使命，与酒泉市农业发展集团合作，是西安城乡融合要素交易市场<strong>走出西安、跨省合作的又一重要突破，</strong>也是构建互联互通机制、助力全国要素统一大市场建设迈出的重要一步。后续将充分发挥平台优势，实现信息共享、资源整合，充分发掘利用酒泉市丰富的自然资源和独特的地理优势，通过整合西安市与酒泉市的优势资源，共同探索农村产权的创新模式，<strong>&ldquo;双城&rdquo;联动共同推进农业现代化进程，切实贯彻落实习总书记来陕讲话精神和二十届三中全会精神，畅通城乡要素流转渠道，加速激活人力、技术等要素的流通，带动农业增效、农民增收、农村繁荣，赋能更高水平乡村全面振兴。</strong></span></p>', '101', 121, NULL, '联动西北，共谱新篇——西安城乡融合要素交易市场与酒泉市农业发展集团签署合作协议', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
