INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (852, '西安城乡融合要素交易市场与南京农村产权交易中心签订战略合作协议', 999999, '1843462035618451457', 'sjfby', '882', '2024-10-08 09:19:15', '2024-10-08 09:53:02', '2024-10-08 09:19:15', '0', 'sjfby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p>9月25日，西安城乡融合要素交易市场有限公司受邀参加2024年南京金洽会&middot;南京农村集体资源资产招商推介会，现场与南京农村产权交易中心签订战略合作协议。</p>\n<p><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2024/10/08/1d453055d3ef43f5a033d03c3057c83b.jpg\" /></p>\n<p>跨省合作签约会上，西安城乡融合要素交易市场有限公司与南京农村产权交易中心签订农村产权交易体系共建合作协议，就深化集体产权制度改革，推动农村产权交易市场高质量发展，开展农村产权交易领域的全面合作达成共识。双方将积极探索和实践创新模式，推动两地优质农村产业资源共享、优势互补，促进农村集体经济的发展，共同构建西北和华东地区农业城乡融合的新高地，为两地经济社会的繁荣作出更大的贡献。</p>\n<p>西安城乡融合要素交易市场将继续秉持开放合作、互利共赢的原则，全面落实党的二十届三中全会精神，围绕深化城乡融合发展、发展新型农村集体经济的部署要求，进一步盘活农村集体闲置资产资源，充分发挥农村产权交易市场畅通城乡要素流动的重要载体作用，通过构建互联互通机制，推动区域农村集体资产资源的信息共享、功能互补、共赢发展，助力全国要素统一大市场建设。</p>', '101', 64, NULL, '受邀参加2024年南京金洽会、南京农村集体资源资产招商推介会，现场与南京农村产权交易中心签订战略合作协议', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
