INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (859, '庆阳市西峰区农村合作经济经营管理局一行来访调研座谈', 999999, '1846111722772992002', 'sjfby', '882', '2024-10-15 15:42:35', '2024-10-15 16:53:03', '2024-10-15 15:42:35', '0', 'sjfby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 12pt;\">10月14日，庆阳市西峰区农村合作经济经营管理局一行20余人来访西安城乡融合要素交易市场调研座谈。庆阳市西峰区农经局副局长赵守谦、庆阳市西峰区农经局副局长张钊、西安城乡融合要素交易市场相关领导等参加座谈。</span></p>\n<p><span style=\"font-size: 12pt;\">调研团队首先参观了交易服务大厅，观看了宣传视频，详细了解了交易平台的交易流程、交易品种、服务模式及部分案例解读。</span></p>\n<p><span style=\"font-size: 12pt;\">座谈会上，总经理贠涛，副总经理龚文军、李婧分别就西安城乡融合要素交易市场建设情况、交易内容及业务类型、制度体系建设情况等内容进行了详细介绍。</span></p>\n<p><span style=\"font-size: 12pt;\">赵守谦表示，西安城乡融合要素交易市场的体系建设、交易模式等对西峰区农村产权交易市场建设具有重要借鉴意义。西峰区作为省级农村产权流转交易市场示范县，通过搭建城乡要素流动平台，不仅能够有效激活农村闲置资源，促进城乡资源的合理流动与高效配置，还能为农民增收提供新的途径，进一步激发乡村经济的活力和发展潜力。</span></p>\n<p><span style=\"font-size: 12pt;\">双方还就交易鉴证书进行了讨论，西安城乡融合要素交易市场出具的交易鉴证书可作为银行、保险、融资和担保公司等机构根据标的资产提供抵押融资服务的评估依据之一，可优先为交易主体提供融资、担保等金融服务。</span></p>\n<p><span style=\"font-size: 12pt;\">未来，双方将整合各地特色产业项目和资源，共同构建一个开放合作的信息共享网络，打破地域限制，促进优质资源的自由流动与高效配置，使各类资产得以充分盘活，进而深入挖掘并实现其潜在价值的最大化。</span></p>\n<p><span style=\"font-size: 12pt;\">实现资源的有效配置与价值的最大化，离不开一个健康、活跃的市场环境。西安城乡融合要素交易市场将进一步加强与各界伙伴的合作，致力于构建一个更为开放、高效且透明公正的交易系统，持续改进和完善平台的各项功能，积极拓展业务领域，力求覆盖更广泛的资源和服务类型，促进资源的最优分配，为推动城乡一体化发展贡献力量。</span></p>', '101', 105, NULL, '庆阳市西峰区农村合作经济经营管理局一行来访调研座谈', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
