INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (941, '西北大学2025年经济学基地班来访开展教学实践活动', 999999, '1878721885631516674', 'sjfby', '882', '2025-01-13 16:31:34', '2025-01-13 16:46:13', '2025-01-13 16:31:34', '2', 'sjfby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<p><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/01/13/a54284a8db024395b3d32b8fffb10ab1.jpg\" width=\"1100\" height=\"770\" /></p>\n<p><span style=\"font-size: 14pt;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-size: 18pt;\">1月8日，西北大学经济管理学院高扬教授、金鑫博士带队，经济学基地班90名学生来访西安城乡融合要素交易市场开展教学实践活动。陕西省决策咨询委员会农业组组长、陕西省村促会名誉会长郑梦熊，西安城乡融合要素交易市场有限公司总经理贠涛、分管领导及相关部门负责同志等参加活动。</span></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 同学们首先观看了西安城乡融合要素交易市场宣传片、听取了西安城乡融合要素交易平台、交易流程、交易案例等内容的讲解。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 总经理贠涛为大家介绍了西安城乡融合要素交易市场的组建过程、发展历程与初步成效，从公司最初的组建筹备到落地成型，市场如何依托本地优势资源，打破城乡二元壁垒，搭建起各类要素流通的桥梁进行了系统讲解，让同学们对西安城乡融合要素交易市场这个&ldquo;新生事物&rdquo;有了宏观、全面的了解和认知。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;公司综合管理部、交易部负责同志分别就从校园步入工作的适应转变过程、工作的心得体会等方面进行了交流分享，强调了持续学习和适应变化的重要性，并从自身角度对来访的大学生们提出了几点建议。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 高扬表示，<strong>此次实践调研增强了大家对现实经济社会发展的认知，让书本知识找到了落地生根的土壤，希望同学们可以将书本知识与教学实践相互结合，充分利用自身所学，探索研究城乡融合要素交易新课题，争取学有所用、学有所成。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 郑梦熊认为，本次教学实践形式新颖、内容详实，既有专业知识讲解，又为同学们的职业生涯发展提供了借鉴。他强调，<strong>一是城乡要素交易的难点在于怎么推动城市的资源流向农村</strong>，西安城乡融合要素交易市场这个新生改革产物的出现，将有力推动西安、陕西乃至全国的城乡融合发展进程；<strong>二是西安城乡融合要素交易的重点是城乡的资源要素</strong>，目标是推动要素在城乡之间双向流动，核心是要通过市场化的手段，促进村集体经济壮大和农民增收致富；<strong>三是城乡要素交易正处于时代的风口</strong>，既提供了大量的就业机会，也有美好的职业前景，希望要素交易市场能够充分利用西安丰富的教育资源，与西北大学等高校合作，为在校大学生们提供实践的基地；<strong>四是要素交易市场要抓住机遇、抢占先机</strong>，借鉴广东深圳从&ldquo;小渔村&rdquo;变身为全国经济中心城市的经验，发展成为全国性城乡融合要素交易中心。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 最后，同学们针对流转交易服务体系、需求信息发布、交易流程等方面进行了互动提问。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安城乡融合要素交易市场将继续促进产、学、研深度融合，在优化交易程序、提升服务能力、加强人才培养等方面继续深耕，同时加强与各高校之间的互动、联系，积极履行社会责任，助力教育发展，促进产教融合、理实融合。</span></p>', '101', 96, NULL, '西北大学2025年经济学基地班来访开展教学实践活动', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
