INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1017, '甘肃省农业农村厅一行来访考察调研', 999999, '1899998042075009025', 'sjfby', '882', '2025-03-13 09:35:41', '2025-03-13 09:36:50', '2025-03-13 09:35:41', '2', 'sjfby', '西安城乡融合要素交易市场', '西安发布员', '100,101', '0', 0, 0, 0, '<p style=\"text-align: center;\"><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/13/61a712017f574201ab23f16ef4bee272.jpg\" width=\"1100\" height=\"825\" /></p>\n<p><span style=\"font-size: 14pt;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-size: 18pt;\">3月5日，甘肃省农业农村厅一行来访西安城乡融合要素交易市场考察调研。甘肃省农业农村厅党组成员、副厅长李文瑞，西安市农业农村局党组成员、市农业综合执法支队支队长王富荣，西安农投集团党委委员、副书记李英杰，陕西省农业农村厅政改处、西安城乡融合要素交易市场有限公司主要负责同志参加调研。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;调研组一行首先观看了西安城乡融合要素交易市场的宣传片，随后详细听取了关于交易平台架构、交易流程设计及典型案例的介绍。调研过程中，调研组重点了解了平台的建设进展、交易品种的多样性以及档案管理的规范性等方面。李文瑞对西安城乡融合要素交易市场在交易品种创新、交易模式探索及监管机制完善等方面所取得的成果给予了高度评价。他强调，<strong>西安城乡融合要素交易市场作为一个公开透明、阳光操作的交易平台，不仅有效解决了农村集体&ldquo;三资&rdquo;合同管理中的不规范问题，还切实保障了受让方的合法权益，实现了政府工作的透明化和群众利益的保障，真正达到了政府放心、群众安心的双重目标。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 未来，西安城乡融合要素交易市场将持续加强与各方的协同合作，积极探索并优化工作模式，全力为农民群众提供优质服务，推动农村经济高质量发展，为实现城乡融合发展不懈努力。&nbsp;</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 甘肃省农业农村厅政改处、农经站，西安市农业农村局政改处、西安市农村产权流转交易服务中心（市农经站）等部门主要负责同志，西安城乡融合要素交易市场有限公司分管领导等参加活动。</span></p>', '101', 33, NULL, '甘肃省农业农村厅一行来访考察调研', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
