INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1025, '贵州省农业农村厅调研灞桥区城乡融合要素交易市场', 999999, '1902617287011176449', 'sjfby', '882', '2025-03-20 15:02:30', '2025-03-20 15:04:53', '2025-03-20 15:02:30', '2', 'sjfby', '西安城乡融合要素交易市场', '省级发布员', '100,101', '0', 0, 0, 0, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月6日，贵州省农业农村厅政策与改革处处长王心猛带队，赴灞桥区城乡融合要素交易市场考察调研。陕西省农业农村厅、西安市农业农村局、灞桥区农业农村局、西安市城乡融合要素交易市场、西安白鹿原现代农旅投资集团有限公司、秦农银行等部门相关负责同志陪同调研。</span></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\"><strong><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/21/427951f240c24309b731fd6ba08f6357.jpg\" width=\"1000\" /></strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 调研会上，<strong>双方围绕西安城乡融合要素交易市场的政策支持、体系搭建、平台建设、运营模式以及配套服务等方面进行了深入的交流讨论，考察了灞桥公司在政府主导、纪委推动下，促进村集体增收节支、增加农民收入方面取得的显著成绩，进一步了解了灞桥区在农村集体&ldquo;三资&rdquo;管理、推进城乡融合发展方面的主要做法和工作成效。</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 王心猛同志针对区交易市场去年交易及收入情况进行了咨询，详细询问了交易鉴证书的金融属性以及为农民增收的相关信息。他表示，灞桥区在农村集体&ldquo;三资&rdquo;管理方面思路清晰、措施有力、成效显著，城乡融合要素交易平台在促进城乡资源优化配置、盘活农村闲置资源资产、规范农村&ldquo;三资&rdquo;交易方面发挥着不可替代重要作用，值得贵州借鉴和学习。他强调，<strong>贵州省各级农业农村部门要认真学习借鉴灞桥区的先进经验，结合自身实际，积极探索创新，加快推进<span class=\"\">农村集体产权制度改革</span>，加强农村集体资产清产核资，完善农村集体&ldquo;三资&rdquo;监管平台建设，不断提升农村集体&ldquo;三资&rdquo;管理水平，为全面推进乡村振兴、推进城乡融合发展提供有力支撑。</strong></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<section>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 未来，西安城乡融合要素交易市场将继续深化与各地的合作交流，不断创新和完善服务模式，更好地服务于广大农民，助力农村经济的发展，为实现城乡融合发展贡献力量。</span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 贵州省农经站、陕西省农业农村厅政改处、陕西省农经站、西安市农经站、灞桥区经管站、西安城乡融合要素交易市场有限公司领导、分管负责同志等参加会议。</span></p>\n</section>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p>', '101', 41, NULL, '贵州省农业农村厅调研灞桥区城乡融合要素交易市场\n', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1026, '周至县楼观镇周一村开展“盘活集体资产 规范流转交易”宣贯座谈会', 999999, '1902618266326634497', 'sjfby', '882', '2025-03-20 15:06:56', '2025-03-20 15:09:57', '2025-03-20 15:06:56', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月7日，周至县楼观镇周一村村民委员会联合西安市公物拍卖行有限责任公司开展&ldquo;盘活集体资产 规范流转交易&rdquo;宣贯座谈会，县纪委监委、县财政局、县农业农村局、县农经站、楼观镇政府等相关部门负责人，周一村及西安城乡融合要素交易市场有限公司代表参加会议。</span></span></p>\n<p style=\"text-align: center;\"><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/20/c1795211a164475aaabdb3a0794cac4f.jpg\" width=\"1000\" height=\"500\" /></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会上，县财政局赵西平科长就周至县产业发展和财政衔接资金投入情况作专题汇报。周一村党支部书记齐平详细介绍了财政衔接资金使用、扶贫（衔接）项目资产管理、资产利用现状及闲置集体资产盘活等方面的工作进展和存在问题。西安城乡融合要素交易市场有限公司总经理贠涛以&ldquo;规范集体资产开发利用，用市场化手段打造城乡要素阳光交易&rdquo;为主题，系统介绍了平台建设运行情况，并通过农村&ldquo;<span class=\"\">四荒地</span>&rdquo;流转等典型成交案例，生动展示了平台在优化资源配置、促进村集体经济发展和农民增收方面的显著成效。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 各部门对西安城乡融合要素交易市场取得的成效给予了充分肯定。<strong>会议认为，西安城乡融合要素交易市场为周一村农村集体资产的盘活带来了一场&ldquo;及时雨&rdquo;，补齐了农村资源资产市场运营的短板，也为城乡融合发展建立了&ldquo;高速通道&rdquo;。</strong>通过建立规范、透明、公开的交易机制，在引导社会资本参与农村基础设施建设、产业发展等方面发挥了重要作用，为集体资源保值增值拓宽了渠道，也降低了基层廉政风险。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议强调，要充分发挥要素交易平台作用，切实做好周一村闲置资源资产盘活工作：<strong>一是要尽快摸清家底，强化专业评估</strong>，通过科学化、规范化手段准确评估资源资产价值，为后续管理和运营提供可靠数据支撑；<strong>二是加强政策学习</strong>，深入领会相关政策精神，确保政策执行不走样、不跑偏；<strong>三是加大宣传力度</strong>，多渠道、多形式推广要素交易市场的成功经验，同时加强基层干部业务培训，提升专业化管理水平；<strong>四是坚持因地制宜</strong>，立足本地资源禀赋、产业基础和发展需求，确保资源资产高效利用和产业可持续发展。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 周一村党支部书记齐平表示，此次交流研讨为盘活村内闲置资源资产指明了方向、厘清了思路。下一步，周一村将重点做好以下工作：<strong>一是强化宣传引导，</strong>通过专题培训、政策解读等形式，提升村干部和群众对资源资产盘活工作的认知度和参与度；<strong>二是先行先试，做好排头兵</strong>。在县财政局、县农业农村局等部门的指导下，稳妥开展业务工作；<strong>三是坚持示范引领</strong>，优先筛选条件成熟的资源资产进场交易，形成优质案例，为周边村镇做好示范引领。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会后，与会人员实地考察了帐篷营地、终南雅集民宿、温室育苗大棚及猕猴桃示范园、马岔村闲置村委会等重点项目。</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; </span></p>\n<p style=\"text-align: center;\">&nbsp;</p>', '101', 60, NULL, '周至县楼观镇周一村开展“盘活集体资产 规范流转交易”宣贯座谈会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1027, '要素交易市场党支部召开2024年度组织生活会', 999999, '1907012434952032257', 'sjfby', '882', '2025-03-20 15:10:31', '2025-03-20 15:12:19', '2025-03-20 15:10:31', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '500,501', '0', 0, 1, 0, '<p>&nbsp; <span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/20/7f1ce62b5e2c4d82b79c6d61640b310f.jpg\" width=\"1000\" height=\"500\" /></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月12日，要素交易市场党支部召开2024年度组织生活会，全体党员参加会议。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议专题学习了《习近平总书记关于党的建设重要思想概论》相关章节，通报了党支部上年度工作开展情况和本次组织生活会会前准备情况。党支部班子作对照检查，主动接受批评。随后，全体党员逐一作个人对照检查，深入开展批评和自我批评。现场气氛既严肃认真又坦诚相见，达到了通过批评促进团结的目的。会议还开展了党员民主评议。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议强调，<strong>一是全体党员干部要加强理论学习</strong>，继续把学习贯彻习近平新时代中国特色社会主义思想作为首要政治任务，深入学习党的二十大及二十届三中全会精神，坚持常态化学习，常学常新、常悟常进。<strong>二是要始终把党的政治建设摆在首位</strong>，在学思践悟中强化实干担当，提高能力本领，围绕主责主业，激发工作的主动性与创造性，不断打开工作新局面。<strong>三是要抓实问题整改</strong>，以此次组织生活会为契机，对查摆梳理和点评的问题，明确整改措施，切实做好&ldquo;后半篇&rdquo;文章，为推动要素交易市场工作高质量发展提供坚实保障。</span></p>', '501', 51, NULL, '要素交易市场党支部召开2024年度组织生活会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1028, '共话城乡融合新机遇！西安城乡融合要素交易市场召开企业座谈会', 999999, '1902619660630724610', 'sjfby', '882', '2025-03-20 15:12:40', '2025-03-20 15:14:13', '2025-03-20 15:12:40', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p style=\"text-align: center;\"><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/20/c93dfba8dc70485e98febe94a18d74e6.jpg\" width=\"1000\" height=\"500\" /></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\"><span style=\"font-family: verdana, geneva;\"><span style=\"font-size: 14pt;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; </span><span style=\"font-size: 18pt;\">为进一步加强企业用户对平台的了解，提前布局合作机会，3月18日，西安城乡融合要素交易市场特别邀请了16家商业运营企业和秦农银行召开座谈交流会。西安城乡融合要素交易市场有限公司分管领导、子（分）公司负责人等参加会议。</span></span></span></span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 首先，参会企业观看了西安城乡融合要素交易市场宣传片，详细了解了平台的建设背景、交易流程以及多个重点交易案例。随后，子（分）公司负责人逐一介绍了储备项目的基本情况，与会企业代表们围绕当前业务需求、面临的挑战以及未来合作方向展开了热烈讨论。</span></p>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 座谈会上，企业代表们对西安城乡融合要素交易平台的工作成果给予了高度评价。<strong>大家一致认为，平台为西安市城中村商业运营开辟了全新模式，注入了强劲动能。通过规范化、透明化的操作流程，不仅提升了交易效率，保障了各方权益，还为闲置集体资产的盘活提供了有效途径，有力推动了城乡融合发展。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 下一步，西安城乡融合要素交易市场将充分发挥平台优势，整合各方资源，推动成立企业联盟，共享安置房资源、政策信息以及运营管理经验，为盘活城中村改造后的闲置集体资产提供新动力。同时，<strong>平台将会针对不同资源类型不定期在各分子公司召开专场推介会，与优质企业建立长期稳定的合作关系，探索更多合作新模式</strong>，携手推动农村经济社会发展，为城乡融合发展贡献更大力量。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 此次座谈会不仅加深了企业对平台的了解，更为未来的合作奠定了坚实基础。西安城乡融合要素交易市场将继续秉持开放、共享的理念，与各方携手共进，共同书写城乡融合发展的新篇章！</span></p>', '101', 87, NULL, '共话城乡融合新机遇！西安城乡融合要素交易市场召开企业座谈会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1029, '党建引领 | 要素交易市场党支部开展“党旗飘扬映初心 绿色行动践使命”主题党日活动', 999999, '1902621022311518210', 'sjfby', '882', '2025-03-20 15:14:34', '2025-03-20 15:19:42', '2025-03-20 15:14:34', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '500,501', '0', 0, 1, 0, '<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/20/ee262cc3e5cc4a84ac7723c0e3da8af6.jpg\" width=\"1000\" height=\"700\" /><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 春山可望，绿意渐浓，为深入学习贯彻习近平生态文明思想，3月19日，西安城乡融合要素交易市场有限公司党支部在长安区杜曲街道杜北村开展了&ldquo;党旗飘扬映初心 绿色行动践使命&rdquo;主题党日活动，支部全体党员、中层以上干部参加活动。</span></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 当日上午，参加活动人员手持铁锹列队而立，党支部书记发出动员令：<strong>&ldquo;每一锹土、每一棵树，都是践行&lsquo;两山理论&rsquo;的生动注脚！&rdquo;</strong>杜北村相关林业人员现场演示植树要领，5分钟的&ldquo;田间党课&rdquo;将生态理念转化为具体行动。</span></p>\n<section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 活动现场，气氛十分热烈，大家充分发挥团结协作精神，各小组分工明确、紧密配合，从移苗入坑、挥锹铲土、培土围堰到提水浇灌，每一道工序都完成得认真细致、有条不紊。活动总共种植红叶李树木50余棵，一株株新栽的树苗在春日里昂首挺立，错落有致，为杜北村的初春土地孕育了勃勃生机。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n</section>\n</section>\n</section>\n<p>&nbsp;</p>\n</section>\n</section>', '501', 77, NULL, '党建引领 | 要素交易市场党支部开展“党旗飘扬映初心 绿色行动践使命”主题党日活动', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
