INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1031, '招商推介活动一（长安区安置房）', 999999, '1903023763244552193', 'sjfby', '882', '2025-03-21 09:02:07', '2025-03-21 09:04:43', '2025-03-21 09:02:07', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 14pt;\">&nbsp; &nbsp; &nbsp; &nbsp;<img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/21/94388ccae0784238be2bbfa7190be22c.jpg\" width=\"1000\" height=\"500\" /></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 14pt;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月20日，城乡融合要素交易市场 （西安长安）有限公司召开安置房商铺资源现场推介活动。意向商业运营企业负责人，长安区郭杜街道温国堡社区、西崔家庄社</span></span><span style=\"font-size: 18pt;\">区、皇子坡社区、枣园社区等社区主要领导，西安城乡融合要素交易市场有限公司主要领导、分管领导以及子公司负责人等参加活动。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <span style=\"background-color: #ffffff;\"><strong>活动现场，各社区负责人详细介绍了商铺的建筑面积、周边环境、配套设施、交通状况及社区的未来发展规划。</strong>意向企业负责人实地查看了商铺情况，并就商铺的潜在商业价值、业态定位及未来发展前景等展开了深入的探讨和交流。</span></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 参与活动的企业纷纷表示，本次推介会展示的项目资源质量高、潜力大、特点鲜明，非常符合他们的投资需求。多家企业表示，接下来将积极与各社区以及长安城乡融合要素交易市场对接，进一步深化合作，共同开发利用这些优质资源。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<span style=\"background-color: #ffffff;\"> 村集体对这次活动赞誉有加，言谈间满是认可，<strong>他们一致认为，此次活动成效显著，吸引了众多优质商业运营商纷至沓来。这不仅为村集体发展注入了全新活力，更为盘活村集体长期闲置的资源资产开辟了广阔前景，堪称意义非凡，影响深远。更是殷切期望今后多组织类似活动。</strong></span></span><strong><span style=\"background-color: #ffffff;\"><span style=\"font-size: 18pt; background-color: #ffffff;\">&nbsp;</span></span></strong></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"background-color: #ffffff;\">本次推介会的成功举办，<strong>不仅有效促进了各方之间的信息共享与经验交流，还实现了供需双方的有效衔接，达成了互利共赢的良好局面。通过此次活动，为社区、与企业之间的合作搭建了&ldquo;桥梁&rdquo;，为项目未来的发展奠定了坚实基础。</strong></span></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 下一步，西安城乡融合要素交易市场有限公司将继续在各区县、开发区，针对不同的资源类型开展现场推介活动。公司将充分利用平台的信息集聚和资源聚合功能，发挥好供需之间的引导衔接作用，不断探索更多创新合作模式，推进城乡共同繁荣发展。</span></p>', '101', 180, NULL, '安置房商铺资源推介活动成功举办', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1032, '铜川市耀州区农业农村局一行赴灞桥区城乡融合要素交易市场考察调研', 999999, '1903975531453452290', 'sjfby', '882', '2025-03-24 08:59:08', '2025-03-24 09:01:57', '2025-03-24 08:59:08', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/24/7d98b05f155f43fd9c920e401731259d.jpg\" width=\"1000\" height=\"500\" /></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3月21日，铜川市耀州区农业农村局组织相关科室负责人及业务骨干赴西安市灞桥区城乡融合要素交易市场进行考察学习,白鹿原现代农旅投资集团有限公司、灞桥区相关部门负责人陪同调研。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 考察组实地参观考察了城乡融合要素交易市场的运营情况，详细了解了市场在土地流转、产权交易、金融服务、农业产业化等方面的创新做法和成功经验。在随后的座谈会上，灞桥区城乡融合要素交易市场介绍了市场建设的背景、运营模式及取得的成效。双方就如何推动城乡融合发展、优化农村资源配置、提升农业产业化水平等议题进行了深入交流。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 耀州区农业农村局负责人表示，<strong>灞桥区的经验对耀州区具有重要的借鉴意义，此次考察学习不仅拓宽了视野，也为耀州区农业农村局下一步工作提供了新思路，未来将结合本地实际，探索适合耀州区的城乡融合发展路径，进一步推动农业农村现代化。</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; </span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 双方表示，<strong>将以此次交流为契机，进一步加强合作，共同推动城乡融合发展，助力乡村振兴战略实施。</strong></span></p>', '101', 35, NULL, '铜川市耀州区农业农村局一行赴灞桥区城乡融合要素交易市场考察调研', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1033, '2025年中央一号文件公布', 999999, NULL, 'sjfby', '882', '2025-03-24 10:41:13', '2025-03-24 10:49:14', '2025-03-24 10:41:13', '2', 'sjfby', '新华社', '省级发布员', '300,301', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>中共中央国务院关于进一步深化农村改革&emsp;扎实推进乡村全面振兴的意见</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">（2025年3月23日）</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 实现中国式现代化，必须加快推进乡村全面振兴。当前，农业再获丰收，农村和谐稳定，同时国际环境复杂严峻，我国发展面临的不确定难预料因素增多。越是应对风险挑战，越要夯实&ldquo;三农&rdquo;工作基础。做好2025年及今后一个时期&ldquo;三农&rdquo;工作，要坚持以习近平新时代中国特色社会主义思想为指导，全面贯彻落实党的二十大和二十届二中、三中全会精神，深入贯彻落实习近平总书记关于&ldquo;三农&rdquo;工作的重要论述和重要指示精神，坚持和加强党对&ldquo;三农&rdquo;工作的全面领导，完整准确全面贯彻新发展理念，坚持稳中求进工作总基调，坚持农业农村优先发展，坚持城乡融合发展，坚持守正创新，锚定推进乡村全面振兴、建设农业强国目标，以改革开放和科技创新为动力，巩固和完善农村基本经营制度，深入学习运用&ldquo;千万工程&rdquo;经验，确保国家粮食安全，确保不发生规模性返贫致贫，提升乡村产业发展水平、乡村建设水平、乡村治理水平，千方百计推动农业增效益、农村增活力、农民增收入，为推进中国式现代化提供基础支撑。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 一、持续增强粮食等重要农产品供给保障能力</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （一）深入推进粮油作物大面积单产提升行动。稳定粮食播种面积，主攻单产和品质提升，确保粮食稳产丰产。进一步扩大粮食单产提升工程实施规模，加大高产高效模式集成推广力度，推进水肥一体化，促进大面积增产。加力落实新一轮千亿斤粮食产能提升任务。多措并举巩固大豆扩种成果，挖掘油菜、花生扩种潜力，支持发展油茶等木本油料。推动棉花、糖料、天然橡胶等稳产提质。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二）扶持畜牧业稳定发展。做好生猪产能监测和调控，促进平稳发展。推进肉牛、奶牛产业纾困，稳定基础产能。落实灭菌乳国家标准，支持以家庭农场和农民合作社为主体的奶业养殖加工一体化发展。严格生猪屠宰检疫执法监管，强化重大动物疫病和重点人畜共患病防控。提升饲草生产能力，加快草原畜牧业转型升级。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三）强化耕地保护和质量提升。严格耕地总量管控和&ldquo;以补定占&rdquo;，将各类耕地占用纳入占补平衡统一管理，确保省域内年度耕地总量动态平衡。完善补充耕地质量评价和验收标准。持续整治&ldquo;大棚房&rdquo;、侵占耕地&ldquo;挖湖造景&rdquo;、乱占耕地建房等问题，坚决遏制破坏耕地违法行为。制定基本农作物目录，建立耕地种植用途监测体系。分类有序做好耕地&ldquo;非粮化&rdquo;整改，结合产业发展实际、作物生长周期等设置必要的过渡期。高质量推进高标准农田建设，优化建设内容，完善农民全过程参与项目实施机制，强化工程质量全流程监管。稳步推进盐碱地综合利用试点，加强东北黑土区侵蚀沟、南方酸化退化耕地治理。分类推进撂荒地复垦利用。在确保省域内耕地保护任务不降低前提下，稳妥有序退出河道内影响行洪安全等的不稳定耕地。加强传统梯田保护。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （四）推进农业科技力量协同攻关。以科技创新引领先进生产要素集聚，因地制宜发展农业新质生产力。瞄准加快突破关键核心技术，强化农业科研资源力量统筹，培育农业科技领军企业。深入实施种业振兴行动，发挥&ldquo;南繁硅谷&rdquo;等重大农业科研平台作用，加快攻克一批突破性品种。继续推进生物育种产业化。推动农机装备高质量发展，加快国产先进适用农机装备等研发应用，推进老旧农机报废更新。支持发展智慧农业，拓展人工智能、数据、低空等技术应用场景。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （五）加强农业防灾减灾能力建设。强化气象为农服务，加强灾害风险监测预警预报，用好区域农业社会化服务中心等力量，最大程度减轻灾害损失。建设现代化防洪减灾体系，全面开展病险水库除险加固，加强中小河流治理和大中型灌区建设改造，推进蓄滞洪区关键设施建设和管理机制改革。加强平原涝区治理，推进农田水利设施建设和沟渠整治，加快修复灾毁农田及灌排设施。加强农田防护林建设。做好农作物病虫害监测预警和统防统治。提升森林草原防灭火能力。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （六）健全粮食生产支持政策体系。落实稻谷、小麦最低收购价政策，完善玉米大豆生产者补贴、稻谷补贴政策，稳定耕地地力保护补贴政策。降低产粮大县农业保险县级保费补贴承担比例，推动扩大稻谷、小麦、玉米、大豆完全成本保险和种植收入保险投保面积。鼓励地方开展粮油种植专项贷款贴息试点。健全粮食主产区奖补激励制度，加大对产粮大县支持。启动实施中央统筹下的粮食产销区省际横向利益补偿，做好资金筹集和分配。逐步扩大产粮大县公共服务能力提升行动实施范围。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （七）完善农产品贸易与生产协调机制。综合施策推动粮食等重要农产品价格保持在合理水平，稳定市场供需，保护种粮农民积极性，维护好农民利益。严厉打击农产品走私等违法行为。加强口岸生物安全体系建设。健全农产品产业损害预警体系。有序做好粮食收购，强化储备粮监管。加强农产品市场信息发布和预期引导。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （八）构建多元化食物供给体系。践行大农业观、大食物观，全方位多途径开发食物资源。加强蔬菜应急保供基地建设，实施大中城市周边现代设施农业更新工程。促进渔业高质量发展，支持发展深远海养殖，建设海上牧场。发展森林食品，丰富&ldquo;森林粮库&rdquo;。推动食用菌产业提质增效，促进藻类食物开发。培育发展生物农业，开拓新型食品资源。加快建立粮食和大食物统计监测体系。强化食品安全和农产品质量安全监管，深化农产品药物残留治理，推进兽用抗菌药减量使用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （九）健全粮食和食物节约长效机制。扎实推进粮食节约和反食品浪费行动，建立健全激励约束机制。大力提倡健康饮食，加强公共食堂、餐饮机构等用油指导，推广减油减盐减糖和全谷物等膳食。推动粮食机收减损、适度加工和科学储存。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 二、持续巩固拓展脱贫攻坚成果</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十）守牢不发生规模性返贫致贫底线。持续巩固提升&ldquo;三保障&rdquo;和饮水安全保障成果，加强易地搬迁后续扶持，防止思想松懈、工作松劲。提升防止返贫致贫监测帮扶效能，及时将存在返贫致贫风险的农户纳入帮扶。深入开展防止返贫就业攻坚行动，增强帮扶车间就业吸纳能力，稳定脱贫人口务工规模和收入。按照巩固、升级、盘活、调整原则，分类推进帮扶产业提质增效。深入开展科技、医疗、教育干部人才&ldquo;组团式&rdquo;帮扶。加强消费帮扶平台企业和产品管理。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十一）统筹建立农村防止返贫致贫机制和低收入人口、欠发达地区分层分类帮扶制度。开展巩固拓展脱贫攻坚成果同乡村振兴有效衔接总体评估，研究制定过渡期后帮扶政策体系。统筹开展农村低收入人口及防止返贫致贫对象的识别认定。注重激发内生动力，强化对有劳动能力的农村低收入人口的开发式帮扶，加强社会救助兜底保障，支持欠发达地区振兴发展，建立分层分类帮扶制度，通过东西部协作、定点帮扶等机制给予差异化支持。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十二）健全脱贫攻坚国家投入形成资产的长效管理机制。全面清查脱贫攻坚国家投入形成资产，建立统一的资产登记管理台账。制定帮扶项目资产管理办法，健全资产形成、确权移交、管护运营、收益分配等全程监管制度，推动经营性资产保值增效、公益性资产持续发挥作用。完善资产分类处置制度，支持各地盘活低效闲置资产。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 三、着力壮大县域富民产业</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十三）发展乡村特色产业。坚持按市场规律办事，大力发展绿色、特色农产品种养，推进农产品加工业转型升级，实施农业品牌精品培育计划，打造特色农业产业集群，提升农业产业化水平。深入实施农村产业融合发展项目，培育乡村新产业新业态。推进乡村文化和旅游深度融合，开展文化产业赋能乡村振兴试点，提升乡村旅游特色化、精品化、规范化水平。加快构建农产品和农资现代流通网络，支持各类主体协同共建供应链。推动农村电商高质量发展。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十四）完善联农带农机制。健全新型农业经营主体扶持政策同带动农户增收挂钩机制，将联农带农作为政策倾斜的重要依据。引导企业、农民合作社、家庭农场与农户等紧密联合与合作，通过保底分红、入股参股、服务带动等方式，让农民更多分享产业增值收益。规范和引导农业农村领域社会投资，健全风险防范机制。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十五）拓宽农民增收渠道。引导农民发展适合家庭经营的产业项目，因地制宜发展庭院经济、林下经济、民宿经济。加大稳岗就业政策支持力度，强化就业服务和劳务协作，培育推介特色劳务品牌。推进家政兴农行动。加强大龄农民工就业扶持。推动农民工工资支付保障制度全面覆盖和有效运转，依法纠治各类欠薪问题。发展各具特色的县域经济，支持发展就业容量大的富民产业，促进农民就近就业增收。实施数字乡村强农惠农富农专项行动。扩大以工代赈项目实施规模，在重点工程项目和农业农村基础设施建设领域推广以工代赈。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 四、着力推进乡村建设</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十六）统筹县域城乡规划布局。顺应人口变化趋势，推动乡村全面振兴与新型城镇化有机结合，发挥县乡国土空间规划的空间统筹和要素保障作用，促进城乡产业发展、基础设施、公共服务一体化。提高村庄规划编制质量和实效，不得要求村庄规划编制全覆盖，对不需要编制的可在县乡国土空间规划中管控引导或出台通则式管理规定。合理确定村庄建设重点和优先序，统筹建设和管护，探索具有地域特色的乡村建设模式。在耕地总量不减少、永久基本农田布局基本稳定的前提下，开展以县域为统筹单元、以乡镇为基本实施单元的全域土地综合整治。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十七）推动基础设施向农村延伸。分类推进城乡供水一体化、集中供水规模化、小型供水规范化建设，有条件的地方可推行农村供水县域统管和专业化管护。实施好新一轮农村公路提升行动，开展农村公路及桥梁隧道风险隐患排查和整治，持续推动&ldquo;四好农村路&rdquo;高质量发展。巩固具备条件的乡镇和建制村通客车成果，改善农村水路交通出行条件，推进农村客货邮融合发展。深化快递进村，加强村级寄递物流综合服务站建设。深化县域商业体系建设，支持连锁经营布局县域市场，推动冷链配送和即时零售向乡镇延伸。推动农村消费品以旧换新，完善废旧家电等回收网络。巩固提升农村电力保障水平，加强农村分布式可再生能源开发利用，鼓励有条件的地方建设公共充换电设施。提升农村地区电信普遍服务水平。继续实施农村危房改造和农房抗震改造，做好受灾地区因灾倒损住房恢复重建。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十八）提高农村基本公共服务水平。优化区域教育资源配置，提升寄宿制学校办学条件和消防、安全等管理水平，办好必要的乡村小规模学校。全面加强农村义务教育学生营养改善计划管理，确保食品安全和资金规范使用。以人员下沉为重点推进紧密型县域医共体建设，提升中心乡镇卫生院服务能力，推动远程医疗服务体系建设。加强农村传染病防控和应急处置能力建设，深入开展全民健身和爱国卫生运动。健全基本医保参保长效机制，对连续参保和当年零报销的农村居民，提高次年大病保险最高支付限额。逐步提高城乡居民基本养老保险基础养老金。健全县乡村三级养老服务网络，开展县域养老服务体系创新试点，鼓励开展村级互助性养老服务。发展农村婴幼儿照护服务。扩大未成年人救助保护机构覆盖面，提升留守儿童和困境儿童关爱服务质量。加强残疾人保障和康复救助。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （十九）加强农村生态环境治理。深入打好农业农村污染治理攻坚战，持续推进农村人居环境整治提升，建设美丽乡村。健全农村改厕实施机制，完善社会化管护和服务体系。因地制宜选择农村生活污水治理模式，推动厕所粪污和生活污水协同治理，基本消除农村较大面积黑臭水体。推动农村生活垃圾源头减量、就地就近处理和资源化利用。强化农业面源污染突出区域系统治理，加强畜禽粪污资源化利用和水产养殖尾水处理。推进受污染耕地安全利用，加强农用地土壤重金属污染溯源和整治。支持秸秆综合利用，精准划定禁烧范围，依法依规落实禁烧管控要求。大力推进&ldquo;三北&rdquo;工程，强化资源协同和联防联治，提升防沙治沙综合治理效果。加强草原生态保护修复。推进生态清洁小流域建设。坚定推进长江十年禁渔，强化长江珍稀濒危物种拯救和重要栖息地生态修复。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 五、着力健全乡村治理体系</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十）加强农村基层党组织建设。深入推进抓党建促乡村振兴，坚持以党建引领基层治理，提高基层党组织领导能力。保持县级党政领导班子成员任期稳定，持续加强乡镇领导班子和干部队伍建设，着力解决部分年轻干部在农村基层&ldquo;水土不服&rdquo;问题。做好全国村&ldquo;两委&rdquo;换届，集中整顿软弱涣散村党组织，推动驻村第一书记和工作队担当作为。加强农村党员、干部教育培训。完善基层监督体系，严格村干部特别是&ldquo;一肩挑&rdquo;人员管理监督。进一步完善村务公开和民主议事制度，支持农民群众多渠道参与村级议事协商。持续深化整治乡村振兴领域不正之风和腐败问题，扎实开展对村巡察，细化完善新时代农村基层干部廉洁履行职责规范。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十一）持续整治形式主义为基层减负。全面建立乡镇（街道）履行职责事项清单，强化执行情况监督评估，健全动态调整机制。健全从县乡借调工作人员从严管控长效机制。严格控制对基层开展督查检查考核，精简优化涉农考核。巩固&ldquo;一票否决&rdquo;和签订责任状事项清理成果。清理整合面向基层的政务应用程序，持续深化整治&ldquo;指尖上的形式主义&rdquo;。推进农村基层网格化治理&ldquo;多格合一&rdquo;。通过&ldquo;减上补下&rdquo;等方式推动编制资源向乡镇（街道）倾斜，加强分类管理、统筹使用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十二）加强文明乡风建设。进一步加强新时代农村精神文明建设，强化思想政治引领，实施文明乡风建设工程，推动党的创新理论更加深入人心、社会主义核心价值观广泛践行。培育时代新风新貌，深化&ldquo;我为群众办实事&rdquo;等实践活动，推进和睦家庭与和谐邻里建设。加强民族团结进步宣传教育，铸牢中华民族共同体意识。建立优质文化资源直达基层机制，丰富农村文化服务和产品供给，创新开展&ldquo;戏曲进乡村&rdquo;等文化惠民活动，加强乡土文化能人扶持，引导群众性文体活动健康发展。推进传统村落特色保护区建设，加强乡村文化遗产保护传承和活化利用，深入实施乡村文物保护工程。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十三）推进农村移风易俗。推进农村高额彩礼问题综合治理，发挥妇联、共青团等组织作用，加强对农村适婚群体的公益性婚恋服务和关心关爱。加大对婚托婚骗等违法行为的打击力度。加强宗祠规范管理。深化殡葬改革，推进公益性生态安葬设施建设。持续整治人情攀比、大操大办、厚葬薄养、散埋乱葬等突出问题，完善约束性规范和倡导性标准。规范农村演出市场，深入整治低俗表演活动。加强农村科普阵地建设，反对封建迷信。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十四）维护农村稳定安宁。坚持和发展新时代&ldquo;枫桥经验&rdquo;，强化农村矛盾纠纷排查化解，完善信息共享、协同解决机制，营造积极健康的社会氛围。健全农村地区扫黑除恶常态化机制，防范遏制&ldquo;村霸&rdquo;、家族宗族黑恶势力滋生蔓延。加强农村宗教事务管理。深入打击整治农村赌博，筑牢农村禁毒防线，严厉打击涉农领域传销、诈骗等经济犯罪。健全农村应急管理组织体系，强化农村道路交通、燃气、消防、自建房等领域安全风险源头管控和排查整治。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 六、着力健全要素保障和优化配置体制机制</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十五）稳定和完善农村土地承包关系。坚持&ldquo;大稳定、小调整&rdquo;，有序推进第二轮土地承包到期后再延长三十年试点，扩大整省试点范围，妥善化解延包中的矛盾纠纷，确保绝大多数农户承包地总体顺延、保持稳定。健全承包地经营权流转管理服务制度，不得通过下指标、定任务等方式推动土地流转。鼓励通过发布流转价格指数、实物计租等方式，推动流转费用稳定在合理水平。培育新型农业经营主体，提高农业社会化服务质效，增强带动农户能力。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十六）管好用好农村资源资产。扎实做好房地一体宅基地确权登记颁证。探索农户合法拥有的住房通过出租、入股、合作等方式盘活利用的有效实现形式。不允许城镇居民到农村购买农房、宅基地，不允许退休干部到农村占地建房。有序推进农村集体经营性建设用地入市改革，健全收益分配和权益保护机制。因地制宜发展新型农村集体经济，不对集体收入提硬性目标，严控集体经营风险和债务。持续深化农村集体资金、资产和资源管理专项治理。推进新增耕地规范管理和合理利用。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十七）创新乡村振兴投融资机制。优先保障农业农村领域一般公共预算投入，强化绩效管理激励约束。加大中央预算内投资、超长期特别国债和地方政府专项债券对农业农村领域重大项目建设的支持力度。运用再贷款、再贴现、差别化存款准备金率等货币政策工具，推动金融机构加大对乡村振兴领域资金投放。支持金融机构发行&ldquo;三农&rdquo;专项金融债券。鼓励符合条件的企业发行乡村振兴债券。深入推进农村信用体系建设，加强涉农信用信息归集共享。推广畜禽活体、农业设施等抵押融资贷款。坚持农村中小银行支农支小定位，&ldquo;一省一策&rdquo;加快农村信用社改革，稳妥有序推进村镇银行改革重组。健全多层次农业保险体系，支持发展特色农产品保险。严厉打击农村各类非法金融活动。加强涉农资金项目全过程监管，着力整治骗取套取、截留挪用惠农资金等问题。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十八）完善乡村人才培育和发展机制。实施乡村振兴人才支持计划，加强农民技术技能培训，壮大农村各类专业人才和实用人才队伍。推进乡村工匠培育工程。以产业需求为导向，优化调整涉农学科专业。提升涉农职业教育水平，鼓励职业学校与农业企业等组建产教联合体。扎实推进&ldquo;三支一扶&rdquo;计划、科技特派员、特岗计划、大学生志愿服务西部计划等基层服务项目。深入实施乡村巾帼追梦人计划和乡村振兴青春建功行动。支持科技小院扎根农村助农惠农。继续实施大学生乡村医生专项计划。鼓励和引导城市人才服务乡村，健全评聘激励机制。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （二十九）统筹推进林业、农垦和供销社等改革。深化集体林权制度改革，调整优化林木采伐管理制度，赋予权利人更加充分的林木处置权和收益权。深化农垦改革，健全资产监管和公司治理等体制机制。完善国有农用地权利体系，促进规范管理利用。推进供销社综合改革。深化农业水价综合改革和用水权改革，加强取用水管理，持续推进地下水超采治理，推广运用节水灌溉技术。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （三十）健全农业转移人口市民化机制。推动转移支付、新增建设用地指标、基础设施建设投资等与农业转移人口市民化挂钩。完善全国公开统一的户籍管理政务服务平台，推行由常住地登记户口提供基本公共服务制度。鼓励有条件的城市逐步将稳定就业生活的农业转移人口纳入城市住房保障政策范围。进一步提高农业转移人口义务教育阶段随迁子女在流入地公办学校就读比例。全面取消在就业地参保户籍限制。依法维护进城落户农民的土地承包权、宅基地使用权、集体收益分配权，探索建立自愿有偿退出的办法。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 办好农村的事，实现乡村全面振兴，关键在党。必须坚持不懈把解决好&ldquo;三农&rdquo;问题作为全党工作重中之重，夯实五级书记抓乡村振兴政治责任，完善城乡融合发展体制机制，完善强农惠农富农支持制度，推动学习运用&ldquo;千万工程&rdquo;经验走深走实，健全推进乡村全面振兴长效机制。保持历史耐心，尽力而为、量力而行，集中力量抓好办成一批重点实事，让农民群众可感可及、得到实惠。全面落实&ldquo;四下基层&rdquo;制度，走好新时代党的群众路线，密切党群干群关系。坚持从农村实际出发，充分尊重农民意愿，改进工作方式方法，防止政策执行简单化和&ldquo;一刀切&rdquo;。鼓励各地实践探索和改革创新，充分调动广大党员干部和农民群众积极性，激发乡村全面振兴动力活力。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 让我们更加紧密团结在以习近平同志为核心的党中央周围，坚定信心、攻坚克难，真抓实干、久久为功，加快农业农村现代化步伐，推动农业基础更加稳固、农村地区更加繁荣、农民生活更加红火，朝着建设农业强国目标扎实迈进。</span></p>', '301', 36, NULL, '2025年中央一号文件公布', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1034, '国务院关于《陕西省国土空间规划  （2021—2035年）》的批复', 999999, NULL, 'sjfby', '882', '2025-03-24 10:51:13', '2025-03-24 10:52:40', '2025-03-24 10:51:13', '2', 'sjfby', '中央人民政府', '省级发布员', '300,301', '0', 0, 0, 0, '<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>国务院关于《陕西省国土空间规划</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>（2021&mdash;2035年）》的批复</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">国函〔2024〕18号</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">陕西省人民政府、自然资源部：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 你们关于报请批准《陕西省国土空间规划（2021&mdash;2035年）》的请示收悉。现批复如下：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 一、原则同意自然资源部审查通过的《陕西省国土空间规划（2021&mdash;2035年）》（以下简称《规划》）。</strong>《规划》是陕西省空间发展的指南、可持续发展的空间蓝图，是各类开发保护建设活动的基本依据，请认真组织实施。陕西省地处我国内陆腹地，是中华民族和华夏文明重要发祥地之一，是新时代西部大开发、共建&ldquo;一带一路&rdquo;的重要节点地区。《规划》实施要坚持以习近平新时代中国特色社会主义思想为指导，全面贯彻落实党的二十大精神，完整、准确、全面贯彻新发展理念，坚持以人民为中心，统筹发展和安全，促进人与自然和谐共生，奋力谱写中国式现代化建设陕西篇章。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>二、筑牢安全发展的空间基础。</strong>到2035年，陕西省耕地保有量不低于4326.00万亩，其中永久基本农田保护面积不低于3377.60万亩；生态保护红线面积不低于4.86万平方千米；城镇开发边界扩展倍数控制在基于2020年城镇建设用地规模的1.3倍以内；单位国内生产总值建设用地使用面积下降不少于40%；用水总量不超过国家下达指标，其中2025年不超过107.0亿立方米。明确自然灾害风险重点防控区域，划定洪涝等风险控制线，落实战略性矿产资源、历史文化保护等安全保障空间，全面锚固高质量发展的空间底线。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 三、构建支撑新发展格局的国土空间体系。</strong>深入实施区域协调发展战略、区域重大战略、主体功能区战略、新型城镇化战略和乡村振兴战略，促进形成主体功能明显、优势互补、高质量发展的国土空间开发保护新格局。深度融入黄河流域生态保护和高质量发展、新时代西部大开发等国家重大战略，加强关中平原城市群与成渝地区双城经济圈以及长江中游、中原、山西中部、兰州&mdash;西宁等城市群协同发展，加快形成面向中亚南亚西亚国家的重要对外开放通道，打造内陆改革开放高地。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>四、系统优化国土空间开发保护格局。</strong>发挥区域比较优势，优化主体功能定位，细化主体功能区划分，完善差别化支持政策。以陕北长城沿线旱作区和黄土高原旱作区、关中灌区、汉中盆地为重点，拓展特色农产品生产空间，夯实粮食安全基础。健全秦岭常态化长效化保护机制，筑牢秦岭&mdash;大巴山、陕北黄土高原&mdash;长城沿线生态屏障，实施江河源头、森林、河湖、湿地系统保护修复，加强珍稀野生动植物和古树名木保护。加强黄河流域生态保护修复，推进水土流失、荒漠化综合治理，守护好黄河母亲河。严格河湖水域空间管控，实施地下水污染综合治理，强化南水北调中线水源地保护。积极培育西安都市圈，提升宝鸡、渭南、汉中、榆林等城市的综合承载能力，促进陕北、关中、陕南区域协调发展，分类优化乡村空间布局，加强传统村落和乡村特色风貌保护，提升城乡人居环境品质。发挥区位、科教和能源资源优势，统筹传统和新型基础设施空间布局，积极参与西部陆海新通道建设，打造国家重要科研和文教中心、高新技术产业和制造业基地。加强自然灾害防治，优化防灾减灾救灾设施区域布局，提高国土空间安全韧性。健全文化遗产与自然遗产空间保护机制，强化文化遗产与自然遗产整体保护和系统活化利用，加强秦岭、华山、黄帝陵、兵马俑等自然和文化遗产保护，加强延安宝塔等红色文化遗产传承保护，构建文化资源、自然资源、景观资源整体保护的空间体系。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>五、维护规划严肃性权威性。</strong>《规划》是对陕西省国土空间作出的全局安排，是省域国土空间保护、开发、利用、修复的政策和总纲，必须严格执行，任何部门和个人不得随意修改、违规变更。按照定期体检和五年一评估的要求，健全各级各类国土空间规划实施监测评估预警机制，将规划评估结果作为规划实施监督考核的重要依据。建立健全规划监督、执法、问责联动机制，实施规划全生命周期管理。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> 六、做好规划实施保障。</strong>陕西省人民政府要加强组织领导，明确责任分工，健全工作机制，完善配套政策措施。做好《规划》印发和公开，强化社会监督。组织完成地方各级国土空间总体规划、详细规划、相关专项规划编制工作，加快形成统一的国土空间规划体系，统筹国土空间开发保护。强化对水利、交通、能源、农业、信息、市政等基础设施以及公共服务设施、军事设施、生态环境保护、文物保护、林业草原等专项规划的指导约束，在国土空间规划&ldquo;一张图&rdquo;上协调解决矛盾问题，合理优化空间布局。建立健全国土空间规划委员会制度，发挥对国土空间规划编制实施管理的统筹协调作用。按照&ldquo;统一底图、统一标准、统一规划、统一平台&rdquo;的要求，完善国土空间规划&ldquo;一张图&rdquo;系统和国土空间基础信息平台，建设国土空间规划实施监测网络，提高空间治理数字化水平。自然资源部要会同有关方面根据职责分工，密切协调配合，加强指导、监督和评估，确保实现《规划》确定的各项目标和任务。各有关部门要坚决贯彻党中央、国务院关于&ldquo;多规合一&rdquo;改革的决策部署，不在国土空间规划体系之外另设其他空间规划。《规划》实施中的重大事项要及时请示报告。</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp; 国务院&nbsp; 　&nbsp; 　　　</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">2024年1月31日　</span></p>', '301', 29, NULL, '国务院关于《陕西省国土空间规划  （2021—2035年）》的批复', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
