INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1036, '西安市建设占用耕地占补平衡指标交易流程', 999999, NULL, 'sjfby', '882', '2025-03-24 17:44:57', '2025-03-24 17:50:14', '2025-03-24 17:44:57', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '300,302', '0', 0, 0, 0, '<p style=\"text-align: center;\"><strong><span style=\"font-size: 18pt;\">西安市建设占用耕地占补平衡指标交易流程</span></strong></p>\n<p><strong><span style=\"font-size: 18pt;\">一、政策依据</span></strong></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; 《中共陕西省委、陕西省人民政府关于进一步加强耕地保护和改进占补平衡的实施意见》(陕发〔2018〕9号）、《陕西省耕地占补平衡指标交易管理办法》（陕自然资发〔2023〕45号）、《西安市自然资源和规划局关于印发《西安市建设占用耕地占补平衡指标交易流程〉的通知》（市资源发〔2023〕173号）等。</span></p>\n<p><strong><span style=\"font-size: 18pt;\">二、指标交易来源</span></strong></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 根据《陕西省耕地占补平衡指标交易管理办法》（陕自然资发〔2023〕45号），省自然资源厅统一管理全省补充耕地指标各区县、开发区在符合补充耕地指标出让条件的前提下，可将富余的补充耕地指标通过西安城乡融合要素交易市场，以协议出让、公开挂牌等方式实现有偿出让，获取指标交易收入。</span></p>\n<p><strong><span style=\"font-size: 18pt;\">三、指标交易程序</span></strong></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 指标使用方通过西安城乡融合要素交易市场公开发布交易信息，征求意向受让方，交易双方确定后，持西安城乡融合要素交易市场出具的《耕地占补平衡指标交易确认函》，签订《补充耕地指标使用协议》。双方协议签订后10个工作日内报省自然资源厅审核备案，省厅审核后在耕地占补平衡监管系统中，将耕地占补平衡指标划转给市级资源规划部门，完成指标挂钩，出具《占补平衡信息确认单》。逾期不备案的，省厅不予将耕地占补平衡</span><span style=\"font-size: 18pt;\">指标划转使用方使用。</span></p>\n<p><strong><span style=\"font-size: 18pt;\">四、指标交易价格</span></strong></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 耕地占补平衡指标指导价按照《陕西省自然资源厅关于改进管理方式切实落实耕地占补平衡的通知》(陕自然资发〔2020〕27号）、省人民政府办公厅《印发关于加强耕地占补平衡管理十条措施的通知》（陕政办发〔2023〕22号）执行。</span></p>\n<p><strong><span style=\"font-size: 18pt;\">五、协议履行</span></strong></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;《补充耕地指标使用协议》签订后，指标产生方、指标使用方应严格履行协议，按照协议约定时间转付耕地开垦费，并承担相关法律责任.省级有规定的从其规定.</span></p>\n<p><span style=\"font-size: 18pt;\">西安市自然资源和规划局办公室&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2024年4月7日印发</span></p>', '302', 23, NULL, '西安市建设占用耕地占补平衡指标交易流程', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
