INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1043, '盘活沉睡资源 绘就振兴蓝图 | 首笔闲置农村住房和宅基地使用权交易项目成交一周年“回头望”', 999999, '1905082999885631490', 'sjfby', '882', '2025-03-27 10:17:51', '2025-03-27 10:22:45', '2025-03-27 10:17:51', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/03/27/af86234c69724e5ab0c1ffe86bbdd2ff.jpg\" width=\"1000\" />&nbsp;<span style=\"font-family: verdana, geneva;\"> &nbsp; &nbsp; 2025年3月26日，西安城乡融合要素交易市场有限公司举行全市首笔业务一周年&ldquo;回头望&rdquo;活动。西安城乡融合要素交易市场有限公司总经理贠涛、长安区王曲街道副主任陈冰、长安区王曲街道曙光村股份经济合作联合社理事长朱林林、西安国梦御秀投资发展有限公司执行董事李纬卜出席了此次活动。央广网、陕西日报、三秦都市报、西安日报等媒体记者参加。</span></span></p>\n<p>&nbsp;</p>\n<p data-pm-slice=\"0 0 []\"><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 2024年3月22日，西安城乡融合要素交易市场首笔交易业务&mdash;&mdash;闲置农村住房和宅基地使用权交易项目成功交易。项目涉及长安区8宗闲置农村住房，交易总面积达到1256.02㎡，成交总额206万元。盘活利用的闲置农村住房后续被开发打造为民宿项目，每宗预计带动周边农民就业2人，增加农民工资性年收入约3.6万元（3000元/人/月)。<strong>据了解，现已带动周边农民就业80人，增加村民工资性收入约130万元。</strong></span></p>\n<p data-pm-slice=\"0 0 []\">&nbsp;</p>\n<p><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 活动中，各方代表齐聚一堂。朱林林介绍了闲置农村住房的初始状况和当前的发展规划。李纬卜详细阐述了对闲置农村住房的整体规划设计及目前的建设情况。双方均表示，通过西安城乡融合要素交易市场，规范了交易过程，保障了双方利益。同时，希望项目能得到更多的政策支持，期盼更多&ldquo;新农人&rdquo;返乡创业，带动&ldquo;家门口&rdquo;就业。</span></p>\n<p>&nbsp;</p>\n<p data-pm-slice=\"0 0 []\"><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 贠涛表示，<strong>西安城乡融合要素交易市场的组建是贯彻落实党的二十届三中全会精神、践行习总书记关于&ldquo;健全城乡融合发展体制机制，完善城乡要素平等交换、双向流动的政策体系，促进城市资源要素有序向乡村流动，增强农业农村发展活力&rdquo;重要指示的生动实践，也是市委、市政府正确领导的一项改革成果。回首这段历程，曙光村首笔业务顺利成交，恰似破晓的第一缕曙光，为城乡融合要素交易市场照亮了前行的方向，开启了蓬勃发展的崭新篇章。后续我们将继续做好各项服务，充分发挥平台的信息集聚功能、价格发现功能和要素资源配置功能，通过平台实现资源资产价值变现、增加各方收益，走好&ldquo;政府搭台、企业运作、村集体壮大、村民受益&rdquo;的联新路。</strong></span></p>\n<p data-pm-slice=\"0 0 []\">&nbsp;</p>\n<p data-pm-slice=\"0 0 []\"><span style=\"font-size: 18pt; font-family: verdana, geneva;\">&nbsp; &nbsp; &nbsp; 随后，参会人员实地观摩项目现场。媒体记者在现场对交易主体和要素市场进行了深入采访，详细了解了项目的交易过程、建设过程，以及一年来要素市场对这宗交易的关注和帮助。他们感慨，这种&ldquo;保姆式&rdquo;的贴心服务能为村集体和社会资本方带来更好的体验，也为城乡融合发展注入了一股强大且温暖的力量。</span></p>', '101', 47, NULL, '盘活沉睡资源 绘就振兴蓝图 | 首笔闲置农村住房和宅基地使用权交易项目成交一周年“回头望”', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1044, '西安城乡要素交易市场一周年成交近15亿元', 999999, NULL, 'sjfby', '882', '2025-03-27 17:51:36', '2025-03-27 17:55:33', '2025-03-27 17:51:36', '2', 'sjfby', '三秦都市报', '省级发布员', '100,101', '0', 0, 0, 0, '<h2 id=\"title\" class=\"title\" style=\"text-align: center;\" data-v-f8b0aa2c=\"\"><strong><span style=\"font-size: 18pt;\">西安城乡要素交易市场一周年成交近15亿元</span></strong></h2>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2024年3月26日，西安市城乡融合发展迎来重要里程碑&mdash;&mdash;西安城乡融合要素交易市场成功完成全市首笔闲置农村住房和宅基地入市交易。一年后的今天，该市场举办&ldquo;首笔业务一周年&lsquo;回头望&rsquo;&rdquo;主题活动，交出了一份令人瞩目的成绩单：累计完成452宗交易，总成交额达14.75亿元。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 首笔交易的8户闲置农村房和宅基地位于长安区王曲街道江兆村及曙光村，总面积1256.02平方米，由西安国梦御秀投资发展有限公司竞得，用于打造农文旅融合项目。该项目创新性地将闲置农房改造为多功能艺术综合体&ldquo;艺术客厅&rdquo;，包含项目展示、商业运营、艺术展销和公共教育四大功能区块，预计明年秋季正式开放。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> &ldquo;艺术客厅不仅是一个文化空间，更是艺术赋能乡村振兴的重要实践。&rdquo;</strong>西安国梦御秀投资发展有限公司执行董事李纬卜介绍，<strong>该项目将实现艺术展示、商业运营和公益教育的有机融合，打造特色乡村艺术生态。目前，该项目已带动周边80名农民就业，新增工资性收入约130万元。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 长安区王曲街道曙光村股份经济合作联合社党支部书记朱林林表示：<strong>&ldquo;过去&lsquo;沉睡&rsquo;的资源通过规范入市被激活，真正实现了村民共享发展红利。&rdquo;</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>作为全国首家以&ldquo;城乡融合要素&rdquo;命名的专业交易平台，该市场一年来始终秉持&ldquo;以人为本、服务三农&rdquo;理念，通过市场化手段盘活农村资源。数据显示，平台已为村集体增收13.42亿元，带动农民增收9625.9万元，为相关单位创收664.02万元。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安城乡融合要素交易市场有限公司总经理贠涛表示：<strong>&ldquo;我们将继续发挥平台的信息集聚、价格发现和资源配置功能，走好&lsquo;政府搭台、企业运作、村集体壮大、村民受益&rsquo;的发展新路，为乡村振兴贡献更大力量。&rdquo;</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 这一创新实践不仅为西安市城乡融合发展探索出新路径，也为全国乡村振兴提供了可借鉴的&ldquo;西安样本&rdquo;。随着更多农村资源通过市场化途径实现价值变现，城乡要素流动将更加畅通，乡村振兴动能将更加强劲。</span></p>', '101', 31, NULL, '西安城乡要素交易市场一周年成交近15亿元', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1045, '农村宅基地相关【政策解读】', 999999, NULL, 'sjfby', '882', '2025-03-31 09:02:18', '2025-03-31 09:09:29', '2025-03-31 09:02:18', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '300,301', '0', 0, 0, 0, '<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>01</strong><strong>什么是农村宅基地？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 农村宅基地是指农村村民基于本集体经济组织成员身份而享有的可以用于修建住宅的集体建设用地，包括住房、附属用房和庭院等用地。在目前，农户无需交纳土地费用即可取得，具有福利性质和社会保障功能，一般不能继承。但宅基地上建成的房屋，则属于村民个人财产，可以依法继承。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>02</strong><strong>农村宅基地归谁所有？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 按照国家相关法律规定，宅基地的所有权属于农民集体所有。农户只拥有宅基地的使用权。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>03</strong><strong>&ldquo;一户一宅&rdquo;是指什么？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &ldquo;一户一宅&rdquo;是指农村村民一户只能拥有一处宅基地，其宅基地的面积不得超过当地政府规定的标准。人均土地少、不能保障一户拥有一处宅基地的，当地政府在充分尊重农村村民意愿的基础上，可以采取措施，按照规定的标准保障农村村民实现户有所居。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>04</strong><strong>现行农村宅基地产权制度的基本内容是什么？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 现行宅基地产权制度的基本内容是，农民集体拥有宅基地所有权,农村集体经济组织成员拥有<span class=\"\">宅基地使用权</span>，符合条件的农户具有分配宅基地的资格权。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>05</strong><strong>宅基地&ldquo;三权分置&rdquo;指什么？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 宅基地&ldquo;三权分置&rdquo;指宅基地所有权、资格权、使用权&ldquo;三权分置&rdquo;，即落实宅基地集体所有权，保障宅基地农户资格权和农民房屋财产权，适度放活宅基地和农民房屋使用权。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>06</strong><strong>进城落户的农民能否继续保留宅基地使用权？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 进城落户的农民可以依法保留其原来合法取得的宅基地使用权。国家坚持保障农民土地权益，不以退出承包地和宅基地作为农民进城落户条件，不强迫进城落户农民放弃其合法取得的宅基地使用权。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong>07</strong><strong>农村宅基地和农房能不能继承？</strong></span></p>\n</section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section></section>\n</section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 农村宅基地不能继承，农房可以依法继承。农村宅基地所有权、宅基地使用权和房屋所有权相分离，宅基地使用权人以户为单位，依法享有占有和使用宅基地的权利。在户内有成员死亡而农户存续的情况下，不发生宅基地继承问题。农户消亡时，权利主体不再存在，宅基地使用权灭失。同时，根据继承法的有关规定，被继承人的房屋作为其遗产由继承人继承。因房地无法分离，继承人继承房屋取得房屋所有权后，可以依法使用宅基地，但房屋坍塌的，继承人不能再使用宅基地。</span></p>\n</section>\n</section>\n</section>', '301', 9, NULL, '农村宅基地相关【政策解读】', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
