INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1055, '西安城乡融合要素交易市场有限公司召开业务培训会', 999999, '1907249861952114690', 'sjfby', '882', '2025-04-02 09:49:43', '2025-04-02 09:52:42', '2025-04-02 09:49:43', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/04/02/723f046f46764512acc54f221b75ace5.jpg\" width=\"1000\" />&nbsp; &nbsp; &nbsp; 4月1日，西安城乡融合要素交易市场有限公司组织召开业务培训会。会议由市公司分管领导主持，各子、分公司主要负责人、业务分管负责人及相关部门负责人共同参会。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会上，各子、分公司负责人围绕业务开展过程中的项目信息规范化表述、平台信息标准化录入等重点难点问题，展开深入交流。市公司分管领导以挂牌项目为例，结合项目实际情况，系统梳理当前各项操作中需完善和优化的环节，并提出具体指导意见。</span></p>\n<p>&nbsp;</p>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议强调，<strong>一要规范表述，统一标准。</strong>文字表述要意思明确、清晰易懂，内容呈现形式予以统一。<strong>二要细化审查，明确条件。</strong>明确项目的基本情况、重大事项纰漏等关键信息，建立唯一且明晰的受让方审核标准，为后续报名人的资格审核筑牢根基。<strong>三要严格审核，提高要求。</strong>子分公司负责人应强化责任意识，严格审核项目信息，从源头上把控风险，加强自我监督。</span></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 此次培训会系统梳理了要素交易市场业务开展过程中存在的问题，通过头脑风暴，为项目开展提供了更优路径。下一步，西安城乡融合要素交易市场将<strong>继续秉持专业、严谨的态度，不断优化业务流程，强化内部管理，提升服务质量。同时，加强与各子、分公司的沟通协作，形成合力，挖掘具备高增长潜力及持续盈利能力的优秀企业及项目，为推进城乡要素高效配置、服务乡村振兴战略注入新动能。</strong></span></section>', '101', 21, NULL, '西安城乡融合要素交易市场有限公司召开业务培训会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1056, '中共中央 国务院印发《乡村全面振兴规划（2024－2027年）》', 999999, NULL, 'sjfby', '882', '2025-04-03 09:05:00', '2025-04-03 09:10:21', '2025-04-03 09:05:00', '2', 'sjfby', '农地法实务', '省级发布员', '300,301', '0', 0, 0, 0, '<section></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 实施乡村振兴战略，是以习近平同志为核心的党中央着眼党和国家事业全局作出的重大决策，是新时代新征程&ldquo;三农&rdquo;工作的总抓手。为有力有效推进乡村全面振兴，制定本规划。</span></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>一、总体要求</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 坚持以习近平新时代中国特色社会主义思想为指导，深入贯彻党的二十大和二十届二中、三中全会精神，认真贯彻落实习近平总书记关于&ldquo;三农&rdquo;工作的重要论述，完整准确全面贯彻新发展理念，加快构建新发展格局，着力推动高质量发展，锚定建设农业强国目标，学习运用&ldquo;千万工程&rdquo;经验，健全推动乡村全面振兴长效机制，以确保国家粮食安全、确保农村人口不发生规模性返贫致贫为底线，巩固拓展脱贫攻坚成果，以提升乡村产业发展水平、提升乡村建设水平、提升乡村治理水平为重点，强化科技和改革双轮驱动，强化农民增收举措，扎实推进乡村产业、人才、文化、生态、组织&ldquo;五个振兴&rdquo;，加快农业农村现代化，推动农业全面升级、农村全面进步、农民全面发展，为全面建设社会主义现代化国家提供坚强支撑。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 工作中要做到：坚持和加强党对&ldquo;三农&rdquo;工作的全面领导，坚持农业农村优先发展，坚持城乡融合发展，坚持农民主体地位，坚持因地制宜、分类施策，坚持人与自然和谐共生，坚持深化改革创新，坚持循序渐进、久久为功。要充分调动亿万农民的积极性、主动性、创造性，一件事情接着一件事情办，一年接着一年干，积小胜为大成。要统筹不同区域，合理确定阶段性重点任务和推进时序，尊重客观规律，不超越发展阶段，不提脱离实际的目标；统筹新型工业化、新型城镇化和乡村全面振兴，缩小城乡差别，促进城乡共同繁荣发展；统筹推进农村经济建设、政治建设、文化建设、社会建设、生态文明建设和党的建设，实现乡村全面提升。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 主要目标是：到2027年，乡村全面振兴取得实质性进展，农业农村现代化迈上新台阶。国家粮食安全根基更加稳固，农业综合生产能力稳步提升，确保中国人的饭碗牢牢端在自己手中；乡村产业更加兴旺，实现乡村产业全链条升级；乡村更加生态宜居，人居环境明显改善，农村基础设施更加完备，城乡基本公共服务均等化水平不断提升；乡风文明持续提升，中华优秀传统文化充分传承发展，农民综合素质全面提高；乡村治理更加有效，乡村治理体系和治理能力现代化水平明显提升；农民生活更加美好、收入水平持续提高，农村低收入人口和欠发达地区分层分类帮扶制度基本建立。东部发达地区、中西部具备条件的大中城市郊区乡村率先基本实现农业农村现代化。到2035年，乡村全面振兴取得决定性进展，农业现代化基本实现，农村基本具备现代生活条件。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>二、优化城乡发展格局，分类有序推进乡村全面振兴</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（一）统筹优化城乡发展布局。</strong>以资源环境承载能力和国土空间开发适宜性评价为基础，优化农业、生态和城镇空间。严守耕地和永久基本农田、生态保护红线和城镇开发边界等主要控制线，科学编制实施县级国土空间总体规划。强化耕地保护，坚决整治乱占、破坏耕地等违法行为，严格落实省级党委和政府耕地保护责任，有序恢复耕地，逐步补足耕地保护任务缺口。改革完善耕地占补平衡制度，完善补充耕地质量验收机制，实施耕地有机质提升行动，确保耕地数量有保障、质量有提升。健全保障耕地用于种植基本农作物管理体系，绘制全国耕地种植用途&ldquo;一张图&rdquo;。优化乡村功能和空间布局，充分发挥乡村在保障农产品供给和粮食安全、保护生态环境、传承发展中华民族优秀传统文化等方面的特有功能。加强以&ldquo;七区二十三带&rdquo;为主体的农产品主产区建设，建设国家粮食安全产业带。筑牢&ldquo;三区四带&rdquo;生态安全屏障。保留乡村景观特色，赓续农耕文明。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二）推进城乡融合发展。</strong>实施新一轮农业转移人口市民化行动，推行由常住地登记户口提供基本公共服务制度，完善&ldquo;人地钱挂钩&rdquo;政策，推动城镇基本公共服务覆盖全部常住人口。保障进城落户农民合法土地权益，依法维护进城落户农民的土地承包权、宅基地使用权、集体收益分配权，探索建立自愿有偿退出的办法。构建城乡统一的建设用地市场。推动人才、技术等要素规范有序向乡村流动。率先在县域内破除城乡二元结构，一体推进城镇和乡村规划、建设和治理，推动城乡基本公共服务均等化。支持中西部农产品主产区县城建设。把乡镇建成乡村治理中心、农村服务中心、乡村经济中心。推动县域产业协同发展，以现代种养业和农</span><span style=\"font-size: 18pt;\">产品加工业为基础，构建以县城为枢纽、以小城镇为节点的县域经济体系。发挥各类产业园区带动作用，引导县域产业集聚发展。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（三）分类推进乡村全面振兴。</strong>细化村庄分类标准，科学确定发展目标。集聚提升类村庄重点强化产业发展，改造提升基础设施；城郊融合类村庄重点推动城乡一体化建设；特色保护类村庄重点改善基础设施和公共环境；搬迁撤并类村庄有序实施搬迁撤并，解决好民生保障、就业增收和生态保护等问题。短期内难以判断的村庄，留足观察和论证时间，重点保障基本民生需要。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> （四）衔接推进脱贫地区全面振兴。</strong>过渡期内保持主要帮扶政策总体稳定，抓好防止返贫监测，落实帮扶措施，完善覆盖农村人口的常态化防止返贫致贫机制，牢牢守住不发生规模性返贫致贫的底线。加快补齐脱贫地区农村基础设施短板，优先布局产业发展所需配套设施。推进脱贫地区帮扶产业高质量发展，构建成长性好、带动力强的帮扶产业体系，大力发展特色产业，推进消费帮扶，完善脱贫群众参与产业发展和分享收益机制。深化东西部协作、定点帮扶。推进携手促振兴行动，鼓励经济相对发达地区到脱贫地区共兴产业。推动帮扶政策体系向推进乡村全面振兴转换。建立农村低收入人口和欠发达地区分层分类帮扶制度，对有劳动能力的人口，落实产业就业等开发式帮扶措施；对缺乏劳动能力、无法通过产业就业获得稳定收入的人口，完善相关社会救助政策。健全脱贫攻坚国家投入形成资产的长效管理机制。加大对欠发达地区县域振兴发展的支持力度，创新帮扶协作机制。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>三、加快现代农业建设，全方位夯实粮食安全根基</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（五）提高粮食和重要农产品供给保障水平。</strong>把确保国家粮食安全作为现代农业建设的首要任务，全面落实粮食安全党政同责，坚持稳面积、增单产两手发力，确保粮食播种面积稳定在17.5亿亩左右、谷物面积14.5亿亩左右。组织实施新一轮千亿斤粮食产能提升行动，大力实施粮食单产提升工程，推动粮食产能稳步迈上1.4万亿斤台阶。深入实施国家大豆和油料产能提升工程，压实&ldquo;菜篮子&rdquo;市长负责制，树立大农业观、大食物观，农林牧渔并举，构建多元化食物供给体系。落实棉花目标价格政策，加强糖料、天然橡胶基地建设。健全市场监测预警体系，统筹做好粮食市场化收购和政策性收储，完善储备体系和制度建设，推进全链条节约减损，优化生猪、棉花、食糖市场调控机制，加强应急保供能力建设。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（六）加强农业基础设施建设。</strong>加大高标准农田建设投入力度，推动逐步把具备条件的永久基本农田建成高标准农田，提高建设标准和质量，完善建设、验收、管护机制，支持引导新型农业经营主体、农村集体经济组织等参与建设管护。探索耕地地力保护补贴发放与耕地保护责任落实相挂钩。深入实施国家黑土地保护工程，加快灌区建设改造。发展现代设施农业，推进设施种养业建设，完善烘干、物流等设施。加强农业生产防灾减灾救灾能力建设，加强农业气象灾害监测预警防控，实施动植物保护工程，健全农作物病虫害防治和动物防疫体系。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（七）强化农业科技和装备支撑。</strong>优化科技创新体系，稳定支持基础研究和公益性研究科研机构，建立健全科技创新分类评价制度。建设重大科技基础设施，加强原创性研究；加大关键核心技术攻关力度，强化技术集成应用；完善农技推广服务体系。加快种业振兴，建设种质资源保存鉴定等设施平台；加快核心种源技术攻关，强化企业科技创新主体地位，培育大型种业企业。实施农机装备补短板行动，加快大型高端智能农机和丘陵山区适用小型机械等农机装备和关键核心零部件研发应用；大力发展智慧农业；完善农机购置与应用补贴政策。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（八）加大粮食生产支持力度。</strong>健全种粮农民收益保障机制，完善粮食生产补贴，落实最低收购价政策，健全农资保供稳价应对机制，落实粮食作物完全成本和种植收入保险政策，鼓励有条件的省份实施差异化保费补贴政策，加大对产粮大县的支持力度。完善粮食主产区利益补偿机制，强化对粮食主产区的支持，合理安排产粮大县奖励资金规模，实施产粮大县公共服务能力提升行动，统筹建立粮食产销区省际横向利益补偿机制，建立健全稳定的协作关系。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>四、推动乡村产业高质量发展，促进农民收入增长</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（九）构建现代乡村产业体系。</strong>培育现代乡村产业，做好&ldquo;土特产&rdquo;文章，发展乡村种养业、加工流通业、休闲旅游业、乡村服务业。以农产品主产区和特色农产品优势区为重点，打造现代乡村产业走廊，实施农业品牌精品培育计划。优化产业链组织方式，培育农业产业化龙头企业，支持企业协同共建产业链供应链。建强产业发展载体，支持县域农村产业融合发展项目建设，发展优势特色产业集群。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十）深化农村一二三产业融合发展。</strong>实施农产品加工业提升行动，支持主产区建设加工产业园。完善流通骨干网络，改造提升批发市场，布局建设城郊大仓基地，实施农村电商高质量发展工程。有序发展农事体验等新业态，探索现代农业、休闲旅游、田园社区融合发展方式。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十一）强化农民增收举措。</strong>落实农民工稳岗就业政策和权益保障机制，加强技能培训和就业服务，支持返乡入乡创业，加大以工代赈项目实施力度。完善产业链利益联结机制，发展农业产业化联合体，引导企业与小农户加强利益联结，推动增值收益分配向农户倾斜。培育壮大新型农业经营主体，推动相关扶持政策同带动农户增收挂钩。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十二）全面促进农村消费。</strong>推进县域商业体系建设，健全电子商务和物流服务体系，建设县域集采集配中心，推动客货邮融合发展，加快消费扩容。加大面向农村的产品创新和营销力度，支持新能源汽车、绿色智能家电等下乡，深入实施消费品以旧换新行动，鼓励信息消费，推进消费升级。发展农村生活服务业，实施农村消费市场净化行动，完善消费配套设施。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>五、大力培养乡村人才，吸引各类人才投身乡村全面振兴</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十三）壮大乡村人才队伍。</strong>实施高素质农民培育计划和乡村产业振兴带头人培育&ldquo;头雁&rdquo;项目，加强对青年农民和新型农业经营主体培训指导，实施农村实用人才带头人培训计划。鼓励和引导青年入乡发展和就业创业，加强农业农村科技领军人才、青年人才培养，通过科技小院等形式，推动涉农教育与生产实践紧密结合。实施农技推广服务特聘计划，培养农技推广人才，壮大科技特派员队伍。建设乡村公共服务和治理人才队伍，实施乡村振兴人才支持计划和人才支撑项目，开展全科医生特岗计划、订单定向医学生免费培养等，推动乡村医生向执业（助理）医师转化，加强农村法律人才和儿童服务人才培养。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十四）完善乡村人才培养体系。</strong>健全涉农高等教育体系，优化提升职业教育，鼓励符合条件的村干部、农民等报考高职院校，继续实施&ldquo;一村一名大学生&rdquo;培育计划。健全涉农培训体系，统筹各类培训资源，实行按需培训。强化农村职业教育和成人教育，加强农村数字人才、电商人才培育。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十五）健全乡村人才保障机制。</strong>建立人才定期服务乡村制度，支持返乡人员、退役军人、退休专家等投身乡村全面振兴，健全县域人才统筹使用制度，推动科技、医疗、教育干部人才&ldquo;组团式&rdquo;帮扶，实施大学生志愿服务西部计划。建立健全乡村人才分级分类评价体系，引导各地实行职称评审定向评价、定向使用。激励各类人才投身乡村，做好返乡入乡人才服务保障工作，将符合条件的返乡创业就业人员纳入涉农培训范围。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>六、繁荣乡村文化，培育新时代文明乡风</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十六）提升乡村精神风貌。</strong>组织学习习近平新时代中国特色社会主义思想，广泛开展党史、新中国史、改革开放史、社会主义发展史宣传教育，深入开展&ldquo;听党话、感党恩、跟党走&rdquo;宣传教育活动。加强民族团结进步宣传教育，铸牢中华民族共同体意识。实施文明乡风建设工程，以社会主义核心价值观为引领，加强文明培育、文明实践、文明创建工作。做好普法、科普工作，反对封建迷信。开展诚信宣传教育，深化诚信缺失突出问题专项治理，加强青少年思想道德教育，强化未成年人网络保护工作。持续推进移风易俗，弘扬良好乡风家风民风，引导村民遵规守约，扎实开展高额彩礼、大操大办、散埋乱葬等突出问题治理，完善婚事新办、丧事简办、孝老爱亲等约束性规范和倡导性标准。加强农村公益性公墓（骨灰堂）建设，推进农村殡葬改革，培育现代文明殡葬新风。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十七）重塑乡村文化生态。</strong>优化文化服务和文化产品供给机制，深入开展文化科技卫生&ldquo;三下乡&rdquo;活动，健全城乡&ldquo;结对子、种文化&rdquo;常态化机制，推进乡村文化志愿服务，开展具有农耕农趣农味的群众性文化体育活动。完善公共文化服务体系，建立优质文化资源直达基层机制，实施文化惠民工程，探索建立群众文艺团队培育引导机制，开展乡村文化指导员相关工作，推动新时代文明实践中心（所、站）联动高效运转，推进农家书屋改革创新。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十八）增强乡村文化影响力。</strong>弘扬中华优秀传统文化，加强传统村落保护传承，强化文物和非物质文化遗产保护利用，实施传统工艺振兴工程，开展古树名木复壮及古树群保护。推进中国传统节日振兴，办好中国农民丰收节。培育壮大乡村文化产业，实施文化产业赋能乡村振兴计划、乡村文旅深度融合工程，提升乡村旅游质量效益，加快数字赋能乡村文化产业</span><span style=\"font-size: 18pt;\">。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>七、深入推进乡村生态文明建设，加快发展方式绿色转型</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十九）加快农业绿色低碳发展。</strong>推广绿色生产技术，鼓励测土配方施肥和增施有机肥，降低经济作物化肥施用强度，推进病虫害绿色防控与统防统治融合，发展节水旱作农业。强化农业面源污染防治，发展生态循环农业，推行农业废弃物减量化、资源化、无害化，加强对农业面源污染治理的监测评估。稳步推进农业减排固碳，推广免耕少耕播种技术，降低农业甲烷和畜禽养殖臭气排放，加快老旧农机报废更新和绿色技术装备应用，开展农业减排固碳技术攻关。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十）改善乡村生态环境。</strong>推进耕地草原森林河湖休养生息，完善耕地轮作休耕、草原保护等制度，实行天然林保护与公益林并轨管理，开展重点河湖治理修复。实施重要生态系统保护和修复重大工程，推进&ldquo;三北&rdquo;等重点生态工程建设，实施生物多样性保护工程，加强外来物种入侵防控，深入实施长江十年禁渔，加强水生野生动物保护。开展生态环境突出问题治理，推进河湖库&ldquo;清四乱&rdquo;，保护和修复小微湿地，推进地下水超采、水土流失、土壤重金属污染防治，建立农村生态环境监测评价制度。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十一）完善生态产品价值实现机制。</strong>落实自然资源资产权益，健全调查监测评价体系，开展生态产品总值核算，健全碳排放权、排污权、用水权交易机制。完善生态保护补偿制度，推进生态综合补偿，健全横向生态保护补偿机制，推进生态保护补偿市场化发展。开展乡村生态产品经营开发，打造生态产品区域公用品牌，加大绿色金融支持。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>八、建设宜居宜业和美乡村，增进农民福祉</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十二）推进基础设施提档升级。</strong>提高路网通达水平，加快农村公路骨干路网提档升级和基础网络延伸连通，深化推进&ldquo;四好农村路&rdquo;、城乡交通一体化高质量发展。强化供水安全保障，因地制宜推进城乡供水一体化、集中供水规模化发展，实施小型供水工程规范化建设和改造，加强中小型水源保障工程建设，实施水质提升行动。优化能源供给，巩固提升农村电网，发展清洁能源。提升农房质量安全水平，持续开展农村危房改造和农房抗震改造，推动现代宜居农房建设。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十三）持续改善人居环境。</strong>深入实施乡村建设行动。因地制宜扎实推进农村厕所革命，引导农民开展户内改厕，完善农村厕所建设管理制度。推进农村厕所革命与生活污水治理有机衔接，鼓励联户、联村、村镇一体处理。分类开展生活污水治理，以乡镇政府驻地和中心村为重点批次推进实施，基本消除较大面积黑臭水体。提高生活垃圾治理水平，推进源头分类减量、就地就近处理和资源化利用，完善收运处置，提升有毒有害垃圾处置能力。提升村容村貌，开展乡村绿化美化行动和村庄清洁行动。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十四）稳步提升基本公共服务水平。</strong>提高农村教育质量，优化县域教育布局，改善义务教育基本办学条件，建设城乡学校共同体，强化学前教育、特殊教育普惠发展，加强教师培养培训。推进健康乡村建设，健全医疗卫生服务体系，提升应对重大疫情及突发公共卫生事件能力，推进县域医共体建设，强化基本医保、大病保险、医疗救助制度保障，开展老年和妇幼健康、优生优育服务，因地制宜建设公共健身设施。完善基础民生保障，健全养老服务网络，根据各地情况和农民实际需求，推进敬老院等设施建设，大力推进乡镇区域养老服务中心提质增效，积极发展互助养老，坚决不搞&ldquo;一刀切&rdquo;。落实城乡居民基本养老保险待遇确定和基础养老金正常调整机制，做好流动儿童、留守儿童、妇女、老年人、残疾人等关心关爱服务，发展普惠托育服务。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十五）完善农村社区服务设施。</strong>统筹规划科学布局，合理安排农村社区服务设施建设，改善服务条件；盘活利用闲置设施用于社区服务，有序推动现有设施&ldquo;一室多用&rdquo;。提升农村社区服务设施效能，鼓励群团组织、合作经济组织、社会组织等参与建设管理和提供服务。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十六）加快数字乡村建设。</strong>完善信息基础设施，实施智慧广电乡村工程，推动基础设施数字化、智能化升级，构建综合信息服务体系。加快管理服务数字化，推进&ldquo;互联网+&rdquo;、&ldquo;智慧广电+&rdquo;政务服务向基层延伸，加强乡村教育、医疗、文化数字化建设。持续实施数字乡村发展行动。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十七）优化乡村规划建设。</strong>因地制宜编制村庄规划，优先建设既方便生活又促进生产的项目，引导农民参与村庄规划建设管理，严禁违背农民意愿搞大拆大建。充分利用各类已建设施，严禁使用财政资金建设景观项目。开展乡村建设评价。健全农村公共基础设施运行管护机制，建立工程建设与管护机制同步落实制度，编制管护责任清单，合理分担管护成本，有条件的地方推进城乡一体化管护。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>九、深化农业农村改革，激发农村发展活力</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十八）巩固和完善农村基本经营制度。</strong>有序推进第二轮土地承包到期后再延长30年试点，深化承包地所有权、承包权、经营权分置改革，发展农业适度规模经营。完善土地承包合同管理制度，做好与不动产统一登记有序衔接，依法纠正撂荒承包地、破坏耕地生产能力等行为。建立健全以农户家庭经营为基础、合作与联合为纽带、社会化服务为支撑的立体式复合型现代农业经营体系，健全土地流转价格形成机制，促进农民合作经营。鼓励小农户以土地经营权等入股新型农业经营主体，健全便捷高效的农业社会化服务体系。巩固提升农村集体产权制度改革成果，发展新型农村集体经济，构建产权明晰、分配合理的运行机制，赋予农民更加充分的财产权益。规范农村集体经济组织及其运行管理、农村产权流转交易，加强农村集体资产监督管理，严控集体经营风险和债务规模。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二十九）深化农村土地制度改革。</strong>加快完成房地一体宅基地确权登记颁证，允许农户合法拥有的住房通过出租、入股、合作等方式盘活利用。有序推进农村集体经营性建设用地入市改革，优先保障乡村全面振兴用地需求，健全土地增值收益分配机制。强化乡村发展用地保障，省级土地利用年度计划安排至少5%新增建设用地指标，保障乡村重点产业和项目用地，落实农村产业融合发展和设施农业用地保障政策，县乡级国土空间规划应统筹安排农业农村发展用地。规范有序稳妥开展城乡建设用地增减挂钩，腾退的建设用地指标可纳入土地利用年度计划。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> （三十）健全多元化乡村振兴投入保障机制。</strong>发挥财政支持作用，坚持将农业农村作为一般公共预算优先保障领域，落实提高土地出让收益用于农业农村比例政策，用好新出台的投融资政策。完善金融服务，健全大中型银行服务&ldquo;三农&rdquo;工作机制，强化政策性金融支农作用，加强农村中小金融机构支农支小定位，加快农村信用社改革化险，推动村镇银行结构性重组。发展农村数字普惠金融，推进农村信用体系建设，建立健全市场化涉农金融风险补偿机制，发展多层次农业保险，完善农业再保险和农业保险大灾风险分散机制。支持以市场化方式依法设立乡村振兴基金。引导和规范农业农村领域社会投资，推进&ldquo;万企兴万村&rdquo;行动。乡村振兴投入要坚持尽力而为、量力而行，避免新增地方债务风险。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（三十一）扎实推进农村改革各项重点任务。</strong>深化供销合作社综合改革，推进农垦垦区集团化、农场企业化改革和垦地合作。完善集体林权、国有林场、草原承包经营、农村集体土地征收等制度。推动农业水价综合改革。发挥全国农村改革试验区、国家城乡融合发展试验区以及农村综合性改革试点试验示范带动作用。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>十、加强农村基层组织建设，推进乡村治理现代化</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（三十二）深入推进抓党建促乡村全面振兴。</strong>增强农村基层党组织政治功能和组织功能，强化县级党委抓党建促乡村振兴责任。选优配强乡镇领导班子，优化村&ldquo;两委&rdquo;班子特别是带头人队伍，全面培训提高乡镇、村班子领导乡村全面振兴能力。发挥农村党员先锋模范作用，推动农村党员进县级党校轮训，常态化整顿软弱涣散村党组织，完善向重点乡村选派驻村第一书记和工作队制度。加强乡镇、村干部关心关爱和待遇保障。加强党风廉政建设，完善党务、村务、财务公开制度，推动基层纪检监察组织和村务监督委员会有效衔接，把纪检监察工作向村延伸覆盖，强化对村干部监督管理，加强农村集体经济组织审计监督。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（三十三）推进以党建引领乡村治理。</strong>健全县乡村三级治理体系，加强县级统筹协调，推动乡镇扩权赋能，完善村党组织领导的村级组织体系，全面落实县级领导班子成员包乡走村、乡镇领导班子成员包村联户、村干部经常入户走访制度，推动治理重心向基层下移、干部力量向基层充实，推动资源、服务、管理向基层下沉，切实提升乡村治理效能。健全乡镇职责和权力、资源相匹配制度，加强乡镇服务管理力量。制定乡镇履行职责事项清单，健全为基层减负长效机制。健全党组织领导的自治、法治、德治相结合的乡村治理体系，落实&ldquo;四议两公开&rdquo;制度；深化法治乡村建设，加强法律顾问和法律援助工作；发挥好村规民约作用。创新治理方式，推动数字赋能乡村治理，创新村民协商议事形式。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（三十四）维护乡村和谐稳定。</strong>健全基层服务体系，加强乡镇政府公共服务职能，完善治理平台。提升应急管理能力，健全乡镇、村级应急管理协调机制和组织体系，完善防汛、防火等责任人制度，加强应急救援能力建设，开展相关宣传和演练。壮大群防群治力量，落实平安建设领导责任制。坚持和发展新时代&ldquo;枫桥经验&rdquo;，推动领导干部下访接访。完善农村社会矛盾纠纷多元预防调处化解综合机制，推进和谐邻里建设。加强农村宗教活动常态化管理，全面防范打击农村邪教违法犯罪活动，持续开展反邪教拒毒防毒宣传教育、赌博违法犯罪专项整治，常态化开展扫黑除恶，打击侵害妇女儿童权益的违法犯罪行为。</span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\"><strong>十一、加强组织实施</strong></span></section>\n<section></section>\n<section></section>\n<section><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 推进乡村全面振兴要坚持和加强党中央集中统一领导，坚持中央统筹、省负总责、市县乡抓落实的乡村振兴工作机制，全面落实乡村振兴责任制。要加强统计监测，适时开展规划实施评估。建立乡村全面振兴工作联系点。加快涉农法律法规制定修订，完善乡村振兴法律规范体系。加强宣传和舆论引导，充分发挥工会、共青团、妇联等作用，激发全社会参与乡村全面振兴的积极性，营造良好社会氛围。各地各有关部门要结合实际推动目标任务落地见效，工作中要坚决防止形式主义、官僚主义、形象工程。重大事项及时按程序向党中央、国务院请示报告。</span></section>', '301', 12, NULL, '中共中央 国务院印发《乡村全面振兴规划（2024－2027年）》\n', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1057, '农业农村部 国家档案局关于印发《第二轮土地承包到期后再延长30年试点工作档案管理办法》的通知', 999999, NULL, 'sjfby', '882', '2025-04-03 09:13:32', '2025-04-03 09:17:06', '2025-04-03 09:13:32', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '300,301', '0', 0, 0, 0, '<h1 id=\"activity-name\" class=\"rich_media_title \" style=\"text-align: center;\">农业农村部 国家档案局关于印发《第二轮土地承包到期后再延长30年试点工作档案管理办法》的通知</h1>\n<p><span style=\"font-size: 18pt;\">各省、自治区、直辖市农业农村（农牧）厅（局、委），档案局：&nbsp; &nbsp; &nbsp;</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 为贯彻落实党中央、国务院决策部署，切实加强第二轮土地承包到期后再延长30年试点工作档案管理，农业农村部、国家档案局组织制定了《第二轮土地承包到期后再延长30年试点工作档案管理办法》，现印发给你们，请结合本地区实际，遵照执行。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">农业农村部&nbsp;&nbsp;&nbsp;&nbsp;国家档案局&nbsp; &nbsp;&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 18pt;\">2025年1月24日&nbsp; &nbsp;&nbsp;</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二轮土地承包到期后再延长</strong><strong>30</strong><strong>年试点</strong><strong>工作</strong><strong>档案管理办法</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第一章&nbsp;&nbsp;总则</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第一条</strong>　为了加强第二轮土地承包到期后再延长30年试点工作档案管理，规范其形成、收集、整理、保护、利用等工作，根据《中华人民共和国档案法》、《中华人民共和国农村土地承包法》等法律法规，制定本办法。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二条</strong>　本办法所称第二轮土地承包到期后再延长30年试点工作档案是指在第二轮土地承包到期后再延长30年试点（以下简称延包）工作中直接形成的，对国家、社会和个人具有保存价值的各种文字、图表、声像、数据等不同形式的历史记录，是土地承包的原始凭证。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第三条</strong>　本办法所称延包档案工作包括延包文件材料形成、整理、归档和延包档案的收集、保管、移交、利用及其监督和指导等。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第四条</strong>　延包档案工作实行统一领导、分级实施、分类管理、集中保管的原则，与延包工作同步部署、同步实施、同步检查、同步验收。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>五</strong><strong>条</strong>&nbsp;&nbsp;延包档案工作经费应当纳入本地区延包工作经费预算，确保延包文件材料收集、整理、归档和延包档案数字化等工作顺利开展。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>六</strong><strong>条　</strong>对在延包档案工作中做出突出贡献的单位或者个人，应当按照国家有关规定给予表彰、奖励。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第二章&nbsp;&nbsp;工作职责</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>七</strong><strong>条</strong>　县级以上农业农村和档案主管部门作为延包工作协调机制成员单位，负责统筹规划、组织协调本行政区域延包档案工作。</span></p>\n<p><span style=\"font-size: 18pt;\">县级农业农村主管部门负责组织延包文件材料的收集、整理、归档及档案移交工作；县级档案主管部门负责延包档案工作的业务培训、监督和指导；县级综合档案馆负责延包档案的接收、保管和利用。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>八</strong><strong>条</strong><strong>&nbsp;&nbsp;</strong>县、乡、村应当落实档案工作责任制，指定人员负责延包档案工作，健全档案管理制度，确保延包档案齐全完整、系统规范。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>九</strong><strong>条　</strong>县、乡、村应当加强延包档案安全管理，提供档案安全保管场所，配备档案装具和防火防盗防潮等必要的设施设备。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>十</strong><strong>条</strong>　县级以上农业农村和档案主管部门应当组织延包档案工作检查，重点检查延包档案的真实、有效、完整和安全保管情况。</span></p>\n<p><span style=\"font-size: 18pt;\">对于检查不合格的单位，应当督促其及时整改。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第三章&nbsp;管理要求</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十一条</strong>&nbsp;&nbsp;延包文件材料的形成、收集应当与延包工作各环节紧密衔接、同步开展。归档的延包文件材料应当齐全完整、真实有效、内容清晰、手续完备，印制书写材料、纸张和装订材料等应当符合档案保管要求。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十二条</strong>&nbsp;&nbsp;延包档案包括综合管理类、合同管理类和特殊载体类，其保管期限分为永久和定期，定期一般分为30年和10年。具体参照《第二轮土地承包到期后再延长30年试点工作文件材料归档范围和档案保管期限表》（见附件）执行。&nbsp;</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>十</strong><strong>三</strong><strong>条</strong>　延包文件材料整理应当遵循文件的形成规律，保持文件之间的有机联系，便于保管和利用。整理方法可以按照《归档文件整理规则》（DA/T 22）、《文书档案案卷格式》（GB/T 9705）、《科学技术档案案卷构成的一般要求》（GB/T 11822）、《照片档案管理规范》（GB/T 11821）、《数码照片归档与管理规范》（DA/T 50）、《录音录像档案管理规范》（DA/T 78）等要求进行。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; 电子文件按照《电子文件归档与电子档案管理规范》（GB/T 18894）等进行整理。实物按照类别或者保管期限，</span></p>\n<p><span style=\"font-size: 18pt;\">以件为单位进行整理。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十</strong><strong>四</strong><strong>条</strong>　合同管理类中具体涉及农户有关的身份信息和承包合同等文件材料，应当以农户为单位&ldquo;一户一档&rdquo;进行整理。&nbsp;</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十五条</strong>&nbsp;&nbsp;各类延包文件材料应当及时归档。延包工作结束后，县级农业农村主管部门应当会同同级档案主管部门及时开展档案验收工作。&nbsp;</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十六条&nbsp;&nbsp;</strong>县级农业农村主管部门应当按照国家有关规定向县级综合档案馆移交验收合格的延包档案。经协商同意，可以提前移交。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十</strong><strong>七</strong><strong>条</strong>　村级延包档案一般自行保管，不符合档案保管条件的，可以由乡（镇）档案部门代管。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第十</strong><strong>八</strong><strong>条</strong>　县级综合档案馆应当按照规定向社会提供延包档案利用服务。涉及国家秘密、个人隐私和法律另有规定的除外。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第四章&nbsp;信息化建设</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>十</strong><strong>九</strong><strong>条</strong>&nbsp;&nbsp;县级以上农业农村和档案主管部门应当积极推进延包档案信息化建设，加强延包电子文件归档和电子档案管理，保障电子档案、传统载体档案数字化成果等档案数字资源安全。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第</strong><strong>二十</strong><strong>条</strong>&nbsp;&nbsp;建设或者使用延包信息管理系统，应当充分考虑延包档案管理需求，设置并完善延包电子文件归档功能，做好延包电子文件形成、办理、归档和延包电子档案保管、利用、移交等工作，确保电子档案真实、完整、可用、安全。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二十</strong><strong>一</strong><strong>条</strong>&nbsp;&nbsp;来源可靠、程序规范、要素合规的延包电子档案与传统载体档案具有同等效力。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二十</strong><strong>二</strong><strong>条</strong>&nbsp;&nbsp;县级农业农村主管部门应当会同档案部门统筹开展延包档案数字化。委托档案数字化等系列服务的，应当遵守有关安全保密规定，加强对受托方的监督和管理，确保档案实体与信息安全。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二十</strong><strong>三</strong><strong>条</strong>&nbsp;&nbsp;县级以上档案部门应当积极开展延包档案数字资源利用服务，推动延包档案数字资源跨地域、跨部门共享。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>第五章&nbsp;&nbsp;附&nbsp;&nbsp;则</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二十</strong><strong>四</strong><strong>条</strong>　各省、自治区、直辖市农业农村和档案主管部门可以根据本办法，结合本地实际，制定实施细则。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二十</strong><strong>五</strong><strong>条　</strong>本办法由农业农村部、国家档案局负责解释。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 第二十</strong><strong>六</strong><strong>条</strong>　本办法自发布之日起施行。</span></p>\n<p><br /><br /></p>', '301', 10, NULL, '农业农村部 国家档案局关于印发《第二轮土地承包到期后再延长30年试点工作档案管理办法》的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1058, '自然资源部 农业农村部关于严格遵守“八不准”要求 坚决遏制新增农村乱占耕地建房问题的通知', 999999, NULL, 'sjfby', '882', '2025-04-03 09:29:13', '2025-04-03 09:31:20', '2025-04-03 09:29:13', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '300,301', '0', 0, 0, 0, '<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>自然资源部 农业农村部关于严格遵守&ldquo;八不准&rdquo;要求 坚决遏制新增农村乱占耕地建房问题的通知</strong></span></p>\n<section style=\"padding-left: 390px;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 自然资发〔2025〕10号&nbsp; &nbsp;</span></section>\n</section>\n<section class=\"js_darkmode__1\"><span style=\"font-size: 18pt;\">各省、自治区、直辖市自然资源主管部门、农业农村（农牧）厅（局、委），新疆生产建设兵团自然资源局、农业农村局：</span></section>\n<p class=\"js_darkmode__2\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2020年7月《关于农村乱占耕地建房&ldquo;八不准&rdquo;的通知》（自然资发〔2020〕127号）印发以来，各地疏堵结合，以&ldquo;零容忍&rdquo;的态度坚决遏制新增乱占耕地建房问题，初步遏制了新增问题高发的态势。但一些地方仍然存在违反&ldquo;八不准&rdquo;要求，乱占、破坏耕地等问题，影响了农村乱占耕地建房专项整治工作质效。为进一步贯彻落实好党中央、国务院决策部署，现就严守&ldquo;八不准&rdquo;要求，坚决遏制新增问题通知如下。</span></p>\n<p class=\"js_darkmode__2\">&nbsp;</p>\n<p class=\"js_darkmode__3\"><span style=\"font-size: 18pt;\"><strong>一、切实提高政治站位，扛牢耕地保护政治责任</strong></span></p>\n<p class=\"js_darkmode__4\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（一）旗帜鲜明讲政治。</strong>耕地是粮食生产的命根子。保护耕地关乎14亿人吃饭问题，容不得半点闪失。要从维护国家粮食安全和实现共同富裕的大局出发，深刻认识耕地保护的极端重要性，坚决扛起耕地保护政治责任，坚持社会主义土地公有制，落实最严格的耕地保护制度，严守&ldquo;八不准&rdquo;要求。</span></p>\n<p class=\"js_darkmode__5\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（二）深入贯彻新发展理念。</strong>要坚持目标导向和问题导向，坚决整治乱占、破坏耕地违法行为，严防变相买卖、兼并土地，切实防止&ldquo;大棚房&rdquo;死灰复燃，坚决整治征占黑土地建&ldquo;特色小镇&rdquo;等各类侵占耕地问题。强化全链条监管，把好土地资源开发利用全过程、各环节监管关口，进一步严格耕地保护工作，确保牢牢守住18亿亩耕地红线和土地管理政策底线。</span></p>\n<p class=\"js_darkmode__6\"><span style=\"font-size: 18pt;\"><strong>二、大力开展宣传教育引导，推动形成全社会耕地保护普遍共识和行动自觉</strong></span></p>\n<p class=\"js_darkmode__7\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（三）加大政策宣贯力度。</strong>要结合全国土地日、永久基本农田公告等，会同宣传部门加大耕地保护法律法规和政策的宣传力度，提升全民法治意识；要将&ldquo;八不准&rdquo;要求制作成通俗易懂的宣传材料，更好地向群众讲解政策要求，鼓励社会参与宣传，进一步增加宣传的多样性和互动性；要加大宣传投入，扩大覆盖面，提升效果。自然资源部已正式上线永久基本农田查询平台，向全社会提供永久基本农田查询服务，保障群众知情权和监督权。</span></p>\n<p class=\"js_darkmode__8\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（四）强化警示教育。</strong>要进一步发挥典型案例警示教育和指导示范作用，以案释法、以案为鉴。对于乱占、破坏耕地问题特别是涉嫌犯罪的，要会同法院、检察院、公安等有关方面依法依规严肃处置，向社会公布典型案例，切实形成有力震慑，起到查处一案、震慑一片、教育一方的效果，促进各方面自觉遵法守法。</span></p>\n<p class=\"js_darkmode__9\"><span style=\"font-size: 18pt;\"><strong>三、坚持严格监管和执法，巩固好农村乱占耕地建房专项整治工作成果</strong></span></p>\n<p class=\"js_darkmode__10\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（五）&ldquo;零容忍&rdquo;遏制新增问题。</strong>对违反&ldquo;八不准&rdquo;要求的新增违法占用耕地问题，要以&ldquo;零容忍&rdquo;的态度依法严肃处理，特别是对于强占多占、非法出售等恶意占地建房行为，要从严从重惩处。进一步压紧压实耕地保护主体责任，加强违法行为监测监管力度，切实做到早发现、早制止、严查处。对违法占用耕地从事非农建设的，在整改处置到位前，实行非农建设占补挂钩的省份，须先行冻结当地相应的储备补充耕地指标。对于重大典型问题，采取挂牌督办、直接立案、公开通报等方式处理。</span></p>\n<p class=\"js_darkmode__11\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; （<strong>六）加强部门协同配合。</strong>要充分发挥农村乱占耕地建房专项整治工作机制统筹协调作用，督促指导各有关职能部门切实履行耕地保护共同责任，加强协作配合、形成管控合力，以实际行动巩固好农村乱占耕地建房专项整治工作成果。要加强与法院、检察院和公安机关的协作配合，充分运用行政执法与非诉执行监督、行政执法与公益诉讼、行刑衔接等方面的协作配合机制，提升执法工作质效。要强化与纪检监察、组织、审计等方面的信息互通与共享，落实相关贯通协调机制，对涉嫌违规决策、失职渎职的，及时移送转送问题线索，推动形成监督约束合力。</span></p>\n<p class=\"js_darkmode__12\"><span style=\"font-size: 18pt;\"><strong>四、坚持疏堵结合，切实保障农村村民住宅建设等合理用地需求</strong></span></p>\n<p class=\"js_darkmode__13\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（七）加大土地资源要素保障力度。</strong>要统筹安排补充耕地指标和新增建设用地计划，切实保障和规范农村一二三产业融合发展用地。按照《关于保障农村村民住宅建设合理用地的通知》（自然资发〔2020〕128号）要求，继续单列农村村民住宅用地规划和计划指标，依法保障农村村民住宅建设合理用地需求。</span></p>\n<p class=\"js_darkmode__14\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（八）建立健全容错纠错机制。</strong>坚决反对不顾实际情况，只依据管理数据进行合法性判定的&ldquo;唯技术论&rdquo;工作方式。对原为建设用地误划为耕地的，经举证核实已办理用地手续的，已建成或正在建设的项目不认定为违法占地，在年度国土变更调查时按实地现状认定地类。要按照中央整治形式主义为基层减负工作机制有关要求，分类稳妥开展违规占用耕地整改复耕，坚决杜绝不切实际下任务、定时限，强行砍树、毁果、填塘，虚假复耕等行为，给基层留出合理必要的过渡期，循序渐进推动。严防出现简单化、&ldquo;一刀切&rdquo;问题。</span></p>\n<p class=\"js_darkmode__15\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（九）严守农村用地建房法律政策底线红线。</strong>严禁违法违规买卖宅基地和违规搞合作建房。严禁城镇居民到农村购买宅基地和住房，严禁为城镇居民在农村违法建造和违法购买的住房登记发证。严禁下乡利用农村宅基地建设别墅大院和私人会馆。不得以所谓的&ldquo;特殊资格权&rdquo;、村民决议等变相给回乡退休干部等非农村集体经济组织成员分配宅基地建房。农村村民应严格按照批准的面积和规划许可建设住宅，不得超面积占地，不得超规模建房。不得以各种名义违背农民意愿强制退出宅基地和强迫农民&ldquo;上楼&rdquo;，不得违法收回农户合法取得的宅基地，不得以退出宅基地作为农民进城落户的条件。</span></p>\n<p class=\"js_darkmode__16\"><span style=\"font-size: 18pt;\"><strong>五、严肃考核问责追责，驰而不息推进全面从严治党</strong></span></p>\n<p class=\"js_darkmode__17\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十）做好问题线索和案件移送工作。</strong>针对落实党中央、国务院耕地保护决策部署不力，存在不作为、慢作为、乱作为等形式主义、官僚主义，甚至滥用职权、牟取私利的党员干部，各地要依规依纪依法移送纪检监察机关；依法需要移送司法机关追究刑事责任的，应当坚决予以移送。同时，主动配合做好追责问责、刑事责任追究等工作，切实形成严厉打击土地资源违法犯罪行为的合力。</span></p>\n<p class=\"js_darkmode__18\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>（十一）加强党风廉政建设。</strong>要将党建与业务工作同谋划、同部署、同推进、同考核,结合党纪学习教育，进一步加强党员干部廉政监督和廉政教育，推动地方各级自然资源和农业农村主管部门严守政治纪律和政治规矩，坚决纠治形式主义、官僚主义，坚决反对玩忽职守、滥用职权、徇私舞弊以及工作简单化、&ldquo;一刀切&rdquo;等行为。要坚持以人民为中心的发展思想，大力弘扬务实重干、勤政担当的优良作风，加强政策解读、业务培训和宣传引导,不断提升全系统干部为民服务水平和依法履职能力。</span></p>\n<p class=\"js_darkmode__20\" style=\"text-align: right;\"><span style=\"font-size: 18pt;\">自然资源部 农业农村部</span></p>\n<section class=\"js_darkmode__21\" style=\"padding-left: 450px;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2025年1月7日</span></section>', '301', 16, NULL, '自然资源部 农业农村部关于严格遵守“八不准”要求 坚决遏制新增农村乱占耕地建房问题的通知', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
