INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1076, '西安城乡融合要素交易市场激活资源潜能', 999999, '1909893936572370946', 'sjfby', '882', '2025-04-09 16:59:08', '2025-04-09 17:01:51', '2025-04-09 17:49:57', '2', 'sjfby', '三秦都市报-秦闻讯', '省级发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\"><img class=\"content-image picture-illustrating\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://qinwen.sanqin.com/pic/2025-04/09/11131494_87a6b3ad-6fd4-408e-b9d2-1250e3f925bbcopy.jpg\" width=\"1100\" data-original=\"https://qinwen.sanqin.com/pic/2025-04/09/11131494_87a6b3ad-6fd4-408e-b9d2-1250e3f925bbcopy.jpg\" data-src=\"https://qinwen.sanqin.com/pic/2025-04/09/11131494_87a6b3ad-6fd4-408e-b9d2-1250e3f925bbcopy.jpg\" data-currindex=\"0\" data-toggle=\"tooltip\" data-original-title=\"\" data-load=\"true\" />&nbsp; &nbsp; &nbsp; 4月7日，西安市雁塔区曲江街道孟村股份经济合作社副理事长益国国与村民代表一行，将一面绣有&ldquo;公平公正，诚信为民&rdquo;的锦旗送至西安城乡融合要素交易市场，以表达对市场高效规范服务的由衷赞誉。&ldquo;这个平台就像&lsquo;阳光房&rsquo;，让咱村的资产交易明明白白，集体收入实实在在！&rdquo;村民代表手持锦旗，喜悦之情溢于言表。</span></p>\n<div><span style=\"font-size: 18pt;\">&nbsp;</span></div>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 此次合作的&ldquo;标杆项目&rdquo;源于孟村8200平方米安置房商铺的出租。自2024年11月起，西安城乡融合要素交易市场与孟村股份经济合作社紧密协作，通过专业化服务精准梳理项目需求，量身定制挂牌方案，科学设置受让条件，确保交易流程合法合规、公开透明。<strong>今年3月26日，项目经过六轮激烈竞价，最终以7.69%的溢价率成功落槌，为村集体创造增值收益近784万元，不仅盘活了&ldquo;沉睡资产&rdquo;，更实现了集体经济的提质增效。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<strong> &ldquo;过去总怕&lsquo;人情交易&rsquo;，现在全程网拍、动态可查，村民心里亮堂了！&rdquo;</strong>益国国感慨道。这面锦旗既是基层对要素市场化改革成效的&ldquo;口碑认证&rdquo;，更是西安深化农村集体产权制度改革的生动注脚。孟村社区党支部书记牛红波表示：<strong>&ldquo;平台既减轻了基层负担，又通过&lsquo;阳光操作&rsquo;规范了&lsquo;三资&rsquo;管理，为乡村振兴筑牢了制度屏障。&rdquo;</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安城乡融合要素交易市场总经理贠涛表示，<strong>市场将以此次合作为契机，持续优化要素配置服务体系，畅通城乡资源流动渠道，打造更多&ldquo;资源增值、农民增收、产业增效&rdquo;的示范案例，为新时代城乡融合发展注入强劲动能。</strong></span></p>', '101', 40, NULL, '西安城乡融合要素交易市场激活资源潜能', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1077, '溢价7.69% 增收784万元 西安专业平台助农村集体资产保值增值', 999999, '1909894649767628802', 'sjfby', '882', '2025-04-09 17:02:11', '2025-04-09 17:04:20', '2025-04-09 17:02:11', '2', 'sjfby', '西安发布', '省级发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\"><span class=\"inner-article-image\" style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/04/09/4cc2e0a5a50e4613a7934a9f39785634.jpg\" width=\"1100\" /></span>&nbsp; &nbsp; &nbsp; 4月7日，在西安城乡融合要素交易市场门前，雁塔区曲江街道孟村社区党支部书记牛红波带领社区相关成员，将一面印着&ldquo;公平公正 诚信为民&rdquo;字样的锦旗郑重地交到上述市场总经理贠涛的手中。</span></p>\n<div><span class=\"inner-article-image\" style=\"font-size: 18pt;\">&nbsp;</span></div>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp;<strong> &nbsp; &ldquo;成交结果太好了！这是我们的共同心声。&rdquo;牛红波握着贠涛的手如是说。记者了解到，今年3月26日，经过6轮竞价，孟村社区安置房商铺出租项目以70元/平方米/月成交，较65元底价溢价7.69%，为村集体增收约784万元。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 为确保交易顺利进行，西安城乡融合要素交易平台工作人员与村集体进行了多轮深入沟通，不断完善交易方案。经过前期充分准备，上述项目在今年3月第3次挂牌时成功吸引了10家意向方参与竞标。&ldquo;平台的专业指导让我们少走了很多弯路。&rdquo;孟村社区相关负责人益国国说。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 据贠涛介绍，<strong>该项目体现了交易平台的信息集聚、价值发现和阳光交易三大功能。按15年租期计算，该项目将为498户村民带来约1.097亿元的稳定收益。</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>作为陕西省推动城乡融合发展的重要平台，西安城乡融合要素交易市场自成立以来，遵循&ldquo;公开、公平、公正&rdquo;原则，为农村集体资产交易提供专业化服务，助力更多农村集体资产规范流转、保值增值。</strong></span></p>', '101', 9, NULL, '溢价7.69% 增收784万元 西安专业平台助农村集体资产保值增值', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1078, '西安宇昕商贸有限公司一行来访交流座谈', 999999, '1909899998138568706', 'sjfby', '882', '2025-04-09 17:23:09', '2025-04-09 17:24:49', '2025-04-09 17:23:09', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;<img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/04/09/06ab40e822784e8881a2fa3f5cb50416.jpg\" width=\"1100\" />&nbsp; &nbsp; &nbsp; 4月9日，西安宇昕商贸有限公司负责人申晓荣一行到访西安城乡融合要素交易市场有限公司，双方就深化合作、共促城乡融合发展进行座谈交流。西安城乡融合要素交易市场有限公司总经理贠涛出席会议。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 座谈会上，双方围绕市场资源整合、平台协同赋能、金融服务创新等展开深入交流，并就具体合作路径进行务实探讨。在与会人员的共同见证下，双方现场签署了战略合作协议，在共建资源对接平台、促进城乡要素高效流通等领域达成重要合作共识。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 申晓荣表示，<strong>宇昕商贸作为本土商贸企业，长期深耕市场运营与渠道开发，积累了丰富的行业资源与实践经验。此次与要素交易市场的合作，将充分发挥双方优势，聚焦农副产品流通和特色产业培育，通过平台化运作打通城乡资源流动通道，助力农民增收致富，为乡村振兴注入新动能。</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 贠涛对宇昕商贸的专业能力与资源优势表示高度认可。<strong>他指出，此次交流为双方合作开辟了广阔空间，西安城乡融合要素交易市场将依托政策支持、信息整合及金融服务能力，与宇昕商贸形成优势互补，在农副产品流通等领域率先突破，共同探索&ldquo;市场+平台+实体&rdquo;的创新合作模式，助推城乡融合发展。</strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 西安城乡融合要素交易市场有限公司分管领导、相关部门负责人等参加会议。</span></p>', '101', 22, NULL, '西安宇昕商贸有限公司一行来访交流座谈', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
