INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1082, '周至县召开农村“三资”管理暨城乡融合要素交易工作会', 999999, '1911709334296956929', 'sjfby', '882', '2025-04-14 17:12:17', '2025-04-14 17:14:54', '2025-04-14 17:12:17', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 0, 0, '<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/04/14/7dd7448201344c09972a27bc091eb9e9.jpg\" width=\"1100\" />&nbsp; &nbsp; &nbsp; 为进一步规范农村集体&ldquo;三资&rdquo;管理，4月14日，周至县组织召开农村&ldquo;三资&rdquo;管理暨城乡融合要素交易工作推进会。副县长王刚利出席会议并讲话，县政府督查专员师卫宁、县农业农村局局长付毓伟，西安城乡融合要素交易市场有限公司、周至县各镇（街）分管领导及农办主任参加会议。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 会议围绕规范农村集体资产交易、促进城乡要素有序流动等重点工作进行部署。付毓伟表示，<strong>一是</strong>要规范交易行为，落实入场交易；<strong>二是</strong>要坚持民主决策，严格交易流程；<strong>三是</strong>要强化监督管理，确保应进必进；四是要抓好宣传培训，提升工作成效。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 要素交易市场分管领导结合周至县实际，<strong>从农村资源资产现状、规范交易重要意义、发展壮大村集体经济三个维度</strong>，深入分析了推动农村资源资产公平交易、高效配置的必要性和实施路径。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 王刚利在总结讲话中强调，各镇（街）要以此次会议为新起点，重点抓好三方面工作：<strong>一是</strong>认真学习领会相关政策文件精神，严格落实&ldquo;应进必进&rdquo;要求，通过阳光交易防范廉政风险；<strong>二是</strong>明确目标任务，确保今年6月底前，平原镇（街）至少完成5宗、山区镇至少完成1宗规范交易；<strong>三是</strong>明确要求自4月14日起，全县农村集体资产处置必须全部进入城乡融合要素交易市场进行规范交易，对未按规定执行、擅自处置集体资产的街镇，将依规追究相关责任人责任。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 此次会议的召开，标志着周至县农村集体&ldquo;三资&rdquo;管理迈入规范化、制度化、市场化新阶段，将为发展新型农村集体经济、推进城乡融合发展提供有力保障。</span></p>', '101', 76, NULL, '周至县召开农村“三资”管理暨城乡融合要素交易工作会', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
