INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1163, '2025年中央一号文件：农村宅基地法律政策解读', 999999, NULL, 'sjfby', '882', '2025-05-13 15:39:53', '2025-05-13 15:41:51', '2025-05-13 15:39:53', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '300,301', '0', 0, 0, 0, '<section data-pm-slice=\"0 0 []\">\n<section>\n<section data-support=\"96编辑器\" data-style-id=\"58387\">\n<section data-align=\"title\">\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong data-form=\"0\" data-num=\"6\">01</strong></span></p>\n</section>\n<section></section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img __bg_gif\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg&amp;wxfrom=13&amp;tp=wxpic\" alt=\"图片\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg\" data-ratio=\"0.2926829268292683\" data-w=\"82\" data-width=\"100%\" data-original-style=\"width:100%;display:block;vertical-align:bottom;background-color:transparent !important;background-size:0px !important;border-radius:initial;\" data-index=\"1\" data-sec-load-status=\"2\" data-report-img-idx=\"0\" data-order=\"0\" /></span></section>\n</section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>宅基地，农民的&ldquo;根&rdquo;与&ldquo;魂&rdquo;</strong></span></p>\n</section>\n</section>\n<section></section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 农村宅基地是农民安身立命的根基，承载着农村家庭的居住需求与情感寄托。随着2025年中央一号文件的发布，宅基地管理政策再次成为社会焦点。作为与农民权益息息重要的集体土地资源，宅基地的法律属性、使用规则和改革方向，既关乎农民的切身利益，也牵动着乡村振兴的战略布局。本文将从法律视角出发，结合最新政策，为广大群众解读宅基地的&ldquo;权利边界&rdquo;与&ldquo;合规路径&rdquo;。</span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-support=\"96编辑器\" data-style-id=\"58387\">\n<section data-align=\"title\">\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong data-form=\"0\" data-num=\"5\">02</strong></span></p>\n</section>\n<section></section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img __bg_gif\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg&amp;wxfrom=13&amp;tp=wxpic\" alt=\"图片\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg\" data-ratio=\"0.2926829268292683\" data-w=\"82\" data-width=\"100%\" data-original-style=\"width:100%;display:block;vertical-align:bottom;background-color:transparent !important;background-size:0px !important;border-radius:initial;\" data-index=\"2\" data-sec-load-status=\"2\" data-report-img-idx=\"1\" data-order=\"1\" /></span></section>\n</section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>宅基地的法律属性：所有权、资格权与使用权&ldquo;三权分置&rdquo;</strong></span></p>\n</section>\n</section>\n<section></section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 根据《宪法》和《土地管理法》，农村宅基地属于农民集体所有，村民仅享有使用权，这是我国土地公有制的基本体现。具体而言：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 所有权归集体：宅基地由村集体统一管理，村民无权擅自买卖、转让所有权。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 资格权归农户：只有本集体经济组织成员（即&ldquo;村民&rdquo;）才有资格申请宅基地，且遵循&ldquo;一户一宅&rdquo;原则。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 使用权可流转：在符合法律的前提下，宅基地可通过出租、入股等方式盘活，但不得改变土地用途（如建房只能用于居住，不可用于商业开发）。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 政策新规：2025年中央一号文件明确&ldquo;三不得&rdquo;原则：非集体成员不得取得宅基地使用权、不得强制农民退出宅基地、村集体不得违规分配宅基地。这一规定进一步强化了宅基地的&ldquo;身份属性&rdquo;，杜绝外部资本无序侵占农村土地资源。</span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-support=\"96编辑器\" data-style-id=\"58387\">\n<section data-align=\"title\">\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong data-form=\"0\" data-num=\"4\">03</strong></span></p>\n</section>\n<section></section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img __bg_gif\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg\" data-ratio=\"0.2926829268292683\" data-w=\"82\" data-width=\"100%\" data-original-style=\"width:100%;display:block;vertical-align:bottom;background-color:transparent !important;background-size:0px !important;border-radius:initial;\" data-index=\"2\" data-order=\"2\" data-before-load-time=\"1747122011809\" data-report-img-idx=\"3\" data-fail=\"0\" /></span></section>\n</section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>政策红线：这些行为涉嫌违法</strong><strong>！</strong></span></p>\n</section>\n</section>\n<section></section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 1. 城镇居民购买农房或宅基地：合同无效，权益难保</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2025年中央一号文件重申：&ldquo;禁止城镇居民到农村购买农房、宅基地，禁止退休干部到农村占地建房&rdquo;。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 法律依据在于：</span></p>\n<p><span style=\"font-size: 18pt;\">《土地管理法》第62条明确规定，宅基地使用权人必须是本集体经济组织成员。</span></p>\n<p><span style=\"font-size: 18pt;\">《民法典》第363条虽赋予农民宅基地使用权，但未开放给非集体成员。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 风险提示：城镇居民通过&ldquo;以租代售&rdquo;（如签订超长期租赁合同）或&ldquo;合作建房&rdquo;等方式变相交易宅基地的，均属无效行为，相关权益不受法律保护。例如，某地法院曾判决城镇居民与农民签订的20年宅基地租赁合同无效，承租人投入的建房资金无法追回。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2. 占用耕地建房：触碰法律底线，面临强制拆除</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 宅基地申请需严格遵循土地用途管制。2025年中央一号文件强调&ldquo;严禁占用耕地建房&rdquo;，依据《土地管理法》第77条，违法占用耕地建房将面临限期拆除、罚款甚至刑事责任。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 典型案例：某村民未经审批在耕地上建造住宅，被自然资源部门责令拆除并恢复土地原状，直接经济损失达数十万元。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3. 违规&ldquo;小产权房&rdquo;交易：风险自担，后果严重</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 部分农村以&ldquo;新农村建设&rdquo;名义对外销售小产权房，此类房屋无法办理合法产权登记，购房者既无法落户，也无法享受拆迁补偿。根据司法解释，小产权房买卖合同因违反法律强制性规定而无效，买方需自行承担损失。</span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-support=\"96编辑器\" data-style-id=\"58387\">\n<section data-align=\"title\">\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong data-form=\"0\" data-num=\"3\">04</strong></span></p>\n</section>\n<section></section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img __bg_gif\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg\" data-ratio=\"0.2926829268292683\" data-w=\"82\" data-width=\"100%\" data-original-style=\"width:100%;display:block;vertical-align:bottom;background-color:transparent !important;background-size:0px !important;border-radius:initial;\" data-index=\"3\" data-order=\"3\" data-before-load-time=\"1747122012373\" data-report-img-idx=\"4\" data-fail=\"0\" /></span></section>\n</section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>农民合法权益：这些权利必须知晓</strong><strong>！</strong></span></p>\n</section>\n</section>\n<section></section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 1. 宅基地确权登记：&ldquo;房地一体&rdquo;保障产权</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2025年中央一号文件要求全面推开&ldquo;房地一体&rdquo;确权登记，将宅基地使用权与房屋所有权统一登记，颁发不动产权证书。确权后，农民可依法行使以下权利：</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 抵押融资：部分试点地区允许农民以<span class=\"\">宅基地使用权抵押贷款</span>，解决生产经营资金需求。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 流转收益：闲置宅基地可通过出租、入股乡村旅游项目等方式盘活，获取租金或分红。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 案例参考：浙江某村村民将老宅出租改造成民宿，年租金收入超5万元，既保留宅基地资格权，又实现资产增值。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2. 进城落户不退地：农民可&ldquo;带权进城&rdquo;</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 文件明确&ldquo;依法维护进城落户农民的宅基地使用权&rdquo;，进城农民无需以退出宅基地作为落户条件。例如，张某一家迁入城市后，仍保留农村老宅，逢年过节返乡居住，宅基地使用权不受影响。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 3. 自愿有偿退出：合法途径实现资产变现</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 对于长期在城镇定居且无意保留宅基地的农民，可依《土地管理法》向村集体申请有偿退出，补偿标准由省级政府制定。例如，安徽某县规定退出宅基地每亩补偿20万-30万元，帮助农民在城市安家置业。</span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-support=\"96编辑器\" data-style-id=\"58387\">\n<section data-align=\"title\">\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong data-form=\"0\" data-num=\"2\">05</strong></span></p>\n</section>\n<section></section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img __bg_gif\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg\" data-ratio=\"0.2926829268292683\" data-w=\"82\" data-width=\"100%\" data-original-style=\"width:100%;display:block;vertical-align:bottom;background-color:transparent !important;background-size:0px !important;border-radius:initial;\" data-index=\"4\" data-order=\"4\" data-before-load-time=\"1747122012804\" data-report-img-idx=\"5\" data-fail=\"0\" /></span></section>\n</section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>合规使用宅基地：乡村振兴中的&ldquo;活水之源&rdquo;</strong></span></p>\n</section>\n</section>\n<section></section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 1. 盘活闲置资源的合法路径</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 出租：最长租赁期限20年，可用于民宿、仓储等短期项目，需经村集体备案。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 入股合作：与村集体或涉农企业合作发展乡村旅游、康养产业，按协议分享收益。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 集体经营性建设用地入市：经村民会议同意，村集体可将闲置宅基地转为经营性用地，通过拍卖、出让等方式引入资本，收益由集体和农户共享。</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2. 返乡人才的居住解决方案</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 退休干部、乡贤等返乡人员可通过租赁农民闲置房屋解决居住需求，但不得购买宅基地。例如，某退休教师租用村民闲置农房开设书院，既促进文化传播，又避免法律风险。</span></p>\n</section>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section data-support=\"96编辑器\" data-style-id=\"58387\">\n<section data-align=\"title\">\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\"><strong data-form=\"0\" data-num=\"1\">06</strong></span></p>\n</section>\n<section></section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img __bg_gif\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" data-src=\"https://mmbiz.qpic.cn/mmbiz_gif/Ljib4So7yuWhoUeKpgRsicc8sPuoYYI8APwfHcic3NFJE3Lcj6hbTribMcdCBff7dWI2OBOkbyiaEJmicqdjuHnUslibQ/640?wx_fmt=gif&amp;from=appmsg\" data-ratio=\"0.2926829268292683\" data-w=\"82\" data-width=\"100%\" data-original-style=\"width:100%;display:block;vertical-align:bottom;background-color:transparent !important;background-size:0px !important;border-radius:initial;\" data-index=\"5\" data-order=\"5\" data-before-load-time=\"1747122012856\" data-report-img-idx=\"6\" data-fail=\"0\" /></span></section>\n</section>\n<section>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>守住法律底线，共绘乡村振兴蓝图</strong></span></p>\n</section>\n</section>\n<section></section>\n</section>\n</section>\n</section>\n</section>\n<section>\n<p>&nbsp;</p>\n</section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; 2025年中央一号文件对宅基地管理的严格规范，既是保护农民&ldquo;退路&rdquo;的民生之举，也是维护粮食安全、遏制土地资源流失的战略选择。对农民而言，需增强法律意识，拒绝违法交易，善用确权与盘活政策实现资产增值；对城镇居民而言，应尊重农村土地制度，通过合法途径参与乡村振兴。唯有各方共同遵守法律红线，才能让宅基地真正成为农民安居乐业的&ldquo;幸福基石&rdquo;，为乡村振兴注入持久动力。</span></p>\n</section>\n</section>\n</section>\n</section>', '301', 9, NULL, '2025年中央一号文件：农村宅基地法律政策解读', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
