INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1197, ' “城乡E+”党建品牌正式发布!', 999999, NULL, 'sjfby', '882', '2025-02-05 14:03:30', '2025-05-20 14:10:30', '2025-02-05 14:03:30', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '500,501', '0', 0, 0, 0, '<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 32px;\"><strong>&ldquo;城乡E+&rdquo;党建品牌正式发布!</strong></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/05/20/17c5d7fee9e846b48bfafea3aec19676.png\" /></strong></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;西安城乡融合要素交易市场有限公司党支部深入贯彻落实集团党委工作部署，紧密围绕公司改革发展大局，认真践行新时代国有企业党的建设总要求，压紧压实党建工作责任，持续夯实基层党建基础，全面提升党建工作标准化水平，以高质量党建引领企业高质量发展。下面让我们一起了解<span style=\"color: #ff0000;\">西安城乡融合要素交易市场有限公司党支部&ldquo;城乡E+&rdquo;党建品牌。</span></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 32px; color: #ff0000;\"><strong>品牌名称</strong></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/06/03/0ea719e462f64cf39e2e7d226258937a.png\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 32px; color: #ff0000;\"><strong>指导思想</strong></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;以习近平新时代中国特色社会主义思想和党的二十大、党的二十届三中全会精神为指导，认真贯彻落实新时代党的建设总要求，创建特色鲜明、实效突出的党建特色品牌。全面提升党支部标准化、规范化建设，坚持围绕中心抓党建、抓好党建促业务，以高质量党建引领公司高质量发展，使二者在融合发展中相互促进。通过加强组织建设、发挥党员模范作用、创新党建工作机制，为城乡融合发展注入强大动力，为全面推进乡村振兴贡献力量。</span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 32px; color: #ff0000;\"><strong>品牌标识及释义</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp;&nbsp;</strong></span></p>\n<p><span style=\"font-size: 18pt;\">1.<strong><span style=\"color: #ff0000;\">城</span></strong>，代表城市，<strong><span style=\"color: #ff0000;\">乡</span></strong>，指乡村。</span></p>\n<p><span style=\"font-size: 18pt;\">2.<strong><span style=\"color: #ff0000;\">&ldquo;E&rdquo;</span></strong>有多重解读</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;(1) 代表&ldquo;电子化&rdquo;（Electronic），意味着利用互联网技术和数字化手段促进城乡之间的信息交流，实现城乡资源的高效连接；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;(2) 代表&ldquo;高效&rdquo;（Efficient），强调要素交易市场的高效服务在促进农民和村集体致富增收方面发挥的重要性；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;(3) 作为&ldquo;指数函数&rdquo;的象征，寓意着交易活跃度、市场规模、村民及村集体收入等均将呈指数级增长。</span></p>\n<p><span style=\"font-size: 18pt;\">3.<strong><span style=\"color: #ff0000;\">&ldquo;＋&rdquo;</span></strong>（加号）</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;一方面，表示交易平台通过信息发布、中介服务、竞价等服务，提高农村资源资产价值，推动农民和村集体致富增收；</span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;另一方面，&ldquo;+&rdquo;表示连接、融合之意，象征看城市与乡村之间的紧密联系与相互促进，通过资源共享、优势互补实现共同发展:</span></p>\n<p><span style=\"font-size: 18pt;\">4.<strong><span style=\"color: #ff0000;\">&ldquo;Cheng Xiang E Jia&rdquo;</span></strong></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;即&ldquo;城乡E+&rdquo;的拼音，采用了绿色作为底色，寓意着深入贯彻习近平总书记强调的&ldquo;绿色发展是高质量发展的底色&rdquo;。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/05/20/36b24e858e8748c0b6e22c758588c7c9.png\" /></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp;</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; 红色党旗</strong>位于整个logo最上方，突出党对公司中心卫作的绝对领导寓意我们永远跟党走，坚持党建引领，充分发挥支部战斗堡全作用和党员先锋模范作用：红色圆弧，寓意要素市场发展充满无限活力和希望。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/05/20/5963647d5cc44d53a0bbbd6272b5164a.png\" /></strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp;</strong></span></p>\n<p><span style=\"font-size: 18pt;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;莫比乌斯&ldquo;环&rdquo;</strong>展现了双重性的特点，象征着平台促进城乡之间的平等交换和双向流动。同时，它也其备无穷性，代表着各类交易品种的丰富多样，并将业务延伸至陕西周边市区，辐射整个西部地区，拥有无限的发展空间，黄色的曲线以小麦的抽象化图形设计，突显了农业主题，左侧的抽象交纵的线条为乡村田间的抽象图形化处理，代表农村，右侧图形为城市写字楼的抽象化图形化处理，代表城市。标志中间相交部分用小要连接的农村和城市，形象的表现出城乡融合要素交易市场。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; <strong>&nbsp;<span style=\"background-color: #ffff00;\">&ldquo;城乡E+&rdquo;作为支部的党建品牌，其名称和理念蕴含了丰富的含义。</span></strong></span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;&ldquo;城乡E+&rdquo;中的<span style=\"color: #ff0000;\">&ldquo;城乡&rdquo;</span>直接指向公司的主营业务范围，即促进城市与乡村之间的人、地、钱、技等多方面的交流与融合。这里的<span style=\"color: #ff0000;\">&ldquo;E+&rdquo;</span>巧借&ldquo;一家&rdquo;之谐音，旨在推进城乡一体化发展；同时，亦蕴含&ldquo;溢价&rdquo;之意，通过市场机制合理竞价，催生溢价效应，保障并提升农村资产资源价值。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp; 综上所述，&ldquo;城乡E+&rdquo;不仅体现了西安城乡融合要素交易市场有限公司党支部在党建工作中坚持服务城乡融合发展的宗旨，还展现了利用现代信息技术手段提升工作效率、增进社会福祉的美好愿景。这一品牌旨在通过党建引领，凝案力量，促进城乡资源的有效配置，实现互利其赢的局面。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 24pt; color: #ff0000;\"><strong>工 作 体 系</strong></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt; color: #000000; background-color: #ffff00;\"><strong>一个中心</strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">1.高质量党建引领保障 高质量发展</span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"background-color: #ffff00; font-size: 18pt;\"><strong><span style=\"background-color: #ffff00;\">两大作用 </span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">1.支部战斗堡垒作用 </span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">2.党员先锋模范作用</span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"background-color: #ffff00; font-size: 18pt;\"><strong><span style=\"background-color: #ffff00;\">三基建设 </span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">1.夯实基层组织</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">2.建强基层队伍</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">3.健全基础制度</span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"background-color: #ffff00; font-size: 18pt;\"><strong><span style=\"background-color: #ffff00;\">三个同步</span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">1.同步发力</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">2.同步提升</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\">3.同步培养</span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><span style=\"font-size: 24pt; color: #960303;\"> &nbsp;<span style=\"color: #ff0000;\"><strong>党旗领航&nbsp; 城乡交融</strong></span></span></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp;未来，要素交易市场党支部将充分发挥自身优势，紧密围绕乡村振兴战略，以促进城乡融合发展、破除城乡二元结构、拓展高质量发展空间为根本出发点和落脚点，坚持以壮大村集体经济、带动农民致富增收和推动城乡融合发展为目标，聚力城乡融合，推动资源要素在城乡之间平等交换、双向流动。 </span></p>\n<p>&nbsp;</p>\n<p><strong><img class=\"wscnph\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/05/20/53d723c9a23948e6a6f0e54487449dca.png\" /></strong></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 18pt;\"><strong>扫描上方二维码，观看精彩视频内容！</strong></span></p>', '501', 36, NULL, '“城乡E+”党建品牌正式发布!', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1198, '酒泉农发集团（寒旱经投集团）、湖北省农业农村厅莅临西安城乡融合要素交易市场有限公司调研考察', 999999, '1924994317463687169', 'sjfby', '882', '2025-05-21 09:00:28', '2025-05-21 09:08:15', '2025-05-21 09:00:28', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 1, 0, '<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;5月20日，酒泉农发集团（寒旱经投集团）、湖北省农业农村厅莅临西安城乡融合要素交易市场有限公司调研考察。西安农投集团党委委员、副总经理沈建锋出席。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<p>&nbsp;</p>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg0eQjR69vDq5KhxXWljOkI2T2mJHRQyoWx6KgEF7AT18QN4ppeoLiac2Q/640?wx_fmt=jpeg&amp;from=appmsg&amp;wxfrom=13&amp;tp=wxpic\" alt=\"图片\" width=\"1050\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg0eQjR69vDq5KhxXWljOkI2T2mJHRQyoWx6KgEF7AT18QN4ppeoLiac2Q/640?wx_fmt=jpeg&amp;from=appmsg\" data-ratio=\"0.562962962962963\" data-type=\"jpeg\" data-w=\"1080\" data-width=\"100%\" data-imgfileid=\"100036439\" data-original-style=\"width:100%;border-radius:initial;vertical-align:bottom;background-size:0px !important;background-color:transparent !important;\" data-index=\"1\" data-sec-load-status=\"2\" data-report-img-idx=\"0\" /></span></section>\n<section></section>\n</section>\n</section>\n<section>\n<section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;上午，酒泉农发集团（寒旱经投集团）党委委员、董事、副总经理李婧带队，赴西安农投集团考察调研。座谈会上，双方围绕集团重点项目、混改实践、城乡融合发展经验以及战略合作机制等内容展开了深入探讨，沈建锋强调，加强双方沟通交流意义重大，希望双方建立常态化沟通机制，并通过派驻业务骨干跟岗学习的方式，促进人才交流与经验共享，推动双方合作走深走实。</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg046nCZqicFfoeZSbXsbObtYTGAibiaReN0WyzlkgcnEZBbfw2UNhN9htjA/640?wx_fmt=jpeg&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" width=\"1050\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg046nCZqicFfoeZSbXsbObtYTGAibiaReN0WyzlkgcnEZBbfw2UNhN9htjA/640?wx_fmt=jpeg&amp;from=appmsg\" data-ratio=\"0.5619678334910123\" data-type=\"jpeg\" data-w=\"1057\" data-width=\"100%\" data-imgfileid=\"100036438\" data-original-style=\"width:100%;border-radius:initial;vertical-align:bottom;background-size:0px !important;background-color:transparent !important;\" data-index=\"2\" data-before-load-time=\"1747789277023\" data-report-img-idx=\"2\" data-fail=\"0\" /></span></section>\n</section>\n</section>\n<section></section>\n<section></section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;会后，调研组一行参观了西安城乡融合要素交易大厅，详细了解了平台运营模式、交易现状、服务体系等情况。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;当日下午，湖北省农村经济经营管理局局长张晓军带队，莅临西安城乡融合要素交易市场开展考察交流。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg04n3yPpic16lfGkj4e2CZ8pAhlOSU2xsKrQHt4Ua3ricvlxATS5YQfksQ/640?wx_fmt=jpeg&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" width=\"1050\" data-imgfileid=\"100036440\" data-ratio=\"0.562962962962963\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg04n3yPpic16lfGkj4e2CZ8pAhlOSU2xsKrQHt4Ua3ricvlxATS5YQfksQ/640?wx_fmt=jpeg&amp;from=appmsg\" data-type=\"jpeg\" data-w=\"1080\" data-width=\"100%\" data-original-style=\"width:100%;border-radius:initial;vertical-align:bottom;background-size:0px !important;background-color:transparent !important;\" data-index=\"3\" data-before-load-time=\"1747789277755\" data-report-img-idx=\"3\" data-fail=\"0\" /></span></section>\n</section>\n</section>\n<section></section>\n<section></section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;调研组观看了西安城乡融合要素交易市场宣传片，听取了关于平台架构、交易流程及典型案例的介绍。随后的座谈会上，要素交易市场负责人详细讲解了组建背景、过程及市委市政府政策支持情况，双方围绕交易流程、项目公告、农业融资等关键内容展开深度交流与探讨。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section>\n<section>\n<section><span style=\"font-size: 18pt;\"><img class=\"rich_pages wxw-img\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg0azibP2me90WgJRVLnvonX8q2c2xotcy9hG2QpTq7OwWchk0ic84J8iayQ/640?wx_fmt=jpeg&amp;from=appmsg&amp;tp=wxpic&amp;wxfrom=5&amp;wx_lazy=1\" alt=\"图片\" width=\"1050\" data-imgfileid=\"100036441\" data-ratio=\"0.562962962962963\" data-src=\"https://mmbiz.qpic.cn/mmbiz_jpg/AaMhzTYxfNhEHE4T5t4QdxWOVX9QGsg0azibP2me90WgJRVLnvonX8q2c2xotcy9hG2QpTq7OwWchk0ic84J8iayQ/640?wx_fmt=jpeg&amp;from=appmsg\" data-type=\"jpeg\" data-w=\"1080\" data-width=\"100%\" data-original-style=\"width:100%;border-radius:initial;vertical-align:bottom;background-size:0px !important;background-color:transparent !important;\" data-index=\"4\" data-before-load-time=\"1747789277871\" data-report-img-idx=\"4\" data-fail=\"0\" /></span></section>\n</section>\n</section>\n<section></section>\n<section></section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;调研组对西安城乡融合要素交易市场自成立以来所取得的成绩给予了高度认可。张晓军指出，西安城乡融合要素交易市场作为公开透明、阳光操作的平台，其在平台搭建、交易模式探索及监管机制等方面的宝贵经验值得借鉴和学习，此次考察为两地农业农村领域搭建起重要的交流合作桥梁，为湖北省相关工作开展提供了极具价值的参考样本。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;未来，西安城乡融合要素交易市场将持续加强跨省域合作，共享政策资源与实践经验，推动实现优势互补、经验共享、协同创新，不断探索多样化、多领域合作模式，实现互利共赢。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>\n<section>\n<section>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;酒泉市农发集团（寒旱经投集团）相关子公司、部门负责人，湖北省农业农村厅、武汉市农业农村局、武汉农业集团、武汉农村综合产权交易所相关单位主要、负责同志，西安农投集团相关部门负责人，西安城乡融合要素交易市场有限公司班子成员、相关部门负责人等参加调研。</span></p>\n<p>&nbsp;</p>\n</section>\n</section>', '101', 44, NULL, '酒泉农发集团（寒旱经投集团）、湖北省农业农村厅莅临西安城乡融合要素交易市场有限公司调研考察', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
