INSERT INTO `sys_article` (`id`, `article_title`, `article_weight`, `small_picture`, `create_username`, `create_userid`, `create_time`, `update_time`, `publish_time`, `status`, `article_author`, `article_source`, `charge_editor`, `article_type`, `has_video`, `put_on_top`, `home_carousel`, `make_headlines`, `article_content`, `column_value`, `view_num`, `file_path`, `abstract_text`, `tenders_id`, `pro_id`, `is_gszd`, `tenders_rule_history_id`, `village_code`, `pro_type`, `area`, `amount`, `pro_code`, `village_name`, `transfer_mode`, `tenders_code`, `dept_code`, `dept_name`) VALUES (1207, '“业务大练兵，实战见真章！城乡融合要素交易‘技能争霸赛’圆满收官”', 999999, '1925724909130059778', 'sjfby', '882', '2025-05-23 09:23:26', '2025-05-23 09:27:02', '2025-05-23 09:23:26', '2', 'sjfby', '西安城乡融合要素交易平台', '省级发布员', '100,101', '0', 0, 1, 0, '<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;<img class=\"wscnph\" src=\"https://www.xianchengxiangietp.com.cn:8001/znxd/2025/05/23/a1f540be1ab245b5b12deed8fcfa18f7.jpg\" /></span></p>\n<p><span style=\"font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;5月22日，一场别开生面的&ldquo;业务技能争霸赛&rdquo;在西安高新区丝路创智谷火热开战！由西安高新区农业农村和水务局联合西安城乡融合要素交易市场有限公司主办的&ldquo;高新区城乡融合要素交易业务知识大比武&rdquo;活动圆满落幕，来自全区10个街办的20名业务精英组成10支战队同台竞技，上演了一场专业与智慧的精彩碰撞。西安市农业农村局、秦农银行以及灞桥区、长安区、蓝田县、西咸新区农经站负责人、高新区相关领导、西安城乡融合要素交易市场有限公司相关领导等近百人现场观摩，共同见证这场以&ldquo;专业赋能&rdquo;助力城乡融合发展的知识盛宴。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-size: 18pt;\"><strong>以赛促学，实战练兵强本领</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;本次活动以&ldquo;强专业、促规范、提效能&rdquo;为主题，设置知识竞赛、案例分析、颁奖仪式三大环节，内容涵盖城乡融合要素交易相关的政策法规，以及业务受理、审核、结算等全流程操作规范，全面考察选手的政策理解深度与实际应用能力。</span></p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;比赛现场，必答题考验基本功，抢答题比拼反应速度，案例分析则模拟真实交易场景，考验选手的临场应变与团队协作能力。经过激烈角逐，最终丈八街道凭借扎实的业务知识和出色的临场表现拔得头筹，灵沼街道、庞光街道分获第二名，鱼化寨街道、细柳街道、秦渡街道荣获第三名，由西安市农业农村局、高新区管委会、要素交易市场及秦农银行领导为获奖团队颁发定制奖杯。</span></p>\n<p><br /><br /></p>\n<p><span style=\"font-size: 18pt;\"><strong>以学促干，赋能城乡融合发展</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;高新区农业农村和水务局副局长荆莉在致辞中强调，此次活动是深化城乡融合改革、激活要素市场活力的重要实践，要求业务人员以专业能力护航农村集体&ldquo;三资&rdquo;规范流转，助力乡村振兴。</span></p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;西安城乡融合要素交易市场有限公司相关负责人表示，公司将进一步完善交易平台功能，提升服务效能，为区域城乡融合发展提供坚实保障。</span></p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;活动还特别设置秦农银行业务宣讲环节，深入解读&ldquo;三资&rdquo;管理服务创新模式，为农村集体经济高效运转提供金融助力。</span></p>\n<p><span style=\"font-size: 18pt;\"><strong>以干促优，打造专业化服务标杆</strong></span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;&ldquo;这次比赛就像一面镜子，照出了我们的优势，也发现了需要提升的地方。&rdquo;丈八街道参赛选手王博在赛后采访时表示，&ldquo;我们提前两个月就开始系统梳理政策文件，组织模拟演练。这次能取得好成绩，关键是把政策吃透了，把流程摸熟了。接下来我们要把这些经验带回街道，组织村级业务培训，让基层工作人员都成为行家里手，为村集体资源入场交易把好关、护好航。&rdquo;</span></p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;参赛选手代表袁方表示：&ldquo;这次比武不仅是一次业务能力的&lsquo;大体检&rsquo;，更让我们在比拼中取长补短，学到了兄弟街办的先进经验，为今后的工作提供了新思路。&rdquo;</span></p>\n<p><span style=\"font-weight: 400; font-size: 18pt;\">&nbsp; &nbsp; &nbsp; &nbsp;此次大比武不仅是对业务人员能力的一次全面检验，更是城乡融合要素交易市场服务效能升级的重要契机。未来，要素交易市场将以此次比武成果为导向，持续优化系统功能、简化业务流程，并强化与街办、村集体的常态化对接机制，精准破解基层交易难题，为城乡融合发展注入更强动能！</span></p>', '101', 37, NULL, '“业务大练兵，实战见真章！城乡融合要素交易‘技能争霸赛’圆满收官”', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '01', '陕西城乡融合要素交易市场');
